INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `MaleText_lang`, `FemaleText_lang`, `VerifiedBuild`) VALUES
(123548, 'frFR', 'La Légion se dirige droit sur nous, il ne nous reste que quelques instants.\n\nL’onde de choc des énergies arcaniques que nous sommes sur le point de libérer devrait être assez puissante pour les renvoyer dans leur dimension maudite pendant un moment. Nous aurons ainsi le temps de reprendre notre souffle et de partir à la recherche des piliers de la Création.\n\nJ’attends votre signal.', '', 25549),
(122469, 'frFR', 'Retrouvez-moi à Dalaran, au-dessus du défilé de Deuillevent. J’ai un plan. Certes extrêmement dangereux… mais un plan quand même !', '', 25549),
(122467, 'frFR', 'Nous sommes assiégés, $n ! La Légion a bien l’intention de renverser Dalaran !', '', 25549);
