(97140, @GROUP_ID+4, @ID+0, 'I feel them. His memories.', 12, 0, 100, 0, 0, 67447, 112383, 'First Arcanist Thalyssra to Player'),
(97140, @GROUP_ID+5, @ID+0, 'The day he was exiled...', 12, 0, 100, 0, 0, 67449, 111663, 'First Arcanist Thalyssra to Player'),
(97140, @GROUP_ID+6, @ID+0, 'Dismiss these memories so we can proceed.', 12, 0, 100, 0, 0, 67450, 111813, 'First Arcanist Thalyssra to Player'),
