(97140, @GROUP_ID+15, @ID+0, 'Nnngh- ENOUGH!!', 12, 0, 100, 0, 0, 67461, 112350, 'First Arcanist Thalyssra to Player'),
(97140, @GROUP_ID+16, @ID+0, 'There... you found something, didn''t you?', 12, 0, 100, 0, 0, 67463, 118575, 'First Arcanist Thalyssra to Player'),
