INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
('39495','esES','Excelente. Has absorbido el poder del señor vil y te has vuelto más $gpoderoso:poderosa;.$B$BAhora podrenos concentrarnos en destruir a la Legión y a su Reina de la progenie.','22522');