INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES
(25073, 'ruRU', 'Деревня Сен''джин', 'Поговорите с мастером Гадрином в деревне Сен''джин.', 'Ты совсем $gмолодой:молодая;, но справляешься отлично. Не сомневаюсь, что еще не раз услышу твое имя.$B$BА теперь ступай на северо-запад через лощину и доберись до деревни Сен''джин. Найди в ней мастера Гадрина – думаю, он поручит тебе парочку заданий, прежде чем отправить в дебри Дуротара.$B$BПомни, $n, мы не всегда согласны с юным Адским Криком, но им движут благородные мотивы, и всех нас объединяет Орда. Ступай и не посрами Черное Копье.', '', '', '', '', '', '', 26365),
(24814, 'ruRU', 'Древний враг', 'Поговорите с Вол''джином в гроте Злобной Чешуи и помогите ему убить морскую ведьму Зар''джиру.', 'На острове объявилась морская ведьма. Мы столько ждали шанса отомстить ей, и сейчас эта честь выпала тебе.$B$BОтправляйся на северо-восток и встреться с Вол''джином. Дай ему знать, когда будешь $gготов:готова; вступить в бой.', '', 'Морская Ведьма, убившая отца Вол''джина и прогнавшая троллей с родных островов, вернулась, и Вол''джин наконец может ей отомстить.', 'Зар''джира', '', '', '', 26365),
(8468, 'ruRU', 'Разыскивается: Таэлис Ненасытный', 'Принесите голову Таэлиса сержанту Ка''рену на площадь Соколиных Крыльев в лесах Вечной Песни.', 'Город Луносвет назначает награду за голову Таэлиса Ненасытного, известного главаря Презренных. Он разыскивается за то, что неоднократно совершал преступления против жителей города Луносвета, в том числе убил двух городских стражников.$b$bОтносительно награды осведомляться у сержанта Ка''рена.', '', '', '', '', '', '', 26365),
(25170, 'ruRU', 'Зачистка побережья', 'Добудьте 5 пригоршень слизи крабов.', 'Эй, $gприятель:подруга;! Бом''бей работает над новым настоем. Хочешь глотнуть? Только прежде собери кое-какие штучки для Бом''бея.$b$bПрежде всего, Бом''бею нужно немного слизи подводных крабов. Покажи-ка мне свои руки!$b$b<Бом''бей внимательно изучает ваши ладони.>$b$bБом''бей хочет, чтобы ты $gпринес:принесла; 5 пригоршень. Поторопись!', '', '', '', '', '', '', 26365),
(25236, 'ruRU', 'Подводное электричество', 'Обвяжите магической веревкой 8 утонувших рокочущих ящериц на Громовом хребте.', 'Нашей армии жизненно необходима вода, и нам повезло – разбушевавшиеся стихии после Катаклизма вдоволь обеспечили нас ею. Вода, заполнившая каньон на юго-западе, вполне сгодится.$b$bК сожалению, трупы рокочущих ящериц до сих плавают по поверхности воды, а их электрические разряды весьма опасны. У меня хватает рядовых, чтобы вытащить их из воды, но нужен кто-то достаточно ловкий для того, чтобы привязать к тушам веревку. Ну что, не о тебе ли я говорю?', '', '', '', '', '', '', 26365),
(24812, 'ruRU', 'Пощады не будет', 'Убейте 12 наг Злобной Чешуи.', 'Наги принесли много горя нашему народу. Они не заслуживают пощады.$B$BОтправляйся в пещеру и убей всех наг, которых встретишь. Чем меньше их останется, тем меньше вероятность их дальнейшего нападения. Не сомневаюсь, что ты не пропустишь ни одной.', '', '', '', '', '', '', 26365),
(24813, 'ruRU', 'Территориальный амулет', 'Разместите 8 территориальных амулетов на флагах Злобной Чешуи.', 'Это не просто контратака. Мы намерены уничтожить всех оставшихся наг и обезопасить себя от их возвращения.$B$BРазмести эти амулеты на каждом флаге Злобной Чешуи, которые найдешь в пещере. Это четко даст понять нагам, как мы относимся к их присутствию. Кроме того, магия, заключенная в амулетах, подорвет силы любого приблизившегося к ним представителя Злобной Чешуи. Пусть прочувствуют наше гостеприимство...', '', '', '', '', '', '', 26365),
(25035, 'ruRU', 'Прорыв обороны', 'Поговорите с Йорнуном, чтобы отправиться в грот Злобной Чешуи, а там найдите Моракки.', 'Вол''джин приказал начать крупномасштабную атаку на наг, оккупировавших наш северный остров. Многие уже вступили с ними в бой у моста, но несколько бойцов должны прорвать их оборону и пробраться вглубь острова.$B$BПоговори с Йорнуном, чтобы отправиться на остров, а там найди капитана дозора Моракки. Он расскажет, чем ты можешь помочь.', '', '', '', '', '', '', 26365),
(24626, 'ruRU', 'Молодой и горячий', 'Поймайте Стремительного Когтя и приведите его в стойла возле крепости Черного Копья.', 'Ты молодец, $n.$B$BМорайа давно просила привести ей новых ящеров, пора выполнить эту просьбу! Я уже направила других помощников на поиски ящеров, но есть один особенный зверь, которого я давно обещала Морайе.$B$BСтремительный Коготь – самый быстрый и храбрый из молодых ящеров, правда, немного непослушный. Сейчас он бродит по этому острову. Возьми лассо, поймай Стремительного Когтя и приведи в стойла на главный остров. Но знай: будет нелегко!', '', '', '', '', '', '', 26365),
(24974, 'ruRU', 'Невыносимое одиночество', 'Накиньте поводок на мурлока из племени Злобного Плавника, а затем приведите его Седрику Калстону в поместье Калстона.', 'Вот представь: работаешь ты усердно всю свою жизнь – и что потом? Умираешь от чумы, переходишь в ряды нежити, работаешь еще усердней. А потом что? Да, накопишь деньжат, купишь небольшой домик, познакомишься с парочкой надоедливых соседей... А счастье? Его-то впрок не напасешься.$b$bЯ чувствую себя одиноким стариком, $n. Мне нужен спутник. Вот, возьми этот поводок и приведи мне одного из мурлоков с Шепчущего берега на северо-западе. Запомни: пока мурлок полон сил, он не даст накинуть на себя поводок. Поэтому тебе придется сначала измотать его, а потом уже привести ко мне.', '', '', '', '', '', '', 26365),
(14438, 'ruRU', 'Недобрые соседи', 'Убейте 15 гноллов из стаи Бледногривов и возвращайтесь к Ахмо Громовому Рогу в деревню Кровавого Копыта.', '$n, нашей земле причиняют боль раздоры ее обитателей. Я надеюсь, что тебе не суждено увидеть столько войн, сколько выпало мне. И все же я вынужден просить тебя вступить в бой. Так ты поймешь, что малая угроза, если не искоренить ее, со временем разрастается.$B$BГноллы-бледногривы поселились на юге от нашей деревни и в пещере на западе. Они не обращают внимания на наши попытки договориться и без жалости убивают животных Мулгора.$B$BСловами их не остановить.', '', '', '', '', '', '', 26365),
(24625, 'ruRU', 'Спутник морской ведьмы', 'Убейте Наж''тесса и заберите его сферу.', 'Кажется, мне удалось понять, кто оскверняет наших детенышей. Это некий нага. Я видел, как он поднимал над малышами какую-то красную сферу, и они... начинали меняться.$B$BПолагаю, он уверен, что о его проделках никто не знает, поэтому ты сможешь застать его врасплох. Отправляйся в Падение Залазана на северо-запад отсюда, убей этого нагу и принеси мне его сферу.', '', 'Наж''тесс надеялся обратить союзников троллей против них, но просчитался.', 'Наж''тесс', '', '', '', 26365),
(24624, 'ruRU', 'Жест милосердия', 'Убейте 8 оскверненных кровопалых ящеров.', 'Тот, кто украл наших ящеров, осквернил их какой-то магией. И теперь нам ничего не остается, кроме как помочь им упокоиться с миром. Отправляйся в Падение Залазана, что к северо-западу отсюда, и доведи дело до конца.$B$BНадеюсь, тебе по плечу эта работа. Я бы точно не смог это сделать, если даже нет другого выхода.', '', '', '', '', '', '', 26365),
(24623, 'ruRU', 'Спасение детенышей', 'Спасите 12 потерянных кровопалых детенышей с помощью свистка Кровопалых.', 'У меня есть одно важное дело, $gприятель:подруга;.$B$BКто-то с соседнего острова ворует наших детенышей. Сначала я думала, что они сами убегают, но сейчас мы не досчитались более десятка.$B$BПожалуйста, отправляйся в Падение Залазана, что к северо-западу отсюда, и приведи детенышей назад. Бедные малыши...', '', '', '', '', '', '', 26365),
(25037, 'ruRU', 'Ловля крабов', 'Убейте карликовых приливных крабов и добудьте 5 порций свежего крабового мяса.', 'Похоже, на старости лет мой организм забастовал – кабанину уже не принимает. Видать, она только для молодых хороша.$B$BМы уже расставили в округе несколько ловушек для крабов, но мне просто не терпится – хочется уже сейчас насладиться свежим крабовым мясом. Может, выполнишь прихоть старика? Крабов полно по всему острову, но лучше всего ловить их на восточном побережье.', '', '', '', '', '', '', 26365),
(24759, 'ruRU', 'Азы мастерства: поражение цели', 'Уничтожьте 6 манекенов-тики.', 'Ну ладно, $gдружище:подруга;. Начнем с самого простого. Хочу убедиться, что ты владеешь азами тайных искусств.$B$BДавай-ка попробуем поразить эти манекены-тики. Не торопись. Делай, как получится. А я посмотрю, насколько ты умеешь творить заклинания.', '', '', '', '', '', '', 26365),
(24758, 'ruRU', 'Возрождение Черного Копья', 'Повстречайтесь с Некали на тренировочной площадке Черного Копья.', 'Племя Черного Копья стало жертвой обстоятельств. Мы попали в изгнание. Раз за разом мы были вынуждены покидать свои дома... Однако на сей раз мы не сбежим как трусы.$B$BВол''джин всегда мудро правил племенем и долгое время оберегал нас от невзгод. Сейчас же его мудрость подсказывает ему, что под началом Гарроша Адского Крика Орду ждет непонятное будущее.$B$BВ этот раз мы не сбежим. Мы будем сражаться за свой дом до конца.$B$BПора начать твое обучение, $n. Отправляйся на восток и поговори с Некали.', '', '', '', '', '', '', 26365);
