INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES
(24814, 1, 0, 0, 0, 0, 0, 0, 0, 'В момент смерти морской ведьмы высвободилось огромное количество энергии, и теперь с ее помощью я смогу увидеть отдаленные земли.$B$BЯ собираюсь связаться с Траллом и хочу, чтобы ты $gувидел это сам:увидела это сама;. Может, это откроет тебе глаза на многое.', 26365), -- 24814
(24813, 1, 0, 0, 0, 0, 0, 0, 0, 'Теперь они не посмеют вернуться.', 26365), -- 24813
(24812, 1, 0, 0, 0, 0, 0, 0, 0, 'Ты молодец, $gюный:юная:r; |3-6($r).', 26365), -- 24812
(25035, 1, 0, 0, 0, 0, 0, 0, 0, 'Здорово, что ты с нами, $gбратишка:сестренка;.$B$BВ такие минуты начинаешь гордиться, что служишь Вол''джину, и хочется шагнуть прямо навстречу опасности.', 26365), -- 25035
(24626, 0, 0, 0, 0, 0, 0, 0, 0, 'Стремительный Коготь! Киджара говорила, что с ним хлопот не оберешься. Но я не против трудностей – из ящеров с характером получаются самые преданные и храбрые спутники.$B$BСпасибо, $gдорогой:дорогуша;. Ты мне здорово $gпомог:помогла;.', 26365), -- 24626
(24625, 1, 273, 0, 0, 0, 60, 0, 0, 'Молодец, $n! Этот нага нам здорово помешал, но никто не может сдерживать нас вечно.$B$BХорошо, что ты с нами, $n. У тебя горячее сердце.', 26365), -- 24625
(24624, 1, 0, 0, 0, 0, 0, 0, 0, 'Спасибо, |3-6($c).$B$BИменно такие храбрецы, как ты, смогут возродить Черное Копье. Я надеюсь, наши ряды пополнятся теми, кто хотя бы вполовину силен, как ты.', 26365), -- 24624
(24623, 1, 0, 0, 0, 0, 0, 0, 0, 'Большое спасибо! Они бывают агрессивны, если их рассердить, но большую часть времени такие милые.', 26365), -- 24623
(24622, 3, 0, 0, 0, 0, 0, 0, 0, 'Я всегда рада новым лицам. Мы тут будто в изоляции, с тех пор как ушли из Оргриммара.', 26365), -- 24622
(25037, 0, 0, 0, 0, 0, 0, 0, 0, 'Ммм, как вкусно. Спасибо тебе за доброту. Ты $gзаслужил:заслужила; расположение духов.', 26365), -- 25037
(25064, 0, 0, 0, 0, 0, 0, 0, 0, 'Привет, $gдорогой:дорогуша;. Тебя прислал Вол''джин? Как мило с его стороны.$B$BДа, помощь мне нужна, и на месте тебе сидеть не придется.', 26365), -- 25064
(24763, 6, 0, 0, 0, 0, 0, 0, 0, 'Так ты и есть $gтот новенький:та новенькая;, о $gкотором:которой; все говорят? Добро пожаловать в Черное Копье.', 26365), -- 24763
(24762, 4, 0, 0, 0, 0, 0, 0, 0, 'А ты молодец. Когда-нибудь станешь очень $gсильным:сильной;.', 26365), -- 24762
(24761, 1, 0, 0, 0, 0, 0, 0, 0, 'Хорошие шкуры, $gмалыш:малышка;. Из них можно справить отличные плащи.', 26365), -- 24761
(24759, 1, 0, 0, 0, 0, 0, 0, 0, 'Неплохо, $gдружище:подруга;. У тебя прирожденные способности к магии. Прямо-таки настоящий талант.$B$BКонечно, тебе предстоит потрудиться, чтобы овладеть магией по-настоящему. Но я тебя научу.', 26365), -- 24759
(24758, 6, 5, 0, 0, 0, 60, 0, 0, 'Еще один новобранец?$B$BВид у тебя бывалый.И глаза мудрые. Может, ты и вправду $gнастоящий:настоящая:c; |3-6($c).$B$BНе будем терять время.', 26365); -- 24758
