INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES
(14348, 'ruRU', 'Одному не справиться', 'Воспользуйтесь бочонками с черным порохом, чтобы взорвать 4 кошмарных поганища.', 'Смотри и запоминай, $n. Труднее всего справиться с этими большими толстыми тварями – поганищами, так что не нападай на них в одиночку.$B$BПравда, у меня есть одна идея, настолько безумная, что это может сработать. Должен тебя предупредить: это связано с риском для жизни.$B$BВидишь бочки с порохом, стоящие под навесом и рядом с мельницами? Хватай их и кидай прямо на головы поганищ. А дальше я с ними сам разберусь с помощью верного мушкета.', '', '', '', '', '', '', 26365),
(14347, 'ruRU', 'Держать строй!', 'Убейте 10 Отрекшихся-захватчиков.', '$n! Я все еще не знаю, $gжив ты или мертв:жива ты или мертва;... человек ты или ворген...$B$BДа что там говорить – я даже не уверен, что это все происходит на самом деле. Но одно я знаю точно – сегодня мы убьем множество этих чертовых Отрекшихся.', '', '', '', '', '', '', 26365),
(14336, 'ruRU', 'Убивай или будешь убит', 'Поговорите с принцем Лиамом Седогривом. Вы найдете его рядом с Темной Гаванью.', 'Раньше рифы защищали нас от нападения с моря, но теперь землетрясения открыли путь кораблям Отрекшихся.$B$BБыстрее, $n. Мы должны замедлить продвижение врага и оповестить оставшихся ополченцев.$B$BКажется, я слышу голос принца и его людей, сражающихся с нежитью. Спроси Лиама, чем ты можешь ему помочь.$B$BДа... и постарайся защитить его. Боюсь, что в его теперешнем состоянии он несколько беспечен.', '', '', '', '', '', '', 26365),
(14321, 'ruRU', 'Вторжение', 'Поговорите с Гвен Армстед в Темной Гавани.', '<В груди ополченца торчит длинный кинжал с черепом на рукоятке. Оглядевшись, вы замечаете на побережье два боевых корабля устрашающего вида.>$B$B<Гилнеас под осадой! Об этом надо срочно доложить мэру Темной Гавани Гвен Армстед.>', '', '', '', '', '', '', 26365),
(14320, 'ruRU', 'В поисках ингредиентов', 'Найдите ящик с эссенцией мандрагоры.', 'Ах, да. Насколько нам известно, последствия Проклятия невозможно снять полностью.$B$BОднако с помощью лечения ты сможешь контролировать свой разум... и держать зверя на привязи.$B$BТы не представляешь, как мы рады видеть, что лекарство тебе помогло. Обычно мне удавалось вылечить только недавно заразившихся.$B$BТеперь мы должны продолжить курс лечения – если, конечно, ты хочешь обратить эффекты Проклятия.$B$BДля новой порции лекарства мне понадобится эссенция мандрагоры – к счастью, на юго-западе от города, под навесом еще остались ящики с этим снадобьем.', '', '', '', '', '', '', 26365),
(14313, 'ruRU', 'Снова среди людей', 'Поговорите с Креннаном Аранасом в Темной Гавани.', 'Так снадобье Креннана тебя не прикончило? Надо же, значит, твоя человеческая часть души победила.$B$BПожалуй, я не стану тебя убивать – во всяком случае, не сейчас.$B$BИди, сообщи хорошие новости Креннану Аранасу. Он сейчас в том доме, где мы держим таких, как ты.$B$BНо помни, $n: я за тобой слежу. Только попробуй выкинуть какой-нибудь фокус – сразу получишь пулю промеж глаз.', '', '', '', '', '', '', 26365),
(14375, 'ruRU', 'Последняя капля человечности', '', '', '', '', '', '', '', '', 26365);
