-- Quest: Sacrifices

-- Reverting temporal fix made by @cooler-SAI over the quest Sacrifices
-- The Horse had a gossip_menu to teleport the player to the other side(temporaly), this is no longer needed, because with this patch the quest works correctly.
UPDATE `creature_template` SET `gossip_menu_id` = 0, `npcflag` = 0 WHERE `entry` = 44427;

-- Remove the SAI from the Horse, no longer needed
SET @ENTRY := 44427;
UPDATE `creature_template` SET `AIName`='' WHERE `entry`=@ENTRY;
DELETE FROM `smart_scripts` WHERE `entryorguid`=@ENTRY AND `source_type`=0;

-- Reverting to the correct amount of tarjet
UPDATE `quest_objectives` SET  `Amount`='30' WHERE (`ID`='267253');

-- Changing from ScriptName to SAI of crowley horse
UPDATE `creature_template` SET `AIName`='SmartAI', `spell1`='67063', `ScriptName`='' WHERE (`entry`='35231');

-- SAI For crowly horse
DELETE FROM `smart_scripts` WHERE `entryorguid` in (35231,3523100);
