INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES
(50432, 'esMX', 'Innovación titánica', '', '', '', '', '', '', '', '', 26365),
(50374, 'esMX', 'La sangre de Azeroth', 'Entrega el mensaje de Magni Barbabronce.', '¿Acaso los líderes de la Alianza y la Horda saben por qué están peleando?\n\n¡Esta sustancia que brota de la herida es la fuerza vital de Azeroth!\n\nDebemos sanar la herida, ¡no explotarla para obtener poder! Azeroth es el único hogar que tenemos. ¡No podemos permitir que muera!\n\nConfío en ti, $n, y sé que los miembros de la Alianza respetan tu opinión. ¡Debes hacer que entren en razón para que detengan esta locura!\n\nEs la única esperanza que tiene nuestro mundo...', '', '', '', '', '', '', 26365),
(50373, 'esMX', 'Una llegada reciente', 'Habla con el archimago Khadgar.', 'Parece que la situación de Silithus atrae cada vez más atención.\n\nEl archimago Khadgar llegó mientras no estabas. Creo que está aquí para hablar contigo, $n.\n\nParece que se trata de algo urgente. Te recomendaría que hables con él cuanto antes.', '', '', '', '', '', '', 26365),
(50372, 'esMX', 'Investigación del desierto', 'Habla con Tammy Girojalata.', 'La espada oscura de Sargeras echa una sombra de destrucción sobre esta tierra.\n\nParecía que el ataque había acabado con las colmenas de silítidos, pero he recibido informes de que los silítidos están regresando.\n\nTenemos que descubrir cómo evitaron la destrucción y cómo afecta la espada a sus colmenas.\n\nLa corona quiere que se diseccione y se estudie a los silítidos que sobrevivieron.\n\nTammy Girojalata está a cargo de la investigación y necesita un \"recolector\".', '', '', '', '', '', '', 26365),
(50371, 'esMX', 'Convocatoria en Ventormenta', 'Reúnete con Mathias Shaw en Ventormenta.', 'El rey Wrynn ha pedido que regreses a Ventormenta cuanto antes. Hay un asunto urgente al que debes atender.\n\nTe contaré los detalles apenas llegues.\n\nEstaré en el Reposo del León. Apresúrate... ¡se nos agota el tiempo para actuar!', '', '', '', '', '', '', 26365),
(50364, 'esMX', 'La sangre de Azeroth', 'Entrega el mensaje de Magni Barbabronce.', '¿Acaso los líderes de la Alianza y la Horda saben por qué están peleando?\n\n¡Esta sustancia que brota de la herida es la fuerza vital de Azeroth!\n\nDebemos sanar la herida, ¡no explotarla para obtener poder! Azeroth es el único hogar que tenemos. ¡No podemos permitir que muera!\n\nConfío en ti, $n, y sé que los miembros de la Horda respetan tu opinión. ¡Debes hacer que entren en razón para que detengan esta locura!\n\nEs la única esperanza que tiene nuestro mundo...', '', '', '', '', '', '', 26365),
(50360, 'esMX', 'El pedido de Khadgar', 'Habla con el archimago Khadgar.', 'El archimago Khadgar llegó mientras no estabas. Parece que está aquí para hablar contigo, $n.\n\nApresúrate a ver qué necesita. Lo quiero fuera de aquí cuanto antes.\n\n¡Lo último que necesitamos es un humano hurgando en asuntos de la Horda!', '', '', '', '', '', '', 26365),
(50358, 'esMX', 'Investigación del desierto', 'Habla con Pixni Explóxida.', 'La espada oscura de Sargeras echa una sombra de destrucción sobre esta tierra.\n\nParecía que el ataque había acabado con las colmenas de silítidos, pero he recibido informes de que los silítidos están regresando.\n\nSi de mí dependiera, ¡quemaría todas esas colmenas despreciables de una vez por todas! Sin embargo, no depende de mí.\n\nLa Dama Oscura quiere que se diseccione y se estudie a los silítidos que sobrevivieron.\n\nPixni Explóxida está a cargo de la investigación, y necesita un \"recolector\".', '', '', '', '', '', '', 26365),
(50341, 'esMX', 'Un descubrimiento reciente', 'Habla con Nathanos Clamañublo.', 'Hace unas horas, los líderes de la Horda se reunieron para celebrar la victoria sobre la Legión Ardiente con un festín.\n\nMientras todos se deleitaban con la comida, Gallywix solicitó una audiencia privada con la Dama Oscura.\n\nParece que ha encontrado una sustancia misteriosa en Silithus... una mena mágica que no se parece a nada que hayamos visto antes. \n\nLa jefa de guerra me ha ordenado que te cuente los detalles para prepararte para esta misión especial.', '', '', '', '', '', '', 26365),
(50338, 'esMX', 'El Equipo G', 'Mata al radiactivo viscoso.', '¿Cómo has llegado aquí, $n?\n\nSoy Murd Doc, del Equipo G, y no me vendría mal la ayuda de $Gun:una; $r como tú.\n\nSi nos ayudas a derrotar a los habitantes irradiados que ahora llaman a Gnomeregan su hogar, ¡te convertiré en miembro honorario del Equipo G!\n\nToma este paracaídas, salta a la Sala de Máquinas y mata al radiactivo viscoso. Cuando hayas terminado, informa al comando M. E. Barrecus en el pasillo oeste. Se van a llevar muy bien, estoy seguro.', '', '', '', '', '', '', 26365),
(50337, 'esMX', 'El Equipo G', 'Mata al Electrocutor 6000.', 'Ese Murd Doc está loco, $c. Nunca jamás te subas a un avión con ese gnomo.\n\nEl sigilo no suele ser lo mío, y está claro que no va a ser lo tuyo. Baja por este pasillo hasta el Aeropuerto y deshazte del Electrocutor 6000..\n\nEl Electrocutor está al cargo de los aviones....\n\n... Y yo odio los aviones. ¡No pienso subir a ningún tipo de avión!.\n\nDespués utiliza este paracaídas para dejarte caer y habla con el comando Fénix para comenzar tu siguiente misión.', '', '', '', '', '', '', 26365),
(50336, 'esMX', 'El Equipo G', 'Mata al mekigeniero Termochufe.', 'Yo me alejaría de M. E. Barrecus y de Murd Doc, ninguno de los dos es especialmente estable.$b$b¡Manos a la obra! Baja por este túnel hasta que llegues a la Cámara Manitas. Espero que sepas qué hacer cuando veas al mekigeniero Termochufe.\n\nNuestro líder, el comando Hann Ibal, te espera por allí. Te recompensará generosamente por tu trabajo.\n\nQuizás...', '', '¡Cuidado con las bombas!', 'Mekigeniero Termochufe', '', '', '', 26365),
(50335, 'esMX', 'Quiero mi venganza', 'Derrota a Randolph Moloch, Lord Quemado y Hogger.', 'No me importa a quién hayas jurado fidelidad, $r. ¡Necesito a Hogger muerto!\n\nÉl, y los demás cretinos de este lugar abandonado por la Luz, han asesinado a mis hombres. Puede que la Alianza y la Horda no estén de acuerdo en todo, pero no puedo permitir que se siga matando a sangre fría.', '', '', '', '', '', '', 26365),
(50319, 'esMX', 'Forastero en tierra extraña', 'Busca el tablón de mando del Jefe de Guerra en Orgrimmar.', 'Hola. Soy el embajador Mortharón.\n\n<Mortharón mira un papel que tiene en la mano.>\n\nEn nombre de la jefa de guerra, es un honor darte la bienvenida a Orgrimmar, $r.\n\n<Te lanza una sonrisa poco convincente y vuelve a mirar el papel.>\n\nTe esperan un sinfín de aventuras y peligros que... ¡agh!\n\n¿Sabes qué? Estuve repitiendo la misma bienvenida todo el día. Ya tuve suficiente.\n\nHabla con tu emisario y averigua dónde puedes seguir tus aventuras. Y ahora, vete.', '', '', '', '', '', '', 26365),
(50316, 'esMX', 'Perturbación detectada: Ulduar', 'Vuelve a vivir la expedición a Ulduar.', 'Llegas tarde. ¿No recuerdas la última vez que tuviste que encargarte de los problemas que permitieron que Yogg-Saron escapara de su prisión debajo de Ulduar? Esa vez también llegaste tarde. Deberías ser un poco más puntual.\n\n¡Vamos, anda! ¡Dioses antiguos! ¡Guardianes! ¡Titanes! Quítatelo de la lista rápido, así podemos seguir adelante. Tenemos muchos lugares y épocas que visitar.\n\n<Vormu, impaciente, hace gestos para que te retires.>', '', '', '', '', '', '', 26365),
(50313, 'esMX', 'Forastero en tierra extraña', 'Busca el tablero de ¡Se busca héroe! en la Ciudad de Ventormenta.', '$gBienvenido:Bienvenida; a Ventormenta, $r. El rey Anduin Wrynn te invita a explorar nuestra bella ciudad. Tu aspecto me dice que eres $gun hombre devoto:una mujer devota;. ¡Tenemos una catedral preciosa que sin duda te interesará visitar!\n\nAh, pero eso puede esperar. Un emisario de los Templeluz te espera para contarte todas las aventuras que te esperan más allá de los muros de la ciudad.\n\nDisfruta tu estadía, $n. Ah, y disculpa los desórdenes ocasionales. ¡Me temo que muchos de los ciudadanos no comparten la disciplina que caracteriza a los soldados del Ejército de la Luz!', '', '', '', '', '', '', 26365),
(50305, 'esMX', 'Forastero en tierra extraña', 'Busca el tablero de ¡Se busca héroe! en la Ciudad de Ventormenta.', '$gBienvenido:Bienvenida; a Ventormenta, $r. El rey Anduin Wrynn te invita a explorar nuestra bella ciudad. Tu aspecto me dice que te gusta la vida nocturna. ¡Tenemos varias tabernas famosas y lugares para escuchar música llenos de vida! \n\nAh, pero eso puede esperar. Un emisario de los ren\'dorei te espera para contarte todas las aventuras que te esperan del otro lado de los muros de la ciudad.\n\nDisfruta tu estadía, $n. Ah, y un consejo amistoso.... harías bien en controlar tus poderes sombríos. ¡No todos en Ventormenta están tan iluminados como yo!', '', '', '', '', '', '', 26365),
(50303, 'esMX', 'Forastero en tierra extraña', 'Busca el tablón de mando del Jefe de Guerra en Orgrimmar.', 'Hola. Soy el embajador Mortharón.\n\n<Mortharón mira un papel que tiene en la mano.>\n\nEn nombre de la jefa de guerra, es un honor darte la bienvenida a Orgrimmar, $r.\n\n<Te lanza una sonrisa poco convincente y vuelve a mirar el papel.>\n\nTe esperan un sinfín de aventuras y... ¡agh!\n\n¡Basta! Estoy cansado de repetir esta bienvenida todos los días, una y otra vez.\n\nVe y averigua dónde puedes luchar y habla con tu emisario.\n\nY, por favor, déjame en paz.', '', '', '', '', '', '', 26365),
(50254, 'esMX', 'Un segundo aliado para la causa', 'Elige un segundo aliado.', 'No nos vendría mal un nuevo aliado. Con dos aliados, seremos aún más fuertes.\n\nIncluso ahora, la Alianza idea planes para atacarnos. Debemos apresurarnos, o nos asaltarán antes de que estemos preparados para contraatacar.\n\nAvísame cuando estés $Glisto:lista; para buscar al segundo aliado. No es momento de vacilar.', '', '', '', '', '', '', 26365),
(50248, 'esMX', 'Un segundo aliado para la causa', 'Elige un segundo aliado.', 'Has dado un primer paso importante para fortalecer a la Alianza, $n. Bien hecho.\n\nPero me temo que un solo aliado no amedrentará a la Horda. La jefa de guerra no descansará hasta que las fuerzas de Orgrimmar superen vastamente a los defensores de Ventormenta.\n\nDebo pedirte que marches para sellar una nueva alianza. No podemos demorarnos.', '', '', '', '', '', '', 26365),
(50247, 'esMX', 'El correo nunca se detiene', 'Ayuda al jefe de correos a clasificar al menos 15 cartas dentro del límite de tiempo.', 'Con todas las locuras de estos días, casi ya no tengo elementales postales para clasificar las cartas.\n\nEs un trabajo sencillo: arroja las cartas al portal adecuado y llegarán a los confines más remotos de Azeroth y más allá.\n\nMe gusta hacerlo como si fuera un juego. ¿Cuántas podrás clasificar en un minuto?', '', '', '', '', '', '', 26365),
(50242, 'esMX', 'Elección de aliados', 'Habla con Baine y Liadrin para obtener más información sobre los posibles aliados.', 'Baine Pezuña de Sangre y Lady Liadrin han venido a sugerir posibles aliados que podrían fortalecer nuestras filas. Los dos saben muy bien lo que se necesita para preservar la fuerza de la Horda.\n\nHas demostrado ser $gun campeón:una campeona; formidable, $n. Confío en ti para decidir dónde deberíamos concentrar primero nuestros esfuerzos.\n\nEscucha lo que tengan para decir y toma una decisión. Luego, ven a informarme para que podamos proceder con nuestro plan de fortalecer a la Horda.', '', '', '', '', '', '', 26365),
(50239, 'esMX', 'Elección de aliados', 'Habla con Turalyon y Alleria para obtener más información sobre los posibles aliados.', 'El Alto exarca Turalyon y Alleria Brisaveloz se han presentado para sugerir posibles aliados para incorporar en nuestras filas. Estos héroes han dedicado sus vidas a defender nuestro mundo, y confío en su consejo.\n\nTú has sido $gun campeón:una campeona; incondicional de la Alianza, $n. Confío en ti para decidir en qué deberíamos concentrarnos primero.\n\nEscucha sus planteos y toma una decisión. Luego infórmame a quién crees que deberíamos buscar como aliados en primer lugar.', '', '', '', '', '', '', 26365),
(50232, 'esMX', 'El sobreviviente crepuscular', 'Consigue la cabeza de Michiel Zancavacío.', 'En otros tiempos, estas tierras estaban plagadas de miembros del Martillo Crepuscular.\n\nSe creía que el culto había sido exterminado, pero la oscuridad siempre sobrevive en las sombras.\n\nLa mayoría de los cultistas fueron destruidos por Sargeras durante el ataque, pero unos pocos siguen con vida. Su líder, Michiel Zancavacío, se siente más osado con la destrucción de Silithus.\n\n¡No podemos permitir que este fanático reúna a los sobrevivientes en torno a un nuevo propósito! ¡Tráeme su cabeza!', '', '', '', '', '', '', 26365),
(50231, 'esMX', 'Larvas por docena', 'Recoge $1oa crías silítidas.', '¿Cómo sobrevivieron los silítidos a la espada de Sargeras?\n\n¡Nunca vi una especie que pudiera adaptarse a su entorno tan rápido como ellos!\n\n¡Debe haber alguna forma de domesticar a estos tontos! ¡Podríamos controlar su biología evolutiva para nuestro beneficio!\n\nDebemos empezar a entrenarlos desde jóvenes. Olvídate de esos viejos adultos cascarudos, ¡necesitamos bebés!\n\n¡Roba todas las larvas silítidas que puedas, y yo buscaré la forma de entrenarlas!', '', '', '', '', '', '', 26365),
(50230, 'esMX', 'La fuente de poder', 'Recoge $1oa cerebros silítidos.', '¡Los silítidos han enloquecido, y están más fuertes que nunca!\n\nSupongo que se alimentan de una fuente de poder subterráneo. ¡Seguramente cerca de la espada de Sargeras!\n\nMe han encomendado la tarea de estudiar los efectos de este misterioso poder sobre los silítidos.\n\n¡Tráeme todos los cerebros de silítidos intactos que puedas y yo me encargaré de la investigación!', '', '', '', '', '', '', 26365),
(50229, 'esMX', 'Un retacito de tela', 'Recoge $1oa retazos de seda crepuscular.', '$n, ¿eres tú $gel:la; que está haciendo ruido? ¡Soy yo, Angus Cebatormenta!\n\nHay cultistas por todas partes, así que será mejor que bajes la voz. ¡Acércate y cúbrete la nariz mientras hablamos!\n\n¡Esta es la única letrina en todo Silithus! En serio, pasé horas y horas buscando en la noche. Decidí escabullirme para... hacer un pequeño depósito antes de que los cultistas despertaran, pero tengo un... un problema...\n\nNo tengo ningún paño para... ya sabes...\n\n¿Por qué no vas a ver si encuentras algo de tela y me la pasas por debajo de la puerta?', '', '', '', '', '', '', 26365),
(50228, 'esMX', 'El sobreviviente crepuscular', 'Consigue la cabeza de Michiel Zancavacío.', 'En otros tiempos, Silithus estaba plagada de miembros del Martillo Crepuscular.\n\nSe creía que el culto había sido exterminado, pero en realidad siempre sobrevivió en las sombras. Sus números han menguado, pero su fanatismo no ha disminuido en lo más mínimo.\n\nLa mayoría de los cultistas fueron destruidos por Sargeras durante el ataque, pero aún quedan unos pocos. Su líder, Michiel Zancavacío, se siente más osado con la destrucción de Silithus.\n\nNo podemos permitir que Michiel reúna a los sobrevivientes en torno a nuevo propósito. ¡Hay que detenerlo!', '', '', '', '', '', '', 26365),
(50227, 'esMX', 'Larvas por docena', 'Recoge $1oa crías silítidas.', '¿Cómo sobrevivieron los silítidos a la espada de Sargeras? ¡No lo comprendo!\n\n¡Nunca vi una especie que pudiera adaptarse a su entorno tan rápido como ellos!\n\nLas larvas, o crías como se las llama usualmente, pueden evolucionar a diferentes variedades de criaturas silítidas según las necesidades de la colmena. ¡Esta característica los convierte en una verdadera mina de oro de información evolutiva!\n\n¿Es la mente de la colmena lo que guía el desarrollo evolutivo de las larvas, o se tratará de un factor ambiental?\n\n¡La ciencia debe encontrar la respuesta!', '', '', '', '', '', '', 26365),
(50226, 'esMX', 'La fuente de poder', 'Recoge $1oa cerebros silítidos.', 'Aunque los silítidos desaparecieron por un tiempo inmediatamente después de la destrucción de Silithus, han regresado desde las profundidades de la tierra.\n\n¡Estos nuevos silítidos parecen más fuertes!\n\nMi teoría es que los silítidos se alimentan de alguna fuente de poder que está debajo de la espada de Sargeras.\n\nMe han encomendado la tarea de estudiar los efectos de este misterioso poder sobre las colmenas de silítidos.\n\n¡Tráeme todos los cerebros de silítidos intactos que puedas y yo me encargaré de la investigación!', '', '', '', '', '', '', 26365),
(50131, 'esMX', 'Perspectiva ajena', 'Alcanza la reputación neutral con Esporaggar.', 'Gracias por todo lo que has hecho por mi pueblo, pero aún temen a los forasteros.\n\nLucha contra los señores de la marisma y rescata las bolsas de esporas de la Cañada Emergente. ¡El futuro de mi pueblo está en juego!', '', '', '', '', '', '', 26365),
(50130, 'esMX', 'Un gesto de amistad', 'Alcanza la reputación amistoso con Esporaggar.', 'La vida en la marisma no ha sido fácil, $n. Queremos vivir en armonía con quienes la llaman hogar, pero nuestros enemigos aquí son una amenaza para nuestra existencia.\n\nTrabajemos juntos. Ayuda al pueblo de Esporaggar a prosperar.', '', '', '', '', '', '', 26365),
(50071, 'esMX', '¡Por la Luz!', 'Regresa a Ventormenta y habla con el rey Anduin Wrynn.', 'Los draenei templeluz estarán complacidos de unirse a la Alianza.\n\nNuestra causa ahora es tuya, $n.\n\nRegresemos a Ventormenta. La capitana Fareeya hablará con nuestro pueblo.', '', '', '', '', '', '', 26365),
(50055, 'esMX', 'La perspectiva del Portavoz', 'Entrega la carta de Khadgar a Magni Barbabronce en Silithus.', 'Sargeras dejó una cicatriz en nuestro mundo, $n. Una que no sanará pronto...\n\nParece que los viejos rencores de la Alianza y la Horda también están lejos de sanar.\n\nYa se han perdido demasiadas vidas en la batalla para salvar a Azeroth de la destrucción. No me quedaré de brazos cruzados mientras se derrama más sangre.\n\nDebo pedirte algo antes de marcharme. Llévale esta carta a Magni Barbabronce. Está en Silithus buscando una forma de curar el mundo.\n\nLa carta contiene información que podría serle de utilidad.', '', '', '', '', '', '', 26365),
(50054, 'esMX', 'La Costa Bullente', 'Completa el campo de batalla de la Costa Bullente.', 'Recibimos informes de una isla en la costa de Silithus donde abundan los depósitos de azerita.\n\nEs fundamental que aseguremos la isla antes de que la Alianza sepa de su existencia.\n\nNathanos Clamañublo liderará la fuerza expedicionaria en persona y ha solicitado que te presentes al servicio. ¡Ve con honor!', '', '', '', '', '', '', 26365),
(50053, 'esMX', '¡Prospectores perezosos!', 'Motiva a $1oa prospectores perezosos.', 'Ya he vivido lo suficiente para saber que nunca debes confiarle el trabajo de un orco a los goblins.\n\nPuede que Gallywix y su cártel sirvan a la Jefa de Guerra, ¡pero debemos vigilarlos de cerca si queremos completar nuestro trabajo!\n\nYa he visto a varios de los trabajadores holgazaneando y durmiendo durante el trabajo. ¡La Horda castiga la pereza!\n\n¡Ve y dales una lección a esos prospectores perezosos!', '', '', '', '', '', '', 26365),
(50052, 'esMX', 'Prohibido el acceso a espías', 'Mata a $1oa operativos del IV:7 en Silithus.', 'Hola, $n, me alegra que estés aquí.\n\n¡Esta situación me supera! Insectos asesinos, una extraña sustancia... esa espada GIGANTE que atraviesa la tierra... ¡Este lugar no me gusta para nada!\n\n¡Y como si fuera poco, he recibido informes de que hay espías de la Alianza colados en el campamento!\n\nMira, $gniño:niña; la Dama Oscura tiene planes para esta mena misteriosa y yo no voy a decepcionarla, ¿entiendes?\n\n¡Ve al campamento minero y elimina a esos espías del IV:7 antes de que arruinen toda la operación!', '', '', '', '', '', '', 26365),
(50049, 'esMX', 'La perspectiva del Portavoz', 'Entrega la carta de Khadgar a Magni Barbabronce en Silithus.', 'Sargeras dejó una cicatriz en nuestro mundo, $n. Una que no sanará pronto...\n\nParece que los viejos rencores de la Alianza y la Horda también están lejos de sanar.\n\nYa se han perdido demasiadas vidas en la batalla para salvar a Azeroth de la destrucción. No me quedaré de brazos cruzados mientras se derrama más sangre.\n\nDebo pedirte algo antes de marcharme. Llévale esta carta a Magni Barbabronce. Está en Silithus buscando una forma de curar el mundo.\n\nLa carta contiene información que podría serle de utilidad.', '', '', '', '', '', '', 26365),
(50048, 'esMX', 'La Costa Bullente', 'Completa el campo de batalla de la Costa Bullente.', 'Recibimos informes de una isla en la costa de Silithus donde abundan los depósitos de azerita.\n\nEs fundamental que aseguremos la isla antes de que la Horda sepa de su existencia.\n\nEl maestro Mathias Shaw liderará la fuerza expedicionaria en persona y ha solicitado que te presentes al servicio. ¡Buena suerte!', '', '', '', '', '', '', 26365),
(50047, 'esMX', 'Muestras gratis', 'Reúne $1oa muestras de mena misteriosa.', 'La corona ha ordenado que el IV:7 se infiltre en la operación minera goblin y reúna toda la información posible sobre esta nueva sustancia misteriosa.\n\nMis agentes se han infiltrado en el campamento, pero el tiempo apremia y debemos conseguir unas muestras de la mena pronto.\n\nEn otras palabras, necesito a alguien con un enfoque más directo. Alguien capaz de superar una pelea en desventaja y salir con vida.\n\nCuando regreses, llévale las muestras de mena a Khargus Mantopétreo para que las analice.', '', '', '', '', '', '', 26365),
(50046, 'esMX', 'Sabotaje', 'Coloca $1oa cargas de dinamita.', 'La Horda no ha perdido el tiempo y ha extraído esta mena misteriosa de la gran herida sin medir las consecuencias.\n\nPor fortuna para nosotros, la ingeniería goblin está muy lejos de la sofisticación de nuestras creaciones de Gnomeregan. Solo se necesita un poco de la vieja y confiable dinamita para deshabilitar los trituradores goblin.\n\n¡Qué maquinaria más básica!\n\nToma estas cargas de dinamita y colócalas en los trituradores inactivos del campamento de minería.\n\n¡Esos avaros goblins demorarán semanas en volver a hacerlos funcionar!', '', '', '', '', '', '', 26365),
(50035, 'esMX', 'Bret Test - SQL', '', '', '', '', '', '', '', '', 26365),
(49982, 'esMX', 'Testigo de la Herida', 'Habla con Grol Hojabélica en Silithus.', 'El Cártel Pantoque ya ha iniciado operaciones de minería en Silithus para extraer esta misteriosa sustancia.\n\nLa Jefa de Guerra insistió en que vigilaras los esfuerzos de extracción personalmente y evitaras que la Alianza interfiriera.\n\nGallywix no parecía muy a gusto con esa asignación, pero nadie contradice a Sylvanas...\n\nDebes ir a Silithus de inmediato. El comandante de la base espera tu llegada.', '', '', '', '', '', '', 26365),
(49981, 'esMX', 'Testigo de la Herida', 'Reúnete con el comandante del IV:7 en Silithus.', 'Ya he enviado agentes a Silithus para establecer un campamento.\n\nSus órdenes consisten en vigilar las operaciones de la Horda y recolectar cuanta información puedan acerca de esta misteriosa sustancia.\n\nEn este momento, uno de mis comandantes del IV:7 espera tu llegada. Ve con presteza.', '', '', '', '', '', '', 26365),
(49977, 'esMX', 'Convocatoria en Orgrimmar', 'Habla con Nathanos Clamañublo.', 'La Jefa de Guerra ha pedido que regreses a Orgrimmar cuanto antes. Hay un asunto urgente al que debes atender.\n\nTe contaré los detalles apenas llegues.\n\nEstaré cerca de la entrada del Fuerte Grommash. La Dama Oscura tiene planes para ti, $n. No la haría esperar...', '', '', '', '', '', '', 26365),
(49976, 'esMX', 'Dádivas de los caídos', 'Habla con Mathias Shaw.', 'Hace unas horas, el rey Wrynn se dirigió a los ciudadanos de Ventormenta en Reposo del León en un discurso para anunciar formalmente el fin de la guerra.\n\nCuando se disipó la multitud, me reuní en privado con el rey y sus consejeros para hablar sobre la misteriosa sustancia que se ha descubierto en Silithus.\n\nAl parecer, esta mena brillante es poderosísima, y la Horda no ha perdido tiempo y ya ha comenzado a extraerla de la gran herida.\n\nEl rey me ha ordenado que te cuente los detalles como preparación para una misión importante.', '', '', '', '', '', '', 26365),
(49973, 'esMX', 'Dominio de Thalyssra', 'Reúnete con Lady Liadrin en el Dominio de la Primera Arcanista de Suramar.', 'El Señor regente Theron ha invitado a los Natonocturno a una reunión en la Ciudad de Lunargenta.\n\nPrimero, debemos informarles sobre la invitación. Prefiero hacer esas cosas personalmente.\n\nAcompáñame a Suramar y reúnete con Lady Thalyssra.', '', '', '', '', '', '', 26365),
(49933, 'esMX', 'Por la Horda', 'Reúnete con el embajador Mortharón en la Embajada de Orgrimmar.', 'Después de eras de soledad, una vez más, es hora de que seamos parte de algo más grande. Nuestra alianza con la Horda nos brinda la oportunidad de empezar desde cero, una oportunidad única que no podemos desperdiciar.\n\nEl embajador de la jefa de guerra te espera en Orgrimmar, $n. Desde allí, emprenderás una travesía por todo Azeroth. Lucha para defender esta tierra y reclama la paz para sus shal\'dorei\n\nMuéstrale al mundo quiénes somos, $c. Honra a la Horda y ellos honrarán a Suramar.', '', '', '', '', '', 'Reúnete con el embajador Mortharón en la Embajada de Orgrimmar.', 26365),
(49930, 'esMX', 'En busca de aliados', 'Reúnete con la Jefa de Guerra Sylvanas Brisaveloz en la Embajada de Orgrimmar.', 'Por el liderazgo altruista de la Horda en la guerra contra la Legión, nuestras filas quedaron diezmadas. Nuestros enemigos se regocijan ante la idea de robarnos lo que es nuestro. No los dejaremos.\n\nHe autorizado la construcción de una embajada en Orgrimmar. Con ella ganaremos más aliados para nuestra causa. Encontrémonos allí de inmediato.', '', '', '', '', '', '', 26365),
(49929, 'esMX', 'En busca de aliados', 'Reúnete con el rey Wrynn en la Embajada de Ventormenta.', 'La Horda no se detendrá ante nada en su afán por robar la fuerza vital de Azeroth. Me temo que después de la guerra contra la Legión, nuestras filas se redujeron demasiado para resistir su ataque.\n\nEs hora de que la Alianza incorpore miembros nuevos a la causa. Hemos establecido una embajada en Ventormenta para impulsar esta iniciativa. Nos reuniremos allí.', '', '', '', '', '', '', 26365),
(49928, 'esMX', 'Legado del Vacío', 'Reúnete con Alleria Brisaveloz en la Falla de Telogrus.', 'A lo largo de tu viaje por Azeroth, has logrado dominar las fuerzas que siempre tratan de controlarte.\n\nTe has convertido en un arma formidable de la Alianza, $p. Regresa a la falla del Vacío para que podamos honrar tu logro.', '', '', '', '', '', '', 26365),
(49866, 'esMX', '¡A Pandaria!', 'Preséntate ante Rell Brisa Nocturna en Ventormenta.', 'Solicitamos que vengas al Castillo de Ventormenta de inmediato para recibir instrucciones del Rey Varian Wrynn.$b$bNos ha llegado información sobre un conflicto nuevo en los mares del sur, y necesitamos la ayuda de $gun:una; $c $gexperimentado:experimentada;  para que nos ayude con la investigación.', '', '', '', '', '', '', 26365),
(49865, 'esMX', 'El primero en responder al Cataclismo', 'Preséntate ante Naraat el Hablatierra en Ventormenta.', 'Alamuerte, el aspecto dracónico de la muerte, ha enviado a sus esbirros a erradicar toda la vida de Azeroth.$b$bNaraat el Hablatierra puede contarte más sobre el Monte Hyjal, Vashj\'ir y otras áreas que Alamuerte ha elegido atacar.', '', '', '', '', '', '', 26365),
(49864, 'esMX', 'Guerras en dos frentes', 'Usa a las Comisiones especiales para elegir tu siguiente destino de aventuras.', '¡$n! Han vuelto a solicitar tu presencia para que ayudes a la Alianza en asuntos de suma importancia.\n\n¿Lucharás con la Alianza contra Alamuerte y el Martillo Crepuscular?\n\n¿O volarás a Pandaria para quedarte con sus recursos antes que la Horda?\n\n¡Decídete y lánzate a la batalla!', '', '', '', '', '', '', 26365),
(49863, 'esMX', '¡A Rasganorte!', 'Toma una nave que te lleve a Rasganorte.$b$bPuedes abordar la nave de la Tundra Boreal en el Puerto de Ventormenta o la nave del Fiordo Aquilonal en Los Humedales.', 'El Rey Exánime se mueve por el norte.$b$bUn ejército de no-muertos acata cada una de sus órdenes y marcha por Rasganorte esparciendo peste y muerte a su paso.$b$bNecesitamos que los detengas, $n.', '', '', '', '', '', '', 26365),
(49862, 'esMX', '¡A Terrallende!', 'Atraviesa el Portal Oscuro en las Tierras Devastadas.', 'La Legión Ardiente, un ejército inconmensurable de demonios, ha asaltado Terrallende, el mundo natal y devastado de los draenei. Se necesitan aventureros pertenecientes a la Alianza para proteger lo que queda de Terrallende antes de que desaparezca para siempre a manos de la invasión demoníaca.$b$bViaja a las Tierras Devastadas y atraviesa el Portal Oscuro.', '', '', '', '', '', '', 26365),
(49860, 'esMX', 'La aventura llama', 'Usa a las Comisiones especiales para elegir tu siguiente destino de aventuras.', 'Las historias sobre tu heroísmo se han esparcido por todo Azeroth... y más allá. Se necesita a alguien de tus habilidades en otro sitio.\n\n¿Atravesarás el Portal Oscuro para luchar contra la Legión Ardiente en el devastado mundo de Terrallende?\n\n¿O volarás al norte para unirte a las tropas que buscan derrotar al Rey Exánime en Rasganorte?\n\nTú eliges, $gaventurero:aventurera;.', '', '', '', '', '', '', 26365),
(49855, 'esMX', 'Desastre en el Monte Hyjal', 'Habla con el emisario Cenarion Pezuñanegra para garantizarte un medio de teletransporte a Claro de la Luna.', 'Nuestros hermanos del Círculo Cenarion nos informan que los esbirros de Alamuerte han comenzado a atacar el Árbol del mundo, ¡y tienen la intención de hacerlo arder!$B$BHabla con el emisario Cenarion Pezuñanegra para que te permitan ingresar a Claro de la Luna. Lo podrás encontrar en el distrito tauren del Valle de la Sabiduría, en Orgrimmar.', 'Transporte garantizado a Claro de la Luna', '', '', '', '', 'Habla con el emisario Son del viento en el Amparo de la Noche, en Claro de la Luna.', 26365),
(49852, 'esMX', '¡A Pandaria!', 'Preséntate en el Fuerte Grommash en Orgrimmar.', 'Por la presente, se te ordena presentarte en el Fuerte Grommash de inmediato.$b$bHemos recibido noticias de la aparición de un continente nuevo y lleno de recursos hacia el sur y te enviaremos con un grupo pequeño de soldados a tomar el control de esta zona nueva en nombre de la Horda.', '', '', '', '', '', '', 26365),
(49851, 'esMX', 'El primero en responder al Cataclismo', 'Preséntate ante el clarividente Krogar en Orgrimmar.', 'Alamuerte, el aspecto dracónico de la muerte, ha enviado a sus esbirros a erradicar toda la vida de Azeroth.$b$bEn Orgrimmar, el clarividente Krogar puede contarte más sobre el Monte Hyjal, Vashj\'ir y otras áreas que Alamuerte ha elegido atacar.', '', '', '', '', '', '', 26365),
(49846, 'esMX', 'Guerras en dos frentes', 'Usa a las Comisiones especiales para elegir tu siguiente destino de aventuras.', '¡$n! Han vuelto a solicitar tu presencia para que ayudes a la Horda en asuntos de suma importancia.\n\n¿Lucharás con la Horda contra Alamuerte y el Martillo Crepuscular?\n\n¿O volarás a Pandaria para quedarte con sus recursos antes que la Alianza?\n\n¡Decídete y lánzate a la batalla!', '', '', '', '', '', '', 26365),
(49817, 'esMX', '¡A Rasganorte!', 'Toma un zepelín y viaja a Rasganorte.$b$bPuedes subirte al zepelín del Fiordo Aquilonal en los Claros de Tirisfal o al zepelín de la Tundra Boreal en Orgrimmar.', 'El Rey Exánime se mueve por el norte.$b$bUn ejército de no-muertos acata cada una de sus órdenes y marcha por Rasganorte esparciendo peste y muerte a su paso.$b$bNecesitamos que los detengas, $n.', '', '', '', '', '', '', 26365),
(49816, 'esMX', '¡A Terrallende!', 'Atraviesa el Portal Oscuro en las Tierras Devastadas.', 'La Legión Ardiente, un ejército inconmensurable de demonios, ha asaltado Terrallende, el mundo natal y devastado de los orcos. Se necesitan aventureros pertenecientes a la Horda para proteger lo que queda de Terrallende antes de que desaparezca para siempre a manos de la invasión demoníaca.$b$bViaja a las Tierras Devastadas y atraviesa el Portal Oscuro.', '', '', '', '', '', '', 26365),
(49813, 'esMX', 'La aventura llama', 'Usa a las Comisiones especiales para elegir tu siguiente destino de aventuras.', 'Las historias sobre tu heroísmo se han esparcido por todo Azeroth... y más allá. Se necesita a alguien de tus habilidades en otro sitio.\n\n¿Atravesarás el Portal Oscuro para luchar contra la Legión Ardiente en el devastado mundo de Terrallende?\n\n¿O volarás al norte para unirte a las tropas que buscan derrotar al Rey Exánime en Rasganorte?\n\nTú eliges, $gaventurero:aventurera;.', '', '', '', '', '', '', 26365),
(49812, 'esMX', 'Delas en Dalaran', 'Encuentra a Delas Dienteluna en el Monumento de Antonidas, en Dalaran.', 'Delas Dienteluna necesita nuestra ayuda. A cambio, ella nos ayudará a descifrar el códice de comando.$B$BTe ruego que la encuentres lo más rápido posible. Es sabido que le gusta mucho pasar tiempo en el monumento dedicado al gran líder caído del Kirin Tor, Antonidas.', '', '', '', '', '', 'Encuentra a Delas Dienteluna en el Monumento de Antonidas, en Dalaran.', 26365),
(49788, 'esMX', 'Por la Alianza', 'Reúnete con el embajador Moorgard en la Embajada de Ventormenta.', 'Para dominar los suspiros se necesita una concentración constante y disciplina. Tu dedicación inspira a todos los que llevan el nombre de ren\'dorei, pues nos hemos convertido en hijos del Vacío.\n\nHas aprendido mucho aquí, pero es hora de que dejes atrás la Falla de Telogrus.\n\nVe a Ventormenta, el corazón de la Alianza. El embajador del rey te recibirá y te guiará en tu travesía.\n\nDebes defender Azeroth. No conozco a nadie mejor para esta tarea, $p.', '', '', '', '', '', 'Reúnete con el embajador Moorgard en la Embajada de Ventormenta.', 26365),
(49787, 'esMX', 'Las Tierras Fantasma', 'Encuentra el hogar de Umbric en las Tierras Fantasma.', 'Un elfo de sangre llamado Umbric y sus seguidores fueron desterrados de la Ciudad de Lunargenta por investigar la magia del Vacío.\n\nUna solución drástica, aunque la práctica estuviera prohibida.\n\nSin embargo, ofrece posibilidades para la Alianza. Si damos con el paradero de estos desterrados, quizás podamos reclutarlos para nuestra causa y, más importante que eso, ayudarlos a comprender el Vacío antes de que los corrompa.', '', '', '', '', '', '', 26365),
(49784, 'esMX', 'Legado de los Natonocturno', 'Reúnete con la Primera Arcanista Thalyssra en la Fortaleza Nocturna.', 'Eres de los primeros shal\'dorei que se aventuran a explorar los confines de Azeroth. Pero ya es tiempo de que vuelvas a casa.\n\nRegresa a Suramar para que podamos agradecerte por ayudar a nuestro pueblo a reclamar su lugar en este mundo.', '', '', '', '', '', '', 26365),
(49783, 'esMX', 'Herencia de Altamontaña', 'Reúnete con Mayla Altamontaña en Tótem del Trueno.', 'Has recorrido un largo camino a través de Azeroth, $p. Y has traído honor a Altamontaña.\n\nAhora, Tótem del Trueno te llama. Es momento de darte las gracias por todo lo que has hecho por nosotros.', '', '', '', '', '', '', 26365),
(49782, 'esMX', 'Legado de los Templeluz', 'Reúnete con la capitana Fareeya a bordo del Vindicaar.', 'Le has prestado un gran servicio a la Alianza, $p. Ni yo podría haber hecho un mejor trabajo. Bueno, en realidad sí... pero no mucho mejor.\n\nVeámonos en el Vindicaar. Es hora de recompensar tu dedicación.', '', '', '', '', '', '', 26365),
(49773, 'esMX', 'Por la Horda', 'Reúnete con el embajador Mortharón en la Embajada de Orgrimmar.', 'Se avecina una nueva era para Altamontaña, $n.\n\nDespués de pasar siglos separados, hemos vuelto a forjar lazos con nuestros hermanos de Kalimdor. Y durante la lucha para salvar a nuestra tierra, afianzamos muchas amistades con la gente de la Horda.\n\nPara demostrar el poder que brindamos a nuestros aliados, los tauren valientes como tú deben responder al llamado de la aventura.\n\nTe han invitado a Orgrimmar. Reúnete con el embajador en la embajada de la jefa de guerra, luego comienza tu travesía por Azeroth.\n\nQue la Madre Tierra te acompañe, $n.', '', '', '', '', '', 'Reúnete con el embajador Mortharón en la Embajada de Orgrimmar.', 26365),
(49772, 'esMX', 'Por la Alianza', 'Reúnete con el embajador Moorgard en la Embajada de Ventormenta.', 'El Ejército de la Luz se fundó con un único propósito: derrotar a la Legión. Aunque hemos cumplido ese objetivo, un nuevo deber nos llama.\n\nAzeroth es un mundo rodeado de enemigos. Usa tu entrenamiento para derrotar a la oscuridad y defender los ideales de la Alianza.\n\nVe a Ventormenta. Busca al embajador del rey en la embajada y comienza a extender la Luz por todas las tierras.\n\nPheta thones gamera, $n.', '', '', '', '', '', 'Reúnete con el embajador Moorgard en la Embajada de Ventormenta.', 26365),
(49698, 'esMX', 'Los Templeluz', 'Viaja al Vindicaar con Turalyon.', '$n, has visto de primera mano de qué es capaz mi pueblo. Durante milenios, nos hemos convertido en especialistas en la lucha contra la Legión.\n\nBien, creo que es hora de tomar armas junto a nuestros hermanos de la Alianza.\n\nReúnete con nosotros en el Vindicaar. Creo que tenemos mucho que discutir con la capitana Fareeya.', '', '', '', '', '', 'Reúnete con la Capitana Fareeya en el Vindicaar.', 26365),
(49614, 'esMX', 'Los Natonocturno', 'Regresa con la jefa de guerra en Orgrimmar.', 'La jefa de guerra estará complacida con nuestro éxito.\n\nNo tengo duda de que Thalyssra y sus Natonocturno serán aliados poderosos.', '', '', '', '', '', '', 26365),
(49613, 'esMX', 'Ciudad de Lunargenta', 'Reúnete con Lady Liadrin en la Ciudad de Lunargenta.', 'Los Natonocturno han estado aislados del mundo durante miles de años. Ahora que han reaparecido, nos corresponde a nosotros guiarlos por el camino correcto.\n\nEl señor regente Theron les ha enviado una invitación a los Natonocturno para que se reúnan con nosotros en Lunargenta.\n\nVamos, $n, viajemos a Lunargenta con Thalyssra.', '', '', '', '', '', '', 26365),
(49567, 'esMX', 'Se busca héroe: ¡Nagrand!', 'Habla con el teniente Balfor en Nagrand, Draenor.', 'Todos los ciudadanos aptos de la Alianza deben presentarse ante el teniente Balfor en Nagrand, Draenor.\n\nYrel recomienda que la Alianza monte un puesto de avanzada en Nagrand para que nos sea más fácil luchar contra el clan Grito de Guerra.\n\nConsigan un vuelo a Nagrand y reúnanse con el teniente Balfor y la caravana de suministros.\n\n¡Gloria a la Alianza!', '', '', '', '', '', 'Habla con el teniente Balfor en Nagrand, Draenor.', 26365),
(49566, 'esMX', 'Se busca héroe: ¡Pináculos de Arak!', 'Habla con Azik en los Pináculos de Arak.', 'Todos los ciudadanos aptos de la Alianza deben presentarse ante Azik en los Pináculos de Arak.\n\nSe ha visto a arakkoa sin alas huyendo de los Pináculos de Arak. Según los rumores, las criaturas escapan de una superarma en poder de la Horda de Hierro.\n\nTenemos que descubrir qué está causando la huida de los arakkoa de su hogar y por qué razón han perdido las alas.\n\n¡Gloria a la Alianza!', '', '', '', '', '', 'Habla con Azik en los Pináculos de Arak.', 26365),
(49565, 'esMX', 'Se busca héroe: ¡Talador!', 'Habla con el supervisor Eksos en Talador.', 'Todos los ciudadanos aptos de la Alianza deben presentarse ante el supervisor Eksos en Talador.\n\nLa Horda de Hierro intenta quedarse con el control de Talador porque su posición es perfecta para enviar refuerzos a cualquier parte de Draenor rápidamente y sin impedimento alguno.\n\nEl supervisor Eksos se encuentran montando un puesto de avanzada en Talador. Viaja hasta allí y detén a la Horda de Hierro\n\n¡Gloria a la Alianza!', '', '', '', '', '', 'Habla con el supervisor Eksos en Talador.', 26365),
(49564, 'esMX', 'Se busca héroe: ¡Gorgrond!', 'Habla con Yrel en Gorgrond.', 'Todos los ciudadanos aptos de la Alianza deben presentarse ante Yrel en Gorgrond.\n\nYrel y Maraad solicitan el envío de una expedición pequeña a las tierras salvajes de Gorgrond.\n\nNuestro objetivo es recabar toda la información posible sobre la zona en afán de descubrir cómo podemos aprovecharnos de las debilidades de la Horda de Hierro.\n\nAyuden a Yrel y a Maraad con la expedición.\n\n¡Gloria a la Alianza!', '', '', '', '', '', 'Habla con Yrel en Gorgrond.', 26365),
(49561, 'esMX', 'Se busca héroe: ¡Desierto del Pavor!', 'Habla con la maestra arquera Li en Espinazo del Dragón.', 'Todos los ciudadanos aptos de la Alianza deben presentarse ante la maestra arquera Li en Espinazo del Dragón, en el Valle de la Flor Eterna.\n\nLos Shadopan están tratando de impedir que el ejército de mántides se apodere por completo de Pandaria.\n\nLa mejor manera de llegar hasta la maestra arquera Li es conseguir un cometa y volar hasta Espinazo del Dragón.\n\n¡Gloria a la Alianza!', '', '', '', '', '', 'Habla con la maestra arquera Li en Espinazo del Dragón.', 26365),
(49560, 'esMX', 'Se busca héroe: ¡Estepas de Tong Long!', 'Habla con Taran Zhu en las Estepas de Tong Long.', 'Todos los ciudadanos aptos de la Alianza deben presentarse ante Taran Zhu en las Estepas de Tong Long.\n\nLos Shadopan quieren recuperar sus tierras, que ahora están en las garras de los yaungol, pero no tienen las tropas necesarias para lograrlo.\n\nAyuda a los Shadopan a atacar a los yaungol.\n\n¡Gloria a la Alianza!', '', '', '', '', '', 'Habla con Taran Zhu en las Estepas de Tong Long.', 26365),
(49559, 'esMX', 'Se busca héroe: ¡Cima Kun-Lai!', 'Habla con el mensajero grúmel en el Valle de los Cuatro Vientos.', 'Todos los ciudadanos aptos de la Alianza deben ayudar al eremita Cho en la Cima Kun-Lai.\n\nHablen con un grúmel mensajero en el Valle de los Cuatro Vientos. Estas criaturas les mostrarán el camino que deben tomar para llegar a la Cima Kun-Lai.\n\n¡Gloria a la Alianza!', '', '', '', '', '', 'Habla con el mensajero grúmel en el Valle de los Cuatro Vientos.', 26365),
(49558, 'esMX', 'Se busca héroe: ¡Espesura Krasarang!', 'Habla con Ken-Ken en la Espesura Krasarang.', 'Todos los ciudadanos aptos de la Alianza deben presentarse en la Atalaya de Zhu, en la Espesura Krasarang.\n\nHay rumores de que los pandaren que viven en la Espesura Krasarang se están comportando de manera extraña.\n\nHablen con Ken-Ken en la Atalaya de Zhu. Quizá él tenga más información sobre lo que pudo haberles sucedido a los pandaren para que se muestren tan aletargados.', '', '', '', '', '', 'Habla con Ken-Ken en la Espesura Krasarang.', 26365),
(49557, 'esMX', 'Se busca héroe: ¡Valle de los Cuatro Vientos!', 'Habla con Chen Cerveza de Trueno en el Valle de los Cuatro Vientos.', 'Todos los ciudadanos aptos de la Alianza deben presentarse ante Chen Cerveza de Trueno en el Valle de los Cuatro Vientos.\n\nLa Alianza tiene mucho que aprender del espíritu aventurero de los Cerveza de Trueno.\n\nViaja con los Cerveza de Trueno y descubre los secretos que guarda Pandaria.\n\n¡Gloria a la Alianza!', '', '', '', '', '', 'Habla con Chen Cerveza de Trueno en el Valle de los Cuatro Vientos.', 26365),
(49556, 'esMX', 'Se busca héroe: ¡Bosque de Jade!', 'Preséntate ante el gran almirante Jes-Tereth en el Castillo de Ventormenta.', 'Todos los ciudadanos aptos de la Alianza deben presentarse en el Castillo de Ventormenta.\n\nEl gran almirante Jes-Tereth está reuniendo un equipo para llevar a cabo una misión crítica.\n\nVayan al Castillo de Ventormenta de inmediato para recibir las órdenes correspondientes.\n\n¡Gloria a la Alianza!', '', '', '', '', '', '', 26365),
(49555, 'esMX', 'Se busca héroe: ¡Corona de Hielo!', 'Habla con el Oficial Van Rossem en Dalaran, dentro del Bosque Canto de Cristal.', 'Todos los ciudadanos aptos de la Alianza deben presentarse ante el Oficial Van Rossem en Dalaran, dentro del Bosque Canto de Cristal.\n\nLa Expedición Denuedo necesita más aventureros para fortalecer su ejército antes de lanzar su ataque contra la Ciudadela de la Corona de Hielo y el mismísimo Arthas.\n\nEs hora de que el Rey Exánime pague por sus crímenes.\n\n¡Gloria a la Alianza!', '', '', '', '', '', 'Habla con el Oficial Van Rossem en Dalaran, dentro del Bosque Canto de Cristal.', 26365),
(49554, 'esMX', 'Se busca héroe: ¡Las Cumbres Tormentosas!', 'Habla con Rin Duoctane en Dalaran, dentro del Bosque Canto de Cristal.', 'Todos los ciudadanos aptos de la Alianza deben presentarse ante Rin Duoctane en Dalaran, dentro del Bosque Canto de Cristal.\n\nLos goblin de K3 han construido una ciudad en las Cumbres Tormentosas y han enviado a su embajador para reclutar aventureros.\n\nHablen con Rin y averigüen qué necesita la gente de K3. Los habitantes de esta ciudad serían aliados valiosos para nuestra lucha contra el Rey Exánime.\n\n¡Gloria a la Alianza!', '', '', '', '', '', 'Habla con Rin Duoctane en Dalaran, dentro del Bosque Canto de Cristal.', 26365),
(49553, 'esMX', 'Se busca héroe: ¡Cuenca de Sholazar!', 'Habla con el archimago Pentarus en Dalaran, dentro del Bosque Canto de Cristal.', 'Todos los ciudadanos aptos de la Alianza deben presentarse ante el archimago Pentarus en Dalaran, dentro del Bosque Canto de Cristal.\n\nHemet Nesingwary, junto a su fuerza expedicionaria, llevaba a cabo una misión de exploración de la Cuenca de Sholazar, pero no se ha vuelto a saber de él.\n\nNo tenemos mucha información sobre los exuberantes paisajes de Sholazar, pero es posible que en esa cuenca haya secretos que nos ayuden a luchar contra el Rey Exánime.\n\n¡Gloria a la Alianza!', '', '', '', '', '', 'Habla con el archimago Pentarus en Dalaran, dentro del Bosque Canto de Cristal.', 26365),
(49552, 'esMX', 'Se busca héroe: ¡Zul\'Drak!', 'Habla con el Señor Cruzado Lantinga en Zul\'Drak.', 'Todos los ciudadanos aptos de la Alianza deben presentarse ante el Señor Cruzado Lantinga en Zul\'Drak.\n\nLa Plaga está masacrando a los habitantes locales de Zul\'Drak y los está convirtiendo en parte de su ejército.\n\nAyuda a la Cruzada Argenta y a la Espada de Ébano a detener esta amenaza.\n\n¡Gloria a la Alianza!', '', '', '', '', '', 'Habla con el Señor Cruzado Lantinga en Zul\'Drak.', 26365),
(49551, 'esMX', 'Se busca héroe: ¡Fiordo Aquilonal!', 'Habla con Macalroy en el Fiordo Aquilonal.', 'Todos los ciudadanos aptos de la Alianza deben presentarse ante Macalroy en el Fiordo Aquilonal.\n\nLa ciudad portuaria de Valgarde se encuentra en el corazón del Fiordo Aquilonal, una posición ventajosa para que la Alianza ataque a la Plaga.\n\nAyuda a los soldados que están allí.\n\n¡Gloria a la Alianza!', '', '', '', '', '', 'Habla con Macalroy en el Fiordo Aquilonal.', 26365),
(49550, 'esMX', 'Se busca héroe: ¡Valle Sombraluna!', 'Habla con el comandante del aire Nuainn en el Valle Sombraluna de Terrallende.', 'Todos los ciudadanos aptos de la Alianza deben presentarse ante el comandante del aire Nuainn del Valle Sombraluna, en Terrallende.\n\nLa Legión Ardiente asedia el Bastión Martillo Salvaje; nuestra gente y nuestras vidas corren peligro\n\n¡Necesitamos reforzar el bastión y detener los constantes embates de la Legión Ardiente!\n\n¡Gloria a la Alianza!', '', '', '', '', '', 'Habla con el comandante del aire Nuainn en el Valle Sombraluna de Terrallende.', 26365),
(49549, 'esMX', 'Orden de la jefa de guerra: ¡Nagrand!', 'Habla con el Guardia de piedra Brox en Nagrand, Draenor.', 'Se les ordena a todos los miembros aptos de la Horda que se presenten ante el Guardia de piedra Brox en Nagrand, Draenor.\n\nThrall está montando un puesto de avanzada en nombre de la Horda y necesita ayuda para detener al clan Grito de Guerra.\n\nHablen con Bron Cuerno del Cielo para volar a Nagrand y reúnanse con la caravana de suministros.\n\n¡Por la Horda!', '', '', '', '', '', 'Habla con el Guardia de piedra Brox en Nagrand, Draenor.', 26365),
(49548, 'esMX', 'Orden de la jefa de guerra: ¡Pináculos de Arak!', 'Habla con Azik en los Pináculos de Arak.', 'Se les ordena a todos los miembros aptos de la Horda que se presenten ante Azik en los Pináculos de Arak.\n\nSe ha visto a arakkoa sin alas huyendo de los Pináculos de Arak. Según los rumores, las criaturas escapan de una superarma en poder de la Horda de Hierro.\n\nTenemos que descubrir qué está causando la huida de los arakkoa de su hogar y por qué razón han perdido las alas.\n\n¡Por la Horda!', '', '', '', '', '', 'Habla con Azik en los Pináculos de Arak.', 26365),
(49547, 'esMX', 'Orden de la jefa de guerra: ¡Talador!', 'Habla con el supervisor Grobash en Talador.', 'Se les ordena a todos los miembros aptos de la Horda que se presenten ante el supervisor Grobash en Talador.\n\nLa Horda de Hierro intenta quedarse con el control de Talador porque su posición es perfecta para enviar refuerzos a cualquier parte de Draenor rápidamente y sin impedimento alguno.\n\nEl supervisor Grobash se encuentran montando un puesto de avanzada en Talador. Viaja hasta allí y detén a la Horda de Hierro.\n\n¡Por la Horda!', '', '', '', '', '', 'Habla con el supervisor Grobash en Talador.', 26365),
(49546, 'esMX', 'Orden de la jefa de guerra: ¡Gorgrond!', 'Habla con Durotan en Gorgrond.', 'Se les ordena a todos los miembros aptos de la Horda que se presenten ante Durotan en Gorgrond.\n\nDurotan está buscando aliados que nos ayuden a transitar los paisajes salvajes de Gorgrond en afán de encontrar poderes ancestrales que nos sirvan para destruir a la Horda de Hierro.\n\nHablen con él y descubran qué secretos guarda la tierra.\n\n¡Por la Horda!', '', '', '', '', '', 'Habla con Durotan en Gorgrond.', 26365),
(49543, 'esMX', 'Orden de la jefa de guerra: ¡Desierto del Pavor!', 'Habla con la maestra arquera Li en Espinazo del Dragón.', 'Por la presente, se ordena a todos los miembros aptos de la Horda que se presenten ante la maestra arquera Li en Espinazo del Dragón, en el Valle de la Flor Eterna.\n\nLos Shadopan están tratando de impedir que el ejército de mántides se apodere por completo de Pandaria.\n\nLa mejor manera de llegar hasta la maestra arquera Li es conseguir un cometa y volar hasta Espinazo del Dragón.\n\n¡El destino llama!', '', '', '', '', '', 'Habla con la maestra arquera Li en Espinazo del Dragón.', 26365),
(49542, 'esMX', 'Orden de la jefa de guerra: ¡Estepas de Tong Long!', 'Habla con Taran Zhu en las Estepas de Tong Long.', 'Se les ordena a todos los miembros aptos de la Horda que se presenten ante Taran Zhu en las Estepas de Tong Long.\n\nLos Shadopan quieren recuperar sus tierras, que ahora están en las garras de los yaungol, pero no tienen las tropas necesarias para lograrlo.\n\nAyuda a los Shadopan a atacar a los yaungol.\n\n¡Por la Horda!', '', '', '', '', '', 'Habla con Taran Zhu en las Estepas de Tong Long.', 26365),
(49541, 'esMX', 'Orden de la jefa de guerra: ¡Cima Kun-Lai!', 'Habla con el mensajero grúmel en el Valle de los Cuatro Vientos.', 'Se les ordena a todos los miembros aptos de la Horda que ayuden al eremita Cho en la Cima Kun-Lai.\n\nHablen con un grúmel mensajero en el Valle de los Cuatro Vientos. Estas criaturas les mostrarán el camino que deben tomar para llegar a la Cima Kun-Lai.\n\n¡Por la Horda!', '', '', '', '', '', 'Habla con el mensajero grúmel en el Valle de los Cuatro Vientos.', 26365),
(49540, 'esMX', 'Orden de la jefa de guerra: ¡Espesura Krasarang!', 'Habla con Ken-Ken en la Espesura Krasarang.', 'Se les ordena a todos los miembros aptos de la Horda que se presenten en la Atalaya de Zhu, en la Espesura Krasarang.\n\nHay rumores de que los pandaren que viven en la Espesura Krasarang se están comportando de manera extraña.\n\nHablen con Ken-Ken en la Atalaya de Zhu. Quizá él tenga más información sobre lo que pudo haberles sucedido a los pandaren para que se muestren tan aletargados.', '', '', '', '', '', 'Habla con Ken-Ken en la Espesura Krasarang.', 26365),
(49539, 'esMX', 'Orden de la jefa de guerra: ¡Valle de los Cuatro Vientos!', 'Habla con Chen Cerveza de Trueno en el Valle de los Cuatro Vientos.', 'Se les ordena a todos los miembros aptos de la Horda que se presenten ante Chen Cerveza de Trueno en el Valle de los Cuatro Vientos.\n\nLa Horda tiene mucho que aprender del espíritu aventurero de los Cerveza de Trueno.\n\nViaja con los Cerveza de Trueno y descubre los secretos que guarda Pandaria.\n\n¡Por la Horda!', '', '', '', '', '', 'Habla con Chen Cerveza de Trueno en el Valle de los Cuatro Vientos.', 26365),
(49538, 'esMX', 'Orden de la jefa de guerra: ¡Bosque de Jade!', 'Preséntate ante el general Nazgrim en el Fuerte Grommash.', 'Se les ordena a todos los miembros aptos de la Horda que se presenten ante el general Nazgrim en Orgrimmar.\n\nLas victorias recientes en las batallas navales contra la Alianza fortalecen nuestra determinación.\n\nPreséntense en Fuerte Grommash y ayuden a la flota invasora de la Horda.\n\n¡Por la Horda!', '', '', '', '', '', '', 26365),
(49537, 'esMX', 'Orden de la jefa de guerra: ¡Corona de Hielo!', 'Habla con el Atracador del cielo Klum en Dalaran, cerca del Bosque Canto de Cristal.', 'Por la presente, se ordena a todos los miembros aptos de la Horda que se presenten ante el Atracador del cielo Klum en Dalaran, dentro del Bosque Canto de Cristal.\n\nLa Ofensiva Grito de Guerra necesita más aventureros para fortalecer su ejército antes de lanzar su ataque contra la Ciudadela de la Corona de Hielo y el mismísimo Arthas.\n\nEs hora de que el Rey Exánime pague por sus crímenes.\n\n¡Por la Horda!', '', '', '', '', '', 'Habla con el Atracador del cielo Klum en Dalaran, cerca del Bosque Canto de Cristal.', 26365),
(49536, 'esMX', 'Orden de la jefa de guerra: ¡Las Cumbres Tormentosas!', 'Habla con Rin Duoctane en Dalaran, dentro del Bosque Canto de Cristal.', 'Se les ordena a todos los miembros aptos de la Horda que se presenten ante Rin Duoctane en Dalaran, dentro del Bosque Canto de Cristal.\n\nLos goblin de K3 han construido una ciudad en las Cumbres Tormentosas y han enviado a su embajador para reclutar aventureros.\n\nHablen con Rin y averigüen qué necesita la gente de K3. Los habitantes de esta ciudad serían aliados valiosos para nuestra lucha contra el Rey Exánime.\n\n¡Por la Horda!', '', '', '', '', '', 'Habla con Rin Duoctane en Dalaran, dentro del Bosque Canto de Cristal.', 26365),
(49535, 'esMX', 'Orden de la jefa de guerra: ¡Cuenca de Sholazar!', 'Habla con el archimago Pentarus en Dalaran, dentro del Bosque Canto de Cristal.', 'Se les ordena a todos los miembros aptos de la Horda que se presenten ante el archimago Pentarus en Dalaran, dentro del Bosque Canto de Cristal.\n\nHemet Nesingwary, junto a su fuerza expedicionaria, llevaba a cabo una misión de exploración de la Cuenca de Sholazar, pero no se ha vuelto a saber de él.\n\nNo tenemos mucha información sobre los exuberantes paisajes de Sholazar, pero es posible que en esa cuenca haya secretos que nos ayuden a luchar contra el Rey Exánime.\n\n¡Por la Horda!', '', '', '', '', '', 'Habla con el archimago Pentarus en Dalaran, dentro del Bosque Canto de Cristal.', 26365),
(49534, 'esMX', 'Orden de la jefa de guerra: ¡Zul\'Drak!', 'Habla con el Señor Cruzado Lantinga en Zul\'Drak.', 'Se les ordena a todos los miembros aptos de la Horda que se presenten ante el Señor Cruzado Lantinga en Zul\'Drak.\n\nLa Plaga está masacrando a los habitantes locales de Zul\'Drak y los está convirtiendo en parte de su ejército.\n\nAyuda a la Cruzada Argenta y a la Espada de Ébano a detener esta amenaza.\n\n¡Por la Horda!', '', '', '', '', '', 'Habla con el Señor Cruzado Lantinga en Zul\'Drak.', 26365),
(49533, 'esMX', 'Orden de la jefa de guerra: ¡Fiordo Aquilonal!', 'Habla con el boticario Lysander en el Fiordo Aquilonal.', 'Se les ordena a todos los miembros aptos de la Horda que se presenten ante el boticario Lysander en el Fiordo Aquilonal.\n\nLos Renegados han montado una fortaleza en las costas orientales de Rasganorte.\n\nLlegó la hora de golpear a la Plaga con nuestra fuerza militar y una peste nueva que la Sociedad Real de Boticarios está desarrollando.\n\n¡Por la Horda!', '', '', '', '', '', 'Habla con el boticario Lysander en el Fiordo Aquilonal.', 26365),
(49532, 'esMX', 'Orden de la jefa de guerra: ¡Valle Sombraluna!', 'Habla con el Guardia de sangre Gulmok en el Valle Sombraluna, en Terrallende.', 'Se les ordena a todos los miembros aptos de la Horda que se presenten ante el Guardia de sangre Gulmok en el Valle Sombraluna, en Terrallende.\n\nLa Legión Ardiente intenta asediar la Aldea Sombraluna, pero nuestra fuerza Kor\'kron de élite está demostrando lo que significa luchar contra la Horda.\n\nAplasten a la Legión.\n\n¡Por la Horda!', '', '', '', '', '', 'Habla con el Guardia de sangre Gulmok en el Valle Sombraluna, en Terrallende.', 26365),
(49354, 'esMX', 'Recuerda la Fuente del Sol', 'Completa la gesta \"Recuerda la Fuente del Sol\".', 'El señor regente quiere mostrarle nuestra Fuente del Sol a la Primera Arcanista Thalyssra.\n\nNo sé si le permitirá a Alleria Brisaveloz ser parte de la reunión, pero nosotros estamos invitados.', '', '', '', '', '', '', 26365),
(49266, 'esMX', 'Forja de los Eones', 'Contempla el ritual de los templeluz.', 'Los Templeluz han sido un faro de esperanza en la lucha contra la Legión durante añares.\n\nSin embargo, pocos comprenden lo que se necesita para convertirse en la encarnación de la cólera de la Luz.\n\nEstos pocos adalides del Ejército de la Luz están listos para someterse a la última prueba.\n\n¿Nos harías el honor de contemplarla?', '', '', '', '', '', '', 26365),
