INSERT INTO creature_text (CreatureID, GroupID, ID, Text, Type, Language, Probability, Emote, Duration, Sound, BroadcastTextId, TextRange, comment)VALUES
(@ALLERIA, 0, 0, 'Some will tell you that the Light is the only path. The one weapon that can stop the enemies of Azeroth.', 12, 0, 100, 1, 0, 0, 143203, 0, ''),
(@ALLERIA, 1, 1, 'But we see alternatives. Many roads--many possibilities--that are open to us.', 12, 0, 100, 1, 0, 0, 143204, 0, ''),
(@MAGISTER_UMBRIC, 2, 2, 'There will be those who doubt you. Who question your resolve, your ability to harness powers that have caused the downfall of weaker wills.', 12, 0, 100, 1, 0, 0, 143205, 0, ''),
(@MAGISTER_UMBRIC, 3, 3, 'Together, we will prove them wrong.', 12, 0, 100, 1, 0, 0, 143615, 0, ''),
(@ALLERIA, 4, 4, 'Use the lessons I have taught you to maintain control. Become a weapon for the Alliance... even as you skirt the edge of darkness.', 12, 0, 100, 1, 0, 0, 143206, 0, '');
