-- Portuguese Page Text locales.
UPDATE `page_text` SET `text`='Texto Ausente' WHERE `entry`=10;
UPDATE `page_text` SET `text`='Ola Morgan,\r\n\r\nOs negócios em Goldshire estão corridos, tão corridos que não tive tempo de lhe enviar nenhuma remessa!\r\n\r\nEu incumbi a pessoa que traz esta nota para lhe trazer um pacote de grandes velas de cera (você sabe, aquelas que os Kobolds gostam de usar em suas cabeças?).\r\n\r\nPor favor, dê a esta pessoa o agradecimento e pagamento justo.' WHERE `entry`=15;
UPDATE `page_text` SET `text`='Para o Honorável Diretor Crillian,\r\n\r\nMeu ex-Mestre, eu escrevo para você para que você possa saber o que seu aprendiz tem feito ultimamente. Prestando atenção ao seu conselho, busquei construir meu conhecimento e sabedoria através de viagens fora dos portões de nossa amada Stormwind. Minhas viagens me levaram a muitos lugares, mas decidi morar aqui na adorável cidade de Moonbrook. Os campos ao redor de Westfall são mais bonitos à medida que a colheita se aproxima.' WHERE `entry`=16;
UPDATE `page_text` SET `text`='Poucos dias depois de minha visita, encontrei-me ensinando as crianças locais das fazendas próximas. As lições foram tão bem que o prefeito da cidade me encarregou de administrar uma escola e a construção começou em uma nova escola! De Silverpine a Stormwind e agora Moonbrook - quem teria imaginado que eu veria muito de Azeroth!\r\n\r\nAtenciosamente,\r\n\r\nStalvan Mistmantle' WHERE `entry`=17;
UPDATE `page_text` SET `text`='Caro Nobre Senhor,\r\n\r\nA palavra de sua necessidade de um tutor para seus filhos viajou para mim aqui em Goldshire, onde eu moro temporariamente no Lion\'s Pride Inn. Devido ao infeliz estado dos acontecimentos na região, fui forçado a abandonar meu posto de diretor da Moonbrook Schoolhouse. Por favor, aceite minha inscrição para servir como tutor para seus filhos. O diretor Crillian da Academia pode falar com você sobre minhas habilidades, se necessário.' WHERE `entry`=18;
UPDATE `page_text` SET `text`='Vou viajar para conhecê-lo pessoalmente quando as chuvas de inverno diminuírem e as estradas forem adequadas para viajar mais uma vez.\r\n\r\nAté então,\r\n\r\nStalvan Mistmantle de Silverpine' WHERE `entry`=19;
UPDATE `page_text` SET `text`='. . .Giles, o menino, parece um pouco indisciplinado e será um desafio educar. No entanto, a filha mais velha, Tilloa, parece excepcionalmente inteligente. Eu não pude deixar de notar sua beleza cativante também. Ela está à beira da feminilidade agora. Supostamente, o Senhor providenciou o casamento dela para o próximo ano. Mas eu divago. Nesta semana, acompanharei a família até a cabana de veraneio perto do campo de extração de Eastvale, em Elwynn, perto das Montanhas Red Ridge. Espero escrever mais enquanto estiver lá.' WHERE `entry`=20;
UPDATE `page_text` SET `text`='. . .mais estranho e incontrolável sentimento. Nunca me senti como hoje. Enquanto assistia Giles com sua aula de história, Tilloa estava do lado de fora cuidando do jardim de flores. Depois de alguns minutos, ela entrou e colocou uma begônia escarlate na palma da mão aberta e sorriu para mim de tal maneira que meu coração parecia tremer dentro do meu peito. . . .' WHERE `entry`=21;
UPDATE `page_text` SET `text`='. . .estou certo que ela compartilha os mesmos sentimentos por mim agora. Ela até colocou a mão na minha esta manhã. Quando ela sorri, seus olhos brilham como diamantes cintilantes. Palavras não ditas passam entre nós. Eu posso senti-la no meu coração batendo e veias aquecidas.' WHERE `entry`=22;
UPDATE `page_text` SET `text`='...raiva e fúria de coisas que eu nunca soube que existiam! Como ela ousa. Como eu estava instruindo Giles no significado de números, Tilloa aparece diante de mim com um pretendente, de mãos dadas em público! Que jovem rude. Em vez de me apresentar adequadamente, Tilloa simplesmente disse: \'Oh, esse é apenas meu tutor, tio Stalvan. Ele é um bom velhinho. Velho! Com essa palavra, minhas bochechas coraram com a raiva. Eu sou apenas alguns anos mais velha e ainda assim ela me trai...' WHERE `entry`=23;
UPDATE `page_text` SET `text`='...espiral descendente de desespero. Primeiro ela zomba de mim e agora ela está noiva. A charlatã indecente fingia me amar quando realmente desejava me machucar o tempo todo. Um vazio negro espreita dentro de mim agora e cresce a cada momento. O sangue que eu derramarei empalidece em comparação com as lágrimas que eu já derramei...' WHERE `entry`=24;
UPDATE `page_text` SET `text`='Tommy Joe - A cada hora que passa, meu coração murcha por sua ausência. Ah, se ao menos nossos pais pudessem ver além dos crimes bobos que fizeram um com o outro, e saberem que o único crime verdadeiro é o ódio. O ódio consome, e temo que nesses tempos de escuridão o ódio se apodere de mais do que apenas nossas famílias. Se eles percebessem isso, poderíamos, finalmente, ficar juntos. É para esse dia espero, e para esse dia vivo. Seu amor, Maybell' WHERE `entry`=25;
UPDATE `page_text` SET `text`='Olá William,\r\n\r\n\r\nFaz anos que não falamos, mas acredito que você e seu irmão estão bem e que seu boticário prospera.\r\n\r\n\r\nEu devo pedir um favor a você, William. Em suma, meu neto Tommy Joe perdeu o coração para o jovem Maybell Maclure. E embora eles se adorem... nossas famílias, bem nossas famílias têm brigado há anos.\r\n\r\n' WHERE `entry`=26;
UPDATE `page_text` SET `text`='Olá William, Faz anos que falamos, mas acredito que você e seu irmão estão bem e que seu boticário prospera. Eu devo pedir um favor a você, William. Em suma, meu neto Tommy Joe perdeu o coração para o jovem Maybell Maclure. E embora eles se adorem... nossas famílias, bem nossas famílias têm brigado há anos. ' WHERE `entry`=27;
UPDATE `page_text` SET `text`='Argus, \r\n\r\nComo você sabe, estou até o pescoço em pedidos de reparo do Exército. Não posso reclamar de todo o trabalho, mas está esgotando meu suprimento de ferro.\r\n\r\nEu não tenho ferro suficiente para ferraduras. Eu sei que você sempre mantém um estoque grande - eu gostaria de pegar emprestado 50 pares de sapatos até receber o próximo carregamento de ferro.\r\n\r\nVocê tem minha gratidão,\r\n-Verner' WHERE `entry`=28;
UPDATE `page_text` SET `text`='Uma nota é anexada à caixa. Lê:\r\n\r\n"Verner - desculpe por ouvir que Redridge está tendo tantos problemas. Aqui estão os sapatos que você precisa. Por favor, me pague 100 prata o mais cedo possível."\r\n\r\n"Ou, se preferir, você pode me pagar com escamas de filhotes de dragões negros (eu ouvi que Dragon Whelps são comuns nas Montanhas Redridge). Por sermos amigos... 4 escamas serão suficientes. Obrigado - Argus"' WHERE `entry`=29;
UPDATE `page_text` SET `text`='Meu Senhor,\r\n\r\nA guerra está sobre nós. O destino vem de nossa porta.\r\n\r\nComo magistrado do município de Lakeshire, é meu dever relatar a recente atividade militar que ocorreu na colônia oriental de Sua Majestade.\r\n\r\nContido dentro destas páginas, você encontrará uma conta do Estado de Lakeshire, situado dentro das Montanhas Redridge.' WHERE `entry`=30;
UPDATE `page_text` SET `text`='A perda do Stonewatch Keep foi muito grave. Em relatórios anteriores, detalhei para você a aliança entre o Clã de Orcs da Rocha Negra e o Bruxo conhecido como Morganth. Os Orcs Rocha Negra lutaram com uma selvageria horrível, sem dúvida alimentados pela magia maligna de Morganth. Muitos dos mais valentes soldados de Sua Majestade pereceram tentando defender a Fortaleza, mas nada pôde ser feito.' WHERE `entry`=31;
UPDATE `page_text` SET `text`='Curiosamente, Morganth ligou seus companheiros orcs pouco depois de sua infeliz vitória. O Feiticeiro construiu uma torre arcana no Nordeste e usou seus poderes para construir um pequeno exército de Gnolls Shadowhide para servir suas licitações maliciosas. Tanto os Orcs quanto os Homens caíram vítimas dos brutos viscosos que servem ao Bruxo. Quanto à traição ocorrida entre Morganth e Gath\'Ilzogg, o Blacklock Warlord, só se pode especular neste ponto.' WHERE `entry`=32;
UPDATE `page_text` SET `text`='Para nosso perigo, os falsos orcs parecem mais preocupados com o fim dos bons cidadãos de Sua Majestade. Na última quinzena, o Clã da Rocha Negra organizou uma ofensiva implacável em Lakeshire. Muitos bravos homens perderam suas vidas naquele dia sombrio. As forças Orcs avançaram com tanta velocidade e fúria que a ponte que atravessa Everstill foi arruinada. Nossos suprimentos de guerra diminuíram muito. Nós não temos bandagens para curar as feridas dos caídos.' WHERE `entry`=33;
UPDATE `page_text` SET `text`='Agora peço a Sua Majestade que apoie as pessoas boas de Lakeshire neste momento de necessidade. Precisamos de novos soldados para defender o território, assim como novos instrumentos de guerra para substituir os perdidos no campo de batalha. Alimentos e material de construção estão baixos também. Por favor, meu Senhor, ajude-nos a manter o último bastião entre os Homens e os miserável Orc que prospera. O Inimigo deve ser mantido fora do Reino de Stormwind.' WHERE `entry`=34;
UPDATE `page_text` SET `text`='Eu temo que um mal maior e mais escuro se infiltre no caldeirão da corrupção que as Terras do Norte se tornaram. É imperativo que o Reino de Stormwind tome providências para que não procuremos nos tornar memórias que estão desaparecendo para os vivos ou escravos para o Inimigo.\r\n\r\nAssinado,\r\n\r\nMagistrado Solomon\r\nMunicípio de Lakeshire\r\nReino de Stormwind' WHERE `entry`=35;
UPDATE `page_text` SET `text`='Honorável Magistrado Solomon:\r\n\r\nEu lamento ao lado de você pelos mortos. Eu cuidarei pessoalmente para que o rei fique ciente da situação e espero que eu possa trazer reforços para Lakeshire. Sua Majestade tem sido ausente ultimamente e age de forma bastante estranha. Eu compartilho isso com você em confiança.\r\n\r\nAté então,\r\n\r\nGeneral Marcus Jonathan\r\nReino de Stormwind' WHERE `entry`=36;
UPDATE `page_text` SET `text`='Stoutmantle - Algum nervo enviando um estranho total para discutir a gangue Defias. Uma vez que você salvou minha vida e agora você coloca isso em grande perigo. Mas eu te devo. A Irmandade Defias é maior do que você pensa. Todas as minas de Westfall a Elwynn Forest estão sob seu controle. Kobolds e Gnolls foram alistados para fazer seu trabalho sujo. Eles têm goblins criando monstros de metal para colocar nos campos de Westfall para atacar as superstições dos moradores locais.' WHERE `entry`=38;
UPDATE `page_text` SET `text`='Eles têm uma vasta rede subterrânea construída. Maior do que você sabe, Stoutmantle. De Booty Bay à direita para Stormwind Keep. Você pode ser um Paladino valente, mas você é um tolo se acha que pode acabar com eles. Eles estão trabalhando em uma arma de destruição em massa. Afinal, se há uma coisa que os pedreiros sabem, é como construir grandes coisas. Você não acha que isso foi sobre fazendas de abóbora e vinhas, agora você fez? Eu paguei seu favor. Agora me deixe em paz!\r\n\r\n\r\n--W' WHERE `entry`=39;
UPDATE `page_text` SET `text`='Lord Stoutmantle - Liderado por Edwin VanCleef, o Sindicato dos Pedreiros era composto pelos construtores mais habilidosos entre os homens. Os pedreiros ajudaram a reconstruir a cidade quebrada de Stormwind que foi arrasada pelos orcs durante a Primeira Guerra. VanCleef e seus comerciantes eram construtores inigualáveis e seu trabalho e talento artístico ficavam evidentes nos edifícios da Catedral da Luz e da Ventania de Stormwind.' WHERE `entry`=41;
UPDATE `page_text` SET `text`='No entanto, os nobres de Stormwind tiveram uma enorme dívida expandindo a presença militar do reino através de Elwynn e em Stranglethorn. A enorme dívida aleijou a economia do reino e despojou VanCleef e seus pedreiros das recompensas prometidas. Depois de passar anos trabalhando para reconstruir a cidade gloriosa, os pedreiros foram deixados quebrados, esquecidos pelos funcionários corruptos da cidade.' WHERE `entry`=42;
UPDATE `page_text` SET `text`='Tendo conhecido pessoalmente Edwin VanCleef toda a minha vida, posso dizer-lhe que encará-lo como um inimigo é uma tarefa assustadora. Você vê, ele era meu amigo de infância, e eu pessoalmente o treinei nos caminhos das sombras pensando que um dia ele poderia considerar uma carreira ao meu lado. Se VanCleef estiver liderando a Irmandade Defias, que a Luz tenha piedade de nossas almas.\r\n\r\nMestre Mathias Shaw\r\nGuilda do Assassino de Stormwind' WHERE `entry`=43;
UPDATE `page_text` SET `text`='Tendo conhecido pessoalmente Edwin VanCleef toda a minha vida, posso dizer-lhe que encará-lo como um inimigo é uma tarefa assustadora. Você vê, ele era meu amigo de infância, e eu pessoalmente o treinei nos caminhos das sombras pensando que um dia ele poderia considerar uma carreira ao meu lado. Se VanCleef estiver liderando a Irmandade Defias, que a Luz tenha piedade de nossas almas. Mestre Mathias Shaw Guild dos Assassinos de Stormwind' WHERE `entry`=44;
UPDATE `page_text` SET `text`='Bom trabalho, amigo! Agora você precisa ir para o leste. Leste até os penhascos, a leste da estrada. Procure as velhas ruínas das chaminés perto do lado da estrada. Lá você encontrará um velho barril com sua próxima pista.' WHERE `entry`=45;
UPDATE `page_text` SET `text`='Agora deste barril, enfrente o norte. Em linha reta como o corvo voa, mantenha-se andando até ver o jarro vazio ao lado do solitário moinho de vento sobre os penhascos do mar. Se você cutucar aquele jarro, você pode encontrar o que você está procurando.' WHERE `entry`=46;
UPDATE `page_text` SET `text`='Agora que você me achou uma garrafa de uísque, você está quase no tesouro! Apenas enfrente o oeste da garrafa e ande até a costa. Uma vez que você chegar à água, continue! Nade direto para o oeste até encontrar a ilha e comigo o baú do tesouro!' WHERE `entry`=47;
UPDATE `page_text` SET `text`='Blacknails-- O mar nos aguarda. Precisamos de seus suprimentos do norte. Envie de tudo: todo railroad pike, todo waterbucket. Nós precisamos de metal. Uma remessa é devida pelo BB, mas há muito atrasada. Venha através de mim, Blacknails. Envie seus suprimentos diretamente para o "celeiro" desta vez. Não há tempo para desperdiçar. --VanCleef' WHERE `entry`=48;
UPDATE `page_text` SET `text`='Lorde Stoutmantle - O município de Lakeshire pede humildemente a ajuda de sua milícia. Enquanto escrevo isso, nos encontramos sob cerco tanto dos Orcs da Rocha Negra quanto dos Gnolls Shadowhide de Morganth. Eu não enviaria esta solicitação se a nossa situação não fosse severa. Nós esperamos ajuda de Stormwind, mas até que tal ajuda chegue, eu imploro a você que nos dê apoio. Uma vez que o Reino é atacado, toda a Humanidade está em perigo. Magistrado Salomão' WHERE `entry`=49;
UPDATE `page_text` SET `text`='Meritíssimo ... Todos de Westfall choram por sua perda, mas somos atormentados com nossa própria guerra em Westfall. Os fazendeiros foram expulsos por ladrões e golens mecânicos. Stormwind retirou suas tropas, deixando as pessoas indefesas. Reuni a Milícia do Povo depois do abandono de Stormwind para salvar o que resta dessa terra. Levar a Milícia para Redridge significaria certa desgraça para Westfall. Meus arrependimentos, Gryan Stoutmantle' WHERE `entry`=50;
UPDATE `page_text` SET `text`='Lord Ebonlocke - O município de Lakeshire pede humildemente a ajuda da Patrulha da Noite. Estamos sob cerco tanto dos orcs Blackrock quanto dos gnolls de Shadowhide de Morganth. Eu não enviaria esta solicitação se a nossa situação não fosse severa. Nós esperamos ajuda de Stormwind, mas até que tal ajuda chegue, eu imploro a você que nos dê apoio. Uma vez que se o reino é violado, toda a humanidade está em perigo. Magistrado Salomão' WHERE `entry`=51;
UPDATE `page_text` SET `text`='Sua honra--\r\n\r\n\r\nLamento informar que a Night Watch não pode deixar seu posto em Darkshire neste momento. Talvez você não tenha percebido que Stormwind não fornece mais apoio militar nessa região. A Night Watch foi formada e treinada inteiramente pelo povo da floresta para defender Darkshire na ausência de Stormwind. Sem a Watch, a cidade cairia. Desejo-lhe boa sorte com sua situação.\r\n\r\n\r\n- Lord Ello Ebonlocke' WHERE `entry`=52;
UPDATE `page_text` SET `text`='As letras nesta nota parecem piscar e dançar em toda a superfície. É impossível obter significado delas...' WHERE `entry`=53;
UPDATE `page_text` SET `text`='Saudações Ello Ebonlocke, prefeito de Darkshire. Receio ter novidades para sua cidade. Notícias graves.\r\n\r\nVocê vê, eu sou um criador. Eu enganei o portador desta nota para me ajudar na minha mais recente e terrível criação - um demônio de carne e osso e metal retorcido! Enquanto você lê isso, é provável que esteja fora da minha humilde residência, rangendo os dentes e esperando que minha palavra saia e seja cumprida.\r\n\r\nMas você saberá em breve.\r\n\r\n-O Embalmer' WHERE `entry`=54;
UPDATE `page_text` SET `text`='Esta é uma ação para uma extensão de terra dentro da região de Westfall. Ele dá direito às pessoas abaixo com direitos de propriedade da área designada, bem como todos os itens produzidos e quaisquer estruturas construídas dentro de suas fronteiras.\r\n\r\nO documento é assinado por:\r\n\r\n     Theodore Furlbrow\r\n     Verna Furlbrow' WHERE `entry`=55;
UPDATE `page_text` SET `text`='[As palavras abaixo foram rapidamente rabiscadas no verso da escritura]\r\n\r\nNós nos apoiamos em Furlbrow e conseguimos sua ação. Pensei que poderia ser útil se você quisesse forjar um deles para o seu próprio lugar. Os Furlbrows não nos causam problemas. Na última vez que os vi, eles estavam saindo de Westfall, presos a uma carroça quebrada.' WHERE `entry`=56;
UPDATE `page_text` SET `text`='Uma lua sobre o Vale brilha\r\nLançando seu brilho sobre a selva\r\nOnde guerreiros orgulhosos atendem ao chamado\r\nPara defender nossa nação e terras sagradas.\r\n\r\nUma lua sobre o Vale brilha\r\nMuito acima dos gritos de batalha\r\nOnde o sangue é derramado\r\nDe inimigo e camarada.' WHERE `entry`=58;
UPDATE `page_text` SET `text`='E quando nossos irmãos passarem\r\nEm reinos além do conhecido\r\nO espírito da alma endurece\r\nNas profundezas do Vale.\r\n\r\nE quando nossos irmãos passarem\r\nNo templo da montanha\r\nNós devemos proteger seu espírito eterno\r\nEncaixado dentro do cristal azul sagrado.\r\n\r\nE quando nossos irmãos passarem\r\nUma lua sobre o Vale brilha.' WHERE `entry`=59;
UPDATE `page_text` SET `text`='Pela lua e fogo, Pela carne e osso, Escrito em sangue, Esculpido em pedra. Deixe este lugar Ou encontre sua desgraça Morte fica de guarda Sobre a Tumba do Imperador.' WHERE `entry`=62;
UPDATE `page_text` SET `text`='Mestre Carevin, O portador desta nota mostrou a si próprio: estar em pé na Luz, capaz de lutar contra os mortos-vivos e demônios que atormentam as fronteiras da Duskwood, e disposto a se juntar à família Carevin em sua causa da Luz. Eu, portanto, sugeriria que você desse a este guerreiro da Luz outros deveres - talvez para investigar alguns dos habitantes da cidade que estão sob suspeita de socorro aos inimigos da Luz. Com os melhores cumprimentos, Calor' WHERE `entry`=63;
UPDATE `page_text` SET `text`='Morgan Ladimore era um grande e nobre cavaleiro que lutava em defesa dos inocentes, dos pobres e dos aflitos. Por muitos anos, ele trabalhou diligentemente em todas as áreas periféricas de Azeroth, trazendo alívio ao sofrimento e justiça rápida aos malfeitores.\r\n\r\nEle era casado com uma jovem garota chamada Lys no verão de seu décimo oitavo ano. Eles estavam muito apaixonados um pelo outro e eventualmente produziriam três filhos, um filho e duas filhas.\r\n\r\nMorgan tinha trinta e dois anos quando a guerra eclodiu em' WHERE `entry`=64;
UPDATE `page_text` SET `text`='Lordaeron. Morgan foi chamado para o lado do lendário paladino Uther the Lightbringer para lutar contra os orcs e os mortos-vivos. Deixando sua esposa e filhos na segurança de sua casa, Morgan partiu para a guerra.\r\n\r\nOs anos passaram e a guerra se arrastou, e Morgan testemunharia muitos eventos horríveis, incluindo o desmantelamento dos Paladinos da Mão de Prata, a morte de Uther e a disseminação da peste. A única coisa que o mantinha à beira da loucura era o conhecimento de que ele' WHERE `entry`=65;
UPDATE `page_text` SET `text`='um dia se reúna com sua esposa e filhos.\r\n\r\nMorgan acabaria voltando para sua terra natal, mas não achava nada parecido com o que lembrava. A floresta outrora verdejante foi corrompida e repleta de mortos-vivos e outras forças das trevas. Casas destruídas e fazendas podiam ser encontradas em todos os lugares, e o cemitério perto de Raven\'s Hill agora dominava grande parte da área. Um Morgan chocado e perplexo finalmente chegou a sua casa, apenas para encontrá-lo em ruínas. Não sabendo o que havia acontecido em sua terra natal, ele' WHERE `entry`=66;
UPDATE `page_text` SET `text`='dirigiu-se para a aldeia para encontrar respostas e, esperava ele, sua esposa e filhos.\r\n\r\nMorgan perguntou sobre sua família, mas não encontrou nenhuma resposta. Um padre em Darkshire, como era agora chamado, disse que poderia procurar no cemitério de Raven Hill por uma lápide. Morgan se recusou a acreditar que sua família estava morta e continuou a revistar todas as fazendas e casas em Duskwood, mas sem sucesso.\r\n\r\nMorgan viajou de Darkshire para a vizinha Lakeshire, pensando que talvez sua família tivesse fugido. A caminho' WHERE `entry`=67;
UPDATE `page_text` SET `text`='ali, decidiu, contra seu melhor julgamento, parar no cemitério de Raven Hill. Morgan passou horas caminhando entre as lápides. Ele reconheceu muitos nomes de pessoas que ele conhecia e ficou cada vez mais perturbado. Então ele os viu: um pequeno lote não tratado entre os muitos com três pequenas lápides. Uma sensação de medo tomou conta dele quando ele se aproximou. Morgan limpou a poeira da lápide mais proeminente para revelar o nome nela. Simplesmente esculpida no túmulo, letras soletradas' WHERE `entry`=68;
UPDATE `page_text` SET `text`='seu pior medo:\r\n\r\nLys Ladimore\r\nAmada esposa e mãe\r\n\r\nA apreensão de Morgan se transformou em desânimo e depois em pesar, e ele caiu de joelhos chorando. Durante horas ele olhou para aquele túmulo, implorando à pedra fria por perdão e soluçando desculpas. Então, horas depois, algo nele estalou, e ele começou a atacar. Ele tirou a espada da bainha e começou a chover golpes nas lápides, gritando de raiva. Cego em sua fúria, ele atacou e balançou loucamente, captando o aviso' WHERE `entry`=69;
UPDATE `page_text` SET `text`='de um trio de atendentes do cemitério. Enquanto tentavam contê-lo, ele voltou seu foco para eles, lançando acusações de culpa sobre os atendentes inocentes, depois matou todos eles.\r\n\r\nMais tarde, quando a raiva passou, a percepção penetrou na mente de Morgan, e ele viu sua espada ensanguentada no peito de um dos atendentes. Levado à beira por suas emoções, ele tirou a faca do cinto e mergulhou-a em seu coração.\r\n\r\nO corpo de Morgan Ladimore e os três corpos de suas vítimas foram encontrados' WHERE `entry`=70;
UPDATE `page_text` SET `text`='O próximo dia. Ele foi rapidamente enterrado, sem cerimônia, em uma cova escavada apressadamente na periferia do cemitério. Porque Morgan cometeu um assassinato contra inocentes, algo que foi completamente contra suas crenças e sua natureza, e por causa do pesar que ele teve em ser incapaz de salvar sua família, Morgan não pôde morrer uma morte pacífica, e viveu como um dos inquietos morto.\r\n\r\nApenas alguns dias depois, seu túmulo foi perturbado e seu corpo não foi encontrado. O ser que era Morgan agora' WHERE `entry`=71;
UPDATE `page_text` SET `text`='vagueia Duskwood, consumido por sua tristeza pela perda de sua esposa e filhos e seu próprio ódio por si mesmo. Mor\'Ladim, como ele agora chama a si mesmo, vaga na Duskwood com vingança e ódio irracionais, e é conhecido por cometer assassinatos indiscriminadamente.' WHERE `entry`=72;
UPDATE `page_text` SET `text`='O terror dessas últimas semanas é quase mais do que posso suportar; ainda assim, sinto que, ao escrever o que vi, de algum modo serei consolado. Então eu faço, e é o único conforto que tenho nestes dias sombrios.' WHERE `entry`=73;
UPDATE `page_text` SET `text`='DIA UM\r\n\r\n\r\nO aprendiz e eu chegamos hoje em Coldridge Valley. A viagem através da caverna foi em sua maioria sem complicações. Acampamos a uma boa distância da caverna que nos disseram que os trolls se reuniram.\r\n\r\n\r\nDIA DOIS\r\n\r\n\r\nLobo sangrento uivando quase me manteve acordado a noite toda.\r\n\r\n\r\nEu terei novas roupas de wolfhide em poucos dias.\r\n\r\n\r\nO rapaz se enroscou com um trogg hoje, brigou e' WHERE `entry`=74;
UPDATE `page_text` SET `text`='Este Marcador de Armadura é bom para uma peça de couro ou armadura de tecido, resgatável na casa Timberlain dentro do campo de exploração florestal de Eastvale.' WHERE `entry`=78;
UPDATE `page_text` SET `text`='Abaixo está o processo e cronograma de coleta de ouro Defias das minas de Elwynn para a nossa sede em Westfall.\r\n\r\nHorário de coleta:\r\n\r\nDomingo: 12:30\r\nQuarta: 12:30\r\n\r\nEm cada dia especificado, o ouro ganho das minas de Elwynn será reunido no canteiro de abóboras de Brackwell. O agente encarregado dessas reuniões, "O Colecionador", será conhecido pelo anel gravado que possui. Um anel que dei a ele.' WHERE `entry`=79;
UPDATE `page_text` SET `text`='Grelin, meu tempo é curto e muitos assuntos insistem em meu tempo, e espero que sua investigação dos trolls não seja um deles. Por isso, permitirei que você use minha autoridade para lidar com os trolls da maneira que julgar necessária, ainda mais se for capaz de encontrar uma solução conveniente. Magni Bronzebeard' WHERE `entry`=80;
UPDATE `page_text` SET `text`='conseguiu tirar o melhor do feio bruto, mas agora ele está carregando um corte desagradável em seu braço. Parece que vou ter que fazer as tarefas por um tempo. DIA TRÊS Frio. Nevou um pouco ao meio-dia. DIA QUATRO O rapaz estava de volta e de bom humor hoje, então demos nossa primeira olhada nos trolls. Frostmane, eu diria, a julgar pelas marcas em sua pele e as várias bugigangas que usavam em seus corpos.' WHERE `entry`=81;
UPDATE `page_text` SET `text`='DIA CINCO O rapaz ficou vigiando o acampamento e eu fui até a caverna e dei uma olhada ao redor. Os números dos trolls são um pouco problemáticos. Os alpinistas podem ter que ser convocados se a caverna for liberada da infestação de trolls. Percebo que não mencionei os troggs ultimamente. Eles aparecem na área em maior número, mas são tão primitivos que não representam uma grande ameaça. O tempo pode dizer se isso é verdade ou não.' WHERE `entry`=82;
UPDATE `page_text` SET `text`='E nem uma gota de cerveja por quase uma semana agora. DIA SEIS Frio novamente. Neves, Trolls, Nenhuma cerveja. DIA SETE Neve abaixo tão branca, Céu acima tão azul, Lobos uivarão a noite, Nem uma gota de bebida. DIA OITO Os trolls talvez não sejam um problema tão grande quanto pensamos inicialmente. Eles têm alguns' WHERE `entry`=83;
UPDATE `page_text` SET `text`='números, mas não estão bem armados ou organizados. Uma pequena demonstração de força deve ser mais que suficiente para lidar com a ameaça que representam.' WHERE `entry`=84;
UPDATE `page_text` SET `text`='Meu diário\r\n\r\nO terror dessas últimas semanas é quase mais do que posso suportar; Por que é que escrevendo palavras neste livro eu posso de algum modo manter a loucura à distância? Talvez seja como se eu estivesse confessando meus pecados para um companheiro silencioso, ou libertando minha mente desses pensamentos torturados e os confinando ao papel.\r\n\r\nComecei um diário antes deste, mas ele permanece em um lugar para o qual não posso retornar. Então eu vou começar de novo. Mas desta vez, vou começar do verdadeiro começo.' WHERE `entry`=85;
UPDATE `page_text` SET `text`='Um relatório sobre o estado dos Trolls Frostmane na área geral do Vale de Coldridge\r\n\r\nPreparado por Grelin Whitebeard, enviado especial do Senado\r\n\r\nDesde o tempo que passei observando o movimento dos trolls Frostmane na área do Vale de Coldridge, determinei que eles não representam uma grande ameaça para assentamentos anões na área. Além disso, eles são uma ameaça que pode ser eliminada com pouco apoio adicional do exército. Através da assistência de Mountaineers já estacionados em' WHERE `entry`=86;
UPDATE `page_text` SET `text`='Coldridge Valley e mercenários (pagos com fundos reservados pelo Senado antes do meu envio), estou confiante de que o problema será resolvido em pouco tempo.\r\n\r\nThis action has been authorized with the sanction given to me by King Bronzebeard.' WHERE `entry`=87;
UPDATE `page_text` SET `text`='Um relatório sobre o estado dos Trolls Frostmane na Área Geral de Dun Morogh Os trolls situados em Dun Morogh são amplamente centralizados no Frostmane Hold, uma caverna de montanha na fronteira ocidental. Eles têm números suficientes para causar alguma preocupação, no entanto, eles parecem mais do que contentes em permanecer na caverna. Isto é, sem dúvida, porque eles não desejam incorrer na ira dos anões novamente, e correm o risco de extermínio total. Suas ações podem ser consideradas territoriais, se alguma coisa, e é' WHERE `entry`=88;
UPDATE `page_text` SET `text`='acredito que eles não representam uma ameaça real para nós, desde que não invadamos seu território. Esta pode ser uma situação pouco atraente para a população anã, mas dada a dispersão de recursos militares, pode ser prudente relegar o extermínio dos trolls para um nível mais baixo de importância, e continuar a focalizar a ameaça representada pelos troggs e pelo Dark Irons. Fechado, você também encontrará uma cópia do relatório do meu irmão Grelin sobre a Anvilmar. Assinado, Senir Barba Branca' WHERE `entry`=89;
UPDATE `page_text` SET `text`='Um relatório sobre o estado dos trolls Frostmane na área geral do vale Coldridge Preparado por Grelin Whitebeard, enviado especial do Senado Desde o tempo que passei observando o movimento dos trolls Frostmane na área do Vale Coldridge, eu determinei que eles representam nenhuma grande ameaça aos assentamentos anões na área. Além disso, eles são uma ameaça que pode ser eliminada com pouco apoio adicional do exército. Através da assistência de Mountaineers já estacionados em' WHERE `entry`=90;
UPDATE `page_text` SET `text`='Coldridge Valley e mercenários (pagos com fundos reservados pelo Senado antes do meu envio), estou confiante de que o problema será resolvido em pouco tempo. Esta ação foi autorizada com a sanção que me foi dada pelo rei Bronzebeard.' WHERE `entry`=91;
UPDATE `page_text` SET `text`='Meu querido Tarrel -\r\n\r\nDesculpe a pressa com que esta correspondência foi escrita, mas o tempo não é um luxo que temos.\r\n\r\nA equipe de escavação estava fazendo progressos significativos ao desenterrar importantes artefatos de Titã. Mas começamos a descobrir outros objetos antigos, especificamente ossos grandes.' WHERE `entry`=92;
UPDATE `page_text` SET `text`='Não muito tempo depois que os ossos foram desenterrados, fomos vítimas de um ataque de Raptors. O batalhão de homens do regimento de Longbraid lutou valentemente. Mas no final, eles ficaram impressionados. Quase todo mundo morreu. Apenas 3 de nós sobrevivemos. Um trabalhador com o nome de Ormer Ironbraid provou ser mais heróico ao garantir a segurança de mim mesmo e do Prospector Whelgar.' WHERE `entry`=93;
UPDATE `page_text` SET `text`='Nós agora nos refugiamos em uma caverna protegida, aparentemente fora de perigo. Mas estamos realmente presos até que uma força possa ser enviada para contar com os Raptores. Whelgar está tentando continuar trabalhando, mas suspeito que ele ainda esteja bastante assustado. Essa é a nossa situação, Tarrel. Por favor, peça a Longbraid para reunir uma força de resgate de uma só vez.\r\n\r\nMeu amor por você continua forte, mesmo neste momento terrível.\r\n\r\n- Merrin' WHERE `entry`=94;
UPDATE `page_text` SET `text`='As Green Hills do Stranglethorn\r\n\r\nNosso primeiro dia foi como já se esperava. A maior parte de nosso tempo estava preocupada em tomar as providências necessárias para estabelecer um acampamento base. Eu localizei um cenário ideal por uma enseada de rio de água doce. A julgar pelas docas antigas e abandonadas nas proximidades, este local foi habitado há algum tempo. Quanto aos habitantes originais, só o tempo pode contar esse conto.' WHERE `entry`=95;
UPDATE `page_text` SET `text`='Nos anos que se seguiram, depois que Stormwind traiu a guilda dos pedreiros, Sir Erlgadin ficou amargurado com o papel dos nobres no reino. Ele não queria mais defender a posição que a linhagem de seu pai havia ganho para ele na Casa dos Nobres.\r\n\r\nMas eu divago. O objetivo desta história não é agir como um tratado político ou uma biografia. Esta é a conta das minhas experiências de caça grande nas colinas verdes de Stranglethorn?' WHERE `entry`=98;
UPDATE `page_text` SET `text`='Quando eu estava prestes a questionar a falta de interesse de Ajeck na estratégia de caça do dia, ela pegou a aljava, desenhou uma flecha e soltou um tiro na direção do pobre Barnil. Mas não foi Barnil que Ajeck estava atirando. Pois quando Barnil se afastou, boquiaberto, um grande crocilisco fluvial flutuou para a superfície com a flecha de Ajeck perfeitamente colocada entre seus dois grandes olhos.\r\n\r\n' WHERE `entry`=100;
UPDATE `page_text` SET `text`='Durante um desses passos errados, Erlgadin colocou uma mão pesada no ombro de Barnil. Ajeck e eu demos uma olhada casual, assumindo que o homem estava simplesmente dando a Barnil uma repreensão muito necessária por seu descuido. Erlgadin, no entanto, gesticulou lentamente com a cabeça em direção a uma árvore caída próxima. Olhando de volta para nós, havia dois olhos negros penetrantes logo acima de um bocado de presas afiadas.\r\n\r\n' WHERE `entry`=102;
UPDATE `page_text` SET `text`='A matança provocou um clima festivo entre a expedição. Barnil serviu hidromel para todos aproveitarem. Mas nossas festividades foram de curta duração. Enquanto estávamos preparando o cadáver para o transporte de volta ao acampamento base, todos nós fomos pegos de surpresa por um grunhido horrendo. Em todos os meus anos eu nunca ouvi nada tão horripilante.' WHERE `entry`=104;
UPDATE `page_text` SET `text`='Em um precipício rochoso acima, em silhueta ao pôr-do-sol, eu podia distinguir o maior gato de presa que eu já vi. Eu consegui soltar um tiro desajeitado com meu rifle, mas o gato se manteve firme. Ele rosnou mais uma vez, desta vez mais alto que o primeiro, e desapareceu. $B$BReunimos nossos pertences e voltamos em silencio para o acampamento.' WHERE `entry`=105;
UPDATE `page_text` SET `text`='Neste dia nós nos aventuramos para o sul, seguindo algumas pegadas frescas. Logo chegamos a uma ravina coberta por uma tremenda ponte de corda. Não pude deixar de pensar nos escritos descritivos de Brann sobre essa região quando vi a maravilha da engenharia. Tantas vezes presumia-se que os Trolls nativos eram uma raça primitiva e inculta, enquanto eu observava a habilidade mestra da ponte, eu era capaz de reconhecer a habilidade com a qual os construtores Trolls superaram a façanha aparentemente impossível.\r\n\r\n' WHERE `entry`=108;
UPDATE `page_text` SET `text`='Tanto Ajeck quanto sir Erlgadin estavam de pé, com as armas apontadas para o crescimento exuberante na base das árvores balançando. O sol do meio dia bateu forte sobre nós. Um fio lento de transpiração desceu da tempora de Elrgadin quando ele puxou o pino de volta. Após o som do clique, a espessa flora se abriu e uma grande pantera negra - um belo espécime - disparou na planície.\r\n\r\n' WHERE `entry`=110;
UPDATE `page_text` SET `text`='Sua arma atirou violentamente em seus braços. O cano voou para o lado e subiu por baixo do rifle de Ajeck. Ajeck havia escolhido aquele exato momento para puxar o gatilho. O rifle, o alvo agora batendo desajeitadamente na direção da linha das árvores, disparou com um estrondo distinto. Um bando de pássaros gritou do dossel, espalhando-se em todas as direções. Uma nuvem de fumaça subiu da árvore. Nós assistimos com admiração quando um ramo tremendo caiu diretamente sobre a pantera em fuga, quebrando suas costas.' WHERE `entry`=112;
UPDATE `page_text` SET `text`='Partimos à primeira luz, indo para o sul, passando pelas Ruínas Tkashi. Barnil expressou preocupação de que poderíamos encontrar membros da Tribo Bloodscalp. Lembrei Barnil de que os Bloodscalps estavam mais preocupados em destruir seu inimigo tribal, os Skullsplitters. Não seria errado dizer que Barnil não foi consolado pelo menos. Eu, no entanto, tinha um rifle carregado, uma sacola cheia de pólvora e três caçadores mortais comigo para aliviar qualquer preocupação de uma emboscada hostil. ' WHERE `entry`=114;
UPDATE `page_text` SET `text`='Eu estive diante de um Infernal imponente no campo de batalha, o exército da Legião Ardente avançando de todas as direções. Um bando indisciplinado de Trolls parece tão inofensivo quanto um coelho nas colinas de Dun Morogh.\r\n\r\n\r\nWe passed the Tkashi Ruins without event, much to Barnil\'s relief.  The party proceeded to head westward, toward the Great Sea, skirting the Ruins of Zul\'Kunda just to the south.  As we ascended the high sea bluffs we spotted our first Raptor.\r\n\r\n' WHERE `entry`=115;
UPDATE `page_text` SET `text`='Nossos tiros apressadamente apontados foram o suficiente para comprar a fuga de Barnil. Barnil voltou a descer a colina e voltou para o grupo. Nós corremos para a selva; um bando de ferozes Raptors perseguindo cada movimento nosso. Os caçadores agora eram caçados. ' WHERE `entry`=118;
UPDATE `page_text` SET `text`='Conduzi o grupo em direção ao mar, esperando que o litoral proporcionasse refúgio aos Raptores. Em nossa pressa, nos afastamos muito para o norte, para uma elevação precária. O erro foi cometido. A culpa foi minha. Paramos perto de um penhasco, os Raptors a poucos passos atrás.' WHERE `entry`=119;
UPDATE `page_text` SET `text`='Eu andei devagar para frente, arma em punho. Eu levei esses bravos caçadores para a morte. Eu morreria defendendo-os. Os Raptors Lashtail são particularmente ferozes, conhecidos pela sua sede de sangue implacável. Eles estavam em maior número do que nós. Mas eu seria amaldiçoado se deixasse que eles matassem a mim e aos meus companheiros sem derramar um pouco do próprio sangue deles primeiro.' WHERE `entry`=120;
UPDATE `page_text` SET `text`='Ajeck e Sir Erlgadin prepararam suas armas, me flanqueando de cada lado, de costas para o mar. Barnil soltou um suspiro derrotado e sacou o machado. Os Lashtails estavam quase em cima de nós. Seu passo firme diminuiu. Eles estavam perseguindo sua presa agora porque sabiam que estavam encurralados.' WHERE `entry`=121;
UPDATE `page_text` SET `text`='  As Colinas Verdes De Stranglethorn$b$b              Por Hemet Nesingwary' WHERE `entry`=124;
UPDATE `page_text` SET `text`='Nosso primeiro dia foi como também pode esperar primeiro dias ir. A maior parte de nosso tempo estava preocupada em tomar as providências necessárias para estabelecer um acampamento. Eu localizei um cenário ideal em uma enseada de rio de água doce. A julgar pelas docas antigas e abandonadas nas proximidades, este local foi habitado há algum tempo. Quanto aos habitantes originais, só o tempo pode contar esse conto. ' WHERE `entry`=153;
UPDATE `page_text` SET `text`='Para esta expedição, juntei Ajeck Rouack e Sir S. J Erlgadin, juntamente com o meu criado de confiança, Barnil Stonepot. Eu lutei ao lado do pai de Ajeck em muitas batalhas em defesa da Aliança. Vê-la crescida é muito especial. Seu pai a ensinou bem nos caminhos do armamento. Suas habilidades com um arco me fazem pensar se há sangue élfico correndo através dessas veias. ' WHERE `entry`=154;
UPDATE `page_text` SET `text`='Sir S. J. Erlgadin vem da aristocracia humana. Seu pai, o conde Erlgadin, era famoso por sua generosidade. Foi o conde que fez lobby por melhores condições de trabalho para a Guild dos Stonemasons durante a restauração de Stormwind após a Segunda Grande Guerra. ' WHERE `entry`=155;
UPDATE `page_text` SET `text`='Nos anos que se seguiram, depois que Stormwind traiu a guilda dos pedreiros, Sir Erlgadin ficou amargurado com o papel dos nobres no reino. Ele não queria mais defender a posição que a linhagem de seu pai havia ganho para ele na Casa dos Nobres. Mas eu divago. O objetivo desta história não é agir como um tratado político ou uma biografia. Esta é a conta das minhas experiências de caçada nas colinas verdes de Stranglethorn ?.' WHERE `entry`=156;
UPDATE `page_text` SET `text`='Nós nos levantamos com o sol. Barnil começou a preparar a refeição da manhã. Percebi que a atenção de Ajeck estava um pouco distraída. A jornada do dia seria longa e nossa caça nos traria perigo. A falta de foco poderia facilmente levar a um contratempo. No entanto, Ajeck parecia incapaz de desviar o olhar de Barnil, que estava à beira do rio, enxaguando o seu kit de bagunça. ' WHERE `entry`=157;
UPDATE `page_text` SET `text`='Quando eu estava prestes a questionar a falta de interesse de Ajeck na estratégia de caça do dia, ela pegou a aljava, desenhou uma flecha e soltou um tiro na direção do pobre Barnil. Mas não foi Barnil que Ajeck estava atirando. Pois quando Barnil se afastou, boquiaberto, um grande crocilisco fluvial flutuou para a superfície com a flecha de Ajeck perfeitamente colocada entre seus dois grandes olhos.' WHERE `entry`=158;
UPDATE `page_text` SET `text`='Partimos para o oeste, através do espesso crescimento da selva emaranhada. Movendo-se com passos lentos e deliberados, passeamos pela folhagem espessa em busca de presas. A manhã passou em um silêncio frustrado. Nada estava mexendo no Vale, nem mesmo uma brisa. À tarde, a expedição ficou inquieta. Barnil já não caminhava com os passos cautelosos de um predador que rastreava a presa. Em vez disso, ele andava desajeitadamente pelo caminho, muitas vezes pisando ruidosamente em folhas secas ou galhos caídos.' WHERE `entry`=159;
UPDATE `page_text` SET `text`='Durante um desses passos errados, Erlgadin colocou uma mão pesada no ombro de Barnil. Ajeck e eu demos uma olhada casual, assumindo que o homem estava simplesmente dando a Barnil uma repreensão muito necessária por seu descuido. Erlgadin, no entanto, gesticulou lentamente com a cabeça em direção a uma árvore caída próxima. Olhando de volta para nós, havia dois olhos negros penetrantes logo acima de um bocado de presas afiadas.' WHERE `entry`=160;
UPDATE `page_text` SET `text`='A besta era um tigre macho de Stranglethorn. Antes que eu pudesse engatilhar meu rifle, Erlgadin ergueu a besta e atirou na besta. A flecha errou o alvo e pegou a besta fortemente no flanco esquerdo. O tigre fez uma tentativa fútil de fugir, mas sua ferida era muito grave. A besta tropeçou por alguns segundos trágicos até que Barnil terminou a matança com um machado lançado.' WHERE `entry`=161;
UPDATE `page_text` SET `text`='A matança provocou um clima festivo entre a expedição. Barnil serviu hidromel para todos aproveitarem. Mas nossas festividades foram de curta duração. Enquanto estávamos preparando o cadáver para o transporte de volta ao acampamento base, todos nós fomos pegos de surpresa por um grunhido horrendo. Em todos os meus anos eu nunca ouvi nada tão horripilante.' WHERE `entry`=162;
UPDATE `page_text` SET `text`='Em um precipício rochoso acima, em silhueta ao pôr-do-sol, eu podia distinguir o maior gato de presa que eu já vi. Eu consegui soltar um tiro desajeitado com meu rifle, mas o gato se manteve firme. Ele grunhiu mais uma vez, desta vez mais alto que o primeiro, e desapareceu.$B$B Reunimos nossos pertences e voltamos em silencio para o acampamento.' WHERE `entry`=163;
UPDATE `page_text` SET `text`='Eu havia prometido à expedição que iríamos passar o dia seguinte caçando panteras, já que suas peles estão em alta demanda por toda Azeroth. Só faz sentido que tal demanda exista, com todos os caçadores fisicamente capazes, caçadores e comerciantes de peles dando suas vidas tão valentemente em nome da Aliança.' WHERE `entry`=164;
UPDATE `page_text` SET `text`='Ajeck e Sir Erlgadin estavam ansiosos para aprender a caçar efetivamente com um Rifle dos Anões. Eu vi os dois humanos deixando suas armas primitivas no campo base. Barnil e eu os equipamos com algumas das melhores armas de fogo de Ironforge.' WHERE `entry`=165;
UPDATE `page_text` SET `text`='Neste dia nós nos aventuramos para o sul, seguindo algumas pegadas frescas. Logo chegamos a uma ravina coberta por uma tremenda ponte de corda. Não pude deixar de pensar nos escritos descritivos de Brann sobre essa região quando vi a maravilha da engenharia. Tantas vezes presumia-se que os Trolls nativos eram uma raça primitiva e inculta, enquanto eu observava a habilidade mestra da ponte, eu era capaz de reconhecer a habilidade com a qual os construtores Trolls superaram a façanha aparentemente impossível.' WHERE `entry`=166;
UPDATE `page_text` SET `text`='Em pouco tempo, Ajeck seguiu a pantera para o sudoeste. Nós andamos em silêncio, armas prontas, em antecipação à nossa presa. Um arranco de gravetos de um bosque próximo de árvores atraiu nossa atenção imediatamente. Algo estava lá. Um olhar severo para Barnil foi o suficiente para transmitir meus pensamentos. Barnil baixou lentamente o rifle. Essa matança não foi para nós; foi para nossos companheiros humanos. Inúmeros panteras perderam a vida diante de nossos rifles fumegantes. Essa matança seria para os humanos.' WHERE `entry`=167;
UPDATE `page_text` SET `text`='Tanto Ajeck quanto sir Erlgadin estavam de pé, com as armas apontadas para o crescimento exuberante na base das árvores balançando. O sol do meio dia bateu forte sobre nós. Um fio lento de transpiração desceu da tempora de Elrgadin quando ele puxou o gatilho de volta. Após o som do tiro, a espessa flora se abriu e uma grande pantera negra - um belo espécime - disparou pela planície.' WHERE `entry`=168;
UPDATE `page_text` SET `text`='Os humanos treinaram seus locais na pantera enquanto corria ao longo da borda da linha das árvores. Os canos de suas armas se moviam em paralelo perfeito. Barnil me lançou um olhar insistente, mas eu balancei a cabeça negativamente. Essa caçada foi para os humanos, não para Barnil nem para mim. Erlgadin disparou um tiro, perdendo a pantera por completo. Aparentemente, ele não estava preparado para o violento recuo da explosão do rifle.' WHERE `entry`=169;
UPDATE `page_text` SET `text`='Sua arma atirou violentamente em seus braços. O cano voou para o lado e subiu por baixo do rifle de Ajeck. Ajeck havia escolhido aquele exato momento para puxar o gatilho. O rifle, o alvo agora batendo desajeitadamente na direção da linha das árvores, disparou com um estrondo distinto. Um bando de pássaros gritou do dossel, espalhando-se em todas as direções. Uma nuvem de fumaça subiu da árvore. Nós assistimos com admiração quando um ramo tremendo caiu diretamente sobre a pantera em fuga, quebrando suas costas.' WHERE `entry`=170;
UPDATE `page_text` SET `text`='Com o passar das semanas, nosso estoque de peles de pantera e tigre ficou imenso. Decidi que era hora da expedição mudar nosso foco para um novo desafio: Raptors. Os humanos, embora apreciaram o treinamento oferecido por Barnil e eu, decidiram abster-se de caçar com armas de fogo. Ajeck estava muito mais confortável com um arco finamente amarrado e Sir Erlgadin nunca saiu do acampamento sem sua besta resistente.' WHERE `entry`=171;
UPDATE `page_text` SET `text`='Partimos à primeira luz, indo para o sul, passando pelas Ruínas Tkashi. Barnil expressou preocupação de que poderíamos encontrar membros da Tribo Bloodscalp. Lembrei Barnil de que os Bloodscalps estavam mais preocupados em destruir seu inimigo tribal, os Skullsplitters. Não seria errado dizer que Barnil não foi consolado com isso. Eu, no entanto, tinha um rifle carregado, uma sacola cheia de pólvora e três caçadores mortais comigo para aliviar qualquer preocupação de uma emboscada hostil.' WHERE `entry`=172;
UPDATE `page_text` SET `text`='Eu estive diante de um Infernal imponente no campo de batalha, o exército da Legião Ardente avançando de todas as direções. Um bando indisciplinado de Trolls parece tão inofensivo quanto um coelho nas colinas de Dun Morogh. Nós passamos pelas Ruínas Tkashi sem nenhum evento, muito para o alívio de Barnil. O grupo seguiu para o oeste, em direção ao Grande Mar, contornando as Ruínas de Zul\'Kunda ao sul. Quando subimos os penhascos do alto mar, avistamos nosso primeiro Raptor.' WHERE `entry`=173;
UPDATE `page_text` SET `text`='A besta nunca mais detectou nossa presença. De fato, a única saudação que recebeu da expedição foi uma bala entre os olhos. Sir Erlgadin soltou um caloroso \'hurrah\' enquanto Ajeck apontava para mim com grande aprovação. Eu peneirei minha mochila em busca do meu cachimbo, na esperança de desfrutar de uma fumaça comemorativa. Barnil começou a subir a encosta para recuperar o cadáver do Raptor. Eu olhei para a fera caída com a satisfação que acompanha cada grande matança.' WHERE `entry`=174;
UPDATE `page_text` SET `text`='Mas eu não pude aproveitar a glória da matança por muito tempo. Quando voltei os olhos para o horizonte, várias silhuetas apareceram no topo da colina, logo acima do pobre Barnil. \'Fuja, Barnil!\' Gritei. Ajeck, Sir Erlgadin e eu soltamos uma saraivada de balas, flechas e mais flechas sobre Barnil e em direção ao raptor que o perseguiam. Um de nós caiu em meio à confusão.' WHERE `entry`=175;
UPDATE `page_text` SET `text`='Nossos tiros apressadamente apontados foram o suficiente para garantir a fuga de Barnil. Barnil voltou a descer a colina e voltou para o grupo. Nós corremos para a selva; um bando de ferozes Raptors perseguindo cada movimento nosso. Os caçadores agora eram caçados.' WHERE `entry`=176;
UPDATE `page_text` SET `text`='Conduzi o grupo em direção ao mar, esperando que o litoral proporcionasse um refúgio contra os Raptores. Em nossa pressa, nos afastamos muito para o norte, para uma elevação precária. O erro foi cometido. A culpa foi minha. Paramos perto de um penhasco, os Raptors a poucos passos atrás. ' WHERE `entry`=177;
UPDATE `page_text` SET `text`='Eu andei devagar para frente, arma levantada. Eu levei esses bravos caçadores para a morte. Eu morreria defendendo-os. Os Raptors Lashtail são particularmente ferozes, conhecidos pela sua sede de sangue implacável. Eles estavam em maior número do que nós. Mas eu seria amaldiçoado se deixasse que eles matassem a mim e aos meus companheiros sem derramar um pouco do próprio sangue deles primeiro. ' WHERE `entry`=178;
UPDATE `page_text` SET `text`='Ajeck e Sir Erlgadin prepararam suas armas, me flanqueando de cada lado, de costas para o mar. Barnil soltou um suspiro derrotado e sacou o machado. Os Lashtails estavam quase em cima de nós. Seu passo firme diminuiu. Eles estavam perseguindo sua presa agora porque sabiam que estavam presos. ' WHERE `entry`=179;
UPDATE `page_text` SET `text`='Eles vieram de todos os lugares, saindo de buracos escondidos aos nossos pés e caindo sobre nós dos silenciosos poleiros acima. Metade dos nossos homens caiu naqueles primeiros minutos de pânico. O resto, inclusive eu, tentou fugir. Enquanto corria, vi tantos dos meus irmãos, tomados por dentes e garras, ouvi tantos gritos cortados ou gorgolejando ao silêncio.\r\n\r\nPelo que sei, sou o único humano a escapar desse lugar.' WHERE `entry`=189;
UPDATE `page_text` SET `text`='Eu só posso adivinhar porque eu sobrevivi aquela noite. Eu sempre fui cauteloso, sempre rápido para recuar de golpes e pular de armadilhas. Meu apelido, Jitters, vem desse traço. Então, talvez tenha sido esse jeito cauteloso que me salvou ...\r\n\r\nOu talvez tenha sido a Foice que eu tirei dos escombros. Não pode ser a própria Foice, pois a perdi durante o voo frenético. Mas se fui eu quem trouxe os Worgen para Duskwood, então talvez os Worgen tenham me proporcionado uma rara cortesia. Maldito seja.' WHERE `entry`=190;
UPDATE `page_text` SET `text`='Ou talvez esteja fadado a testemunhar a mudança que fiz em Duskwood. Talvez seja meu destino assistir enquanto os Worgen rasgam essa terra, manchando-a cada vez mais com sua impureza.\r\n\r\nIf that truly is my fate, then it is twofold.  For the Worgen are not the only power to clutch at Duskwood - the fiends from Deadwind Pass have also staked claim.\r\n\r\nThat is the next chapter of my tale, and I pray it is the last...' WHERE `entry`=191;
UPDATE `page_text` SET `text`='Depois de sobreviver ao vôo de Roland\'s Doom, eu me escondi dentro de um celeiro de propriedade de um homem chamado Sven. Passei alguns dias no celeiro e tal horror permaneceu comigo que nunca me dei conta de Sven ou da família dele. Mas pelo que vi do meu esconderijo, eu sabia que esses fazendeiros eram pessoas bastante decentes. Se eu tivesse saído do meu esconderijo, acho que eles teriam me acolhido, mas a confiança é difícil para mim. Mais difícil ainda depois daquele choque na mina.\r\n\r\nEntão eu permaneci escondido. E isso salvou minha vida.' WHERE `entry`=192;
UPDATE `page_text` SET `text`='Alguns dias depois que cheguei ao celeiro, Sven deixou sua fazenda e foi para Darkshire. Ele beijou sua esposa e sorriu para seus filhos e prometeu voltar em breve com brinquedos e doces. O homem pobre. Essa foi a última vez que ele viu sua desamparada família.\r\n\r\nPelo menos eles se separaram alegremente. E pelo menos sua esposa foi a primeira a morrer, e foi libertada de ver o massacre de seus filhos. Mas essas pequenas graças não fazem nada por mim. Eu vi o que aconteceu e sempre assombrará meus sonhos.' WHERE `entry`=193;
UPDATE `page_text` SET `text`='Minha mão treme quando me lembro dos detalhes daquela noite, quando Sven estava fora e sua família estava condenada a enfrentar os Cavaleiros Negros sozinhos. Mais uma vez me arrependo, pois estava lá e poderia ter me levantado contra aqueles demônios do Deadwind Pass. Mas é um falso arrependimento. É o mesmo que atormenta qualquer sobrevivente de uma tragédia. Eu sei que, se eu tivesse deixado meu esconderijo, eu também teria sido morto, meu corpo rasgado, e seus pedaços espalhados tão amplamente que eu não seria reconhecido. ' WHERE `entry`=194;
UPDATE `page_text` SET `text`='Mas, embora eu saiba que não poderia ter feito nada para impedir esse assassinato hediondo, um verdadeiro arrependimento permanece: levei os Cavaleiros Negros para a fazenda de Sven. Minha descoberta da Foice não só desencadeou os Worgen sobre Duskwood - atraiu os Cavaleiros da Deadwind Pass.\r\n\r\nEu sei disso porque, pouco antes de começarem a matança, fizeram uma pergunta à esposa de Sven, enquanto ela segurava seus filhos, dando-lhes o conforto que podia, embora estivesse certa de que a morte estava próxima.' WHERE `entry`=195;
UPDATE `page_text` SET `text`='"A foice de Elune." Um dos Cavaleiros gritou com uma voz áspera e estridente, como a moagem de um machado na pedra. E a última palavra - Elune - resmungou, como se estivesse se engasgando com o som.\r\n\r\nDread gripped me when I heard that voice, both from the horrid sound of it, and because...I knew the Scythe of which the Rider spoke.  It must be the same cursed thing I drew from the rocks of Roland\'s Doom days before.  It was what the Black Riders sought!\r\n\r\nAnd it was what would kill Sven\'s family.' WHERE `entry`=196;
UPDATE `page_text` SET `text`='Eu nunca soube o nome da esposa de Sven, como ela sempre foi chamada de "querida" e "meu amor" e "mamãe" por seu marido e filhos. Mas eu gostaria de saber. Eu sou a única memória viva de sua ação naquele dia, e embora ela fosse apenas a esposa de um fazendeiro, nunca vi um homem ou uma mulher agir com tanta bravura.\r\n\r\nÉ claro que ela não sabia da Foice, mas quando soube que os Cavaleiros a procuravam, em um instante formou-se um plano em sua cabeça.\r\n\r\nE foi ousado e inteligente. Se ao menos tivesse funcionado.' WHERE `entry`=197;
UPDATE `page_text` SET `text`='"A Foice?" ela disse em uma voz calma. "Claro que sim. Quem aqui não sabe?" Ela olhou para os Cavaleiros com olhos fixos, e eu juraria que ela falava a verdade se eu não soubesse. Não havia como ela saber sobre a foice.\r\n\r\nSua aposta valeu a pena. O mesmo cavaleiro que proferiu a pergunta antes inclinou a cabeça ligeiramente para ela e gritou: "Onde?"\r\n\r\n"Eu vou levar você. Todos vocês", disse ela, e eu pude ver uma pequena esperança cintilando por trás de seus olhos.' WHERE `entry`=198;
UPDATE `page_text` SET `text`='"Mas o caminho é longe, e meus filhos nos atrasariam. Devemos deixá-los."\r\n\r\nSeu truque era simples, mas truques simples têm a melhor chance de sucesso. Se funcionasse, levaria os Cavaleiros para longe da fazenda. Ela estaria perdida, mas seus filhos estariam seguros. E funcionaria, se ao menos os Cavaleiros acreditassem em suas mentiras nobres.\r\n\r\nEmbora eu nunca tenha sido um estudante da Luz, eu rezei ferozmente pela esposa de Sven quando ela se levantou contra aqueles terríveis Cavaleiros.\r\n\r\n"Por favor", eu rezei. "Deixe-os acreditar."' WHERE `entry`=199;
UPDATE `page_text` SET `text`='Eles ficaram parados, congelados, e ela encontrou seus olhares com calma. Então um cavaleiro olhou para cima, como se estivesse ouvindo uma ligação distante. Ele tirou de sua vestimenta uma pequena jóia e olhou para ela. Ele então gesticulou com a bugiganga em direção à esposa de Sven. Uma luz se arrastou do Cavaleiro em direção à mulher, formando-se em uma mão branca e sombria. Ela olhou para a luz, inabalável, mas eu podia ver a incerteza por trás de sua máscara de confiança. Quando a mão a alcançou, espalhou seus dedos sobre sua cabeça.\r\n\r\nE isso apertou.' WHERE `entry`=200;
UPDATE `page_text` SET `text`='A esposa de Sven estava rígida como uma tábua e seus olhos se arregalaram. E apesar de seus lábios puxarem de volta à boca um grito, nenhum som escapou. Depois de alguns momentos dessa tortura, a mão soltou-a, deixando-a cair de joelhos. O Cavaleiro que segurava a bugiganga então se sentou ereto em sua sela, e uma voz alta irrompeu dele.\r\n\r\n"Esta mulher mente", disse em uma voz que marcou meus sonhos. "Ela não viu a foice."' WHERE `entry`=201;
UPDATE `page_text` SET `text`='Depois disso, os ombros do Cavaleiro inclinaram-se ligeiramente, como se um espírito dentro dele tivesse fugido. E então, na velha e estridente voz usada anteriormente, proferiram as palavras finais:\r\n\r\n"O Senhor falou. Mate-os."' WHERE `entry`=202;
UPDATE `page_text` SET `text`='Não consigo descrever o que aconteceu depois. Está claro em minha mente, mas até mesmo a minha alma miserável não pode colocar no papel os eventos que se sucederam, terríveis minutos.\r\n\r\nEu só posso escrever que a família de Sven foi morta. E logo depois, Sven voltou a essa cena sombria e mortal. Tanta dor estava nele que eu estava com medo de me mostrar. E com tanto medo que me encontrasse, fugi do meu esconderijo no celeiro. Eu não sei onde Sven está agora, mas eu rezo para que ele, algum dia, encontre a paz.' WHERE `entry`=203;
UPDATE `page_text` SET `text`='Depois que a Foice foi libertada, eles vieram até nós de todos os lugares, saindo de buracos escondidos aos nossos pés e caindo sobre nós dos silenciosos poleiros acima. Metade dos nossos homens caiu naqueles primeiros minutos de pânico. O resto, inclusive eu, tentou fugir. Enquanto corria, vi tantos dos meus irmãos, tomados por dentes e garras, ouvi tantos gritos cortados ou gorgolejando ao silêncio.' WHERE `entry`=205;
UPDATE `page_text` SET `text`='Eu só posso adivinhar porque eu sobrevivi aquela noite. Eu sempre fui cauteloso, sempre rápido para recuar de golpes e pular de armadilhas. Meu apelido, Jitters, vem desse traço. Então, talvez tenha sido esse jeito cauteloso que me salvou ...\r\n\r\nOu talvez tenha sido a Foice que eu tirei dos escombros. Não pode ser a própria Foice, pois a perdi durante o voo frenético. Mas se fui eu quem trouxe os Worgen para Duskwood, então talvez os Worgen tenham me proporcionado uma rara cortesia. Maldito seja.' WHERE `entry`=206;
UPDATE `page_text` SET `text`='Depois de sobreviver ao vôo de Roland\'s Doom, eu me escondi dentro de um celeiro de propriedade de um homem chamado Sven. Passei alguns dias no celeiro e tal horror permaneceu comigo que nunca me dei conta de Sven ou da família dele. Mas pelo que vi do meu esconderijo, eu sabia que esses fazendeiros eram pessoas bastante decentes. Se eu tivesse saído do meu esconderijo, acho que eles teriam me acolhido, mas a confiança é difícil para mim. Mais difícil ainda depois daquele choque na mina.\r\n\r\nEntão eu permaneci escondido.' WHERE `entry`=207;
UPDATE `page_text` SET `text`='<O resto do livro está em branco>' WHERE `entry`=208;
UPDATE `page_text` SET `text`='RELATÓRIO: Kobolds\r\n\r\nA atividade dos kobolds aumentou nas proximidades do Northshire Valley. Eles acampam ao norte da abadia e dentro da Mina de Echo Ridge em números alarmantes. Eu contratei paladinos, guerreiros e outras pessoas locais para me ajudar a nos livrar dos vermes.\r\n\r\nIsso progride bem. E vou atualizar conforme necessário.' WHERE `entry`=209;
UPDATE `page_text` SET `text`='RELATÓRIO: Ladrões\r\n\r\nMais alarmante do que os kobolds, uma gangue humana de ladrões surgiu no vale. Eles usam bandanas vermelhas e se chamam de irmandade. Este nível de organização pode causar problemas, tanto aqui como em toda a floresta de Elwynn.\r\n\r\nNós identificamos um de seus líderes - um deles, Garrick Padfoot - e esperamos levá-lo à justiça em breve.' WHERE `entry`=210;
UPDATE `page_text` SET `text`='RELATÓRIO: Lobos\r\n\r\nHouve um aumento nos ataques de animais relatados em Northshire. Eagan acredita que os lobos no vale estavam assustados aqui de Duskwood.\r\n\r\nEmbora os lobos raramente caçam presas humanas, o grande número deles tem causado alguns incidentes com os agricultores. E muitos animais desapareceram.' WHERE `entry`=211;
UPDATE `page_text` SET `text`='ELOGIO:\r\n\r\nO portador destes documentos deve ser-lhe atribuído o estatuto de Adjunto do Exército de Stormwind, tendo servido a Northshire com entusiasmo e distinção. Estou confiante de que você achará essa pessoa útil em Elwynn Forest.\r\n\r\nassinado:\r\nDouglas McBride,\r\nExército de Stormwind, Northshire' WHERE `entry`=212;
UPDATE `page_text` SET `text`='Westfall Stew$b$b3 partes de Stringy Vulture Meat$b3 Goretusk Snouts$b3 Murloc Eyes$b3 Okra$bMisture e deixe ferver. Deixe ferver por pelo menos duas horas antes de servir. ' WHERE `entry`=213;
UPDATE `page_text` SET `text`='Querida Yvette\r\n\r\n\r\nEu vou confiar esta carta ao nosso corredor mais rápido. Eu rezo para que ele possa encontrar um caminho através das forças que nos rodeiam. E eu oro para que você leia estas palavras, pois elas são as últimas que você ouvirá de mim.\r\n\r\n\r\nMeu único conforto nestes dias sombrios é o alívio que sinto, sabendo que você está livre do meu destino.' WHERE `entry`=214;
UPDATE `page_text` SET `text`='Texto Ausente' WHERE `entry`=215;
UPDATE `page_text` SET `text`='Temo que a guerra entre os Stonefields e os Maclures acabe com Tommy Joe e o romance florescente de Maybell, e em tempos como este - onde notícias obscuras e rumores de guerra pairam sobre nós - a juventude e o amor devem ser nutridos. Então, o favor: peço que você use suas habilidades e invente uma poção ou elixir para ajudar esses jovens amantes em sua busca um pelo outro. Obrigado, William. E por favor, quando você tiver algum tempo longe do trabalho, venha me visitar. Nós vamos dar algumas risadas sobre o passado. -Mildred ' WHERE `entry`=218;
UPDATE `page_text` SET `text`='Das mãos de Baros Alexston, Escritório do Arquiteto da Cidade Stormwind Vossa Majestade, Há muitas razões pelas quais eu senti que um relatório deveria ser compilado e apresentado sobre os recentes assuntos da "Irmandade Défias" e suas atividades por todo o reino. Por perspectiva, vou começar com um pouco de história. Como você pode ou não saber, meu serviço para a cidade de Stormwind começou como um membro da Guild dos Stonemasons. Através de anos de trabalho, concluímos a reconstrução de ' WHERE `entry`=219;
UPDATE `page_text` SET `text`='Stormwind, quando as contas da Guild dos Stonemasons, honorários e salários não foram pagos nem ditas. Naquela época, Edwin VanCleef havia sido eleito Guildmaster da Stonemasons, e falou, exigindo a restituição de nossos trabalhos. Em resposta, a Casa dos Nobres de Stormwind ordenou a dissolução da Guilda dos Stonemasons, o que, compreensivelmente, enfureceu VanCleef. Levando um motim, VanCleef levou os Stonemasons para fora da cidade. Antes de continuar, há alguns outros eventos que ocorreram durante ' WHERE `entry`=220;
UPDATE `page_text` SET `text`='desta vez, devo chamar sua atenção. Primeiro, foi nessa época que me ofereceram o cargo de arquiteto da cidade se não me unisse à VanCleef. Por causa de certas diferenças idealógicas, optei por permanecer em Stormwind. Durante os tumultos, o tenente e assistente mais confiável da VanCleef, Bazil Thredd, foi capturado e mantido na prisão. Aguardando julgamento e questionamento, Thredd foi quase esquecido na Stockade. Voltando a VanCleef, depois que ele liderou os remanescentes dos ' WHERE `entry`=221;
UPDATE `page_text` SET `text`='Pedreiros de Stormwind, ele se aproveitou do estado relativamente desprotegido de Westfall, e usou sua mão de obra considerável para expulsar muitos dos fazendeiros, e assumir o controle de um punhado de minas de ouro. Aproveitando os recursos à sua disposição, a VanCleef traçou um plano de vingança contra o governo de Stormwind. Esta informação veio à tona recentemente com a ajuda do portador deste documento, que foi fundamental para lançar luz sobre esta vasta conspiração. ' WHERE `entry`=222;
UPDATE `page_text` SET `text`='Dispersar informações conforme julgar necessário.\r\n\r\nComando Regional de Tirisfal\r\nCapitão Melrose\r\nCapitão Vachon\r\nCapitão Perrine\r\n\r\nDiretivas pela ordem do Highlord.\r\n\r\nCapitão Perrine, fortifique ainda mais sua posição na torre sudoeste (como designado). Suprimentos adicionais serão despachados em uma data posterior. Entretanto, o material deve ser obtido nas fazendas vizinhas. Além disso, mais reconhecimento e informação' WHERE `entry`=223;
UPDATE `page_text` SET `text`='deve ser reunido sobre a organização dos mortos-vivos em Brill.\r\n\r\nCapitão Vachon, parece haver um aumento no movimento dos mortos-vivos perto da torre norte. Esse insurgimento deve ser tratado de maneira rápida e decisiva.\r\n\r\nCapitão Melrose, há preocupações sobre o nível de organização dos mortos-vivos perto das fronteiras das Terras Pestilentas. Um novo grupo de homens será enviado para a sua posição nas próximas semanas.\r\n\r\nGlória sob a luz' WHERE `entry`=224;
UPDATE `page_text` SET `text`='Dia 1\r\n\r\nMeu tanque está quebrado, mas eu sei que o minério chiron está nessas colinas em algum lugar. Eu disse a Hammerfoot para ficar e observar nossos tanques.\r\n\r\nEu continuo a busca sozinha. Se eu não voltar, então este diário é meu último testamento.\r\n\r\n- Burton Hildelve, piloto\r\nIronforge Brigada Steam' WHERE `entry`=229;
UPDATE `page_text` SET `text`='Dia 2\r\n\r\nMinha pesquisa continua e não há minério. Eu estou querendo saber se Stonegear estava apenas inventando o rumor daquele minério como uma piada. Bem, se ele fez isso, ele vai estar ferrado quando eu voltar para Steelgrill\'s Depot!\r\n\r\nHoje à noite, enquanto preparava meu acampamento, ouvi um grunhido ecoando pelos canyons.\r\n\r\nNão é um lobo. Pode ser um urso.' WHERE `entry`=230;
UPDATE `page_text` SET `text`='Dia 3\r\n\r\nEsse rosnado continuou durante a noite e me seguiu até hoje, me distraindo da minha busca por minério. Eu acho que tem um urso me seguindo!\r\n\r\nEspero que ele chegue perto. Vou enterrar minha picareta na cabeça dele!' WHERE `entry`=231;
UPDATE `page_text` SET `text`='Dia 4\r\n\r\nMeus suprimentos estão acabando. Ainda tenho comida suficiente, mas não planejei uma estadia tão longa nos campos do lado de fora do meu tanque e só trouxe dois barris de cerveja comigo.\r\n\r\nEu estive acordado a noite toda nas últimas duas noites, ouvindo o rosnado amaldiçoado, e eu quase drenei meus barris secos!\r\n\r\nVou ter que voltar para Hammerfoot e nossos tanques de vapor amanhã. Eu não quero me perder aqui, sem bebida.' WHERE `entry`=232;
UPDATE `page_text` SET `text`='Dia 5\r\n\r\nO urso maldito me fez fugir! Me atacou ao meio-dia, rugindo e me atacando por trás.\r\n\r\nEu teria sentido seu cheiro se tivesse contra o vento - seu fedor era algo horrível! O cheiro desagradável de seu casaco sujo e seu hálito podre quase me fizeram desmaiar!\r\n\r\nEu lutei contra a coisa, mas ela mastigou minha perna. Agora eu não posso me mexer, minha cerveja se foi e eu nunca encontrei o minério. Maldições!' WHERE `entry`=233;
UPDATE `page_text` SET `text`='Dia 6\r\n\r\nO urso ainda não voltou - devo ter lhe dado uma boa surra! Mas ainda posso ouvir o seu rosnado. Eu acho que ele está esperando que eu morra!\r\n\r\nPara quem encontra este livro, tenho uma tarefa para você. Mate aquele urso sarnento. Mate-o e leve este livro de volta ao meu amigo Hammerfoot. Ele vai querer saber o que aconteceu.\r\n\r\nE pegue minha armadura. Você precisará dela contra o velho Mangeclaw!' WHERE `entry`=234;
UPDATE `page_text` SET `text`='Saudações Ello Ebonlocke, prefeito de Darkshire. Receio ter novidades para sua cidade. Notícias graves.\r\n\r\nVocê vê, eu sou um criador. Eu enganei o portador desta nota para me ajudar na minha mais recente e terrível criação - um demônio de carne e osso e metal retorcido! Enquanto você lê isso, é provável que esteja fora da minha humilde residência, rangendo os dentes e esperando que minha palavra saia e seja abatida.\r\n\r\nMas você saberá em breve.\r\n\r\n-O Embalmer' WHERE `entry`=235;
UPDATE `page_text` SET `text`='Através do estudo de várias criaturas fossilizadas deduzi que nos tempos antigos, uma grande praga varria as águas do Lago Lordamere. O que causou isso? Nós podemos nunca saber. Mas a taxa de contaminação parece ser extremamente alta com base em densas concentrações de restos distribuídos pelo leito do lago. ' WHERE `entry`=236;
UPDATE `page_text` SET `text`='Numa tentativa de descobrir o passado, comecei a examinar as criaturas do presente na esperança de encontrar a pista que faltava para esse mistério. O Lake Skulkers e Lake Creepers são animais antigos que habitam as ilhas no centro do Lago Lordamere. Há um musgo que cresce sobre eles que se assemelha a traços de materiais em alguns dos fósseis. Mais pesquisas precisam ser feitas antes que eu possa especular sobre o que essa conexão significa. ' WHERE `entry`=237;
UPDATE `page_text` SET `text`='Enquanto tentava coletar amostras de musgo, me deparei com a cena de uma batalha sangrenta. A tribo Vile Fin de Murlocs havia sido sitiada por um bando de Gnolls. Havia corpos de Gnoll e Murloc espalhados pelo campo de batalha. Quando passei por um corpo mutilado de Murloc, notei um estranho tumor endurecido saindo da ferida. Quando comecei a estudar o tumor, ficou claro que ele possuía propriedades semelhantes ao musgo que eu estava coletando. Infelizmente, não encontrei outros tumores além daquele.' WHERE `entry`=238;
UPDATE `page_text` SET `text`='Enquanto tentava coletar amostras de musgo, me deparei com a cena de uma batalha sangrenta. A tribo Vile Fin de Murlocs havia sido sitiada por um bando de Gnolls. Havia corpos de Gnoll e Murloc espalhados pelo campo de batalha. Quando passei por um corpo mutilado de Murloc, notei um estranho tumor endurecido saindo da ferida. Quando comecei a estudar o tumor, ficou claro que ele possuía propriedades semelhantes ao musgo que eu estava coletando. Infelizmente, não encontrei outros tumores além daquele.' WHERE `entry`=243;
UPDATE `page_text` SET `text`='Senhor Bethor Iceshard,\r\n\r\nDe acordo com suas instruções, eu envio para você o agente que você solicitou. É o portador desta nota.\r\n\r\nEsta pessoa demonstrou ser confiável e capaz de seguir ordens, é comprovada contra as forças do Flagelo, e é a minha escolha para a sua missão.\r\n\r\nQue ela reine suprema\r\n-Magistrate Sevren\r\n\r\n' WHERE `entry`=244;
UPDATE `page_text` SET `text`='Desaparecido: Cabo Keeshan$B$B$B$BVisto pela última vez durante a sangrenta batalha no Stonewatch Keep, o Cabo Keeshan teria sido arrastado pelos Blackrock Orcs.$B$B$B$BAcredita-se que o Cabo Keeshan está sendo mantido como prisioneiro de guerra no acampamento Blackrock, ao norte de Lakeshire, a oeste do desfiladeiro que leva às Burning Steppes.$B$B$B$BPor mandato do Magistrado Solomon, qualquer um que ajude na recuperação do Cabo Keeshan será recompensado pelo Marechal Marris.' WHERE `entry`=245;
UPDATE `page_text` SET `text`='Passei as semanas seguintes indo de um lugar para outro, nunca me demorando por medo dos Cavaleiros. Agora estou na cidade abandonada de Raven\'s Hill, como sempre, escondido. Eu não posso enfrentar qualquer poder que eles usaram contra a esposa de Sven, e eu sei que ele procura na Duskwood pela Foice. Está perdida para mim, e agradeço a Luz por isso, pois se a tivesse mantido comigo, sei que teria sido encontrado. Mesmo agora, sei em meu coração que serei encontrado.\r\n\r\nEstou tão cansado.' WHERE `entry`=246;
UPDATE `page_text` SET `text`='Saudações, Mestre\r\n\r\nPerdoe-me pelo método de envio desta mensagem. Meus magos de sombra em Alterac estão empenhados em suas tarefas, forçando-me a usar um mensageiro estrangeiro - recrutado de nossos novos "aliados" - para lhe trazer este relatório. Mas eu criptografei esta carta com uma de nossas cifras mais esotéricas. Suas palavras estão a salvo desses caipiras.' WHERE `entry`=248;
UPDATE `page_text` SET `text`='Valik, cuide do escravo até o nosso retorno. Não é seguro manter a IT aqui por mais tempo. Vamos nos mover para o norte, mais longe de qualquer possibilidade de sermos vistos. Eles são animais, mas animais brutais, no entanto - dado a quantia deles, nós teríamos motivos para nos preocupar. Melhor esconder qualquer sinal que os provocasse completamente. A última coisa que queremos dar a eles é uma razão para nos reunir e nos atacar.' WHERE `entry`=249;
UPDATE `page_text` SET `text`='Um grupo da sede Defias entrará em contato com o Colecionador, após o qual ele transferirá o ouro acumulado.\r\n\r\nCertifique-se de que este processo seja executado sem falhas e com a máxima discrição. O colecionador é responsável pela transferência de ouro, mas, em última instância, é responsabilidade de cada membro da Irmandade Defias garantir que seu papel seja representado com atenção e disciplina. ' WHERE `entry`=250;
UPDATE `page_text` SET `text`='Lembre-se, meus irmãos, já fomos artesãos orgulhosos. Desempenharemos nossos deveres atuais com a mesma precisão que usamos em nosso trabalho anterior.\r\n\r\n-EVC' WHERE `entry`=251;
UPDATE `page_text` SET `text`='Temo que meu pai tenha cometido um grave erro quando decidiu permanecer e defender nossa casa. As forças do Flagelo atacam as Clareiras de Tirisfal, e apesar de estarmos bem fortificadas e bem abastecidas, depois da traição de meu irmão Devlin, não temos a força e os números para resistir ao contínuo ataque deles. Se você receber esta carta, Yvette, então saiba que Devlin é um traidor. ' WHERE `entry`=252;
UPDATE `page_text` SET `text`='Devlin se desesperou e buscou uma fuga de nossas dificuldades, e ao fazê-lo fez um pacto com o Flagelo. Eu não sei o preço que lhe foi prometido por sua traição, mas tenho certeza que foi atado com mentiras. Se você ver meu irmão, então fuja dele. Ele é um demônio. Algumas noites atrás ele assassinou dois dos nossos vigias e deixou um pequeno grupo de Scourge passar pela brecha, levando-os para onde muitos de nós dormíamos. ' WHERE `entry`=253;
UPDATE `page_text` SET `text`='Sua emboscada foi brutal. Eles mataram um terço de nós antes que pudéssemos derrubá-los. Agora, mal temos homens suficientes para vigiar nossas fronteiras. É apenas uma questão de tempo até que a nossa guarda cansada caia aos ataques do Flagelo. E embora eu visse o rosto de Devlin entre os homens desesperados e o Flagelo de rosto magro naquela maldita noite, ele escapuliu antes que eu pudesse pegá-lo. Ele pode estar morto ou ainda viver como agente do  Flagelo. De qualquer forma, ele é um monstro. ' WHERE `entry`=254;
UPDATE `page_text` SET `text`='Devlin nos advertiu que, se tentássemos enfrentar o Flagelo, estaríamos condenados. Ele nos contou isso quando entraram pela primeira vez em Tirisfal Glades, bem antes de cercarem nossa casa. Talvez ele estivesse implorando para sairmos, ou talvez ele estivesse se regozijando. Sempre foi difícil entender os motivos de Devlin. Desde criança ele era um mistério para nós. ' WHERE `entry`=255;
UPDATE `page_text` SET `text`='Mas ele estava certo. Estamos condenados. A linhagem da família Agamand terminará. Embora eu ainda viva, sei que logo estarei morto. Não tenho medo e não me arrependo de permanecer com minha família. Mas meus últimos pensamentos serão de você. Viva, minha amada Yvette, e eu vou encontrar a paz, -Thurman ' WHERE `entry`=256;
UPDATE `page_text` SET `text`='Nossos planos progridem bem; Como mencionado, nós fizemos uma aliança com um grupo de humanos que se chamam o Sindicato. Seus líderes já foram os nobres de Alterac e eles anseiam por mais uma vez dominar aqui.\r\n\r\nE assim eles fizeram uma barganha conosco. Uma pechincha que eles vão se arrepender.' WHERE `entry`=257;
UPDATE `page_text` SET `text`='Hoje, o Sindicato tem pouco poder nessas terras, mas conhece bem a área e reuniu mão de obra suficiente para as tarefas futuras. Com orientação adequada, estou confiante de que, em breve, o Sindicato voltará a governar Alterac.\r\n\r\nAlém disso, instamos o Sindicato a almejar a cidade de Stromgarde nas proximidades de Arathi Highlands. A cidade é um campo de batalha entre as nossas forças, os ogros e os humanos de Stromgarde, mas sentimos que, com táticas cautelosas, teremos esta grande cidade.' WHERE `entry`=258;
UPDATE `page_text` SET `text`='E quando isso for feito, a próxima etapa do nosso plano pode começar. Com uma nação própria e uma base de poder nesta região, podemos preparar o caminho para o Terceiro Anfitrião.\r\n\r\nEm breve, os Lordes da Legião recompensarão nossas obras ao chover fogo dos céus mais uma vez!' WHERE `entry`=259;
UPDATE `page_text` SET `text`='E quando isso for feito, a próxima etapa do nosso plano pode começar. Com uma nação própria e uma base de poder nesta região, podemos preparar o caminho para o Terceiro Anfitrião. Em breve, os Lordes da Legião recompensarão nossas obras ao chover fogo dos céus mais uma vez!' WHERE `entry`=260;
UPDATE `page_text` SET `text`='O povo de Hillsbrad jura nossa fé e devoção à Aliança mantida pelos grandes monarcas, o Rei Magni Bronzebeard de Ironforge e o Rei Anduin Wrynn de Stormwind. Aqui reside o registro da cidade para fins de governar esta cidade justa no sopé das grandes montanhas de Alterac, bem como servir como um registro daqueles que pagaram seus impostos aos seus reis e à grande Aliança toda-poderosa.' WHERE `entry`=261;
UPDATE `page_text` SET `text`='Magistrado Rutherford Burnside Todas as dívidas liquidadas. Ferreiro Avery Verringtan Todas as dívidas liquidadas. Clerk Horrace Whitesteed Todas as dívidas liquidadas. Vereador Gillis Todas as dívidas liquidadas. Vereador Ganchos Todas as dívidas liquidadas. Fazendeiro Getz Todas as dívidas liquidadas. Farmer Ray Debt em circulação. Pagamento em bens agrícolas prometidos no momento da colheita. Fazendeiro Lyion Dívida pendente. Desalojado da terra.' WHERE `entry`=262;
UPDATE `page_text` SET `text`='Fazendeiro Kalaba Dívidas pendentes. Pagamento em bens agrícolas prometidos no momento da colheita. Citizen Wilkes Todas as dívidas pagas. Cidadão Bonoan Todas as dívidas pagas. Miner Wellty Dívida pendente. Pagamento devido na próxima entrega do minério da Azureload. Mineiro Sidney Dívida pendente. Pagamento devido na próxima entrega do minério da Azureload. Miner Hackett Dívida pendente. Pagamento devido na próxima entrega do minério da Azureload.' WHERE `entry`=263;
UPDATE `page_text` SET `text`='Miner Orwell Dívida pendente. Pagamento devido na próxima entrega do minério da Azureload. Miner Fitzgerald Dívida pendente. Pagamento devido na próxima entrega do minério da Azureload. Citizen Netherand Todas as dívidas pagas. Cidadão pode todas as dívidas pagas. Foreman Bonds Dívida pendente. Pagamento devido na próxima entrega do minério da Azureload.' WHERE `entry`=265;
UPDATE `page_text` SET `text`='Kegan Darkmar, líder do pequeno grupo de mortos-vivos que nos procurou em busca de asilo por seus "irmãos", desafia nossas atitudes comuns em relação à sua espécie. Sua pele pode estar apodrecendo e o sangue pode ter parado de fluir por suas veias, mas ele age muito nobremente e parece se importar mais com a segurança de seus compatriotas do que com a dele.\r\n\r\nDe fato, há uma humanidade dentro dele que, eu confesso, às vezes vejo falta dos humanos ao meu redor.' WHERE `entry`=266;
UPDATE `page_text` SET `text`='Mas por que eu menciono isso? Faço isso para dar crédito ao que estou prestes a escrever, pois essas palavras vieram dos lábios de Kegan e minha esperança é que meus colegas, ao ler este diário, saibam por que acredito no que ele disse:\r\n\r\n"Remanescentes dos Deuses Antigos ainda permanecem nas cavidades profundas do mundo. Novas forças procuram aproveitar esse antigo poder, e aqueles que são bem-sucedidos terão uma arma terrível contra seus inimigos."' WHERE `entry`=267;
UPDATE `page_text` SET `text`='Isso é o que Kegan pronunciou quando ele entregou seu pingente de bloodstone para mim, e havia medo, e talvez reverência, em seus olhos quando ele fez isso. E quando suas mãos se encontraram com as minhas, elas demoraram, como se relutassem em desistir do pingente. Revulsão varreu através de mim, mas até hoje eu não sei se foi sua carne morta pressionada a minha, ou se o próprio pingente fez minha pele arrepiar.\r\n\r\nPois senti um poder dentro dele. Um poder profundo, oculto e faminto. E um anseio por liberação.' WHERE `entry`=268;
UPDATE `page_text` SET `text`='Embora meus colegas em Dalaran fossem cautelosos ao estudar as pedras que Kegan e seus seguidores trouxeram consigo, em vez de colocar em quarentena os quatro refugiados e deixar as pedras do sangue em suas pessoas, a sinceridade de Kegan me obrigou a estudar seu pingente de bloodstone.' WHERE `entry`=269;
UPDATE `page_text` SET `text`='Minha esperança era comprovar para meus colegas que esse tipo de pedra possuía propriedades mágicas, e se nós, magos de Dalaran, não quiséssemos explorar o poder das pedras de sangue, deveríamos pelo menos aprender suas propriedades, já que nossos inimigos podem um dia usar elas contra nós.\r\n\r\nE assim meus estudos começaram.' WHERE `entry`=270;
UPDATE `page_text` SET `text`='Comecei meus testes com a suposição de que bloodstone era um tipo de rocha, como quartzo ou obsidiana. Então eu comecei uma série de procedimentos para determinar: quais minerais estavam contidos no bloodstone, quais forças foram aplicadas para produzir sua cor e dureza, e outras propriedades comuns às rochas e ao minério. Mas o pingente de bloodstone, para minha frustração, não reagiu aos meus procedimentos como o minério normal.' WHERE `entry`=271;
UPDATE `page_text` SET `text`='Na verdade, muitas vezes agiu exatamente da maneira oposta como esperado! Era quase como se o pingente estivesse estragando deliberadamente meus experimentos.\r\n\r\nComo se estivesse pensando e vivo.\r\n\r\nIrritado, mas não desanimado, eu mudei de assumir que o pingente era um pedaço de rocha inerte, para assumir que era uma coisa viva.\r\n\r\nMas novamente, eu falhei.' WHERE `entry`=272;
UPDATE `page_text` SET `text`='Nenhum dos meus novos testes trouxe à luz quaisquer revelações sobre a origem da bloodstone. Na época, o único enigma que resolvi foi que a pedra de sangue não era nem viva nem morta!\r\n\r\nMas foi então, à beira do fracasso, que foi feito um grande avanço. Meu último teste envolveu um copo de vidro cuja borda estava lascada, deixando um espaço pequeno e irregular ao longo de sua borda. Quando o teste acabou, novamente sem revelar nada, fui limpar minha bancada de trabalho e me cortei no béquer.' WHERE `entry`=273;
UPDATE `page_text` SET `text`='O corte não foi profundo, mas sangrou ferozmente. Antes que eu pudesse enrolar meu dedo ferido em uma bandagem, uma boa parte do meu sangue tinha derramado na minha mesa de trabalho.\r\n\r\nE enquanto limpava essa nova bagunça, notei a coisa mais estranha...' WHERE `entry`=274;
UPDATE `page_text` SET `text`='O sangue que se derramara perto do pingente de sangue estava se movendo lentamente em direção à peça de joalheria, como se a gravidade tivesse de algum modo se inclinado em direção à pedra de sangue. O sangue que tocou o pingente pareceu desaparecer, e a cor vermelha da pedra se aprofundou enquanto bebia mais do meu sangue.' WHERE `entry`=275;
UPDATE `page_text` SET `text`='Depois de ver isso, minha cabeça ficou mais leve, talvez devido ao meu ferimento recente (embora eu não acreditasse que tivesse perdido tanto sangue) ou talvez porque finalmente, depois de tanta frustração, descobri uma das propriedades do bloodstone. Cheguei atrás de mim para o meu banco de trabalho e sentei-me, ponderando. Pensamentos e perguntas correram pela minha cabeça, vertiginosos e ameaçando me derrubar.\r\n\r\nA pedra de sangue bebe sangue? Anseia por sangue? Isso atrai sangue?' WHERE `entry`=276;
UPDATE `page_text` SET `text`='Ou a pedra de sangue é feita de sangue? E se sim, então de quem é o sangue? Meu? O sangue de algum humano? Algum animal?\r\n\r\nOu talvez bloodstone seja o sangue de alguma coisa desconhecida, a mesma coisa que Kegan tanto temia quanto reverenciava quando ele me entregou seu pingente.\r\n\r\nEssa é a questão que deve ser respondida. É a chave.' WHERE `entry`=277;
UPDATE `page_text` SET `text`='O fogo reacendeu dentro de mim, então voltei aos meus experimentos. Desta vez não fiz suposições, metodicamente realizando todos os testes à minha disposição. Isso aumentou tremendamente meus esforços necessários, mas eu estaria mais propenso a fazer descobertas.\r\n\r\nE, embora meu laboratório aqui seja pequeno e eu não tenha subordinados para poupar e para ajudar, eu encontrei mais uma qualidade intrigante de bloodstone ...' WHERE `entry`=278;
UPDATE `page_text` SET `text`='Além do sangue, existem forças elementais fundidas na pedra. Fogo, água, trovão e rocha são misturados ao sangue (mas, novamente, o sangue de quê?), E embora essa mistura seja exteriormente inerte, todas essas forças parecem se enfurecer umas contra as outras. Então, muitas outras questões foram levantadas sobre esse material surpreendente e imprevisível.' WHERE `entry`=279;
UPDATE `page_text` SET `text`='Mas, para responder a essas perguntas, mais estudos e experimentos devem ser realizados no pingente, e temo que o Campo de Intermentes de Lordamere não possa reunir os recursos humanos nem o equipamento para a tarefa. Então eu enviei o pingente de bloodstone com um mensageiro para Dalaran com instruções específicas sobre como testá-lo, para que eles possam evitar minhas frustrações anteriores.' WHERE `entry`=280;
UPDATE `page_text` SET `text`='Enquanto esperava pelos resultados desses testes, passei meu tempo falando com Kegan. Embora eu constantemente o pressionasse pelo que ele sabia sobre as pedras de sangue, ele nunca me disse mais do que ele disse no dia em que me deu seu pingente. E ele não costumava falar de seu tempo dentro do rebanho do que ele chamava de "os Forsakens", que é o nome que ele chama de seu clã de mortos-vivos.' WHERE `entry`=281;
UPDATE `page_text` SET `text`='Mas Kegan estava muito ansioso para falar sobre outros assuntos, particularmente seu tempo crescendo em Lordaeron antes de sua queda.\r\n\r\nEle ainda tem muito amor por esse reino perdido, embora esteja agora arruinado e morto.\r\n\r\nMeu crescente gosto por Kegan me deu paciência enquanto eu esperava pelos resultados dos meus testes.' WHERE `entry`=282;
UPDATE `page_text` SET `text`='Mas depois de semanas sem notícias minha paciência vacilou, e depois de mais perguntas a Dalaran, fiquei sabendo que a pedra de sangue nunca alcançou meus colegas. Meu mensageiro foi de alguma forma perdido no caminho, e o pingente de bloodstone estava perdido com ele!\r\n\r\nEsta é uma notícia grave, pois, embora Kegan e seus seguidores ainda tenham espécimes de sangue com os quais podemos experimentar, temo que o pingente caia em mãos erradas.' WHERE `entry`=283;
UPDATE `page_text` SET `text`='Enviei outro mensageiro a Dalaran e ouço que, mesmo agora, eles estão vasculhando o pingente, nas ruínas fora de nossa esfera de proteção.\r\n\r\nEu só espero que não seja tarde demais.' WHERE `entry`=284;
UPDATE `page_text` SET `text`='Conto de Gri\'lek, o Andarilho [...Os primórdios da tabuinha foram usados e apagados. Mas o final era legível ...] Gri\'lek atravessou a selva. E seus olhos queimaram e seu peito retumbou, pois havia muita raiva nele. Em fúria, ele rugiu para o céu e levantou o braço. Ele levantou o braço esquerdo, cresceu forte e seguro de caçar sem seu gêmeo. Pois o braço direito de Gri\'lek desaparecera e não voltaria.' WHERE `entry`=285;
UPDATE `page_text` SET `text`='LEGENDAS DO TROLLS, VOLUME III Pedra das Marés. Pela mão do Arquimago Ansirem Runeweaver Dalaran INTRODUÇÃO O antigo Império Gurubashi foi uma fonte de muitas lendas fascinantes e intrigantes que podem ser sem dúvida rastreadas até seus arredores, como exames de seus sistemas de crenças e práticas sociais têm apontado para uma grande reverência por seu ambiente natural. Enquanto eu mergulhei em muitos' WHERE `entry`=286;
UPDATE `page_text` SET `text`='Kurdran Wildhammer\r\n\r\nRenomado Lutador do Dragão. Grifo Mestre do Aerie Peak. Comandante da Divisão de Gryphon Rider, ligada à Expedição da Aliança, que marchou para o lar orc de Draenor. Presume-se falecido.\r\n\r\nVamos ouvir as chamadas de Sky-Ree aos ventos. Vamos ouvir o seu trovão de martelo no topo das montanhas. Dirija duro para o além, irmão. Os salões dos nossos antepassados esperam por você.\r\n\r\n- High Thane Falstad Wildhammer' WHERE `entry`=287;
UPDATE `page_text` SET `text`='Capitão de Guarda-florestal Alleria Windrunner\r\n\r\nCaçador de Troll renomado de Quel\'Thalas. Agente Principal e Agente de Inteligência para a Expedição da Aliança que marchou para o lar orc de Draenor. Presume-se falecido.\r\n\r\nSeu coração voou direto como qualquer flecha no vento, irmã. Você foi o mais brilhante da nossa Ordem. Você era o mais amado de nossos parentes.\r\n\r\n- Sylvanas Windrunner - Ranger General de Quel\'Thalas' WHERE `entry`=288;
UPDATE `page_text` SET `text`='Arquimago Hadggar do Kirin Tor\r\n\r\nEx-aprendiz de Medivh. Supremo Comandante da Expedição da Aliança que marchou para o mundo orc de Draenor. Presume-se falecido.\r\n\r\nNunca alguém tão abnegadamente mergulhou no coração sombrio da magia e da guerra. Desejamos-lhe bem, viajante corajoso. Onde quer que você esteja.\r\n\r\n- Antonidas - Arquimago de Dalaran' WHERE `entry`=289;
UPDATE `page_text` SET `text`='General Turalyon $B$BEx-Tenente de Lorde Anduin Lothar. Cavaleiro da Mão de Prata. Alto General da Expedição da Aliança que marchou para o mundo orc de Draenor. Presume-se falecido. $B$BEsarus thar no\'Darador \'- Por Sangue e Honra Nós Servimos. $BVocê era a mão mão direita da justiça e da virtude, velho amigo. Seu nome será honrado em nossas salas sempre.$B$B- Lord Uther, o Arauto da Luz - Cavaleiro da Mão de Prata $B' WHERE `entry`=290;
UPDATE `page_text` SET `text`='Danath Trollbane\r\n\r\nComandante da Milícia de Stromgarde.Tactical Advisor do General Turalyon, Comandante da Força da Expedição da Aliança que marchou para o mundo orc de Draenor. Presume-se falecido.\r\n\r\nNós honramos sua memória, sobrinho e seu sacrifício. Desde a fundação do nosso glorioso império, o caminho para a bravura sempre foi encharcado com o sangue dos heróis.\r\n\r\n- Thoras Trollbane, senhor de Stromgarde' WHERE `entry`=291;
UPDATE `page_text` SET `text`='Aqui está Uther, o Lightbringer\r\nPrimeiro Paladino - Fundador da Ordem da Mão de Prata\r\n\r\nUther viveu e morreu para defender o reino de Lordaeron. Embora ele tenha sido traído por seu aluno mais querido, acreditamos que seu espírito continua vivo. Ele continua a cuidar de nós, mesmo quando as sombras se fecham em torno de nossa terra arruinada. Sua luz é a luz de toda a humanidade - e enquanto honrarmos seu exemplo, ela nunca desaparecerá.\r\n- anônimo' WHERE `entry`=292;
UPDATE `page_text` SET `text`='aspectos de seu culto às cobras em volumes anteriores deste estudo, eu apresento aqui um exame da relação interessante e única dos trolls com o mar. O GRANDE MAR O Império Gurubashi foi cercado em três lados pelo mar, por isso não é de surpreender que a água figurasse proeminentemente como um aspecto de sua sociedade. Enquanto os trolls eram capazes de vaguear e controlar as grandes áreas de seu império da selva, o mar escapava deles. Era vasto e imensurável, não' WHERE `entry`=293;
UPDATE `page_text` SET `text`='duvide de um vizinho desconcertante para os poderosos trolls. Deve-se notar aqui que descobertas recentes parecem sugerir que os trolls tinham pouco interesse em explorar as terras além do Grande Mar. Enquanto as espécies de trolls foram encontradas ao longo do comprimento e largura de Azeroth, Khaz Modan e Lordaeron, poucas evidências de sua civilização foram encontradas nas recém-descobertas terras de Kalimdor ou nas ilhas dos mares do sul. Se isso demonstra uma falta de vontade' WHERE `entry`=294;
UPDATE `page_text` SET `text`='dos trolls, para se aventurarem longe de suas posses terrestres, ou um fracasso de sua parte em desenvolver as tecnologias necessárias para realizar tal jornada, necessitarão de mais pesquisas e análises que estejam fora do escopo de meus escritos aqui. Mas dificilmente se pode ignorar uma presença tão grande, e novas descobertas nas extensas ruínas de trolls do Vale Stranglethorn mostram um aspecto de sua relação com o mar até então desconhecido e não documentado. A PEDRA DAS MARÉS Descobertas recentes durante pesquisas' WHERE `entry`=295;
UPDATE `page_text` SET `text`='das ruínas troll de Vale Stranglethorn mostraram referências a um objeto conhecido como a "Pedra das Marés". Vários fragmentos de lendas dos trolls podem ser reunidos para pintar um quadro bastante completo da Pedra e sua importância para o antigo Império Gurubashi. Parece que a Pedra das Marés permitiu que seu portador controlasse a água em suas diversas formas, rios, chuva e marés. Por causa das histórias relacionadas ao uso da Pedra das Marés, conjecturei que é' WHERE `entry`=296;
UPDATE `page_text` SET `text`='na verdade, uma manifestação física dos poderes do Senhor da Água, um poderoso elemental dos mares. Como e por que tal objeto deixaria o controle do Senhor da Água e cairia nas mãos dos trolls é outra questão que está além do escopo do meu conhecimento. O SUPORTE DA MARÉ Como os movimentos das marés epônimas, a Pedra das Marés entrou no mundo dos trolls e partiu, nunca permanecendo constantemente nas mãos dos trolls por mais de uma geração de cada vez.' WHERE `entry`=297;
UPDATE `page_text` SET `text`='Nas lendas dos trolls, diz-se que a primeira vez que a Pedra das Marés chegou ao Império Gurubashi, foi encontrada por um guerreiro troll vagando ao longo da costa do Espinhaço. Ele encontrou uma misteriosa pedra azul dentro da qual leitos brancos flutuavam e fluíam. Intrigado pela pedra, o guerreiro levou-a consigo e continuou sua jornada. Ao longo das semanas e meses, o guerreiro descobriu que a pedra lhe dera controle sobre a água. Ele poderia invocar' WHERE `entry`=298;
UPDATE `page_text` SET `text`='elementais da água, criaturas formadas completamente de água - duplicando um feito que apenas poderosos magos do Kirin Tor são capazes de realizar. O guerreiro viajou para Zul\'Gurub, para o coração do império, para mostrar suas novas habilidades ao Imperador. Ele facilmente ganhou audiência na corte depois de demonstrar seus poderes no centro da capital imperial. Seus poderes facilmente surpreenderam o Imperador Gurubashi, que imediatamente lhe deu um lugar de honra na corte, nomeando o guerreiro "Quebrador de Marés", deixando seu' WHERE `entry`=299;
UPDATE `page_text` SET `text`='nome antigo por trás. Por anos, o Quebrador de Marés serviu o Império Gurubashi, convocando seus elementais da água em batalha e manipulando o fluxo de água no Vale Stranglethorn para o benefício do Império. Mas com o passar dos anos, o Quebrador de Marés tornou-se mais recluso, tendendo a ficar por longos períodos fora da corte. O Quebrador de Marés estava escondendo um segredo dos olhos curiosos da corte. As habilidades concedidas a ele pela Pedra das Marés também vieram com uma maldição. Como os anos' WHERE `entry`=300;
UPDATE `page_text` SET `text`='passou, o Quebrador de Marés estava desaparecendo. Com cada indo e vindo das marés, o Quebrador de Marés tornou-se menos de si mesmo, perdendo sua forma corpórea - pulsando dentro e fora da existência - até que em seus últimos dias, ele viajou para a praia onde ele havia encontrado a Pedra, e entrou no mar, desaparecendo pela última vez. Gerações mais tarde, a Pedra das Marés lavou as margens de Stranglethorn, e outro Quebrador de Marés foi escolhido, como o troll trouxe a Pedra para' WHERE `entry`=301;
UPDATE `page_text` SET `text`='Zul\'Gurub. Assim, o processo continuou por gerações, a Pedra aparecendo com a maré e o Quebrador de Marés saindo na maré. Os relatos modernos da Pedra das Marés têm aparecido de um lugar para outro, mas é preciso ainda se perguntar por que um objeto com tal poder apareceria com tanta regularidade e por cujo design.' WHERE `entry`=302;
UPDATE `page_text` SET `text`='Ordens da Sela Sangrenta\r\n\r\nPreste atenção a estas palavras aqui de perto, meus corações, pois só temos uma chance em Booty Bay.\r\n\r\nMandei Ol Dunken procurando o norte e diga-lhe para esperar pelo sinal.\r\n\r\nA Sorte da Donzela soltará a âncora do cabo sul para fins de suprimento. O Capitão Stillwater, o pequeno baixinho, vai preparar sua tripulação para carregar todo o saque saqueado. Tenho certeza de que Brutus, aquele blockhead, estará cuidando de tudo isso.' WHERE `entry`=303;
UPDATE `page_text` SET `text`='Quero que o capitão Keelhaul leve a Riptide para a costa do sudeste. Enviaremos sua tripulação a pé quando o sinal for dado.\r\n\r\nAquele canhão grande, Salthoof, pode levar os homens a balançar. Eu quero o ponto preto colocado em Revilgaz e Seahorn. Mande-os direto para o fundo do mar é tudo que eu me importo.\r\n\r\nVocês são responsáveis por salvar qualquer coisa que possa ser útil para o nosso pequeno projeto com a VanCleef nas Deadmines.' WHERE `entry`=306;
UPDATE `page_text` SET `text`='Eu vou dar a ordem a Ironpatch para disparar as armas. A barragem de artilharia impedirá que qualquer navio da Blackwater saia ou entre no porto.\r\n\r\nDepois que O Véu Carmesim tiver descarregado suas armas, eu a trarei ao lado do Riptide para apoio e providencie reservas conforme necessário.\r\n\r\nUma vez que Salthoof e seus garotos dispararam a cidade e todas as mãos estão no convés, nós zarpamos e damos à Baía mais um bombardeio da Costa Selvagem para uma boa medida.\r\n\r\n--Fleet-Master Firallon' WHERE `entry`=307;
UPDATE `page_text` SET `text`='Fugir do Zanzil naquele barco podre parecia sensato na época, mas começo a pensar que a morte teria sido um destino mais adequado. Eu pensei que meus ancestrais me deram uma vantagem, permitindo-me escapar da sua ira. Mas agora eu me pergunto qual sabedoria eu vou ganhar se eu viver através da minha prisão.' WHERE `entry`=308;
UPDATE `page_text` SET `text`='Aquela besta que reina sobre esta ilha é terrivelmente poderosa. As outras criaturas aqui se encolhem diante dele: Skyman e Jaguero. Mesmo agora eu posso ouvir seus rugidos e eles me fazem recuar.\r\n\r\nEssas criaturas certamente irão impedir qualquer um que venha para a ilha. Eu preciso ter fé em meus ancestrais.' WHERE `entry`=309;
UPDATE `page_text` SET `text`='Sinto falta da minha tribo e da minha casa. Eu ficaria feliz em receber o vento nas planícies abertas ou o pôr do sol sobre o Thunder Bluff no calor desta selva. Eu posso ver a água do oceano não longe de mim, mas não faz nada para esfriar meu corpo ou meu temperamento. Isso só me lembra da minha prisão.' WHERE `entry`=310;
UPDATE `page_text` SET `text`='Dia 1: das 6h às 11h - acampamento superior das 11h às 11h30 - intervalo, almoço das 11h30 às 16h - passagem do desfiladeiro das 16h às 19h30 - acampamento superior Dia 2: horário igual ao do dia 1 Dia 3: Mesma programação Dia 4: Mesma programação, com uma exceção. Pouco antes do pôr do sol, Thorsen me cobriu com ele enquanto se aproximava do desfiladeiro. Presumi que ele fosse se encontrar com nossos agentes. Mas ele voltou uma hora depois. Isso me surpreendeu. Eu esperava que ele conhecesse nossos agentes e se juntasse a Kurzen. Algo está errado.' WHERE `entry`=311;
UPDATE `page_text` SET `text`='Dia 5: Thorsen estava muito agitado hoje. Tudo o que ele viu ou quem ele conheceu durante a sua excursão ontem deve ter assustado ele. Presumi que nossos agentes falassem com ele, mas quando perguntei a Thorsen onde ele estava enquanto eu o cobria, ele permaneceu quieto. Ele acredita que eu sou seu amigo e acredita que está me protegendo de um segredo perigoso. Suas lealdades são equivocadas, mas são fortes. Quando ele se juntar a nós, ele será uma adição valiosa para as forças de Kurzen.' WHERE `entry`=312;
UPDATE `page_text` SET `text`='Dia 6: De manhã cedo, Thorsen falou longamente com o sargento Yohwa. Eu tinha que fingir que ainda dormia e por isso não consegui me aproximar. Mas suponho que eles falaram da excursão anterior de Thorsen. Eu o observei de perto no almoço e notei Yohwa fazendo o mesmo. Mais tarde, Doren convocou Thorsen. Os três compartilham um segredo. Doren e Yohwa devem saber que Thorsen está se encontrando com seus agentes. Ele está espiando para os rebeldes.' WHERE `entry`=313;
UPDATE `page_text` SET `text`='Não devemos dar a Thorsen mais informações, e devemos matá-lo. Ele não tem mais utilidade para o nosso coronel. Eu acabarei com Thorsen se tiver a oportunidade, mas se você receber esta carta, saberá que eu ainda não tive a chance. Nesse caso, sugiro que a próxima reunião de Thorsen com nossos agentes seja fatal. Hail Kurzen, -M' WHERE `entry`=314;
UPDATE `page_text` SET `text`='O Conselho foi sábio para nomear você chefe do Argus Wake, marechal de suas estratégias na terra dos humanos e anões. E tenho a honra de servir como a mão da sua vontade em Alterac.\r\n\r\nEm nome de Sargeras,\r\n\r\nNagaz,\r\nArgus Heirofante' WHERE `entry`=315;
UPDATE `page_text` SET `text`='Tolos desastrados!\r\n\r\n\r\nMeus planos exigiam que as duas pontes fossem destruídas simultaneamente. E o que vocês, bandidos de mente simples, fazem? Você só tem metade do trabalho feito e alerta toda a Aliança quanto às nossas intenções. Tolos!\r\n\r\n\r\nVocê tem uma chance de compensar suas ações. Temos um esconderijo de explosivos em Arathi Highlands, não muito longe da ponte.\r\n\r\n\r\nTermine o trabalho se você valoriza suas vidas desprezíveis!\r\n\r\n\r\n--Kam Deepfury' WHERE `entry`=316;
UPDATE `page_text` SET `text`='Dun Modr das Wetlands$B$B Minha querida Sara,$B$BAs indicações são muito fortes para nos movermos para tomar conta do Thandol Span em poucos dias. Para que eu não seja capaz de escrever novamente para você. Sinto-me impelido a escrever algumas linhas que podem cair sob o seu olho quando eu não existir mais.' WHERE `entry`=317;
UPDATE `page_text` SET `text`='Nossa tarefa pode ser de poucos dias e cheia de prazer, e pode ser um conflito grave e morte para mim. Se for necessário que eu caia no campo de batalha da Aliança, estou pronto.$B$BEu não tenho dúvidas ou falta de confiança na causa em que estou comprometido e minha coragem não pára ou vacila.' WHERE `entry`=318;
UPDATE `page_text` SET `text`='Eu sei quão fortemente o Reino de Ironforge se apóia no triunfo da Aliança, e quão grande dívida nós devemos àqueles que se foram antes de nós através do sangue e sofrimento das Grandes Guerras, e eu estou disposto, perfeitamente disposto a estabelecer todas as minhas alegrias nesta vida para ajudar a manter esta aliança e pagar essa dívida.' WHERE `entry`=319;
UPDATE `page_text` SET `text`='Sara, meu amor por você é imortal, parece me amarrar em cabos poderosos que nada além de onipotência poderia quebrar e meu amor pelo Reino vem sobre mim como um vento forte e me leva irresistivelmente com todas essas correntes para o campo de batalha.' WHERE `entry`=320;
UPDATE `page_text` SET `text`='As lembranças de todos os momentos felizes que passei com você vêm rastejando sobre mim e eu me sinto profundamente grato à Luz e a você que eu tenho desfrutado deles por tanto tempo e como é difícil para mim desistir deles e queimar cinzas esperanças de anos futuros, quando ainda poderíamos ter vivido e amado juntos e visto nossos filhos crescerem para anões honroso ao nosso redor.' WHERE `entry`=321;
UPDATE `page_text` SET `text`='Sei que tenho poucas e pequenas reclamações sobre a Divina Providência, mas algo sussurra para mim - talvez seja a oração flutuante de meu pequeno Edgar - que eu retorne aos meus entes queridos ilesos. Minha querida Sara nunca esquece o quanto eu te amo e quando meu último suspiro me escapa no campo de batalha eu sussurro seu nome.$B$BPerdoe minhas muitas faltas e as muitas dores que causei a você, quão impensado, quão tolo eu fui muitas vezes.' WHERE `entry`=322;
UPDATE `page_text` SET `text`='Com que alegria eu lavaria com minhas lágrimas cada pequeno lugar em sua felicidade e lutaria com todas as desgraças deste mundo para proteger você e meus queridos filhos do dano.$B$BMas eu não posso.$B$BDevo observá-lo do subterrâneo e pairar perto de você enquanto você ataca as tempestades com seu precioso pequeno carregamento e espera com tristeza paciência até nos encontrarmos para nos separarmos.' WHERE `entry`=323;
UPDATE `page_text` SET `text`='Erguendo-se do oceano, uma torre de água, Neptulon enviou o grande Krakken para o condenado I\'lalai. Suas formas eram tão grandes que selvas de algas balançavam através de seus membros, e leviatãs nadavam através dos corpos. $B$B O maior Krakken, em seguida, levantou os braços e bateu no mar, enviando ondas sobre ele. E eles se enfureceram em direção a l\'alalai.' WHERE `entry`=324;
UPDATE `page_text` SET `text`='O Krakken rugiu, e suas vozes trovejaram como uma tempestade no oceano:$B$B"Nós viemos".$B$BMin\'loth, de pé firme, invocou sua magia. As ondas enviadas para I\'lalia se separaram e se lavaram para os dois lados, e inundaram a floresta além. Min\'loth então ordenou que seus lacaios cantassem feitiços de ligação, e ouviu-se um barulho quando dúzias de vozes de trolls aumentaram.$B$BE uma voz subiu acima das outras.' WHERE `entry`=325;
UPDATE `page_text` SET `text`='Min\'loth berrou e sua magia reuniu o poder dos feitiços de seu lacaio, e ele a lançou na aproximação do Krakken.$B$BOs mares se separaram e o feitiço de Min\'loth disparou em direção aos servos de Neptulon. Relâmpagos rasgaram o céu e o feitiço os atingiu, e mil setas caíram, ferveram água e queimaram crateras na terra.$B$BMin\'loth chorou em seu triunfo, sabendo que seu feitiço derrubaria as grandes feras.' WHERE `entry`=326;
UPDATE `page_text` SET `text`='Mas os Krakken são antigos, muito antigos. Eles se lembravam de quando a terra nasceu do mar. Eles se lembravam quando os Antigos governavam e quando os Viajantes vinham e os derrubavam. Eles se lembravam de quando a magia era nova. Eles são antigos e guardam muitos segredos. E embora o feitiço de Min\'loth fosse forte, ele, como o troll, era mortal. Por isso, falhou.' WHERE `entry`=327;
UPDATE `page_text` SET `text`='Não conseguiu prender o Krakken, mas os enfureceu. Não em aeons um mortal causou-lhes dor, e o feitiço do troll foi doloroso.$B$BE assim eles soltaram as amarras do feitiço de Minloth, mas depois rugiram e ficaram furiosos.$B$BUm barulho foi ouvido quando grandes ondas subiram das profundezas e correram em direção à terra. Quando chegaram a l\'alalai, lançaram uma sombra sobre a cidade.$B$BMas antes de destruí-lo, o Krakken parou, equilibrado.' WHERE `entry`=328;
UPDATE `page_text` SET `text`='Os curandeiros trolls tremiam e gritavam para seu mestre. Min\'loth contemplou as montanhas do mar, condenadas e desafiadoras. Ele se virou para seus adeptos e sussurrou, e os trolls gravaram suas últimas palavras em pedra. Min\'loth então enfrentou o Krakken.$B$BEle fez uma careta e arremessou sua staff, seu último ato ousado.$B$BO Krakken então curvou sua fúria sobre Min\'loth, e um oceano caiu sobre I\'lalai.$B$BE não era mais.' WHERE `entry`=329;
UPDATE `page_text` SET `text`='E então as águas caíram sobre a selva, lavando tudo que encontraram. Trolls e bestas gritavam enquanto as águas se espatifavam e os afogavam.$B$BMuitos Gurubashi se perguntaram por que o oceano os engoliu, mas depois eles morreram e não sabiam de nada.$B$BE finalmente, quando as águas alcançaram as montanhas, elas pararam. Elas recuaram para além das margens e deixaram um rastro de morte.$B$BElas recuaram, mas elas surgiram em torno de I\'lalai e permaneceram, afogando-o para sempre.' WHERE `entry`=330;
UPDATE `page_text` SET `text`='E o chefe Var\'gazul, seguro atrás das montanhas em Zul\'Gurub, foi para a selva e encontrou-o limpo de seu povo.$B$BE ele se desesperou, pois seus sonhos de conquista foram frustrados.$B$BE nunca foi Min\'loth a Serpente encontrada.' WHERE `entry`=331;
UPDATE `page_text` SET `text`='Mas, oh, Sara, se os mortos puderem voltar a Azeroth e fugir sem serem vistos em torno daqueles que amam, estarei sempre perto de você no dia mais feliz e na noite mais escura em meio a suas cenas mais felizes e sempre mais sombrias sempre e sempre, a brisa em suas bochechas, será meu fôlego, como o ar frio ventila sua tempora pulsante, será meu espírito passando.$B$BSara, não me lamentem, pense que eu estou fora e espero por ti, pois nos encontraremos novamente. $B$B- Sully' WHERE `entry`=332;
UPDATE `page_text` SET `text`='Para o meu honorável rei:Não é o lugar da esposa de um soldado humilde questionar as ações de grandes líderes como você. As batalhas que grassam em nossas terras estão sendo travadas para preservar a grande vida que conhecemos e amamos, desde as águas frias do Loch Modan até os picos nevados de Dun Morogh.' WHERE `entry`=333;
UPDATE `page_text` SET `text`='Mas as guerras que travamos em nome da preservação estão por vir a que custo? Após a morte de que soldado caído percebemos que a própria vida que nos esforçamos para salvar está nos escapando no campo de batalha?$B$BSob quantas espadas e machados devemos lançar nosso futuro? Sob quantos pés de terra encharcada devemos enterrar nosso passado?' WHERE `entry`=334;
UPDATE `page_text` SET `text`='Não é o lugar da esposa do soldado humilde fazer essas perguntas. $ B $ B No entanto, eu escrevo para você agora como a viúva do soldado humilde. Meu marido pode ter sido um soldado nas fileiras de milhares para você. Para mim, ele era o grande Sully Balloo, pai dos meus filhos e meu único parceiro nesta vida.$B$BPerdoe-me se minhas perguntas para você, oh grande líder, soarem inapropriadas. Mas os mortos não podem fazer essas perguntas por si mesmos...$B$BSeu Assunto Leal,$BSara Balloo' WHERE `entry`=335;
UPDATE `page_text` SET `text`='Para os Schneiders, bom dia. Eu confio que os negócios no distrito do canal é bom. Estamos crescendo aqui na praça do comercio. Precisamos de artigos de malha, pois a nossa oferta está praticamente esgotada. Por favor, envie a carga padrão assim que você puder. Boa sorte, e espero que os estudos do seu filho Thurman progridam bem, - Harlan Bagley' WHERE `entry`=336;
UPDATE `page_text` SET `text`='Encomendas oficiais para a frota de Kul Tiras\r\n\r\nEu, almirante Proudmoore, liderarei o primeiro desembarque nas margens de Kalimdor.\r\n\r\nO tenente Benedict será o segundo em comando na minha ausência. Todas as questões militares e domésticas de Kul Tiras serão tratadas por ele. Se eu não mandar uma mensagem da Fortaleza de Tiragarde dentro de 12 meses, o tenente Bento levará a segunda onda para segurar o local.' WHERE `entry`=337;
UPDATE `page_text` SET `text`='Enquanto isso, estou enviando o tenente Alverold em uma missão exploratória ao redor do continente de Kalimdor. Suas forças são para completar sua missão e depois se juntar às nossas forças na Fortaleza de Tiragarde.\r\n\r\nPrevê-se que a missão do tenente Alverold durará algum tempo. A nova terra parece vasta e perigosa. Mas se algum infortúnio acontecer a mim ou a Bento, as tropas de Alverold retomarão a Fortaleza.\r\n\r\nNós enviaremos onda após onda até que essa fortaleza seja nossa!\r\n\r\n- Almirante Proudmore' WHERE `entry`=338;
UPDATE `page_text` SET `text`='<HTML>\r\n<BODY>\r\n<H1>Registro de recrutamento da Horda</H1>\r\n<BR></BR>\r\n<P>Esta pessoa demonstrou feitos incríveis de força e habilidade em nome da Horda. Eles devem ser imediatamente designados para trabalhar na Crossroads.</P>\r\n<BR></BR>\r\n<BR></BR>\r\n<P>Respeitosamente:</P>\r\n<BR></BR>\r\n<BR></BR>\r\n<BR></BR>\r\n<BR></BR>\r\n<P>Parece bom para mim.<BR></BR>\r\n- Takrin Pathseeker</P>\r\n</BODY>\r\n</HTML>' WHERE `entry`=340;
UPDATE `page_text` SET `text`='<HTML>\r\n<BODY>\r\n<H1>Registro de recrutamento da Horda</H1>\r\n<BR></BR>\r\n<P>Esta pessoa demonstrou feitos incríveis de força e habilidade em nome da Horda. Eles devem ser imediatamente designados para trabalhar na Crossroads.</P>\r\n<BR></BR>\r\n<BR></BR>\r\n<P>Respeitosamente:</P>\r\n<BR></BR>- Kargal Battlescar</P>\r\n<BR></BR>\r\n<BR></BR>\r\n<BR></BR>\r\n<P>Parece bom para mim.<BR></BR>\r\n- Takrin Pathseeker</P>\r\n</BODY>\r\n</HTML>' WHERE `entry`=341;
UPDATE `page_text` SET `text`='A escavação de Bael Modan\r\n\r\nA equipe de pesquisa original enviada pela Liga dos Exploradores estava de fato correta em suas descobertas. O solo aqui contém artefatos de valor ilimitado para o nosso povo. Sem dúvida, encontraremos muitas respostas em nossa busca de conhecimento sob a areia e a rocha de Bael Modan.\r\n\r\nA geologia da região dita que usamos medidas extremas no entanto. Muitos dos depósitos de areia se solidificaram sob condições adversas e climas variáveis.' WHERE `entry`=342;
UPDATE `page_text` SET `text`='No entanto, a solução é bem simples. Usando polpa de madeira como absorvente, podemos combinar traços de nitroglicerina com nitrato de sódio para desenvolver uma forte carga de detonação, capaz de romper até mesmo as massas mais densas.\r\n\r\nO trabalho será ruidoso e perturbador, mas nossa busca é de muito maior importância do que o conforto dos habitantes locais. Na verdade, nós já tivemos que expulsar um bando de homens-touro da área que estavam provando ser um incômodo.' WHERE `entry`=343;
UPDATE `page_text` SET `text`='O fato de estarmos lidando com vários elementos nocivos aqui fora me leva a acreditar que precisaremos do apoio do Exército do Rei. Não apenas a escavação exigirá apoio militar, como sua principiante pareceria, a localização de Bael Modan poderia ser de valor estratégico para a Aliança, considerando a volatilidade da política mundial em seu estado atual.\r\n\r\nEu já me divaguei. Há muito trabalho a ser feito sob a rocha. Eu gastei muito tempo escrevendo e não estou cavando o suficiente...' WHERE `entry`=344;
UPDATE `page_text` SET `text`='Texto Ausente' WHERE `entry`=345;
UPDATE `page_text` SET `text`='O fato de estarmos lidando com vários elementos nocivos aqui fora me leva a acreditar que precisaremos do apoio do Exército do Rei. Não apenas a escavação exigirá apoio militar, como sua principiante pareceria, a localização de Bael Modan poderia ser de valor estratégico para a Aliança, considerando a volatilidade da política mundial em seu estado atual. Eu já me divaguei. Há muito trabalho a ser feito sob a rocha. Eu gastei muito tempo escrevendo e não estou cavando o suficiente...' WHERE `entry`=349;
UPDATE `page_text` SET `text`='O console de controle do samophlange pode ser usado para regular automaticamente o fluxo de combustíveis, gases, líquidos e maquinário relacionado. Uma vez que a configuração inicial tenha sido concluída, ela deve ficar livre de preocupações com pouca manutenção.\r\n\r\nListagem resumida dos componentes do console de controle (da esquerda para a direita)\r\n\r\nI. Alavanca Principal de Potência\r\n\r\nA posição engatada liga o samoflange. O mecanismo de controle não será desativado automaticamente quando a alavanca for colocada na posição desligada. Isso meramente' WHERE `entry`=350;
UPDATE `page_text` SET `text`='O console de controle do samophlange pode ser usado para regular automaticamente o fluxo de combustíveis, gases, líquidos e maquinário relacionado. Uma vez que a configuração inicial tenha sido concluída, ela deve ficar livre de preocupações com pouca manutenção. Listagem resumida dos componentes do console de controle (da esquerda para a direita) I. Alavanca Principal de Potência A posição engatada liga o samoflange. O mecanismo de controle não será desativado automaticamente quando a alavanca for colocada na posição desligada. Isso meramente' WHERE `entry`=351;
UPDATE `page_text` SET `text`='Supervisor Fizsprocket, por favor, encontre uma lista dos equipamentos requisitados, aprovados pelo Presidente Razdunk. Ele pediu que eu lembrasse e enfatizasse a você o interesse da EVenture Company em estabelecer operações em Kalimdor. Nossos postos avançados de perfuração e mineração nos Barrens provaram ser lucrativos e eficientes, mas ainda esperamos números suficientes de produção de Mulgore, sua jurisdição. O conselho consultivo entende as dificuldades em estabelecer' WHERE `entry`=352;
UPDATE `page_text` SET `text`='uma operação em grande escala tão próxima da pátria de tauren, mas não consideramos que ela represente um grande impedimento para o plano geral de negócios na área. Ficamos felizes em saber que a mineração começou nas planícies ricas em minerais de Mulgore. Por isso, a diretoria aprovou seu pedido de equipamento. Além da mineração, sentimos que Mulgore também tem muito a oferecer em termos de madeira, assim você também será fornecido com vários dos nossos mais novos trituradores e matérias-primas para construir' WHERE `entry`=353;
UPDATE `page_text` SET `text`='o moinho.' WHERE `entry`=354;
UPDATE `page_text` SET `text`='comece o processo de cooldown, em que ponto levará aproximadamente um a dois minutos (dependendo da situação) para desligar.\r\n\r\nNOTA IMPORTANTE: A alavanca de potência principal só pode ser manipulada quando as três válvulas de controle na máquina real foram desengatadas. Essas válvulas podem ser desligadas em qualquer ordem.\r\n\r\nA primeira é a VÁLVULA DE CONTROLE PRINCIPAL, que pode ser encontrada na base da grande chaminé vertical. Em seguida, a VÁLVULA DE CONTROLE DE COMBUSTÍVEL pode ser encontrada no tubo grande que conduz' WHERE `entry`=355;
UPDATE `page_text` SET `text`='dos tanques de combustível (deve haver três deles) para a chaminé. Finalmente, a VÁLVULA REGULADORA pode ser encontrada na parte inferior dos dois grandes tanques cilíndricos que fluem para a chaminé.\r\n\r\nII. Medidores de Combustível\r\n\r\nExibe a pressão atual nos tanques de combustível primário, secundário e terciário.\r\n\r\nIII Medidor de fluxo\r\n\r\nExibe a taxa atual de fluxo no sistema.' WHERE `entry`=356;
UPDATE `page_text` SET `text`='IV. Controles de Fluxo\r\n\r\nAumentar ou diminuir as taxas de fluxo. Isso é controlado automaticamente pelo mecanismo de controle samoflange.\r\n\r\nV. Medidor de Temperatura\r\n\r\nExibe a temperatura de operação do sistema em geral.' WHERE `entry`=357;
UPDATE `page_text` SET `text`='Por muito tempo os filhos das estrelas habitaram as margens das águas cintilantes do Poço Eterno. A todos se sabia que Elune, luz do crepúsculo eterno, aspecto e deusa da lua, habitava dentro de suas águas no momento em que ela descansava de suas obras. Nas margens do Poço, os filhos das estrelas, favorecidos por Elune, construíram suas casas, mesmo com o olhar voltado para o céu, para a noite enluarada. Embora houvesse muitas cidades e lugares de habitação construídos' WHERE `entry`=358;
UPDATE `page_text` SET `text`='A terra tremeu quando as antigas árvores da floresta encantada foram arrancadas e derrubadas. Os bosques e clareiras tendidos pelos filhos e filhas de Cenarius e as torres de pedra dos filhos das estrelas foram trazidos para o solo. Havia nossa rainha, radiante até no desespero, no caos que eram as batalhas. O céu encantado mudou de cor com a descarga de magia, com as explosões que ameaçavam dilacerar o mundo.' WHERE `entry`=359;
UPDATE `page_text` SET `text`='A terra tremeu quando as antigas árvores da floresta encantada foram arrancadas e derrubadas. Os bosques e clareiras tendidos pelos filhos e filhas de Cenarius e as torres de pedra dos filhos das estrelas foram trazidos para o solo. Havia nossa rainha, radiante até no desespero, no caos que eram as batalhas. O céu encantado mudou de cor com a descarga de magia, com as explosões que ameaçavam dilacerar o mundo.' WHERE `entry`=360;
UPDATE `page_text` SET `text`='Mestre Delgren,\r\n\r\nÉ como você temia. Sua teoria sobre a Torre de Althalaxx provou ser verdade. Um culto se reuniu na torre com muitos bruxos entre eles, incluindo até mesmo um sátiro imundo. Eles se chamam de Culto da Cadeia Negra. Eu não posso imaginar o propósito vil que os une, mas isso me preocupa.\r\n\r\nPor favor, perdoe a tensão em que eu compus esta missiva, mas o perigo espreita na floresta ao meu redor, e posso poupar algumas palavras para descrever a situação. Eu' WHERE `entry`=361;
UPDATE `page_text` SET `text`='na margem do lago, Ameth\'Aran e Bashal\'Aran eram os principais, tendo o toque de Azshara, a amada rainha dos Kaldorei, em sua criação. Seus servos favorecidos, os dos Highborne, ela trouxe para as cidades gêmeas para residir, e para governá-los ela colocou Athrikus Narassin, apesar de que ele passaria a maior parte de seus dias em sua Torre de Althalaxx, algumas maneiras removidas do cidades. Seu segundo, um mago conhecido por suas proezas nas artes arcanas, tanto quanto com suas habilidades físicas,' WHERE `entry`=364;
UPDATE `page_text` SET `text`='foi Asterion, que viveu entre o povo de Bashar\'Aran, e foi muitas vezes a Ameth\'Aran, para atender às necessidades e desejos de seu povo. E assim foi, nas margens do Poço da Eternidade, as cidades gêmeas cresceram e prosperaram, enquanto o mundo sob as estrelas caiu lentamente para a loucura.' WHERE `entry`=365;
UPDATE `page_text` SET `text`='abençoado. Não foi todo o Highborne que lutou. Alguns ficaram paralisados quando a loucura os levou. Cidades poderosas e cidades humildes foram derrubadas em torno deles. Em Ameth\'Aran, tudo estava em ruínas, seu povo se agarrava ao chão como se isso pudesse poupá-los da destruição. Foi então que Athrikus Narassin, favorecido por Azshara, apareceu para guiá-los à segurança. Com um feitiço ele poderia protegê-los, ele disse. Enquanto em torno deles, irmão e irmã, pai e mãe pereceram,' WHERE `entry`=366;
UPDATE `page_text` SET `text`='eles teriam a vida eterna, e nada teriam a temer do Sundering que estava por vir...' WHERE `entry`=367;
UPDATE `page_text` SET `text`='eles teriam a vida eterna, e nada teriam a temer do Sundering que estava por vir...' WHERE `entry`=371;
UPDATE `page_text` SET `text`='A estrela de Xil\'yeh\r\n\r\nGrel\'borg, o Miser, tem a Estrela. Ele é um ogro ganancioso que passa seus dias nas Montanhas Alterac, nas Ruínas de Alterac, em busca de bugigangas. A maior parte de sua coleção é inútil, mas um item, a Estrela de Xil\'yeh, tem propriedades valiosas.' WHERE `entry`=372;
UPDATE `page_text` SET `text`='A mão de Dagun\r\n  \r\nTextos antigos afirmam que garras de Dagun excretam um veneno que não mata sua presa, mas a transforma em outra coisa - um membro das antigas raças. Seu valor para nós é inquestionável.\r\n\r\nDagun vive no fundo do mar, mas é regularmente atraído para a superfície por uma tribo de Mirefin Murlocs em Dustwallow Marsh. Seus oráculos o convocam com uma alga marinha encantada. Se você matar oráculos suficientes, encontrará a alga marinha. Em seguida, coloque-o no altar de Murloc e Dagun virá.' WHERE `entry`=373;
UPDATE `page_text` SET `text`='O Legacy Heart\r\n\r\nMogh, o Imortal, é um feiticeiro troll em Stranglethorn. E ele possui o Legacy Heart, dito para evitar a morte para aqueles que podem desvendar seus segredos. Você encontrará Mogh nas Ruínas de Zul\'Mamwe. Derrote-o, se puder, e traga-me o Legacy Heart.' WHERE `entry`=374;
UPDATE `page_text` SET `text`='Traga-me estes três itens, a Estrela, a Mão e o Coração, e o Yagyin\'s Digest será seu.\r\n\r\n-G.B.' WHERE `entry`=375;
UPDATE `page_text` SET `text`='A Estrela de Xil\'yeh\r\n  \r\nUm ogro em Alterac, Grel\'borg the Miser, foi o último proprietário conhecido desta jóia, sussurrou para capacitar seu dono com a habilidade de comungar com outros mundos. Fontes nos dizem que Grelborg perambula pelas Ruínas de Alterac nas Montanhas Alterac, procurando pelos escombros por mais relíquias.' WHERE `entry`=376;
UPDATE `page_text` SET `text`='A Mão de Dagun\r\n  \r\nDagun é uma criatura das águas profundas, adorada por Murlocs em Dustwallow Marsh. Eles o atraem para a superfície com uma alga marinha especial, encantada por seus xamãs. Cace os Murlocs, junte suas algas encantadas e coloque-as no altar de Murloc. Dagun virá e você estará esperando.\r\n\r\nEu quero essa mão.' WHERE `entry`=377;
UPDATE `page_text` SET `text`='O Legacy Heart\r\n\r\nTextos antigos dizem que aquele que possui o Legacy Heart não teme a sepultura. Talvez isso seja verdade, pois o dono do coração, o troll Mogh, é conhecido como Mogh, o Imortal. Um feiticeiro da tribo Skullsplitter, Mogh mora em Stranglethorn nas Ruínas de Zul\'Mamwe.' WHERE `entry`=378;
UPDATE `page_text` SET `text`='Traga-me estes itens, a Estrela, a Mão e o Coração, e eu lhe darei uma cópia do Yagyin Digest.\r\n\r\n-O Guardião' WHERE `entry`=379;
UPDATE `page_text` SET `text`='Relatório de Missão do Espreitador da Morte\r\n\r\nAgentes: Rane Yorick, Quinn Yorick, Erland McKree\r\n\r\nMissão principal: realizar reconhecimento através do norte de silverpine e determinar os níveis de ameaça da vida selvagem e do Flagelo.' WHERE `entry`=380;
UPDATE `page_text` SET `text`='Os agentes começaram a varredura, encontrando presença significativa de worgs. Recomende esquadrões de caça para reduzir essa ameaça.\r\n\r\nGnolls mortos-vivos foram encontrados na fazenda, apelidados de The Dead Field. Seu propósito na fazenda não é conhecido, embora seus movimentos e nível de prontidão sugiram uma ação militar iminente. No tempo em que foram observados, nenhum líder notável foi encontrado entre os gnolls. Supõe-se que eles aguardem a chegada da liderança ou reforços.' WHERE `entry`=381;
UPDATE `page_text` SET `text`='Continuando a varredura, nossos agentes foram detidos na fazenda de Ivar. Ivar, a maioria de sua família e seus trabalhadores sucumbiram ao Flagelo e se tornaram seus subordinados.\r\n\r\nNossos agentes foram atacados e, embora tenham tido sucesso em derrotar seus emboscadores, sofreram ferimentos graves, particularmente o agente Quinn.' WHERE `entry`=382;
UPDATE `page_text` SET `text`='Uma posição defensiva foi tomada na antiga casa de Ivar e, enquanto Rane vigiava os futuros ataques, Erland continuou a missão de reconhecimento.\r\n\r\nErland foi então preso por Worgs em um pomar do norte, escapando apenas com a ajuda de outro agente dos Renegados. Esse agente é o portador deste relatório.' WHERE `entry`=383;
UPDATE `page_text` SET `text`='Deve-se notar aqui que este portador do relatório foi fundamental não apenas no sucesso de nossa missão, mas também na derrota de Ivar, no resgate de Erland e na divulgação deste relatório.\r\n\r\nNós estendemos nossa gratidão, e recomendamos que o comando reconheça este indivíduo com prêmios condizentes com a conduta meritória que foi exibida no campo.\r\n\r\n-Deathstalker Rane Yorick,\r\nLíder de Missão' WHERE `entry`=386;
UPDATE `page_text` SET `text`='E assim ele vagou e procurou. E seu braço permaneceu perdido para ele. E então ele amaldiçoou e rugiu enquanto andava.\r\n\r\n\r\nMas Gri\'lek há muito tempo afastara sua orelha dos espíritos, e eles estavam irados e não escutavam suas maldições.\r\n\r\n\r\nCondenado foi Gri\'lek. Condenado a vagar, sem braços.' WHERE `entry`=387;
UPDATE `page_text` SET `text`='Uma força maligna criou estes, Ele senta no Pico Dreadmist à vontade, Ele ri de ti e teu amigo, Seus elementais não têm fim, Logo você deve confrontar este mago, Pois somente a morte acabará com sua raiva.' WHERE `entry`=394;
UPDATE `page_text` SET `text`='O Santuário de Mel\'Thandris "Que a luz de Elune brilhe sobre este vale eterno e ilumine os caminhos da floresta como tem sido por tempos imemoriais."' WHERE `entry`=395;
UPDATE `page_text` SET `text`='Elune concedeu meus desejos. As vidas de meus amigos caídos não serão em vão, e eu vingarei suas mortes usando a Foice de Elune. É uma coisa antiga, um ramo, torcido e retorcido de madeira que poderia ser mais antigo que os Kaldorei. Por muitas horas eu examinei a ferramenta que a deusa me concedeu. Não é uma arma comum, isso é certo, nem é um simples instrumento mágico. Com isso... é como se as barreiras do tempo e do espaço estivessem enfraquecidas. Segurando a Foice' WHERE `entry`=396;
UPDATE `page_text` SET `text`='nas minhas mãos, recebi uma visão do caos. Homens-lobo... os worgen... lutaram contra um inimigo incrível. Os worgen lutavam selvagemente, como se encaixassem em sua raça primitiva, mas seu inimigo era inflexível: os Senhores da Chama Esmeralda. E foi então que percebi o verdadeiro poder da Foice. Concentrando-me nisso, tornei-me capaz de me comunicar com os worgens. Não era a fala que eu costumava contatá-los, no entanto. Foi algo diferente... não consegui descrever. O que importava era que os worgen ouvissem' WHERE `entry`=397;
UPDATE `page_text` SET `text`='e me entendia. Ao canalizar ainda mais energia para a Foice, as barreiras enfraqueceram mais, e consegui atrair os worgens para o nosso mundo. Uma pontuação e meia consegui convocar em minha primeira tentativa. Eles me seguiram até a batalha e, com sua força bruta ao meu comando, nós invadimos os demônios de Felwood. Elune concedeu meus desejos. A floresta será limpa. Que as bênçãos de Elune estejam comigo até que meu trabalho esteja terminado.' WHERE `entry`=398;
UPDATE `page_text` SET `text`='Tudo vai bem. Eu convoquei um grande número de worgens para lutar ao meu lado. Embora sejam de mente simples, demonstraram capacidade impressionante de coordenar seus ataques e funcionar como um grupo. Eu identifiquei líderes em seus grupos. A eles dei o comando de pequenos grupos de guerreiros. Nós atacamos de muitos lugares na floresta de uma só vez, e minha campanha contra os demônios é frutífera. Que as bênçãos de Elune estejam comigo até que meu trabalho esteja terminado.' WHERE `entry`=399;
UPDATE `page_text` SET `text`='Um grupo foi perdido. Enviei os worgens para percorrer a floresta e identificar áreas de infestação demoníaca, mas eles não retornaram. Começo a me preocupar com os líderes aos quais confiei o controle. Eles são de mente simples, e este pequeno pedaço de autoridade que eu dei a eles, eles se estendem ao máximo. Estou perdendo o controle? Talvez eu pare de invocar worgen adicionais por enquanto. Que as bênçãos de Elune estejam comigo até que meu trabalho esteja terminado.' WHERE `entry`=400;
UPDATE `page_text` SET `text`='Embora eu não tenha convocado worgens adicionais, o número deles continua a aumentar. É como se a Foice já não precisasse da minha intervenção para o processo de convocação. Isso é preocupante. Reuni o maior número de matilhas que pude encontrar e ordenei que ficassem no Santuário de Mel\'Thandris. Enquanto isso, procurei nas bibliotecas de Darnassus e consultei o Círculo dos Antigos em Darkshore. Nenhum fragmento de informação sobre esses worgen eu encontrei. Eu ouvi relatos,' WHERE `entry`=401;
UPDATE `page_text` SET `text`='sussurros, de um mago do Kirin Tor chamado Arugal. Pelo que ouvi, é possível que ele também convoque worgen. Eu consultaria esse mago - por mais desagradável que fosse - e veria o que ele sabe. Os worgens são perigosos e eu quero conter a maré. No dia seguinte viajarei para o porto dos Barrens e reservarei a passagem para o Novo Mundo. Que as bênçãos de Elune estejam comigo até que meu trabalho esteja terminado.' WHERE `entry`=402;
UPDATE `page_text` SET `text`='Eu comecei este diário para que eu pudesse fazer anotações em minha missão para ajudar Ashenvale. Há um mistério para alguns dos eventos que ocorreram não muito tempo atrás, e espero que eu seja capaz de desvendar alguns deles para ajudar o meu povo.\r\n\r\nDeixe-me começar por colocar fatos para que eles não escorreguem na minha mente em uma data posterior.\r\n\r\nEu encontrei recentemente a verdade aos rumores dos esforços do assistente Dartol.' WHERE `entry`=403;
UPDATE `page_text` SET `text`='Onde uma vez eu achava absurdo que um humano viesse a nossas terras e aprendesse nossos caminhos, esse bruxo, esse homem maligno, veio aqui e não apenas sobreviveu, mas também ganhou um pouco de nosso conhecimento. Ainda não tenho certeza de como...\r\n\r\nEu nunca descobri seus objetivos durante a minha investigação, mas seu plano parecia simples: usando magia druídica, ele queria fazer amizade e depois controlar os furbolgs. Se foi em uma tentativa de prejudicar meus parentes, ou por algum outro propósito malicioso, espero descobrir antes que meu caminho termine.' WHERE `entry`=404;
UPDATE `page_text` SET `text`='Independentemente disso, ele foi aparentemente mais bem sucedido do que nada na realização de seus planos. Ele queria tentar suas maquinações nos furbolgs antes de tentar no meu povo? Os furbolgs tinha algo que ele cobiçava? Quem sabe?\r\n\r\nUsando um item que ele criou (uma vara ou um bastão de algum tipo), ele tentou se infiltrar nas tribos furbolgs de Ashenvale. Em algum momento, e é aí que muitos dos detalhes ainda não estão claros, ele foi descoberto pelos furbolgs enquanto tentava manipulá-los para agir contra' WHERE `entry`=405;
UPDATE `page_text` SET `text`='Independentemente disso, ele foi aparentemente mais bem sucedido do que nada na realização de seus planos. Ele queria tentar suas maquinações nos furbolgs antes de tentar no meu povo? Os furbolgs tinha algo que ele cobiçava? Quem sabe?\r\n\r\nUsando um item que ele criou (uma vara ou um bastão de algum tipo), ele tentou se infiltrar nas tribos furbolgs de Ashenvale. Em algum momento, e é aí que muitos dos detalhes ainda não estão claros, ele foi descoberto pelos furbolgs enquanto tentava manipulá-los para agir contra\r\n\r\nuns aos outros. Como tenho certeza de que os elfos ficariam, os furbolg ficaram irritados, mas reagiram com muito mais força e mataram Dartol.\r\n\r\nOs furbolgs, pelo menos neste momento, não eram as mesmas criaturas corrompidas que se tornaram. Eles ainda eram racionais e inteligentes - o relacionamento deles com o nosso povo era amigável.\r\n\r\nEu estou esperando que se eu puder recriar a varinha com a ajuda de um dríade chamado Shael\'dryn, eu posso usar seu poder para ajudar nosso povo... e o furbolgs.' WHERE `entry`=406;
UPDATE `page_text` SET `text`='Nem todos os furbolgs foram corrompidos. Alguns ainda vivem e tentam proteger a floresta. Durante sua corrupção, alguns dos refugiados fugiram, muitos foram mortos e outros foram expulsos de suas tribos.\r\n\r\nEu encontrei um venerável furbolg da tribo Foulweald que vive acima do Lago Mystral - seu nome é Krolg.\r\n\r\nEu o observei por dias, e acho que, se eu me aproximasse dele como disfarce de um furbolg, poderia ajudá-lo e descobrir uma maneira de restabelecer um vínculo entre nosso povo.' WHERE `entry`=407;
UPDATE `page_text` SET `text`='Além de matar Dartol, os furbolgs dividiu a varinha em três partes para que não pudesse ser usado contra eles novamente. Tem sido difícil, mas tenho uma boa ideia de onde as três partes foram colocadas.\r\n\r\nA gema\r\nA primeira parte da varinha, uma joia brilhante, estava escondida em um santuário no noroeste de Ashenvale. Eu acredito que agora este santuário é as ruínas no centro do lago Falathim. Os Sentinelas me disseram que já esta habitado por murlocs agora.' WHERE `entry`=408;
UPDATE `page_text` SET `text`='Se não consigo encontrar a gema escondida no santuário, ela pode estar na posse de um dos murlocs.\r\n\r\nA parte do eixo\r\nEsta parte da varinha foi dada aos entes próximos ao que hoje se chama Felwood. Nem os furbolgs nem meu tipo poderiam ter suspeitado que tais criaturas magníficas poderiam cair na corrupção que foi tomada pela terra.\r\n\r\nMinha pesquisa me diz que os guardiões mantinham a peça em uma pequena clareira trancada dentro de um baú. Para descobrir mais, vou ter que falar com Shael\'dryn.' WHERE `entry`=409;
UPDATE `page_text` SET `text`='1. Consiga um ou oito homens\r\n2. Acerte homem duro\r\n3. Acerte mais homens\r\n4. Coloque o homem no fogo\r\n5. Coma homem' WHERE `entry`=410;
UPDATE `page_text` SET `text`='Shael\'dryn é uma dríade que protege o luar a nordeste do lago Íris. Ela se recusa a deixar a floresta ou abandoná-la à corrupção. Eu sei que ela tem o poder e o conhecimento para reparar a varinha, ou pelo menos me dizer como eu posso fazer isso sozinho.\r\n\r\nO Pomo\r\nO pomo da varinha foi dado aos druidas de Dor\'danil. Há um grande problema com isso: os druidas estão mortos.\r\n\r\nSeus espíritos agora habitam a área e não são mais hospitaleiros.' WHERE `entry`=411;
UPDATE `page_text` SET `text`='Isso pode exigir mais investigação. Eu não tenho certeza se sou poderoso o suficiente para lutar contra os espíritos dos meus parentes mortos... e muito menos mergulhar nas profundezas de Dor\'danil e encontrar o pomo.\r\n\r\n(Nota: Durante meus preparativos em Astranaar, eu conheci recentemente um caçador anão que passou boa parte de seu tempo em Ashenvale tentando ajudar meu povo. Ele veio de um lugar chamado Stormwind.\r\n\r\nFalamos algumas ales uma noite, e suas histórias me deram uma nova esperança de encontrar o pomo...' WHERE `entry`=412;
UPDATE `page_text` SET `text`='Ele me diz que esteve em todo o Dor\'danil, e nunca viu um baú ou qualquer recipiente que possa guardar o pomo.\r\n\r\nEle também me diz que os limos podres habitam a área e devoram quase qualquer coisa menor que eles. Ele os viu "comer" mesas, esculturas, ursos e até gnomos!\r\n\r\nOs limos parecem ganhar muito sustento de coisas mágicas. Se o baú que segurava o pomo estivesse encantado, os limos poderiam tê-lo comido.' WHERE `entry`=413;
UPDATE `page_text` SET `text`='É uma aposta, mas não tenho outras pistas neste momento...\r\n\r\nSe eu descubrir mais vou colocar os detalhes aqui; mas até lá, vou trabalhar em encontrar as partes da varinha e ajudar Ashenvale.' WHERE `entry`=414;
UPDATE `page_text` SET `text`='Jin\'Zil,\r\n\r\nJá faz um tempo, mas pensei que você gostaria de saber que o último dos Bloodfeather\'s foi morto. Não me agradeça, porém, a pessoa que entrega a carta é a assassina. Se você quiser, eu posso ter a cabeça entregue a você depois que a caravana fizer suas rondas através de Barrens. Eu sei que você gosta desse tipo de coisa para suas mágicas vodu.\r\n\r\nTome cuidado velho amigo.\r\n\r\n\r\n-Darsok Swiftdagger' WHERE `entry`=415;
UPDATE `page_text` SET `text`='Jin\'Zil,  Já faz um tempo, mas pensei que você gostaria de saber que o último dos Bloodfeather\'s foi morto. Não me agradeça, porém, a pessoa que entrega a carta é a assassina. Se você quiser, eu posso ter a cabeça entregue a você depois que a caravana fizer suas rondas através de Barrens. Eu sei que você gosta desse tipo de coisa para suas mágicas vodu.  Tome cuidado velho amigo.  -Darsok Swiftdagger' WHERE `entry`=416;
UPDATE `page_text` SET `text`='Ok, você deve ter suas cargas e detonadores do NG-5 prontos. Eu os rotulei para você... O azul é para a serraria. O vermelho é para Windshear Mine.$b$bEu fiz algumas explorações, e há bastante explosivos em Windshear Crag que não deveria ser um problema para você entrar, plantar os explosivos e depois sair antes de detoná-los.$b$bLembre-se, isso é uma distração. Afaste-se das carroças antes que os goblins cheguem lá para ver o que aconteceu, e então esgueire-se atrás deles.' WHERE `entry`=417;
UPDATE `page_text` SET `text`='Tenha cuidado com eles pelo caminho. O Nitromirglicerônio sozinho pode reduzi-lo a poeira. Eu coloquei um pouco do líquido em tubos de cobre que deveria mantê-lo estável e seguro.$b$bO pergaminho que você pegou de Collin eu usei nas caixas de detonador. Eles agora enviarão uma mensagem silenciosa para as cobranças da NG-5 depois que você as tiver configurado. Eu usei um mecanismo Stalthwargon simples para me certificar de que a condutividade do fio seja ótima e o fluxo do Nitroglicerônio entre o fluido diferencial seja melhor que a média.' WHERE `entry`=418;
UPDATE `page_text` SET `text`='Antes de voltar aos detalhes sobre o meu projeto, deixe-me explicar onde você deve colocar os explosivos.$b$bPlano Alpha - a lumber mill:$bHá uma carroça na frente da serraria (a nordeste do desmatamento e da construção da madeira). Eu suspeito que os planos de engenharia estão dentro da serraria.$b$bPara plantar a carga, vá para a parte de trás do vagão. Uma vez plantado, saia rapido e mantenha alguma distância.' WHERE `entry`=419;
UPDATE `page_text` SET `text`='Quando estiver pronto, acerte o detonador.$b$bEu assegurei um sinal forte encaminhando fios de cobre e prata com um arranjo de antenas Melthusianas dentro do invólucro da caixa. Isso deve lhe dar bom alcance. Apenas tenha cuidado com os goblins nas proximidades. Eles não vão gostar da minha criação se eles te pegarem no sitio deles.$b$bOh, apenas no caso de não ter ficado óbvio, não fique perto do vagão quando ele explodir... isso vai doer.' WHERE `entry`=420;
UPDATE `page_text` SET `text`='Plano Beta - Windshear Mine:$b$bEu vi os goblins usando outro vagão de explosivos ao norte da entrada da mina. Eu acho que deveria ser um bom lugar para plantar NG-5 Charge (Red). Depois de colocar a carga na parte de trás do vagão, pegue alguma distância e aperte o botão.$b$bSe os goblins não vierem correndo para verificar o estrondo, é provavelmente porque a montanha desceu sobre suas cabeças.' WHERE `entry`=421;
UPDATE `page_text` SET `text`='As cartas da Venture Co. estão fora da mina... Acho que em algumas caixas no nível inferior.$b$bBoa sorte, e lembre-se...$b$bCarga Azul: serraria$bCarga Vermelha: Mina $b$bOh, e destrua esta carta depois de ter cumprido sua missão. Hmm, talvez eu deva procurar uma maneira de fazer essas coisas se destruírem depois de serem lidas. Isso não é uma má ideia...' WHERE `entry`=422;
UPDATE `page_text` SET `text`='--Dia um--\r\n\r\nMinha jornada de Ironforge finalmente me leva a Kalimdor!\r\n\r\nProspector Khazgorm de Bael Modan está fazendo um grande progresso. Ele descobriu um fóssil hoje que os estudiosos acreditam ser um fragmento de osso de um deus antigo. Khazgorm tem certeza de que o fóssil é parte de um mistério maior. Enquanto ele supervisiona o local de escavação, ele me deu uma missão para descobrir mais fósseis para juntar a história.\r\n\r\nMinha tarefa: siga para o sul em busca de mais pistas.' WHERE `entry`=424;
UPDATE `page_text` SET `text`='--Dia dois--\r\n\r\nAs terras de Barrens são vastas e expansivas. Minha caminhada foi longa e árdua e não rendeu nada de interesse para os arquivos da Liga dos Exploradores até agora.\r\n\r\nO único incidente ocorreu perto da estrada do ouro. Ao longe, notei algumas criaturas estranhas. Parecia meio porco, meio homem. Eu rapidamente me escondi e deixei as bestas passarem. Que cheiro fétido eles deixaram para trás!' WHERE `entry`=425;
UPDATE `page_text` SET `text`='--Dia três--\r\n\r\nÓ grande e esplêndida descoberta!\r\n\r\nEnquanto explorava hoje me deparei com uma grande descoberta: restos maciços do que parece ter sido uma criatura parecida a um javali. Seu crânio se elevava muito acima de mim. Cada dente da besta era do tamanho de um anão mesmo... bem, um anão. Devido ao tamanho da descoberta, sou levado a acreditar que estes são os restos de um dos antigos Deuses.' WHERE `entry`=426;
UPDATE `page_text` SET `text`='Um tempo atrás, na sede da Liga dos Exploradores em Ironforge, lembro-me de uma palestra dada pelo Arqueólogo-Chefe Greywhisker. O velho sujeito mencionou tal deus, que foi reverenciado pelos estranhos elfos de Darnassus por causa de sua suposta natureza altruísta.\r\n\r\nBah! Um momento. Eu ouço bufando fora da tenda. Certamente apenas alguns javalis tentando pegar minhas maçãs escondidas....' WHERE `entry`=427;
UPDATE `page_text` SET `text`='-- Dia 4--\r\n\r\nCapturado!\r\n\r\nDevo... escrever... rapidamente.\r\n\r\nAtacado por homens javalis da tribo Razorfen. Acreditava que eles estavam me levando para o grande javali que restava do deus caído. Em vez disso, contornaram os grandes fósseis e se dirigiram diretamente para o oeste do crânio gigante, atravessando a Estrada do Ouro.' WHERE `entry`=428;
UPDATE `page_text` SET `text`='Arrastado para dentro de uma amoreira infestada de espinhos chamada Razorfen Kraul. Jogado dentro de uma cabana grosseiramente trabalhada e mantido em cativeiro junto com um elfo noturno morrendo.\r\n\r\nSem posses em mim, exceto por este diário, minha pena e meu relógio de bolso...\r\n\r\nAlguém vindo esta agora...' WHERE `entry`=429;
UPDATE `page_text` SET `text`='-- Dia 5--\r\n\r\nMeu companheiro de cela elfo noturno sofre de ferimentos graves. Os homens javali o açoitaram repetidamente e acredito que ele perecerá em breve, a menos que, por algum milagre, um grupo de resgate enfrente esse lugar traiçoeiro. Ele entra e sai da consciência. Quando ele acorda, tento dar-lhe a pouca água que tenho.' WHERE `entry`=430;
UPDATE `page_text` SET `text`='Ontem à noite ele foi capaz de falar pela primeira vez. Ele é um druida, chamado Heralath, enviado para essas partes para examinar os rumores do deus javali caído.\r\n\r\nEm uma voz enfraquecida ele me disse que acreditava que o grande crânio de javali era o lugar onde o deus do grande javali Agamaggan caiu lutando contra a Legião Ardente há cerca de 10.000 anos durante a Guerra dos Anciões. Seu corpo massivo achatou a terra e seu sangue afundou na terra. Torres de espinhos crespas crescem onde o sangue dele caiu...' WHERE `entry`=431;
UPDATE `page_text` SET `text`='Heralath e seu povo procuram descobrir a fonte de corrupção neste lugar. Acredita-se que Agamaggan fosse um deus puro, ele me disse. Por que essas criaturas imundas e espinhos malignos marcam a terra é um mistério.\r\n\r\nAntes que eu pudesse pressionar para mais informações, Heralath entrou em sono intermitente mais uma vez...' WHERE `entry`=432;
UPDATE `page_text` SET `text`='-- Dia 6--\r\n\r\nO estado de Heralath piora.\r\n\r\nSeus olhos abriram apenas uma vez hoje. Ele falou com angústia e aflição, mas sua mensagem era clara. Se eu fosse escapar da minha desgraça aqui em Razorfen Kraul, devia dizer ao Falfindel Waywarder em Thalanaar que Charlga Razorflank estava despertando lacaios no sul dos Barrens. O poder cresce a cada dia. A velha deve ser parada antes que a praga da corrupção possa se espalhar a partir do Kraul.' WHERE `entry`=433;
UPDATE `page_text` SET `text`='O pobre Heralath parece pálido como osso. Sua febre piora a cada minuto. Enquanto isso, os homens javali estão se preparando para uma grande ação militar. Temo que, se eu não tentar fugir logo, encontrarei um destino semelhante ao meu companheiro de cela.\r\n\r\nMeu plano é fugir quando os homens saem para a próxima caçada. Meus guardas são preguiçosos e frequentemente dormem em seus turnos.' WHERE `entry`=434;
UPDATE `page_text` SET `text`='Uma vez livre do Kraul, seguirei em direção a Thousand Needles e depois a oeste até Thalanaar, na fronteira da misteriosa floresta de Feralas. Lá eu procurarei o druida conhecido como Falfindel Waywarder.\r\n\r\nO que começou como uma tentativa de explorar o passado do meu povo se transformou em algo muito maior. A proteção da Aliança está em minhas mãos....' WHERE `entry`=435;
UPDATE `page_text` SET `text`='NÚMERO DE PEDIDO: 45TK-3\r\nCOMPRADOR: Kravel Koalbeard\r\n\r\nFATURA:\r\n\r\nCatraca Hidráulica (3)\r\nParafusos repetidores (50)\r\nTubulação de cobre de 20 "calibre 20 (12)\r\nRapé, Deepfury brand (1)\r\nPrisma de alta qualidade (3)' WHERE `entry`=436;
UPDATE `page_text` SET `text`='entregaria esta mensagem para você, mas temo partir, para não perder um evento de grande importância.\r\n\r\nSe você quiser saber mais sobre minhas observações sobre a Torre de Althalaxx, o portador desta mensagem não me deu nenhuma ajuda, e eu acredito que $gele:ela; pode confiar.\r\n\r\nNa pressa,\r\nSombra de Baltule' WHERE `entry`=437;
UPDATE `page_text` SET `text`='O goblin Nazz Steamboil é realmente o filho de um príncipe comerciante goblin (Qual príncipe? O preço dessa informação é alto). Parece que ele gosta muito de gastar o dinheiro que sua família faz, do que ganhar sozinho. Então ele roubou uma pequena fortuna do tesouro de sua família e agora está se escondendo nos Flats Cintilantes. Se o pai soubesse onde ele estava, ele certamente o procuraria e o arrastaria de volta para casa... menos um dedo por sua insolência.' WHERE `entry`=438;
UPDATE `page_text` SET `text`='Mr. Fizzlebub,\r\n\r\nO portador desta carta e eu estamos planejando um empreendimento que lhe damos as boas vindas.\r\n\r\nComo você sabe, eu gasto muito do meu tempo nos Flats Cintilantes - o negócio acima mencionado lida com as corridas nesses flats.\r\n\r\nSe correr e, mais especificamente, apostar nas corridas, interessa-te então por favor leia, pois sinto que o teu conhecimento de Espinélide seria útil no nosso esquema.' WHERE `entry`=439;
UPDATE `page_text` SET `text`='Mr. Fizzlebub, A bebida que você deu ao meu associado foi um enorme sucesso. Não só a bebida forte, causando um enorme embotamento de inteligência, parece ter uma qualidade viciante. Tenho certeza de que aqueles que já beberam vão desejar mais. Estamos em dívida com você, Crank. E eu os convido a vir a Shimmering Flats, ou enviar um confidente, para aproveitar as novas oportunidades de jogo que temos aqui. --Kravel' WHERE `entry`=440;
UPDATE `page_text` SET `text`='Bom Martek, estou em uma jam e preciso da sua ajuda. Meu irmão e eu estamos construindo um carro de corrida e precisamos de pistões fortes. Nada que eu tente é forte o suficiente. Nada pode aguentar a velocidade do nosso novo carro. E assim o motor quebra constantemente! Você é um especialista em crafting com metal. Você pode ajudar. Você é o único que pode.' WHERE `entry`=441;
UPDATE `page_text` SET `text`='Para enfrentar Frostmaw, você deve atraí-lo com a carne de sua presa favorita.\r\n\r\nVá para as Montanhas Alterac em Azeroth e caçar um imenso leão da montanha. Mate-o e leve sua carcaça para a Growless Cave, um local considerado sagrado pelo wendigo bestial.\r\n\r\nColoque a carcaça na Chama de Uzel e o cheiro de carne queimada sairá da caverna.\r\n\r\nE então, com o tempo, Frostmaw virá.\r\n' WHERE `entry`=442;
UPDATE `page_text` SET `text`='Nunca deixa de me surpreender com a facilidade com que o ódio pode ser usado para curvar as pessoas à sua vontade. Os orcs e trolls eram mais fáceis, seu ódio pela Aliança fazendo-os sujeitos dispostos a ouvir a mensagem da Dark Strand. Eles têm alguma idéia do poder maior que eles servem? Aquele irritante paladino, Delgren, o Purificador, está investigando nossos assuntos na Floresta Ashenvale, enquanto seu lacaio, Balthule, espia minha torre, pensando que sua presença é desconhecida para mim. Delgren acredita em nós umm menor' WHERE `entry`=443;
UPDATE `page_text` SET `text`='ameaça, apenas outro culto da morte, talvez. Mal sabe ele que somos apenas uma das... vertentes do Concílio das Sombras. Quando criança, uma vez me disseram que um inseto cuja mordida podia matar com facilidade, embora parecesse nada mais que uma mosca, uma partícula pouco digna de reconhecimento. São as coisas que você descarta que vão te machucar mais, deixadas para apodrecer.' WHERE `entry`=444;
UPDATE `page_text` SET `text`='Os carros-foguete que correm os flats são altamente sintonizados e exigem manutenção extrema. Portanto, não é surpresa que as equipes de boxe sejam mantidas em cena o tempo todo.\r\n\r\nEssas equipes testam constantemente seus carros e fazem correções e modificações conforme necessário. É a única maneira de manter os carros em um estado de alto desempenho.\r\n\r\nEntão... estamos procurando um meio de forçar um lapso em uma das máquinas , já que qualquer lapso desse tipo faria com que o carro da tripulação caísse rapidamente em desuso.' WHERE `entry`=445;
UPDATE `page_text` SET `text`='Se pudermos controlar qual carro está na melhor forma e qual carro não está, então... eu não preciso explicar a você a vantagem do jogo que isso nos dá.\r\n\r\nEntão, de você, bom Fizzlebub, precisamos de meios para forçar o lapso das equipes de boxe.\r\n\r\nSerá que Stranglethorn possui alguma planta ou erva ou bebida que podemos usar para adivinhar o juízo dos gnomos e goblins?\r\n\r\n--K. Koalbeard' WHERE `entry`=446;
UPDATE `page_text` SET `text`='Eu tenho feito uma grande pesquisa sobre os Aspectos e seus titãs criadores - tanto quanto qualquer humano poderia em toda a vida. Havia cinco Aspectos quando os titãs deixaram este mundo; eles eram poderosos dragões encarregados de proteger o mundo de Azeroth. Seus contos são vastos e variados, e mesmo agora, apesar de todas as informações que reuni, sei que há muito mais a ser aprendido sobre essas criaturas magníficas.' WHERE `entry`=447;
UPDATE `page_text` SET `text`='Boticário Lydon, espero que esta carta chegue a você com rapidez e que esteja em boas condições. A necessidade surgiu em Kalimdor que eu acredito que exigirá suas habilidades. Eu serei breve. Uma crise se desenvolve nas Stonetalon Mountains, não muito longe da terra dos nossos aliados Tauren, Mulgore. A empresa Venture company dos goblins está minando e se arrastando ferozmente naquelas montanhas, enfurecendo os espíritos das montanhas.' WHERE `entry`=448;
UPDATE `page_text` SET `text`='Eu devo assumir a comoção que esses espíritos causam através dos canais sob o auspício dos xamãs Tauren e os druidas os distraem. Para os Tauren, considerar a Venture Company nas Stonetalon Mountains uma terrível ameaça. Ameaça ou não, devemos ajudar nossos aliados. Lydon, você tem um amplo conhecimento de toxinas. Você pode inventar uma que possamos usar para espalhar um contágio entre os goblins em Stonetalon? Sua autoria, P. Zamah' WHERE `entry`=449;
UPDATE `page_text` SET `text`='Muito do conhecimento que tenho agora eu não poderia ter aprendido sozinho. Por causa disso, sou extremamente grato aos elfos noturnos. Foi apenas com a ajuda deles que tenho tantos detalhes. Como resultado de nossas interações, tenho a impressão de que seu começo está muito mais ligado aos Aspectos do que eu pensava inicialmente. No entanto, eles guardam seus segredos muito bem, até mesmo para eu saber com certeza.' WHERE `entry`=450;
UPDATE `page_text` SET `text`='A informação que aprendi sobre os Aspectos que coloquei aqui para outros referirem no futuro. Sei que será útil, pois sinto que esses dragões terão um efeito muito maior em nosso mundo à medida que o tempo passa.' WHERE `entry`=451;
UPDATE `page_text` SET `text`='Alexstrasza: Alexstrasza, a antiga e poderosa Rainha dos Dragões, foi nomeada a Provedora da Vida pelos titãs. Ela foi a primeira a ser criada pelos titãs para proteger o mundo depois que eles saíram. Dizem que ela testemunhou o nascimento de todas as raças modernas no rosto de Azeroth. Sua Revoada Draconica vermelha, conhecida por seu comportamento orgulhoso, uma vez dominou todos os outros dragões.' WHERE `entry`=452;
UPDATE `page_text` SET `text`='Rumores entre os elfos noturnos sugerem que Alexstrasza e o semideus Cenarius eram amigos íntimos, e que foi ele quem a chamou durante a primeira invasão da Legião Ardente.' WHERE `entry`=453;
UPDATE `page_text` SET `text`='Ysera: Ysera, a Sonhadora, representa o subconsciente mortal. Ela engloba o Sonho de Esmeralda... Enquanto ela aprecia a imaginação, a criação e aprova a ambição nas raças mortais, ela e sua Revoada Draconica consideram o mundo real apenas uma ilusão. Ysera é uma das criaturas mais graciosas e poderosas que qualquer mortal poderia ver.' WHERE `entry`=454;
UPDATE `page_text` SET `text`='Malygos: Malygos é conhecido como o Regente de Magia ou o Tecelão de Feitiços. Sua forma natural não é a de um dragão típico, e dizem que ele pode mudar vontade. Raramente visto depois de sua rpartida para o extremo norte, ele está quase sempre na forma de uma criatura parecida com um inseto, mas enquanto em forma de dragão, ele e sua Revoada Draconica estão com cores variadas.' WHERE `entry`=455;
UPDATE `page_text` SET `text`='Nozdormu: Nozdormu é conhecido como o Mestre do Tempo e o Atemporal. Ele e sua Revoada Draconica são de cor bronze e exibem grande astúcia em todas as coisas. A atenção de Nozdormu sempre se concentrou em coletar artefatos do passado, já que ele pouco se importa com as questões atuais das raças mortais, ou mesmo com as relações de seus colegas. Embora sua indiferença em relação à política dos outros não significa que ele é complacente para eventos que acontecem em todo o mundo.' WHERE `entry`=456;
UPDATE `page_text` SET `text`='Considerados protetores, a revoada de bronze encontra-se bastante atraída por áreas de civilização. O dragão e sua revoada são muito pacientes por natureza, sempre confiando na observação e agindo com um plano antes de serem precipitados. Nozdormu sempre tentará proteger o fluxo de tempo e as raças mortais quando ameaçado por inimigos maiores.' WHERE `entry`=457;
UPDATE `page_text` SET `text`='Neltharion: Neltharion, chamado de Guardião da Terra após sua criação, era mais leal e protetor de Azeroth do que qualquer um dos outros Aspectos. Sua Revoada Draconica negra foi responsável pela criação das montanhas e outras formações das terras, incluindo as fronteiras entre as raças mortais. O que causou sua corrupção ainda permanece um mistério; seja uma influência externa ou algo que corrompeu por dentro. Eu me pergunto se os Deuses Antigos não participaram da corrupção do grande dragão negro.' WHERE `entry`=458;
UPDATE `page_text` SET `text`='O dragão negro sofreu uma completa transformação' WHERE `entry`=459;
UPDATE `page_text` SET `text`='PARABÉNS!\r\n\r\nVocê é o novo dono do incrível snufflenose gopher! Embora seja uma criatura tímida, temos certeza de que você vai encontrar a fofura e a incrível capacidade olfativa do seu novo animal de estimação.\r\n\r\nNas páginas seguintes, você encontrará informações sobre o seu gopher:\r\n\r\n1. Alimentação e cuidados\r\n2. Comportamento excêntrico (e adorável)\r\n3. Uso do patenteado Snufflenose Command Stick (tm)\r\n\r\nMais uma vez, parabéns. Você não será desapontado,\r\n\r\n-Marwig Rustbolt\r\nProprietário, Snuff Inc.' WHERE `entry`=467;
UPDATE `page_text` SET `text`='--Dia 13\r\n\r\nO movimento de tropas perto de Theramore aumentou. Um grande número de humanos deixou a cidade e se mudou para o oeste, às pressas.\r\n\r\n\r\n--Dia 14\r\n\r\nMais tropas saindo da cidade. Eu segui um pequeno grupo e os observei removerem seus uniformes depois de deixarem os arcos de Theramore. Talvez eles sejam desertores?\r\n\r\n\r\n--Dia 15\r\n\r\nEu ouvi algumas tropas de Theramore falarem hoje de guardas deixando seus postos. Minhas suspeitas dos desertores devem ser verdadeiras' WHERE `entry`=507;
UPDATE `page_text` SET `text`='--Dia 13 O movimento de tropas perto de Theramore aumentou. Um grande número de humanos deixou a cidade e se mudou para o oeste, às pressas. --Dia 14 Mais tropas saindo da cidade. Eu segui um pequeno grupo e os observei removerem seus uniformes depois de deixarem os arcos de Theramore. Talvez eles sejam desertores? --Dia 15 Eu ouvi algumas tropas de Theramore falarem hoje de guardas deixando seus postos. Minhas suspeitas dos desertores devem ser verdadeiras' WHERE `entry`=527;
UPDATE `page_text` SET `text`='Jorgen,\r\n\r\nMeus medos se tornaram realidade e eles agiram de maneiras que eu nunca achei capazes. Nunca achei que eles chegariam tão perto, mas eles conseguiram.\r\n\r\nPor favor, ajude o portador desta nota e envie-a para ele. Com toda a honestidade, acho que ele vai nos ajudar, diante a seriedade deste assunto. Se fosse qualquer outra ameaça, ele provavelmente continuaria a nos ignorar e aos problemas de nossa cidade justa.\r\n\r\nObrigado, seu velho pescador. Eu estou em dívida com você como sempre.\r\n\r\nSeu amigo,\r\n\r\nDeLavey' WHERE `entry`=528;
UPDATE `page_text` SET `text`='--Dia 16\r\n\r\nUleg e Thargil retornaram de sua missão para as docas de Theramore. Eles viram um novo navio no porto: o Pardal Sangrento. O navio chegou do porto Menethil em Azeroth.\r\n\r\n\r\nAmanhã, Uleg e Thargil retornarão às docas e a bordo do navio. Eles esperam encontrar documentos políticos, que revelem as relações entre Theramore e a Aliança.' WHERE `entry`=548;
UPDATE `page_text` SET `text`='--Dia 16 Uleg e Thargil retornaram de sua missão para as docas de Theramore. Eles viram um novo navio no porto: o Pardal Sangrento. O navio chegou do porto Menethil em Azeroth. Amanhã, Uleg e Thargil retornarão às docas e a bordo do navio. Eles esperam encontrar documentos políticos, que revelem as relações entre Theramore e a Aliança.' WHERE `entry`=571;
UPDATE `page_text` SET `text`='Com o sucesso de Kel\'Thuzad em Lordaeron, o Lich King fez os preparativos finais para seu ataque contra a civilização humana. Colocando suas energias de pragas em um número de artefatos portáteis chamados caldeirões de praga, Ner\'zhul ordenou a Kel\'Thuzad que transportasse os caldeirões para Lordaeron, onde eles seriam escondidos dentro de várias aldeias controladas por cultos.' WHERE `entry`=572;
UPDATE `page_text` SET `text`='Os caldeirões, protegidos pelos cultistas leais, agiriam como geradores de peste, enviando a praga pelos campos e cidades desavisados do norte de Lordaeron. O plano do Lich King funcionou perfeitamente. Muitas das aldeias do norte de Lordaeron foram contaminadas quase que imediatamente. Assim como em Northrend, os cidadãos que contraíram a peste morreram e resurgiram como escravos dispostos do Lich King.' WHERE `entry`=573;
UPDATE `page_text` SET `text`='Os cultistas sob Kel\'Thuzad estavam ansiosos para morrer e ser ressuscitados a serviço de seu lorde negro. Eles exultaram na perspectiva da imortalidade através da morte. Quando a praga se espalhou, mais e mais zumbis ferozes surgiram nas terras do norte. Kel\'Thuzad olhou para o exército crescente de Lich King e o nomeou de Flagelo - pois logo marcharia sobre os portões de Lordaeron... e varreria a humanidade da face do mundo.' WHERE `entry`=574;
UPDATE `page_text` SET `text`='Ninguém sabe exatamente como o universo começou. Alguns teorizam que uma catastrófica explosão cósmica enviou os infinitos mundos para a vastidão dos grandes mundos sombrios que um dia teriam formas de vida de uma diversidade maravilhosa e terrível. Outros acreditam que o universo, tal como existe, foi criado como um todo por uma entidade singular e toda-poderosa.' WHERE `entry`=575;
UPDATE `page_text` SET `text`='Embora as origens exatas do universo caótico permaneçam incertas, fica claro que uma raça de seres poderosos surgiu para trazer ordem aos vários mundos e garantir um futuro seguro para os seres que seguiriam seus passos. Os Titãs, deuses colossais de pele metálica das profundezas do cosmos, avançaram e começaram a trabalhar nos mundos que encontraram. Eles moldaram a forma de seus mundos, levantando montanhas poderosas e dragando vastos mares.' WHERE `entry`=576;
UPDATE `page_text` SET `text`='Eles inspiraram céus e atmosferas furiosas - tudo parte de seu insondável e previdente plano de criar ordem a partir do caos. Eles até mesmo capacitaram raças primitivas para cuidar de suas obras e manter a integridade de seus respectivos mundos. Os Titãs, governados por uma seita de elite conhecida como Panteão, levaram ordem a cem milhões de mundos espalhados pelo Grande Escuro Além durante as primeiras eras da criação.' WHERE `entry`=577;
UPDATE `page_text` SET `text`='O benevolente Panteão, procurando salvaguardar seus mundos estruturados, estava sempre vigilante contra a ameaça de ataque das entidades vil e extradimensionais do Twisting Nether. O Nether, uma dimensão etérea de magias caóticas que conectava a miríade de mundos do universo, abrigava um número infinito de seres maléficos e demoníacos, que procuravam apenas destruir a vida e devorar as energias do universo vivo.' WHERE `entry`=578;
UPDATE `page_text` SET `text`='--Dia 17 Uleg retornou de sua missão. Ele falhou e Thargil foi capturado. Eu enviei Uleg para entregar este relatório para Nazeer. Eu ficarei aqui. Eu testemunhei o aumento da atividade de navegação na costa de Theramore. Vou continuar meu relato e relatar pessoalmente qualquer evento digno de nota.' WHERE `entry`=579;
UPDATE `page_text` SET `text`='Nosso primeiro plano já falhou! Fist foi bem sucedido em mudar a lista de guardas para os turnos do túnel naquela noite, mas devido a alguns esforços imprevistos por alguns dos cidadãos de Stormwind, nosso alvo foi capaz de fugir da cena antes que pudéssemos capturá-lo. Mas nós planejamos para tais contingências. O plano 2 foi posto em movimento e estava bem encaminhado antes que nossa emboscada fosse iniciada em Stormwind.' WHERE `entry`=580;
UPDATE `page_text` SET `text`='O plano 2 é igualmente simples, mas exigirá mais paciência da nossa parte. Nosso homem do lado de dentro de Menethil já foi pago por seus esforços, e ele conseguiu brilhantemente. Não devemos ter problemas em adquirir o nosso alvo e capturá-lo assim que zarparmos.' WHERE `entry`=581;
UPDATE `page_text` SET `text`='Espere que o ataque aconteça a alguns quilômetros do seu destino. É aí que o nossos??? aliados??? será colocado em espera. Não sei por que fomos forçados a formar uma aliança com as criaturas, mas não posso deixar de sentir que estamos sendo coagidos. Em última análise, isso não importa. Com o alvo sob nossa custódia, estaremos bem encaminhados para recuperar Stormwind daqueles que a roubaram de nós!' WHERE `entry`=582;
UPDATE `page_text` SET `text`='Mestre Longbeard,\r\n\r\nComo você sabe, nós Stormpikes temos um olho para a excelência, e assim a qualidade de seus escudos não é um mistério para nós.\r\n\r\nEu, portanto, estou ansioso para contratá-lo para tal escudo. Eu incluí as especificações na seguinte página:\r\n\r\n\r\n' WHERE `entry`=607;
UPDATE `page_text` SET `text`='A devastadora Segunda Guerra contra a horda dos orcs deixou a Aliança de Lordaeron em estado de choque e desordem. Os orcs sedentos de sangue, liderados pelo poderoso líder guerreiro Orgrim Doomhammer, não apenas destruíram as terras dos anões de Khaz Modan, mas também arrasaram muitas das províncias centrais de Lordaeron. Os orcs implacáveis até conseguiram devastar os$B$Breino remoto dos elfos de Quel\'Thalas antes de sua agitação foi finalmente interrompida.' WHERE `entry`=627;
UPDATE `page_text` SET `text`='Os exércitos da Aliança liderados por Sir Anduin Lothar, Uther, o Arauto da Luz e o Almirante Daelin Proudmoore empurraram os orcs para o sul na terra destroçada de Azeroth - o primeiro reino a cair antes do violento ataque dos orcs.$B$B$B$BAs forças da Aliança sob o comando de Sir Lothar conseguiram empurrar os clãs de Doomhammer de Lordaeron e de volta para as terras controladas por orcs de Azeroth. As forças de Lothar cercaram a cidadela vulcânica dos orcs da Blackrock Spire e cercaram suas defesas.' WHERE `entry`=628;
UPDATE `page_text` SET `text`='Em um último esforço, Doomhammer e seus tenentes organizaram um ataque ousado no Spire e entraram em confronto com os paladinos de Lothar no centro das Burning Steppes. Doomhammer e Lothar se enfrentaram em uma batalha titânica que deixou os dois poderosos combatentes feridos e exaustos. Embora Doomhammer tenha conseguido derrotar Lothar, a morte do grande herói não teve o efeito que o warchief esperava.' WHERE `entry`=629;
UPDATE `page_text` SET `text`='Turalyon, o tenente mais confiável de Lothar, pegou o escudo manchado de sangue de Lothar e reuniu seus irmãos aflitos para um contra-ataque cruel. Sob os padrões esfarrapados de Lordaeron e Azeroth, as tropas de Turalyon massacraram a maior parte das forças remanescentes de Doomhammer em uma derrota gloriosa, mas terrível.' WHERE `entry`=630;
UPDATE `page_text` SET `text`='Não havia mais nada para os sobreviventes orcs dispersos, a nao ser fugir para o último bastião de poder dos orcs - o portal escuro.$B$B$B$BTuralyon e seus guerreiros perseguiram os orcs restantes através do Swamp of Sorrows e nas terras destruídas onde o portal escuro estava. Lá, ao pé do portal colossal, a horda quebrada e a Aliança esgotada entraram em confronto naquela que seria a última e mais sangrenta batalha da Segunda Guerra.' WHERE `entry`=631;
UPDATE `page_text` SET `text`='Em desvantagem e enlouquecidos pela maldição de sua sede de sangue, os orcs inevitavelmente caíram diante da ira da Aliança. Doomhammer foi feito prisioneiro e escoltado para Lordaeron enquanto seus clãs quebrados foram recolhidos e levados para o norte - de volta para Lordaeron.' WHERE `entry`=632;
UPDATE `page_text` SET `text`='Apenas alguns meses após a conclusão de Nethergarde, as energias do portal escuro se fundiram e abriram uma nova porta de entrada para Draenor. Os restantes clãs orcs, sob a liderança do xamã mais velho, Ner\'zhul, atacaram Azeroth mais uma vez. Com a intenção de roubar uma série de artefatos mágicos que aumentariam o poder de Ner\'zhul, os orcs planejavam abrir novos portais em Draenor que permitiriam que eles escapassem do seu mundo vermelho condenado para sempre.' WHERE `entry`=633;
UPDATE `page_text` SET `text`='Convencido de que Ner\'zhul estava planejando uma nova ofensiva contra a Aliança, o Rei Terenas de Lordaeron enviou seus exércitos para Draenor para acabar com a ameaça dos orcs de uma vez por todas. Liderados por Hadggar e pelo general Turalyon, as forças da Aliança entraram em confronto com os orcs através da paisagem em chamas. Mesmo com a ajuda do elfo Ranger Alleria, do anão Kurdran e do soldado veterano Danath, Hadggar foi incapaz de impedir que Ner\'zhul abrisse seus portais para outros mundos.' WHERE `entry`=634;
UPDATE `page_text` SET `text`='As tremendas tempestades mágicas causadas pelas energias convergentes dos portais começaram a destruir o mundo devastado. Ner\'zhul, seguido apenas por seus servos mais confiáveis, conseguiu escapar através de um dos portais enquanto Hadggar lutava desesperadamente para retornar seus companheiros a Azeroth. Percebendo que eles estariam presos no mundo doente, Hadggar e seus companheiros decidiram destruir o portal escuro para que Azeroth não fosse prejudicado pela destruição violenta de Draenor.' WHERE `entry`=635;
UPDATE `page_text` SET `text`='Segundo todos os relatos, os heróis foram bem-sucedidos em destruir o portal e salvar Azeroth - mas se eles escaparam da morte de Draenor ainda não se sabe.' WHERE `entry`=636;
UPDATE `page_text` SET `text`='Dez mil anos antes dos orcs e humanos se enfrentarem em sua Primeira Guerra, o mundo de Azeroth embalou apenas um enorme continente cercado pelos mares infinitos e violentos. Essa massa de terra, conhecida como Kalimdor, era o lar de várias raças e criaturas diferentes, todas disputando a sobrevivência entre os elementos selvagens do mundo desperto. No centro do continente negro havia um misterioso lago de energias incandescentes.' WHERE `entry`=637;
UPDATE `page_text` SET `text`='O lago, que mais tarde seria chamado de Poço da Eternidade, era o verdadeiro coração da magia e do poder natural do mundo. Tirando suas energias da infinita Grande Escuridão para além do mundo, o Poço agiu como uma fonte mística, enviando suas potentes energias através do mundo para nutrir a vida em todas as suas formas maravilhosas. Com o tempo, uma tribo primitiva de humanóides noturnos chegou cautelosamente às margens do hipnotizante e encantado lago.' WHERE `entry`=638;
UPDATE `page_text` SET `text`='Os ferozes humanóides nômades, atraídos pelas estranhas energias do lago, construíram casas rudimentares em suas praias tranquilas. Com o tempo, o poder cósmico do lago afetou a estranha tribo, tornando-os fortes, sábios e virtualmente imortais. A tribo adotou o nome Kaldorei, que significava - filhos das estrelas - em sua língua nativa. Para celebrar sua sociedade nascente, eles construíram grandes estruturas e templos ao redor da periferia do lago.' WHERE `entry`=639;
UPDATE `page_text` SET `text`='Os Kaldorei, ou elfos noturnos, como seriam conhecidos mais tarde, adoravam a deusa da lua, Elune, e acreditavam que ela dormia dentro das profundidades cintilantes do Poço durante as horas do dia. Os sacerdotes e videntes elfos noturnos estudavam o Poço com uma curiosidade insaciável, levados a investigar seus incontáveis segredos e poder. Enquanto a sociedade crescia, os elfos norturnos exploravam a largura de Kalimdor e encontravam sua miríade de habitantes.' WHERE `entry`=640;
UPDATE `page_text` SET `text`='As únicas criaturas que lhes deram uma pausa foram os antigos e poderosos dragões. Embora as grandes bestas serpentinas fossem frequentemente reclusas, elas fizeram muito para proteger as terras conhecidas de ameaças potenciais. Os elfos noturnos acreditavam que os dragões se consideravam os protetores do mundo, e que eles e seus segredos deveriam ser deixados em paz.' WHERE `entry`=641;
UPDATE `page_text` SET `text`='Com o tempo, a curiosidade dos elfos noturnos os levou a encontrar e fazer amizade com várias entidades poderosas, entre as quais Cenarius, um semideus poderoso das florestas primordiais. O grande coração de Cenarius gostava dos elfos noturnos inquisitivos e passava grande parte do tempo ensinando-os sobre o mundo natural. Os tranquilos Kaldoreis desenvolveu uma forte empatia pelas florestas vivas de Kalimdor e se deleitou no equilíbrio harmonioso da natureza.' WHERE `entry`=642;
UPDATE `page_text` SET `text`='No entanto, à medida que as idades aparentemente infinitas se passaram, a civilização dos elfos noturnos expandiu-se tanto territorial como culturalmente. Seus templos, estradas e moradas se estendiam por toda a extensão do continente negro. Azshara, a bela e talentosa Rainha dos elfos noturnos, construiu um imenso e maravilhoso palácio na margem do lago que abrigava seus servidores favoritos em seus salões cheios de joias.' WHERE `entry`=643;
UPDATE `page_text` SET `text`='Seus servos, a quem ela chamava de Quel\'dorei ou de cargo materno, adoravam todos os seus mandamentos e acreditavam ser maiores do que o resto de seus irmãos de castas inferiores. Embora a rainha Azshara fosse amada igualmente por todo o seu povo, as pessoas de origem elevada eram secretamente odiadas pelas massas invejosas. Compartilhando a curiosidade dos sacerdotes em direção ao Poço da Eternidade, Azshara ordenou que os instruídos de alto escalão refletissem seus segredos e revelassem seu verdadeiro propósito no mundo.' WHERE `entry`=644;
UPDATE `page_text` SET `text`='Os altos encarregados enterraram-se em seu trabalho e estudaram o poço incessantemente. Com o tempo, desenvolveram a capacidade de manipular e controlar as energias cósmicas do lago. À medida que seus experimentos imprudentes progrediram, os mais experientes descobriram que podiam usar seus novos poderes para criar ou destruir em seu tempo livre. Os desafortunados encarregados haviam tropeçado em magias primitivas e agora estavam decididos a se dedicar à sua maestria.' WHERE `entry`=645;
UPDATE `page_text` SET `text`='Embora eles concordassem que a magia era inerentemente perigosa se manuseada de forma irresponsável, Azshara e seus filhotes começaram a praticar sua habilidade com um fascínio imprudente. Cenarius e muitos dos eruditos avisaram que apenas calamidade resultaria em brincar com as artes da magia claramente voláteis. Mas Azshara e seus seguidores teimosamente continuaram a expandir seus poderes crescentes.' WHERE `entry`=646;
UPDATE `page_text` SET `text`='Conforme seus poderes cresciam, uma mudança distinta veio sobre Azshara e os altos. A classe altiva e indiferente tornou-se cada vez mais insensível e cruel em relação a seus companheiros elfos noturnos. Um manto sombrio cobria a beleza outrora fascinante de Azshara. Ela começou a se retirar de seus assuntos amorosos e se recusou a interagir com qualquer um, exceto seus confiáveis sacerdotes de alta patente.' WHERE `entry`=647;
UPDATE `page_text` SET `text`='Um jovem e descarado erudito chamado Furion Stormrage, que passara grande parte do tempo estudando os efeitos do lago, começou a suspeitar que um poder terrível estava corrompendo os amados e sua amada rainha. Embora ele não pudesse conceber o mal que estava por vir, ele sabia que a vida dos elfos noturnos logo seria mudada para sempre...' WHERE `entry`=648;
UPDATE `page_text` SET `text`='-Realmente redondo, abrangendo do punho ao cotovelo.\r\n\r\n-Studs ao longo da borda. Prata.\r\n\r\n-Um grande garanhão no centro, com três larguras de dedos. Prata.\r\n\r\n-Carregado e reforçado com ferro.\r\n\r\n-Meu nome, Gringer Stormpike, gravado na borda superior.\r\n' WHERE `entry`=649;
UPDATE `page_text` SET `text`='O pagamento será feito através dos canais usuais. E dê ao portador deste pedido uma remuneração padrão de entrega. Você pode adicioná-lo ao custo do escudo.\r\n\r\n\r\n\r\nSinceramente, e muito obrigado\r\n--Gringer Stormpike' WHERE `entry`=650;
UPDATE `page_text` SET `text`='O início dos mortos-vivos sobre Azeroth não começou verdadeiramente com a corrupção do Príncipe Arthas - começou muito antes desse evento. Os eventos que trouxeram a queda de Lordaeron foram por muito tempo postos em movimento antes desse ponto; Começou com a vinda dos clãs orcs e da Legião Ardente.' WHERE `entry`=669;
UPDATE `page_text` SET `text`='Os orcs de Draenor (o mundo de onde vieram originalmente) eram nobres e xamanistas. Eles eram poderosos guerreiros, fortes de corpo e vontade. Foi por essas razões que a Legião Ardente os procurou. Kil\'jaeden, um demônio de grande poder entre a Legião, desejava escravizar os clãs órquicos e usá-los como seu exército contra os mundos que a Legião Ardente ainda tinha que conquistar.' WHERE `entry`=670;
UPDATE `page_text` SET `text`='Kil\'jaeden chegou ao líder dos clãs, um poderoso xamã chamado Ner\'zhul. Ele prometeu a Ner\'zhul incontáveis poderes e conhecimentos místicos se fizesse um pacto com o demônio que escravizaria os clãs da Legião Ardente. Este pacto selou não apenas seu destino, mas o de seu mundo para sempre.' WHERE `entry`=671;
UPDATE `page_text` SET `text`='Com o passar do tempo, Ner\'zhul percebeu o destino dos clãs nas mãos da Legião Ardente, e o xamã orc começou a desafiar Kil\'jaeden. O demônio, irritado com Ner\'zhul, voltou-se para o aprendiz do xamã, Gul\'dan, que era muito mais corrupto que Ner\'zhul.' WHERE `entry`=672;
UPDATE `page_text` SET `text`='Foi Gul\'dan quem foi responsável pelo declínio do xamanismo entre os clãs. A promessa de poder e controle sobre os inimigos fez suas ofertas ainda mais tentadoras. Com a ajuda de Kil\'jaeden, Gul\'dan começou sua manipulação dos clãs. Os orcs, outrora nobres, tornaram-se selvagens corruptos e insensatos. Com a introdução do sangue de demônio em seus corpos, os orcs eram ainda mais impiedosos e bárbaros.' WHERE `entry`=673;
UPDATE `page_text` SET `text`='Apesar de Kil\'jaeden ter corrompido Gul\'dan após seu fracasso com Ner\'zhul, Kil\'jaeden odiava o xamã e prometeu impor seu pacto original. Kil\'jaeden capturou Ner\'zhul e começou uma tortura física incrivelmente lenta no xamã. Enquanto Ner\'zhul implorou pela morte, Kil\'jaeden lembrou a Ner\'zhul que o pacto original ainda era obrigatório. Kil\'jaeden matou o orc, mas apenas fisicamente. Kil\'jaeden segurou o espírito do xamã orc antes que ele pudesse encontrar o caminho para o Twisting Nether.' WHERE `entry`=674;
UPDATE `page_text` SET `text`='Kil\'jaeden colocou o espírito de Ner\'zhul dentro de um bloco de gelo reunido do Twisting Nether. Enquanto estava preso dentro do gelo, ele infundiu ainda mais poder. A perda de seu corpo e a introdução de um poder tão incrível foi a linha que definiu a transformação de Ner\'zhul.' WHERE `entry`=675;
UPDATE `page_text` SET `text`='Kil\'jaeden lançou o bloco de gelo através das dimensões de volta para Azeroth, onde pousou no continente de Northrend, introduzindo este grande mal em nosso mundo. Ner\'zhul foi embora para sempre; em seu lugar havia um trono de gelo e ódio. O outrora respeitado líder xamã havia se tornado o incrivelmente poderoso Lich King.' WHERE `entry`=676;
UPDATE `page_text` SET `text`='Como Kil\'jaeden não confiava no Lich King, ele enviou seus terríveis senhores para vigiar o espírito e assegurar que o Lich King seguisse suas ordens. Os servos vampíricos vieram a Azeroth de boa vontade, atraídos pela destruição e poder do Lich King e pelo potencial genocídio da raça inteira de um planeta.' WHERE `entry`=677;
UPDATE `page_text` SET `text`='Mais de uma década se passou. Durante este tempo, o Lich King usou seus vastos poderes para ganhar controle sobre as mentes das criaturas de Northrend, a quem ele comandou para erigir uma grande cidadela sobre seu trono congelado. Agora que todos os Northrend estavam sob seu domínio, o Lich King sabia que precisava começar sua infiltração em Lordaeron. Preso dentro do gelo, o ex-xamã dos orcs começou a procurar mais mentes para manipular e controlar. Seu chamado alcançou os continentes.' WHERE `entry`=678;
UPDATE `page_text` SET `text`='A convocação do Lich King não passou despercebida, especialmente por um punhado de indivíduos poderosos. Entre eles estava o Arquimago Kel\'Thuzad, que era um membro poderoso do corpo governante de Dalaran, o Kirin Tor. Sua busca por todos os tipos de magia violava as políticas do Kirin Tor contra o aprendizado de magias negras. Kel\'Thuzad abandonou o Kirin Tor e todos os seus laços com o pensamento convencional e prometeu aprender o máximo que pudesse com o Lich King.' WHERE `entry`=679;
UPDATE `page_text` SET `text`='Um pacto foi feito entre os dois. Kel\'Thuzad receberia imortalidade e imenso poder em troca de servidão ao Lich King. A primeira tarefa de Kel\'Thuzad foi usar sua riqueza e influência em Lordaeron para fundar o Culto dos Malditos. O culto prometia igualdade e vida eterna a todos os seus membros, desde que prestassem juramento a Ner\'zhul, o deus do Culto dos Malditos.' WHERE `entry`=680;
UPDATE `page_text` SET `text`='Ner\'zhul então criou artefatos que foram projetados para espalhar a praga entre as civilizações humanas de Lordaeron. Ele os deu a Kel\'Thuzad e ordenou que o bruxo os espalhasse pela terra. Seus seguidores do culto deveriam proteger os artefatos a todo custo.' WHERE `entry`=681;
UPDATE `page_text` SET `text`='Uma vez no lugar, a praga começou a infiltrar-se na terra e afetar seus cidadãos ignorantes. Enquanto Kel\'Thuzad observava, o exército do Lich King cresceu rapidamente e ele logo ganhou controle sobre grandes porções da terra. Este exército tornou-se conhecido como o Flagelo, pois seu único propósito era varrer a humanidade de todo o Azeroth.' WHERE `entry`=682;
UPDATE `page_text` SET `text`='A palavra TRAIDOR está rudemente arranhada no epitáfio gravado que diz:\r\n\r\n\r\nYuriv está aqui.\r\n\r\nPai, Marido, Paladino.\r\n\r\nDeixe seus filhos testemunharem o fato de que sua dedicação à Luz era inquestionável.\r\n\r\nEle nunca perguntaria nada deles que ele mesmo não faria.\r\n\r\n' WHERE `entry`=690;
UPDATE `page_text` SET `text`='Texto Ausente' WHERE `entry`=691;
UPDATE `page_text` SET `text`='Texto Ausente' WHERE `entry`=692;
UPDATE `page_text` SET `text`='Texto Ausente' WHERE `entry`=693;
UPDATE `page_text` SET `text`='Texto Ausente' WHERE `entry`=694;
UPDATE `page_text` SET `text`='A palavra TRAIDOR está rudemente arranhada no epitáfio gravado que diz: Yuriv está aqui. Pai, Marido, Paladino. Deixe seus filhos testemunharem o fato de que sua dedicação à Luz era inquestionável. Ele nunca perguntaria nada deles que ele mesmo não faria. ' WHERE `entry`=695;
UPDATE `page_text` SET `text`='Diário - Dia 4 Fui preso na Ilha por 4 dias, sozinho com meus pensamentos. As bananas são muito saborosas, mas que longa subida para alcançá-las. Quando não estou comendo ou me protegendo da chuva periódica, todos os meus pensamentos são de resgate. Eu não estaria tão esperançoso se não fosse pelas caixas de papel e garrafas que caíam em terra comigo. Eu ri agora em pensar em todo o tempo que passei naquele navio amaldiçoando que eu estava preso com um monte de alquimistas e escribas.' WHERE `entry`=696;
UPDATE `page_text` SET `text`='Diário - Dia 512 As bananas começaram a conversar comigo e eu aprendi muito sobre sua cultura. Eu parei com minha destruição sem sentido de suas casas e consumo dos jovens. Quão pouco eu sabia então da grande civilização que eu estava destruindo. Em outro tópico eu pareço estar ficando sem garrafas e papel. Quando olhei pela primeira vez para aquelas enormes pilhas de papel que caíam na praia, achei que seriam inesgotáveis. Ai de mim, meu diário deve terminar em breve.' WHERE `entry`=697;
UPDATE `page_text` SET `text`='A terra de Azeroth é o lar de um sem fim de maravilhas. Flora, fauna, culturas e magia estão presentes em toda a sua superfície. De fato, os curiosos encontrarão uma variedade ilimitada neste mundo. Só tem que olhar.\r\n\r\nMas, se olharmos com profundidade suficiente, as janelas para novos mundos serão encontradas, e cada mundo abrigará suas próprias maravilhas.\r\n\r\nAssim como cada um é o lar de seus próprios horrores.  ' WHERE `entry`=698;
UPDATE `page_text` SET `text`='Este é o propósito do meu livro: catalogar esses seres, aqueles demônios do outro mundo que destruiriam nossas terras, de modo que os exploradores que acontecerem sobre eles saberão o que enfrentam.\r\n\r\nEntão, se você se considera um guardião de Azeroth, então continue a ler. E conheça seu inimigo.\r\n\r\n-Ur\r\nMago de Dalaran' WHERE `entry`=699;
UPDATE `page_text` SET `text`='O demônio do qual escrevo é o worgen.\r\n\r\nO velho folclore rural pode dar ouvidos a essas criaturas, pois que filho do fazendeiro não ouviu histórias de homens lobos bestiais espreitando os bosques e pântanos fora de sua aldeia?\r\n\r\nE a verdade pode se esconder em tais histórias - talvez sejam advertências contra os worgens, veladas como mitos para nos assustar.' WHERE `entry`=700;
UPDATE `page_text` SET `text`='Mas antes que tais contos sejam descartados, deixe-me agora assegurar ao leitor: worgen são reais. Eles podem não ser do nosso mundo, mas existem passagens entre a sua casa e a nossa e magias poderosas podem trazê-los para cá.\r\n\r\nEsses cantos devem ser deixados de lado, pois onde quer que Worgen trilhe, eles trazem terror e derramamento de sangue com eles.' WHERE `entry`=701;
UPDATE `page_text` SET `text`='Você conhecerá um worgen pela sua semelhança com os lobos do nosso mundo. Ao ver um worgen, pode-se ver facilmente como o cabelo grosso, as orelhas pontudas e o focinho longo são semelhantes aos lobos que conhecemos.\r\n\r\nMas você também verá rapidamente as diferenças: o cabelo grosso envolve um poderoso corpo bípede, com longas presas e garras semelhantes a adagas. E por trás de seu uivo se esconde uma malevolência não possuída por nenhum animal natural.' WHERE `entry`=702;
UPDATE `page_text` SET `text`='A casa dos worgens é um lugar escuro, um lugar de pesadelo. Se esse mundo promover locais a salvo do worgen amaldiçoado, então minha pesquisa não revelou tais bastiões.\r\n\r\nE se considerarmos a ferocidade e a maldade dos worgens, é provável que não existam tais bastiões.\r\n\r\nSupõe-se que os worgens se contentem em permanecer em seu mundo, pois embora alguns worgens possuam magias poderosas, eles não fizeram nenhuma tentativa de alcançar Azeroth por conta própria.\r\n\r\nE por isso, somos afortunados.' WHERE `entry`=703;
UPDATE `page_text` SET `text`='Como mencionado acima, alguns worgens são especializados em artes místicas, e sua magia é de escuridão e corrupção.\r\n\r\nMaldições e venenos sobrenaturais são comuns, por isso, esteja avisado - aqueles que enfrentam magos worgens devem se armar de proteções contra as sombras.' WHERE `entry`=704;
UPDATE `page_text` SET `text`='É minha esperança que nenhum mago de Dalaran procure os Worgen, mesmo que seja feito com consciência leve. Pois nenhum pacto pode ser atingido, nenhum segredo pode ser aprendido, nada de bom pode vir dessas feras.\r\n\r\nEles são melhores no mundo deles. Pois se encontrado no nosso e não destruído, então o nosso perigo será terrível...' WHERE `entry`=705;
UPDATE `page_text` SET `text`='Helgrum,$B$BMeus cumprimentos a quem emitiu a ordem para estabelecer nossa base de Kargath na Badlands. É um local ideal para treinar nossas tropas. Seu clima rigoroso, vida selvagem cruel, nativos guerreiros e completa falta de apoio externo fazem deste local um cadinho do qual apenas os melhores guerreiros e batedores surgirão.$B$BBom planejamento. Bom trabalho.' WHERE `entry`=711;
UPDATE `page_text` SET `text`='Vou descrever as terras vizinhas como os batedores de Kargath pesquisaram:$B$B-Vermelho, colinas rochosas e planícies secas que oferecem pouco apoio à vida.$B-Um sol escaldante e ventos fortes.$B-Não há lagos ou riachos. Nem mesmo piscinas espalhadas. Para encontrar água deve cavar poços profundos e lamacentos, ou sugar a escassa umidade dos cactos raros ou das ervas daninhas espinhosas.$B$BResumindo: vida quente e dura.' WHERE `entry`=712;
UPDATE `page_text` SET `text`='As Badlands estão cheias de inimigos. Ogros vagam pelas areias empoladas, emboscando os desavisados. Anões Darkiron habitam um quartel a leste de Kargath e lutam contra seus primos de pele mais clara no Loch Modan ao norte. Animais brutais e primitivos, chamados Troggs, assombram qualquer pedaço de sombra que encontram e defendem-na de forma selvagem.$B$BE no leste de Badlands no Lethlor Ravine existem dragões. Nós não sabemos quantos e não sabemos o quão grande, já que os batedores que chegam perto demais nunca retornam.' WHERE `entry`=713;
UPDATE `page_text` SET `text`='Não é preciso dizer que nós - os que estão em Kargath - estamos muito felizes com a nossa missão. Nós ficamos duros aqui, porque se ficarmos moles, morremos.$B$BE eu não gostaria de outra maneira.$B$BNeeka Bloodscar$BHead Scout, Kargath' WHERE `entry`=714;
UPDATE `page_text` SET `text`='<HTML>\r\n<BODY>\r\n<BR/>\r\n<BR/>\r\n<H1 align="center">\r\nEliza\r\n</H1>\r\n<BR/>\r\n<BR/>\r\n<BR/>\r\n<BR/>\r\n<P align="center">\r\nAmada esposa, que a terra sacie sua fome.\r\n</P>\r\n</BODY>\r\n</HTML>' WHERE `entry`=731;
UPDATE `page_text` SET `text`='<HTML> <BODY> <BR/> <BR/> <H1 align="center"> Eliza </H1> <BR/> <BR/> <BR/> <BR/> <P align="center"> Amada esposa, que a terra sacie sua fome. </P> </BODY> </HTML> ' WHERE `entry`=750;
UPDATE `page_text` SET `text`='Aqui estão as especificações do pistão: Comprimento: 32 WW Largura: 12 WW Peso: 23.5 IBG a 25.5 IBG Condução: 70 TC Resistência ao cisalhamento: 98 ou superior Classificação do Combobule: ??? super stout ??? ou superior No caso de você ter esquecido as medidas gnômicas: WW = ??? Whisker Width ??? IGB = ??? Gramas Itty-Bitty ??? TC = ??? Toe Curls ???' WHERE `entry`=751;
UPDATE `page_text` SET `text`='Sim, mano. Leve a carta para Frang no Valley of Trials. Ele costuma estar do lado de fora da Den, chicoteando jovens guerreiros para lutar pela Horda.$B$BEle precisa de guerreiros astutos como você para mostrar aos orcs como lutar com seus outros músculos. Você é mais rápido e inteligente, mas não se gabe disso - orcs não gostam de insultos. Eles são bons aliados e talvez também te ensinem uma ou duas coisas.$B$B-Tar\'sere' WHERE `entry`=752;
