UPDATE `page_text` SET `text`='O que eu notei é que a precipitação é extremamente letal. Imagine se <texto ilegível> Hahaha!' WHERE `entry`=1152;
UPDATE `page_text` SET `text`='Eu estou ficando louco? Hoje eu cortei a cabeça de um dos meus patrulheiros mais confiáveis para poder ver melhor a alma deles. Quando olhei para baixo a passagem de respiração rompida, ocorreu-me: eu não havia comido nada em dias!\r\n' WHERE `entry`=1153;
UPDATE `page_text` SET `text`='Mathias Shaw enviou um garoto de recados para me matar. <texto ilegível>\r\n\r\nEle patrulha fora agora. Drone sem mente e sem vida.\r\n\r\nHahahah!' WHERE `entry`=1154;
UPDATE `page_text` SET `text`='VanCleef... <texto ilegível>\r\n\r\nMe ligou? QUEM ELE PENSA QUE É?? Eu sou Klaven Mortwake! Não me dirão o que fazer com um inseto!\r\n\r\n<o restante das páginas do livro está faltando>' WHERE `entry`=1155;
UPDATE `page_text` SET `text`='Curgle\r\n\r\nEu tenho esperado ansiosamente por sua mais nova invenção. Eu não posso esperar para começar a usá-la para documentar meus estudos.\r\n\r\nPor favor, confie no meu mensageiro.\r\n\r\n\r\nCom os melhores cumprimentos,\r\n\r\nDaryn Lightwind' WHERE `entry`=1156;
