UPDATE `page_text` SET `text`='<HTML>\r\n<BODY>\r\n<H1 align="center">UM TRATADO EM POSTOS MILITARES </H1>\r\n<BR/>\r\n<BR/>\r\n<IMG src="InterfacePvPRankBadgesPvPRankAlliance" align="left"/>\r\n<BR/>\r\n<P align="right">O que se segue é</P>\r\n<P align="right">as fileiras militares</P>\r\n<P align="right">da Aliança,</P>\r\n<P align="right">prosseguido por tal</P>\r\n<P align="right">informação como é</P>\r\n<P align="right">conhecido pelas corridas</P>\r\n<P align="right">da Horda. Cada</P>\r\n<P align="right">é apresentado em duas</P>\r\n<P align="right">seções, os oficiais e depois os alistados, com as fileiras de cada um listado em ordem decrescente, do maior para o menor. Viva a Aliança! </P>\r\n</BODY>\r\n</HTML>' WHERE `entry`=2654;
