UPDATE `page_text` SET `text`='Quando você olha no Olho, imagens de uma grande batalha enchem sua mente.$b$bEste conflito aterrorizante parece estar ocorrendo em algum lugar em Lordaeron...$b$bVocê mal consegue distinguir uma figura feminina solitária, em pé no meio mil cadáveres, afastando-se de um mar de Flagelos...$b$bEla está em desvantagem numérica...$b$bSetas de luz escapam de suas mãos, cortando através de mortos-vivos às centenas.' WHERE `entry`=2659;
UPDATE `page_text` SET `text`='<HTML> <BODY> <H1 align="center">RANKS ALISTADOS DA HORDA</H1><BR/> <IMG src="InterfacePvPRankBadgesPvPRank01"/><BR/> <P align="right">First Sergeant</P><BR/><BR/> <IMG src="InterfacePvPRankBadgesPvPRank04"/><BR/> <P align="right">Senior Sergeant</P><BR/><BR/> <IMG src="InterfacePvPRankBadgesPvPRank03"/><BR/> <P align="right">Sergeant</P><BR/><BR/> <IMG src="InterfacePvPRankBadgesPvPRank02"/><BR/> <P align="right">Grunt</P><BR/><BR/> <IMG src="InterfacePvPRankBadgesPvPRank01"/><BR/> <P align="right">Scout</P><BR/><BR/> </BODY> </HTML> ' WHERE `entry`=2660;
UPDATE `page_text` SET `text`='<HTML>\r\n<BODY>\r\n<H1 align="center">RANKS MILITARES DA HORDA E ALIANÇA</H1>\r\n<BR/>\r\n<BR/>\r\n<IMG src="InterfacePvPRankBadgesPvPRankHorde" align="left"/>\r\n<BR/>\r\n<P align="right">Throm\'ka!  Neste</P>\r\n<P align="right">livro que você encontrará</P>\r\n<P align="right">muitas listas de</P>\r\n<P align="right">ranks militares</P>\r\n<P align="right">da Horda e</P>\r\n<P align="right">da franzina Aliança.</P>\r\n<P align="right">Primeiro virá a</P>\r\n<P align="right">Horda, então a Aliança,</P>\r\n<P align="right">cada um com listas de oficiais e ranks de alistados. Como é apropriado, os mais fortes estão listados no topo, com os mais fracos listados abaixo.</P>\r\n</BODY>\r\n</HTML>' WHERE `entry`=2661;
