(219939, 'ptBR', 'Volte com mais dinheiro.', 'Volte com mais dinheiro.', 47187),
(219938, 'ptBR', 'E o clã?', 'E o clã?', 47187),
(219937, 'ptBR', 'O Chefe Coração de Pedra aceitou os \"presentes\" elementais dos Primevistas.', 'O Chefe Coração de Pedra aceitou os \"presentes\" elementais dos Primevistas.', 47187),
(219936, 'ptBR', 'Já quero negociar com você de novo.', 'Já quero negociar com você de novo.', 47187),
(219935, 'ptBR', 'Que sua jornada termine em vitória.', 'Que sua jornada termine em vitória.', 47187),
(219934, 'ptBR', 'Espero que você não tarde a voltar.', 'Espero que você não tarde a voltar.', 47187),
(219933, 'ptBR', 'Adeus.', 'Adeus.', 47187),
