INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(226841, 'koKR', '흠.', '', 47187),
(226840, 'koKR', '안 싸울 거면 떠나라.', '', 47187),
(226839, 'koKR', '원하는 게 뭐냐, 이방인.', '', 47187),
(226838, 'koKR', '사우전본즈 이후로 마루크는 단 한 번도 강한 칸이 지도한 적 없다.', '', 47187),
(226837, 'koKR', '나는 신경 쓸 것 없소.', '', 47187),
(226836, 'koKR', '내가 아닌 칸에게 집중하시오.', '', 47187),
(226833, 'koKR', '그녀의 꿈이 지면 아래에서 노래한다. 우리의 꿈을, 우리의 노래를.', '', 47187),
(226832, 'koKR', '뿌리는 깊이, 더 깊이 파고들어 우리의 품을 반기리라.', '', 47187),
(226831, 'koKR', '무수한 눈으로 그들은 다시 보게 될 것이다. 양껏 마시고 환희를 누리리라.', '', 47187),
(226829, 'koKR', '너는 그를 따라 심연으로 갈 것이고, 그가 지나간 자리에 어둠의 물이 흐를 것이다.', '', 47187),
(226826, 'koKR', '나는... 너를 안다. 네가 어떤 존재였고, 어떤 존재가 될지를.', '', 47187),
(226825, 'koKR', '시간의 합일점에서 소란이 일어났소. 그리고 그 여파로 이 계곡 일대의 시간의 길이 불안정해지고 말았지.\n\n인력이 한정되어 있다고는 하나, 우선 계곡에 퍼진 시간의 마력의 불균형을 해소해 확산을 저지해야 하오. 저 위에 있는 동료들을 돕는 건 그다음에 할 일이오.', '', 47187),
(226823, 'koKR', '그리고 보라색 엘프 친구들은 어딜 가든 꼭 술병을 챙겨 다니더군요.', '', 47187),
(226822, 'koKR', '저기 비행 조련사 근처에는 특이하게 생긴 식물을 다듬는 트롤이 있었고요.', '', 47187),
(226821, 'koKR', '냄새를 보니 중앙 막사 고블린 요리사가 단백질 요리를 만들고 있나 봅니다.', '', 47187),
(226819, 'koKR', '이름이 너무 긴 것 같은데.', '', 47187),
(226818, 'koKR', '매콤한 포도주로 게살을 조린 휴대식량이라고나 할까요?', '', 47187),
(226817, 'koKR', '아, 어흠. 그냥 휴대식량입니다... 그런데...', '', 47187),
(226816, 'koKR', '제법이군요, 용 친구. 이 신 요리 이름은 뭡니까?', '', 47187),
(226815, 'koKR', '꽤 복합적인 향이 나는군. 흥미로운걸.', '', 47187),
(226814, 'koKR', '어련하겠어! 언제 준비되는 거야? 쫄쫄 굶었다고!', '', 47187),
(226813, 'koKR', '이거 냄새가 꽤 괜찮은데요?', '', 47187),
(226812, 'koKR', '<이 돌에 새겨진 각인은 부식과 세월의 풍파로 흐릿해져 해독하기 어려운 상태입니다.\n\n금이 간 표면을 살펴보던 중 어떤 한 상징이 당신의 시선을 사로잡습니다. \n\n고대 비전 상징으로 보입니다.>', '', 47187),
(226809, 'koKR', '<차가운 돌이 당신의 따뜻한 손길을 환영하는 것만 같습니다.>', '', 47187),
(226808, 'koKR', '다음은 너다!', '', 47187),
(226807, 'koKR', '자네는 상상조차 할 수 없을 정도로 참담한 시기였네. 공포가 만연했지. 세계의 분리, 검은용군단의 배신, 용군단의 전쟁까지...', '', 47187),
(226806, 'koKR', '여기 받으세요. 금방 잔달라 사람처럼 요리할 수 있게 될 겁니다.', '', 47187),
(226805, 'koKR', '베리티스트라즈가 당신을 힐끔거립니다.', '', 47187),
(226804, 'koKR', '즐거운 시간 보내길.', '', 47187),
(226803, 'koKR', '내 희생으로 전당은 안전하리라!', '', 47187),
(226802, 'koKR', '우쭐해하지 마라. 당장 없애주마!', '', 47187),
(226801, 'koKR', '질서에 압도되리라.', '', 47187),
(226800, 'koKR', '지나갈 수 없을 거다!', '', 47187),
(226798, 'koKR', '티르시여... 용서하소서... 침입자를... 막지 못했...', '', 47187),
(226795, 'koKR', '들여보내 줄 수 없다.', '', 47187),
(226794, 'koKR', '너희의 최후는 불가피했다.', '', 47187),
(226793, 'koKR', '침입자가 더 있었나? 지나가게 두지 않겠다!', '', 47187),
(226790, 'koKR', '으휴! 저리 안 나가, 이 발톱 달린 쥐새끼들아!', '', 47187),
(226789, 'koKR', '함께 협력합시다.', '', 47187),
(226788, 'koKR', '지혜가 우리를 이끌기를.', '', 47187),
(226786, 'koKR', '드워프가 인상을 찌푸린 채 먼 곳을 응시합니다.', '', 47187),
(226785, 'koKR', '여유가 된다면 저도 돕겠습니다.', '', 47187),
(226784, 'koKR', '늘 그렇듯, 아제로스를 위하여.', '', 47187),
(226782, 'koKR', '검은용군단이 다시 한번 이 세계를 지킬 겁니다.', '', 47187),
(226781, 'koKR', '우리의 유산은 다시 태어날 것입니다.', '', 47187),
(226780, 'koKR', '보고할 사항은?', '', 47187),
(226779, 'koKR', '웨이른을 향한 충심을 지켜라.', '', 47187),
(226778, 'koKR', '흠. 또 얼굴을 보게 될 줄은 몰랐네요.', '', 47187),
(226777, 'koKR', '벌들 건드리지 않게 조심하세요.', '', 47187),
(226775, 'koKR', '', '날갯짓하면서 기침하도록. 참, 날개는 달려 있겠지?', 47187),
(226774, 'koKR', '그래도 섭정이 되면 장점이 있긴 합니다. 식사는 군대 배식에 비교하는 게 실례일 정도고, 알레리아랑 같이 성채 보수 계획도 구상하고 있습니다!', '', 47187),
(226773, 'koKR', '입씨름하는 귀족들을 어르고 달래는데 매일같이 반나절을 보내게 될 줄은 몰랐습니다!', '', 47187),
(226772, 'koKR', '정치에 입문하는 것도 재밌는 도전이 될 거라더니...', '', 47187),
(226770, 'koKR', '어떤 고난이 닥쳐도 용기를 잃지 마십시오.', '', 47187),
(226769, 'koKR', '고향과 가족을 위해.', '', 47187),
(226768, 'koKR', '얼라이언스는 번영할 것입니다.', '', 47187),
(226767, 'koKR', '굳건히 나아가십시오.', '', 47187),
(226766, 'koKR', '부족한 물자는 보충했으니, 이제 전세가 우리 쪽으로 기울면 좋겠습니다.\n\n아직 말씀을 나누지 않으셨다면 타데지우스 사령관님께 가보세요. 지금 전선은 당신 같은 인재가 필요할 겁니다.', '', 47187),
(226765, 'koKR', '경보 포탑이 작동했습니다!', '경보 포탑이 작동했습니다!', 47187),
(226764, 'koKR', '늘 빛의 길을 걸으십시오.', '', 47187),
(226763, 'koKR', '의무를 잊지 마십시오.', '', 47187),
(226762, 'koKR', '책무를 다하십시오.', '', 47187),
(226761, 'koKR', '도움이 필요하십니까?', '', 47187),
(226760, 'koKR', '소임을 다하겠습니다.', '', 47187),
(226759, 'koKR', '', '이번 도전에서는 대지의 아늑함을 뒤로해야만 합니다.', 47187),
(226758, 'koKR', '항상 굳건하십시오.', '', 47187),
(226757, 'koKR', '제 믿음이 곧 방패입니다.', '', 47187),
(226756, 'koKR', '우린 함께합니다. ', '', 47187),
(226755, 'koKR', '우린 얼라이언스의 힘을 재건할 것입니다.', '', 47187),
(226754, 'koKR', '스톰윈드 시민들은 제가 지킵니다.', '', 47187),
(226753, 'koKR', '수년간 마루크는 큰 갈등 없이 지내왔네. 하지만 이번 전쟁은 온 평야를 삼킬 걸세.', '', 47187),
(226752, 'koKR', '내가 대격변 시절 스톰윈드를 떠돌던 파멸의 예언자 중 하나였다는 사실에 몇 번이나 땅을 치고 후회했는지 모른다. 다가오는 폭풍을 예견하는 내 능력에 매몰되어, 정작 스스로 그 폭풍의 일부가 되었음은 자각하지 못했지.\n\n내가 초래한 피해를 되돌릴 순 없겠지만, 속죄는 할 수 있을지도 모른다.\n\n오늘 네가 희망을 보여줬다.', '', 47187),
(226751, 'koKR', '', '정원에서 심상찮은 일이 일어나고 있다. 한데 원시술사들이 지금껏 해 온 것처럼 공격하는 모습은 보이질 않는구나.\n\n경계를 늦추지 마라, $n.', 47187),
(226750, 'koKR', '뾰족부리만 있었어도 제가 이겼을 거예요. 너 들으라고 한 소리는 아냐, 야옹아.', '', 47187),
(226749, 'koKR', '멋진 경주였어요! 용 조련술에 재미가 붙을지도 모르겠는데요.', '', 47187),
(226748, 'koKR', '맹금의 봉우리를 위하여!', '', 47187),
(226747, 'koKR', '하늘에서 와일드해머를 따라올 사람은 없어요. 번개 맛을 보여드리죠!', '', 47187),
(226741, 'koKR', '제가 이상한 흔적은 없는지 살펴보겠습니다, 메리스라 여군주님. 범인이 누구든 빠져나가지 못할 겁니다.', '', 47187),
(226737, 'koKR', '', '또... 드리아드를 잃었단 말이냐. 이번에도 원시술사들이 정원에 침입한 건가?', 47187),
(226734, 'koKR', '책에 그려진 룬을 살펴본 결과, 수정의 방 입구로 통하는 순간이동식으로 보입니다.', '책에 그려진 룬을 살펴본 결과, 수정의 방 입구로 통하는 순간이동식으로 보입니다.', 47187),
(226732, 'koKR', '', '와! 어릴 때 이후로 타이반이 저렇게 들뜬 모습은 처음 봐요! 이번에는 잘 풀려야 할 텐데...', 47187),
(226731, 'koKR', '', '전 저 아래 이스카라까지 쭉 뻗은 광활한 평원의 경치를 감상하는 취미가 있습니다. 어떻게 보면 뱀처럼 생긴 강줄기로 절단된 것 같은 인상도 들죠. 물의 우렁찬 울음소리도 듣는 재미가 있더군요.', 47187),
(226724, 'koKR', '신성 모독의 대가는 네 목숨으로 받아가겠다.', '', 47187),
(226723, 'koKR', '약자에겐 멸종 뿐.', '', 47187),
(226718, 'koKR', '', '방화 사건이 일어나고 스톰윈드 시민들은 우릴 받아줬습니다.\n\n이곳 시민들은 선량한 사람들입니다\n\n그대도 우리처럼 위안을 얻기를 바라죠.', 47187),
(226716, 'koKR', '아첨꾼은 존재가 지워지지.', '', 47187),
(226715, 'koKR', '꼭두각시의 끈이 끊어졌군.', '', 47187),
(226714, 'koKR', '넬타리온의 선제공격에 푸른용군단은 궤멸에 가까운 피해를 받았던지라, 실질적으로 3 대 1의 싸움이었네. 그래도 승산은 높았지. 현실을 보기 전까지는.', '', 47187),
(226713, 'koKR', '반갑다, 기수여. 내가 잃어버린 동료를 찾도록 도와주겠다.', '', 47187),
(226710, 'koKR', '나는 내 땅을 지킬 것이다!', '', 47187),
(226708, 'koKR', '이 땅은 우리만의 터전이다!', '', 47187),
(226705, 'koKR', '용군단의 전쟁... 다른 명칭으로 불리기도 하던데, 현장에 있었던 나는 다른 이름으로는 못 부르겠더군. 모든 용군단이 검은용군단과 맞붙었으니까 말일세.', '', 47187),
(226702, 'koKR', '아우우우!', '', 47187),
(226701, 'koKR', '내 사랑, 앞으로 어떤 일이 닥치든... 우리 유대는 영원하다는 걸 잊지 마시오.', '', 47187),
(226700, 'koKR', '<등반 밧줄의 매듭을 점검하면서 말하는 길잡이 제브>\n\n신출내기 탐험가들이 제게 꼭 묻는 말이 있습니다. 이 대자연에서 그토록 오래 살아남을 수 있었던 비결이 무엇이냐고요.\n\n신이나 지침서, 도구 목록, 행운의 장신구 같은 걸 기대하고 던지는 질문이었죠.\n\n하지만 저는 한 단어로 대답합니다. 준비성이라고. 용사님에게도 같은 말씀을 드리고 싶습니다.\n\n자연은 가혹합니다. 그렇지만 계획과 알맞은 준비물만 있으면...\n\n뭐, 글쎄요. 저는 그렇게 하면서 아직 실패한 경험이 없습니다.', '', 47187),
(226699, 'koKR', '', '알고 보니 고립되어 살아오다 낯선 세계를 마주한 건 우리가 처음은 아닌 것 같더군요. \n\n용의 섬에 변화의 바람이 불고 있습니다. 앞서 적응에 성공한 이들처럼 우리도 그럴 수 있기를 소망합니다.', 47187),
(226698, 'koKR', '', '아제로스에서 숨어 수 세대를 보내고 나서야 그 너머에 있던 경이로운 세계를 발견하게 되는군요. 우리 셋이 이렇게 공통점이 많을 줄은 몰랐네요!', 47187),
(226697, 'koKR', '', '칼도레이가 신성한 숲의 가지 너머로 진출하지 않았더라면 우린 이 경이로운 땅에 발을 들이지 못했겠죠.', 47187),
(226696, 'koKR', '', '어서 오세요, 기수님! 목이 마르신가요? 그렇다면 제대로 찾아오셨어요. 앉을 자리가 많지 않은 점은 양해해 주세요.', 47187),
(226695, 'koKR', '온아라 평야의 주민들은 셀 수 없이 많은 세대를 거치면서 유목 생활을 발전시켜 왔습니다. 불페라가 보고 배울 점이 많을 거예요!', '', 47187),
(226694, 'koKR', '', '고맙소, 용사여. 이걸로 생명의 웅덩이는 안전해졌지만, 그렇다고 원시술사의 위협이 해소된 건 아니오. 또 허를 찔리지 않으려면 경계를 투철히 해야겠지.', 47187),
(226693, 'koKR', '', '추악한 괴물아! 내가 생명의 웅덩이를 지키겠... 안 돼!', 47187),
(226692, 'koKR', '', '마지막 웅덩이까지 안전해졌군! 원시술사 우두머리가 모습을 드러냈소! 지원군을 소환하기 전에 처치하시오.', 47187),
(226691, 'koKR', '', '곧 하수인들의 우두머리가 보이겠군. 서두르시오, 용사여!', 47187),
(226690, 'koKR', '', '계속 놈들을 소탕하시오. 원시술사들이 이 성지를 더럽히지 못하게 해야 하오!', 47187),
(226689, 'koKR', '', '훌륭하오. 첫 웅덩이를 확보했군! 다른 웅덩이들 차례요!', 47187),
(226688, 'koKR', '', '노출된 생명의 웅덩이가 중대한 위기에 처했소. 강력한 원시술사가 부화장을 모조리 불태우려 하오! 웅덩이를 정리해야 적을 칠 수 있소. 나는 계속 상공에서 이 공격을 주도하는 용을 견제하리다.', 47187),
(226687, 'koKR', '', '수고 많았소, 용사여. 하부 웅덩이는 확보됐으니 이제 상부를 지원해 주시오.', 47187),
(226686, 'koKR', '', '알렉스트라자 님은 우리에게 원시술사로부터 생명의 웅덩이를 보호하라 지시하셨소. 놈들이 너무 깊이 침투한 건 아니길. 알의 안전을 확보하고 성지를 위협하는 건 모두 제거하시오.', 47187),
(226685, 'koKR', '나는 이곳이 혈족의 터라고 불리던 시절에 나고 자랐네. 먼 옛날 생명의 제단을 가꿨지. 그러다 필멸자들을 지키러 떠났고 말이야...', '', 47187),
(226684, 'koKR', '이 풍경이 자네 보기엔 아름다울지도 모르겠군. 하지만 난 우리가 얼마나 오랫동안 떠나 있었는지 새삼 되새기게 된다네. 알아보질 못하겠어.', '', 47187),
