INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(224488, 'koKR', '말해라.', '', 47187),
(224487, 'koKR', '', '몸조심하도록.', 47187),
(224484, 'koKR', '', '다른 이들도 돕기를.', 47187),
(224483, 'koKR', '', '서로를 돌봐야 한다.', 47187),
(224482, 'koKR', '', '숨을 깊게 들이쉬어라. 살짝 따끔할 테니.', 47187),
(224479, 'koKR', '', '환자들을 보살펴야 해.', 47187),
(224478, 'koKR', '', '뭐든 함부로 부수지 않게 조심해라.', 47187),
(224477, 'koKR', '', '금방 돌아오길 기원하지.', 47187),
(224476, 'koKR', '', '할말이 있나?', 47187),
(224475, 'koKR', '', '기꺼이 타인을 도우리라.', 47187),
(224474, 'koKR', '', '잘 지내기를.', 47187),
(224473, 'koKR', '', '치유는 내 전문이지.', 47187),
(224472, 'koKR', '', '도움이 필요한가?', 47187),
(224471, 'koKR', '', '건강해 보이는군.', 47187),
(224470, 'koKR', '', '상태를 한번 볼까?', 47187),
(224469, 'koKR', '', '다른 이들을 돕는 게 내 책무지.', 47187),
(224466, 'koKR', '활공 전문가인 셀로르무는 이미 만나봤을 것이고... 참, 비룡에 관해 더 가르쳐줄 만한 사람이 있소. 리스라고사를 찾아가 이야기를 들어보시오.', '', 47187),
(224465, 'koKR', '헤어질 때가 됐군. 모든 것을 발견하기를!', '', 47187),
(224464, 'koKR', '우리 전초기지는 용의 섬 곳곳에 있으니 잘 찾아보시오. 용 조련술을 연습할 때 도움이 필요하면 우리를 찾으시오!', '', 47187),
(224463, 'koKR', '', '용 조련술 숙련도를 더 높이고 싶으면 제 뒤로 보이는 것과 닮은 문양을 찾아보세요. 문양을 모을수록 더 많은 재주와 능력을 구사할 수 있게 됩니다.\n\n처음부터 얻기에는 너무 어려운 것들도 있을 텐데, 너무 걱정하지 않으셔도 됩니다. 용의 섬 방방곡곡을 탐험하면서 힘을 기르다 보면 어느새 그런 곳도 정복할 수 있게 된 자신을 발견하게 될 거예요.\n\n무엇을 배울 수 있는지 확인하려면 전초기지에서 절 찾아오세요.', 47187),
(224459, 'koKR', '용의 섬은 활공하기 딱 좋은 지형으로 가득합니다. 경주는 보통 여건이 괜찮을 때만 열리는데, 참가하면 보상을 추가로 얻을 수 있죠.', '', 47187),
(224454, 'koKR', '', '그럼요, 물론이죠! 말씀하세요.', 47187),
(224452, 'koKR', '', '안녕하세요! 반가워요! 잘 부탁해요! 장인 무역연합이 어떻게 도와드리면 될까요?', 47187),
(224451, 'koKR', '', '위상의 힘을 조금이라도 되찾고 라자게스를 저지하려면 세 개를 더 복원해야 한다.', 47187),
(224449, 'koKR', '<새끼 오리가 큼지막하고 불쌍한 눈망울로 당신을 바라봅니다.>', '', 47187),
(224448, 'koKR', '', '그래서 몇 가지 생각이 있는데요...', 47187),
(224447, 'koKR', '사실 조금 전까지 고위 마법부여사님과 당면한 위기에 관해 상의하고 있었습니다.', '', 47187),
(224445, 'koKR', '풍경이 엄청나지요?', '', 47187),
(224443, 'koKR', '훌륭합니다, $r 친구! 이로써 좀 더 안전해졌군요... 전반적인 상황을 고려했을 때 말이죠.', '', 47187),
(224442, 'koKR', '', '요리를 하는 분이길 바라죠...', 47187),
(224440, 'koKR', '', '인내심의 화신 같은 분이로군요!', 47187),
(224433, 'koKR', '글렌세라는 변신 강단을 관리하는 친구요. 거기서 용에게 새롭고 독특한 형상을 부여하는 법을 배워보시오.', '', 47187),
(224431, 'koKR', '', '용의 섬이나 그 너머를 탐험하다 보면 비룡의 형상을 바꿀 수 있는 물품을 입수하게 되실 겁니다.\n\n그런 물품을 사용한 다음 변신 강단을 찾아가면 선택지로 사용이 가능합니다.', 47187),
(224428, 'koKR', '', '두 서약의 돌이 소생했구나.', 47187),
(224426, 'koKR', '', '변신 강단을 통해 용의 섬에서 보았거나 발견한 형상을 기반으로 비룡을 다채롭게 꾸밀 수 있습니다.\n\n우리 전초기지 중에 강단이 설치되어 있지 않은 곳은 없으니, 마음만 먹으면 방문해 새로운 모습으로 꾸밀 수 있답니다.', 47187),
(224424, 'koKR', '래시온 님도 좋은 분이지만, 담금질이 부족하네. 불 속에서 단련을 마친 사벨리안 님이 지금 우리에게 필요한 지도자네.', '', 47187),
(224423, 'koKR', '', '섬에 오신 것을 환영합니다. 용 조련술의 고급 기술을 배울 준비는 되셨나요?', 47187),
(224422, 'koKR', '다른 용군단이나 공명심에 눈 돌아간 필멸자들, 하물며 세상 젠체는 다 하는 \"왕자\"까지, 모두가 우리 고통받는 이들을 돕느니 차라리 죽이는 걸 택했네.', '', 47187),
(224420, 'koKR', '', '가로쉬를 풀어준 까닭에 전쟁이 일어났지. 하나 군단에 대비하려면 그 전쟁이 필요했네.', 47187),
(224419, 'koKR', '대지의 수호자의 제련소는 가까이 있네. 해낼 수 있네.', '', 47187),
(224417, 'koKR', '', '오늘은 날이 참 좋습니다, 용기수님. 용을 진정 개성 넘치는 모습으로 만들 방법을 배울 준비는 되셨습니까?', 47187),
(224416, 'koKR', '여긴 저주받은 곳이에요. 얻을 게 하나도 없다고요.', '', 47187),
(224414, 'koKR', '서두르셔야 합니다. 알리아가 급격하게 쇠약해지고 있어요!', '', 47187),
(224413, 'koKR', '', '추워... 너무... 추워...', 47187),
