INSERT INTO `journal_encounter_locale` (`ID`, `locale`, `Name_lang`, `Description_lang`, `VerifiedBuild`) VALUES
(2405, 'frFR', 'Tred\’ova', 'Alors que les gorms grignotaient les frontières de Sylvarden et les bogues desséchées des trépassés, la créature nommée Tred\’ova goûta à un mets des plus extraordinaires : la graine sauvage de Lakali, Loa du savoir. Enivrée par son pouvoir et sa sagesse, Tred\’ova ne cherche plus uniquement à survivre…', 54673),
(596, 'frFR', 'Gal\'darah', 'Lorsque le Fléau envahit la nation de Zul\’Drak, les Drakkari se tournèrent vers leur loa. Mais ils apportèrent des armes, plutôt que des supplications. Alors que le sang coulait des veines d\’Akali, leur dieu assassiné, le grand prophète Gal\’darah et les traîtres qui le suivaient s\’attribuèrent la puissance du Loa.', 54673),
(594, 'frFR', 'Moorabi', 'Lorsque les Drakkari cherchèrent à sacrifier Mam\’toth pour obtenir ses pouvoirs, le Loa préféra se détruire lui-même. Mais Moorabi n\’allait pas laisser cet acte le priver de ses pouvoirs quasi divins. Le grand prophète a bu le sang du Loa et ne reculera plus devant rien pour garder Zul\’Drak sous le contrôle des Trolls.', 54673);
