(258949, 'frFR', 'Vous pouvez y aller.', 'Vous pouvez y aller.', 57534),
(258948, 'frFR', 'Nous sommes tombés en disgrâce.', 'Nous sommes tombés en disgrâce.', 57534),
(258947, 'frFR', 'Nous combattons pour la Flamme sacrée.', 'Nous combattons pour la Flamme sacrée.', 57534),
(258946, 'frFR', 'Nous triompherons.', 'Nous triompherons.', 57534),
(258945, 'frFR', 'Vous venez d\’ailleurs, n\’est-ce pas ?', 'Vous venez d\’ailleurs, n\’est-ce pas ?', 57534),
(258944, 'frFR', 'L\’empereur récompense les fidèles.', 'L\’empereur récompense les fidèles.', 57534),
(258942, 'frFR', '', 'Place à la tradition…', 57534),
(258941, 'frFR', 'Non !', '', 57534),
(258940, 'frFR', '', 'Mon cher Klaskin, qui a bien pu donner l\’ordre de te retrouver, à ton avis ?', 57534),
(258939, 'frFR', 'Revenez… si vous survivez.', 'Revenez… si vous survivez.', 57534),
(258938, 'frFR', '$p ! Vous aviez dit que vous ne permettriez pas à ma femme de me voir !', '', 57534),
