INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(277341, 'itIT', 'Ammiro la tua tenacia, ma devi smetterla.', 'Ammiro la tua tenacia, ma devi smetterla.', 57534),
(277340, 'itIT', 'Devi diventare... il nuovo Brandicenere...', 'Devi diventare... il nuovo Brandicenere...', 57534),
(277339, 'itIT', 'Avete avuto l\'occasione per arrendervi.', 'Avete avuto l\'occasione per arrendervi.', 57534),
(277338, 'itIT', 'Non puoi nasconderti da me!', 'Non puoi nasconderti da me!', 57534),
(277337, 'itIT', 'L\'avete voluto voi.', 'L\'avete voluto voi.', 57534),
(277336, 'itIT', 'Voi non siete pronti!', 'Voi non siete pronti!', 57534),
(277335, 'itIT', 'Ti andrebbe una tazza di tè?', 'Ti andrebbe una tazza di tè?', 57534),
(277334, 'itIT', 'Ci sono novità?', 'Ci sono novità?', 57534),
(277333, 'itIT', 'Che razza di magia è questa?', 'Che razza di magia è questa?', 57534),
(277332, 'itIT', 'Ebbene, cos\'abbiamo qui?', 'Ebbene, cos\'abbiamo qui?', 57534),
(277331, 'itIT', 'Bene, diamoci da fare.', 'Bene, diamoci da fare.', 57534),
(277330, 'itIT', 'Li uccideremo tutti.', 'Li uccideremo tutti.', 57534),
(277329, 'itIT', 'Metteremo in ginocchio l\'Alleanza.', 'Metteremo in ginocchio l\'Alleanza.', 57534),
(277328, 'itIT', 'Dovremmo continuare a muoverci.', 'Dovremmo continuare a muoverci.', 57534),
(277327, 'itIT', 'Noi siamo la vera forza di Kalimdor.', 'Noi siamo la vera forza di Kalimdor.', 57534),
(277326, 'itIT', 'Urgh! Ho del fango sugli stivali. Disgustoso!', 'Urgh! Ho del fango sugli stivali. Disgustoso!', 57534),
(277325, 'itIT', 'Insieme, annienteremo la tua oscurità!', 'Insieme, annienteremo la tua oscurità!', 57534),
(277324, 'itIT', 'Combatteremo insieme!', 'Combatteremo insieme!', 57534),
(277322, 'itIT', 'Un gioco da ragazzi.', 'Un gioco da ragazzi.', 57534),
(277321, 'itIT', 'Questo è mio!', 'Questo è mio!', 57534),
(277320, 'itIT', 'Questo è troppo!', 'Questo è troppo!', 57534),
(277319, 'itIT', 'Questo è il prezzo che devi pagare!', 'Questo è il prezzo che devi pagare!', 57534),
(277318, 'itIT', 'Il gioco è cambiato.', 'Il gioco è cambiato.', 57534),
(277317, 'itIT', 'La fornace vi consumerà!', 'La fornace vi consumerà!', 57534),
(277316, 'itIT', 'Non sapete fare di meglio?', 'Non sapete fare di meglio?', 57534),
(277315, 'itIT', 'Questa non me l\'aspettavo!', 'Questa non me l\'aspettavo!', 57534),
(277314, 'itIT', 'È stato fin troppo facile!', 'È stato fin troppo facile!', 57534),
(277313, 'itIT', 'Non mi piace molto...', 'Non mi piace molto...', 57534),
(277312, 'itIT', 'Smettila.', 'Smettila.', 57534),
(277311, 'itIT', 'Mostratevi!', 'Mostratevi!', 57534),
(277310, 'itIT', 'Saluta il mio amichetto!', 'Saluta il mio amichetto!', 57534),
(277309, 'itIT', 'Sbarazzati di questi insetti!', 'Sbarazzati di questi insetti!', 57534),
(277308, 'itIT', 'RITIRATA!', 'RITIRATA!', 57534),
(277307, 'itIT', 'Razza di incosciente...', 'Razza di incosciente...', 57534),
(277306, 'itIT', 'I nemici conosceranno la nostra furia.', 'I nemici conosceranno la nostra furia.', 57534),
(277304, 'itIT', 'Oh, siamo già arrivati?', 'Oh, siamo già arrivati?', 57534),
(277303, 'itIT', 'Non oggi!', 'Non oggi!', 57534),
(277302, 'itIT', 'Come puoi vedere, la Terrazza dei Magisteri era solo un contrattempo.', 'Come puoi vedere, la Terrazza dei Magisteri era solo un contrattempo.', 57534),
(277301, 'itIT', 'Lok narash!', 'Lok narash!', 57534),
(277300, 'itIT', 'Lok\'tar ogar!', 'Lok\'tar ogar!', 57534),
(277298, 'itIT', 'Dai forza!', 'Dai forza!', 57534),
(277297, 'itIT', 'Che l\'ultimo ballo abbia inizio!', 'Che l\'ultimo ballo abbia inizio!', 57534),
(277296, 'itIT', 'Che ti serva di lezione!', 'Che ti serva di lezione!', 57534),
(277295, 'itIT', 'Accidete qualunque cosa respiri!', 'Accidete qualunque cosa respiri!', 57534),
(277291, 'itIT', 'Ma guarda chi c\'è! È bello vederti.', '', 57534),
(277288, 'itIT', 'Ah, sono troppo vecchio per queste assurdità.', 'Ah, sono troppo vecchio per queste assurdità.', 57534),
(277286, 'itIT', 'Mi servirà il mio martello.', 'Mi servirà il mio martello.', 57534),
(277285, 'itIT', 'Questa città è mia!', 'Questa città è mia!', 57534),
(277284, 'itIT', 'So cosa dobbiamo fare.', 'So cosa dobbiamo fare.', 57534),
(277283, 'itIT', ' Io non posso essere sconfitto.', 'Io non posso essere sconfitto.', 57534),
(277282, 'itIT', 'Io sono la vostra morte.', 'Io sono la vostra morte.', 57534),
(277281, 'itIT', 'Colpisco sempre il mio bersaglio.', 'Colpisco sempre il mio bersaglio.', 57534),
(277280, 'itIT', 'Ma insomma!', 'Ma insomma!', 57534),
(277279, 'itIT', 'Quanto riuscirete a resistere?', 'Quanto riuscirete a resistere?', 57534),
(277278, 'itIT', 'La speranza svanisce...', 'La speranza svanisce...', 57534),
(277277, 'itIT', 'Aiutami...', 'Aiutami...', 57534),
(277275, 'itIT', 'Torna da dove sei venuto!', 'Torna da dove sei venuto!', 57534),
(277272, 'itIT', 'Salve.', 'Salve.', 57534),
(277270, 'itIT', 'Buone notizie, gente!', 'Buone notizie, gente!', 57534),
(277269, 'itIT', 'Gelidanima ha fame...', 'Gelidanima ha fame...', 57534),
(277268, 'itIT', 'Per l\'Alleanza!', 'Per l\'Alleanza!', 57534),
(277267, 'itIT', 'Per l\'Orda.', 'Per l\'Orda.', 57534),
(277266, 'itIT', 'Combatti per Azeroth.', 'Combatti per Azeroth.', 57534),
(277265, 'itIT', 'Cieli limpidi e venti forti.', 'Cieli limpidi e venti forti.', 57534),
(277264, 'itIT', 'Accogli la speranza.', 'Accogli la speranza.', 57534),
(277262, 'itIT', 'Elune-adore.', 'Elune-adore.', 57534),
(277261, 'itIT', 'Credi che abbia il vigore di una vera leader?', 'Credi che abbia il vigore di una vera leader?', 57534),
(277260, 'itIT', 'Non lo vedi?', 'Non lo vedi?', 57534),
(277259, 'itIT', 'Bambini! Un po\' di ritegno!', 'Bambini! Un po\' di ritegno!', 57534),
(277258, 'itIT', 'Brucerete... Brucerete TUTTI!', 'Brucerete... Brucerete TUTTI!', 57534),
(277257, 'itIT', 'Sangue e tuono!', 'Sangue e tuono!', 57534),
(277256, 'itIT', 'Vattene.', 'Vattene.', 57534),
(277255, 'itIT', 'Un\'altra volta, magari.', 'Un\'altra volta, magari.', 57534),
(277254, 'itIT', 'Tutti uniti nella Luce.', 'Tutti uniti nella Luce.', 57534),
(277253, 'itIT', 'Ah, eccoti qui.', 'Ah, eccoti qui.', 57534),
(277139, 'itIT', '<Risata minacciosa.>', '<Risata minacciosa.>', 57534),
(276876, 'itIT', '', 'Breanni sarebbe più che felice di aiutarti a trovare l\'animaletto giusto per te!', 57534),
(276873, 'itIT', 'Tanto per cominciare, qui c\'è Flynn.', '', 57534),
(276871, 'itIT', 'Soprattutto a un festival.', '', 57534),
(276868, 'itIT', '', 'È bene ricordare il passato, ma non bisogna viverci ancora dentro.', 57534),
(276867, 'itIT', '', 'Mmh, hai trovato quefta pergamena vicino a dei numeri? Chiffà fe il Cappello Penfante può aiutarti!', 57534),
(276866, 'itIT', '', 'Fecondo me devi cercare i membri della tribù Tortaka. Potrebbero trovarfi nel Rifugio dei Tortaka a Vol\'dun.', 57534),
(276863, 'itIT', 'Lo vedremo.', '', 57534),
(276861, 'itIT', 'Penso al cambiamento, viandante.\n\nSono abituato ai cambiamenti, come indicato dalla mia designazione. Quello a cui non sono abituato è un sistema ordinato, smontato e ridotto in pezzi.\n\nLa nostra città è cambiata molto dall\'ultima volta in cui ho camminato per i suoi distretti. Nuove facce. Nuovi protocolli. È... troppo da assorbire dopo essere rimasti in stasi così a lungo.\n\n<Bargus scuote i suoi baffi con un sospiro prima di sorridere.>\n\nHo sentito molto su quello che il mio popolo ha dovuto sopportare per secoli. Il cambiamento è la risposta più logica, ma non è facile da accettare.\n\nMa ce la farò. Devo solo distrarmi con qualche nuovo progetto.', '', 57534),
(276860, 'itIT', '<Il Terrigeno si ferma e poi fa una pausa di qualche secondo prima di riconoscere la tua presenza con un cenno.>\n\nUn altro nuovo volto! Scuse inviate, viandante. Ero distratto dall\'elaborazione dei miei pensieri.', '', 57534),
(276859, 'itIT', '', 'Percentuale di certezza sconosciuta. Forse è per questo che la Guardiatempesta è in trattative con lui.', 57534),
(276847, 'itIT', 'Isole dei Draghi', 'Isole dei Draghi', 57534),
(276846, 'itIT', 'Shadowlands', 'Terretetre', 57534),
(276845, 'itIT', '', 'Credi che il Custode del Concilio si opporrà al progetto?', 57534),
(276844, 'itIT', 'Ho sentito che hanno intenzione di costruire qualcosa quassù in superficie.', '', 57534),
(276843, 'itIT', '', 'I Campi delle Forge sono più attivi che mai ora che il commercio con gli Arathi è ricominciato.', 57534),
(276842, 'itIT', 'Un cristallo? Curioso. Dev\'essere per questo che gli Arathi sembrano essere interessati.', 'Un cristallo? Curioso. Dev\'essere per questo che gli Arathi sembrano essere interessati.', 57534),
(276841, 'itIT', 'Ho sentito solo alcuni frammenti di conversazione. Qualcosa riguardo... un cristallo su un\'isola?', 'Ho sentito solo alcuni frammenti di conversazione. Qualcosa riguardo... un cristallo su un\'isola?', 57534),
(276840, 'itIT', 'Domanda... Ho sentito dei Goblin che parlavano di una nuova scoperta. Hai idea di cosa abbiano trovato?', 'Domanda... Ho sentito dei Goblin che parlavano di una nuova scoperta. Hai idea di cosa abbiano trovato?', 57534),
(276839, 'itIT', 'Vai. Vedi se riescono a farci arrivare lì più in fretta.', '', 57534),
(276838, 'itIT', '', 'Ho sentito che Sparachiodi ci presterà uno zeppelin. Sicuramente più veloce di una barca, ma ci vorrà del tempo! Secondo te dovrei andare a parlare con gli Svalvolatori?', 57534),
(276837, 'itIT', 'Se l\'hai sentito forte e chiaro da oltre il mare, sono sicuro che non sei stata l\'unica. Faremmo meglio a tornare subito lì se vogliamo quel cristallo.', '', 57534),
(276836, 'itIT', 'Mormora sottovoce.', '', 57534),
(276835, 'itIT', '', 'Ehi, il cielo si stava annuvolando e la mia banda era già coi nervi a fior di pelle. Abbiamo dato un\'occhiata in giro e poi abbiamo levato le tende.', 57534),
(276834, 'itIT', 'Un cristallo? Tipo un cristallo prezioso? In una miniera abbandonata? Santo cielo, Ricky. Perché non ne hai sgraffignato un po\'?', '', 57534),
(276833, 'itIT', '', 'No. Nessuno. Nisba. Non c\'era niente a parte un grosso cristallo.', 57534),
(276832, 'itIT', 'Abbandonato? Non c\'era nessuno dall\'altra parte di questa \"chiamata\"?', '', 57534),
(276831, 'itIT', '', 'Te l\'assicuro, Skaggit. Qualcuno ci ha chiamato da oltre l\'acqua. Ma quando siamo arrivati lì, è stato... inquietante. Era abbandonato.', 57534),
(276823, 'itIT', '', 'Vokgret annuisce in assenso.', 57534),
(276820, 'itIT', '', 'Ah, è per questo che volevi farne parte. Non è un problema parlartene... più tardi. Magari in privato.', 57534),
(276815, 'itIT', '', 'Una preoccupazione logica. Non rilevo alcuna mancanza di rispetto. Ma... sono curiosa del viaggio che la tua gente sta per intraprendere.', 57534),
(276812, 'itIT', '', 'Con tutto il rispetto, gli standard ingegneristici delle nostre navi sono davvero precisi. L\'aiuto di personale esterno rischierebbe di rallentarci.', 57534),
(276811, 'itIT', '', 'Avrete bisogno di operai per il vostro progetto? Molti di noi si offrirebbero subito volontari.', 57534),
(276810, 'itIT', '', 'La Capitana Lancekat sarà felice della rapidità dei nostri progressi. Invieremo in superficie il resto degli strumenti dei nostri ingegneri così che possano iniziare i lavori.', 57534),
(276809, 'itIT', '', 'Ottenere velocemente materiali per il tuo \"progetto di costruzione\" non sarà un problema. Le nostre linee di rifornimenti sono più attive che mai.', 57534),
(276808, 'itIT', '', 'Ho passato tanti secoli come minatrice Legapietra. L\'addestramento da Cavalcatempesta va a un ritmo al quale non sono abituata.\n\nOra, però, ho una nuova direttiva. Lufsela... voglio dire, la Guardiatempesta Lufsela ha creato un nuovo ramo di Cavalcatempesta con i Terrigeni Risvegliati che hanno abbandonato Khaz Algar: le Vedette della Tempesta.\n\nE indovina chi ha dieci dita di pietra ed è la loro nuova leader? Io!\n\nÈ una grande responsabilità, ma ora che Vokmar e Olbarig sono in missione diplomatica, c\'era bisogno di qualcuno che spianasse la strada ai Terrigeni Risvegliati e li aiutasse durante le loro esplorazioni. E quella è la nostra nuova direttiva. Esplorare il mondo al di fuori di Khaz Algar, aprire la strada ai Risvegliati e diventare gli occhi e le orecchie del Concilio.\n\n<Kothilde fa spallucce e sorride.>\n\nMolto meglio che spaccare pietre tutto il giorno.', 57534),
(276807, 'itIT', '', 'Salve, $n. È bello rivederti. Forse ti farà piacere sapere che i Cavalcatempesta continuano a crescere grazie al tuo aiuto.\n\nMan mano che richiamiamo altri corvi nel nostro mondo, le nuove reclute continuano ad accalcarsi a Dhar Oztan e al Trespolo della Tempesta per ricevere il loro addestramento.\n\nC\'è stato anche un aumento di nuovi Risvegliati che si sono uniti alla causa. Di certo non siamo a corto di nuove leve.', 57534),
(276806, 'itIT', '', '$n, hai già dimenticato chi sono? Forse dovresti chiedere agli Oratori delle Macchine di farti una sessione di diagnostica!\n\n<Alba ride.>\n\nSono Alba. Ho supervisionato il tuo protocollo di risveglio e quello di molti altri. Ad alcuni piace vedermi come guida dei Risvegliati, ma in quanto tale...\n\nMi sento più persa che mai. Sembra che tu abbia trovato la tua strada durante i tuoi viaggi. Forse Azeroth vuole che anche io parta all\'avventura per esplorare il mondo e capire chi sono.\n\nForse dovrei chiedere più informazioni alla Guardiatempesta e alle sue nuove Vedette della Tempesta. Ci sono un sacco di voci in città ultimamente che hanno attirato la loro attenzione...', 57534),
(276805, 'itIT', '', 'È grazie al tuo eroismo e quello dei Barbabronzea se posso rispondere a quella domanda. Io sono Alba, la prima Terrigena a emergere dalla Macchina del Risveglio come Terrigena Risvegliata.\n\nOra, in quanto Terrigena senza direttive, ho un\'infinità di scelte davanti a me, anche se non sono mai stata così indecisa. Ho fatto da guida a molti Risvegliati mentre cercavano di scoprire la loro storia, ma non ho avuto tempo di capire quale fosse la mia.\n\nTuttavia, aiutare gli altri Risvegliati è in sé una strada da seguire.\n\nComunque... forse sarebbe l\'ora di smetterla di aiutare gli altri e dedicarmi solo a me stessa... per iniziare un nuovo viaggio.\n\nLa città è piena di storie interessanti ultimamente...', 57534),
(276804, 'itIT', '', 'Io sono Alba, la prima Terrigena a emergere dalla Macchina del Risveglio come Terrigena Risvegliata.\n\nSono una Terrigena senza direttive, senza Editti e senza limiti. Ho fatto da guida a molti Risvegliati mentre cercavano di scoprire il loro nuovo posto nel mondo... \n\nEppure, non ho avuto tempo di capire quale fosse il mio. Come molti Terrigeni, mi sento sopraffatta dall\'ammontare di strade e scelte a mia disposizione.\n\nForse sarebbe l\'ora di smetterla di aiutare gli altri e dedicarmi solo a me stessa... per iniziare un nuovo viaggio.\n\nLa città è piena di storie interessanti ultimamente...', 57534),
(276803, 'itIT', '', 'È bello vederti, $n.\n\nIl tuo ultimo invio di dati all\'Archivio è stato d\'ispirazione a tanti nuovi Risvegliati, e anche per alcuni Svincolati e Oratori delle Macchine. Aspetto con ansia il tuo prossimo aggiornamento!\n\nHo la sensazione che vivremo insieme la nostra prossima avventura!', 57534),
(276802, 'itIT', '', 'È bello vederti, $n. \n\nSono molto curiosa dei tuoi viaggi al di fuori di Khaz Algar. Cos\'hai visto durante il tuo cammino? Chi hai incontrato?\n\nOh, non vedo l\'ora di ascoltare tutte le tue storie. Aspetto con ansia il giorno in cui invierai le tue esperienze all\'Archivio.\n\nNon farmi aspettare troppo!', 57534),
(276801, 'itIT', '', 'Ogni giorno che passa, sempre più Terrigeni si risvegliano nella Volta di Pietra. Molti di loro sono tornati alle loro vecchie designazioni.\n\nQuella strada non fa più per me. Per alcuni, è difficile dimenticare e abbandonare le tradizioni. Non li giudico per questo.\n\nMolti altri, come me, hanno scelto di seguire un altro percorso. Per la prima volta nella storia dei Terrigeni, non abbiamo alcun Editto e l\'intero mondo di Azeroth è a nostra disposizione. Già molti di noi sono partiti verso nuove terre per scoprire gli altri popoli e le altre culture del nostro mondo.\n\nSe non hai ancora incontrato un Terrigeno Risvegliato per Azeroth, ho il sospetto che ne vedrai presto uno.\n\n<Alba ti sorride.>', 57534),
(276799, 'itIT', 'Ti reputi capace di uccidere bestie epiche o sconfiggere incredibili avversari? Sì? Be\', lo vedremo. Solo coloro che riescono ad affrontare il pericolo faccia a faccia sopravvivono in questo mondo.', '', 57534),
(276562, 'itIT', 'Cosa ci fai qui? Vattene prima che chiami le guardie!', 'Cosa ci fai qui? Vattene prima che chiami le guardie!', 57534),
(276561, 'itIT', 'I Ferroscuro prevarranno. Vattene prima che ti uccida con le mie mani.', 'I Ferroscuro prevarranno. Vattene prima che ti uccida con le mie mani.', 57534),
(276525, 'itIT', '<Anche se la Pietra del Tremore sembra essere dormiente, riesci a sentire un debole ronzio di energia vibrare al suo interno.>', '', 57534),
(276471, 'itIT', '', 'Fembra che le carte poffano effere ovunque nell\'area della fefta. Forfe l\'idolo può trovarle!', 57534),
(276400, 'itIT', '', '<Alyza alza gli occhi al cielo.>\n\nChe c\'è, siccome sono una bambina non posso muovermi velocemente?\n\nHo molti luoghi da visitare e Kobyss da uccidere. Non posso restare rinchiusa in un orfanotrofio.', 57534),
(276397, 'itIT', '', 'Mmmmmm... Il profondo nord dev\'effere Nordania, giufto? Zio Benny mi raccontava di un\'ifoletta ancora più a nord del continente! Forfe su quell\'ifola c\'è un \"guardiano\" Tufkarr. E fe è cofì...\n\n<Maru sembra concentrarsi molto.>\n\nIl fole tramonta a oveft...\nVoltandofi verfo oveft e... <Mormora.> La fpalla deftra... è a nord...\n\nQui c\'è proprio da penfarci fu tanto!', 57534),
(276395, 'itIT', 'Il tempo stringe', 'Il tempo stringe.', 57534),
(276393, 'itIT', '', 'Cuocolomeo era un cuoco, no? Forfe uno dei cuochi a Baia del Bottino fa qualcofa fu di lui!', 57534),
(276389, 'itIT', '', 'Fcommetto che puoi trovare alcune delle pagine qui intorno ufando il tuo idolo. E magari i Diavoli di Rena ne hanno altre. Fpeffo si nafcondono nelle Caverne del Tempo.\n\nMa se qualche pagina è stata prefa da altri avventurieri, forfe dovrai recuperarla dai cattivoni in giro per Tanarif, come Calcarovina!', 57534),
(276386, 'itIT', '', 'Fammi vedere...', 57534),
(276363, 'itIT', 'Sì! Andiamo a dare uno sguardo più da vicino.', '', 57534),
(276359, 'itIT', 'Oh, curioso! Chissà se qualche ospite ha perso le sue carte di Lunacupa. Nel caso, potresti dargli una mano?', '', 57534),
(276349, 'itIT', 'Sì?', '', 57534),
(276299, 'itIT', 'Cuocolomeo? No, mi dispiace, non so dove dormiva. \n\nOh! Ma sai chi potrebbe saperlo? Kelsey Yance, alla Vecchia Capitaneria di Porto. Quei due erano ottimi amici, da quel che mi pare di capire.', '', 57534),
(276298, 'itIT', 'Entra, entra. Fai solo attenzione a non sederti troppo vicino alla stufa. Oggi non è ancora esplosa, ma tanto per andare sul sicuro, ti conviene sederti un po\' più in là.', '', 57534),
(276255, 'itIT', 'Vattene!', 'Vattene!', 57534),
(276254, 'itIT', '<Bofonchia qualcosa tra un rutto acido e l\'altro.>', '<Bofonchia qualcosa tra un rutto acido e l\'altro.>', 57534),
(276253, 'itIT', 'Uggghhh.', 'Uggghhh.', 57534),
(276252, 'itIT', 'Tu cosa? Vattene *hic* prima che io...', 'Tu cosa? Vattene *hic* prima che io...', 57534),
(276251, 'itIT', 'Che cavolo vuoi?', 'Che cavolo vuoi?', 57534),
(276241, 'itIT', 'Questa è una splendida proposta. La tua natura premurosa è sempre apprezzata, Dama Gosu.', '', 57534),
(276238, 'itIT', '', 'Potrebbe preparare dell\'ottimo tè, lentamente. E procurare l\'oggetto che ti serve col tempo che ci vuole. Non prima.', 57534),
(276237, 'itIT', 'Di certo una donna con la tua intelligenza e i tuoi mezzi potrebbe...', '', 57534),
(276236, 'itIT', '', 'Irathion. Te l\'ho già detto altre volte. Il tè e gli affari non si fanno di fretta. Sarà pronto quando sarà pronto.', 57534),
(276235, 'itIT', 'E io continuerò a farti complimenti finché continuerai a essere così raggiante. E mentre ci godiamo il tè, hai per caso anche l\'altro oggetto?', '', 57534),
(276234, 'itIT', '', 'Le lusinghe non hanno effetto su di me, Irathion. Eppure, ci provi ogni volta che vieni qui. Vuoi del tè?', 57534),
(276233, 'itIT', 'Dama Gosu. Hai un aspetto meraviglioso oggi.', '', 57534),
(276224, 'itIT', '', 'Capisco. Allora forse dovremmo parlarne un\'altra volta, magari davanti a un buon tè.', 57534),
(276223, 'itIT', '', 'Non volevo mancarti di rispetto, e di certo non voglio intromettermi nei tuoi affari. Irathion può essere... impetuoso. Se riuscissi a farlo desistere da qualsiasi cosa abbia in mente, te ne sarei grata.', 57534),
(276222, 'itIT', '', 'Con tutto il dovuto rispetto, Regina dei Draghi, da quando i miei affari sono anche i tuoi?', 57534),
(276221, 'itIT', '', 'Dama Gosu, vedo che sei in combutta col giovane Irathion.', 57534),
(276220, 'itIT', '', 'Regina Alexstrasza. Spero ti stia godendo la festa.', 57534),
(276214, 'itIT', 'Non è una cattiva idea... Non dovevo sottovalutarti. Non succederà di nuovo.', '', 57534),
(276213, 'itIT', '', 'Nel concilio dell\'Orda, non siamo noi quelli introspettivi, quelli che meditano, comunicano e o che so io. Noi siamo quelli che agiscono, sempre. Quindi oggi, in questa festa, abbiamo deciso di non fare niente.', 57534),
(276212, 'itIT', 'Allora avresti la cortesia di dirmi a cosa dovrebbe servire un incontro sul niente? Sono un uomo impegnato.', '', 57534),
(276211, 'itIT', '', 'Non perdere la calma, Reggente. Non è da te.', 57534),
(276208, 'itIT', 'Niente? Nel senso, niente in programma? Tutto questo è solo una perdita di tempo?', '', 57534),
(276207, 'itIT', '', 'Esattamente quello. Niente.', 57534),
(276206, 'itIT', 'Temo di non capire. Cosa vuoi dire con \"niente\"?', '', 57534),
(276205, 'itIT', '', 'Nulla.', 57534),
(276204, 'itIT', 'Allora... di che cosa volevi parlarmi?', '', 57534),
(276197, 'itIT', '', 'Baci baci.', 57534),
(276196, 'itIT', 'Non ho dubbi sui motivi della vostra espulsione, ma non preoccuparti. Sono sicuro che farete un figurone quando tornerete. Ora devo trovare Lilian.', '', 57534),
(276195, 'itIT', '', 'Sta consegnando i premi alla Mania Modaiola. Se non ci avessero impedito di partecipare, avremmo già vinto.', 57534),
(276194, 'itIT', 'Uhm... già. A proposito, c\'è tuo fratello in giro? Voi due non vi separate mai.', '', 57534),
(276193, 'itIT', '', 'Oh, poverino. Devi sorridere e stringere la mano alle persone più belle di Azeroth. Sarei quasi gelosa, se non fossi già io a essere la persona più bella di Azeroth.', 57534),
(276192, 'itIT', 'Salvo le apparenze, faccio dei giri, soliti affari di stato.', '', 57534),
(276191, 'itIT', '', 'Come sempre, no? E a cosa devo la fortuna di poter posare gli occhi sul tuo splendido viso, Reggente?', 57534),
(276190, 'itIT', 'Thrynviel. Sei bellissima.', '', 57534),
(276189, 'itIT', 'Quindi ho dato l\'oggetto vero a Vorkan. Ha detto che me l\'avrebbe riportato dopo la festa.', '', 57534),
(276184, 'itIT', 'Visto che sei un giocatore d\'azzardo, pensavo sapessi quando NON è il caso di tentare la sorte, Landro.', '', 57534),
(276182, 'itIT', 'Comunque senti, se per caso ti interessa vendere... conosco alcune persone che potrebbero ricompensarti adeguatamente.', '', 57534),
(276181, 'itIT', 'Consideralo già riportato.', '', 57534),
(276180, 'itIT', 'Ascolta, i miei ragazzi avevano bisogno dell\'originale per poter creare una replica credibile. Però... lo stampo continuava a rompersi e non avevamo più tempo.', '', 57534),
(276174, 'itIT', 'Landro Colpolungo. Vorrei parlare con te riguardo il Maglio dei Terrigeni.', '', 57534),
(276173, 'itIT', 'Smettila di farmi perdere tempo!', '', 57534),
(276170, 'itIT', 'Calmati, Vorkan Fronte Tuono. Non sei sospettato di alcun crimine. Ma dobbiamo trovare il Maglio e riportarlo indietro prima che qualcuno si accorga che non è un falso.', '', 57534),
(276169, 'itIT', 'Oh no. Non penserai che lui... non penserai che io...', '', 57534),
(276168, 'itIT', 'Il Maglio dei Terrigeni è scomparso qualche giorno prima dell\'inizio di questa festa.', '', 57534),
(276167, 'itIT', 'Sì!', '', 57534),
(276166, 'itIT', 'E per caso in quella lista c\'era il Maglio dei Terrigeni?', '', 57534),
(276163, 'itIT', 'Ah, c\'era un Goblin davvero gentile che ci ha aiutati con la creazione delle repliche. Gli ho dato una lista e lui si è messo all\'opera con qualcuno, a Meccania presumo, per creare gli oggetti. Ha fatto un vero lavoro di precisione con alcune parti.', '', 57534),
(276162, 'itIT', 'E dove hai trovato queste repliche?', '', 57534),
(276161, 'itIT', 'Ah, ho collezionato un numero di repliche di vari oggetti da tutta Azeroth. Permettono a chi le trova di manifestare le sembianze dei loro proprietari originali. Dicono che l\'imitazione è la miglior forma di adulazione, quindi perché non imitare le persone che rispetti?', '', 57534),
(276160, 'itIT', 'Dimmi cosa ci fai qui alla festa.', '', 57534),
(276155, 'itIT', '', 'Certo!\n\n<Dama Gant indica l\'amaca in basso alla sua sinistra.>\n\nQuella lì era la sua area. Sono abbastanza certa che quel barile sia l\'unico suo effetto personale rimasto.', 57534),
(276154, 'itIT', '', 'Io posso insegnarti a cucinare.\n\nMa tu vuoi imparare?', 57534),
(276153, 'itIT', '', '<Dama Gant ti guarda perplessa.>\n\nQuesti appunti mi sembrano incomprensibili, mi dispiace. \n\nPerò Cuocolomeo non lo conoscevo neanche così bene. Forse uno dei suoi amici riuscirebbe a decifrarli meglio?', 57534),
(276145, 'itIT', 'È tutto l\'opposto, ragazzo.', '', 57534),
(276143, 'itIT', 'Dopo così tanti anni da diamante, non pensavo che ti interessasse ancora il cibo.', '', 57534),
(276142, 'itIT', 'Concordo sul cibo. andiamo. Se mangiamo adesso, forse riusciremo a mangiare anche dopo aver visto tutto il resto degli eventi.', '', 57534),
(276141, 'itIT', 'Gli animali, e il cibo.', '', 57534),
(276140, 'itIT', '', 'Bene. Spero che nessuno si sia fatto male. Allora, cosa vuoi vedere oggi, Dagran?', 57534),
(276137, 'itIT', 'In una tormenta.', '', 57534),
(276136, 'itIT', '', 'Lo ammetto, temevo che la trivella avesse potuto portarvi fuori rotta. A proposito, dov\'è la trivella?', 57534),
(276135, 'itIT', 'Non preoccuparti. Ci ho portato qui sani e salvi.', '', 57534),
(276134, 'itIT', '', 'Dagran. Papà. Ce l\'avete fatta!', 57534),
(276133, 'itIT', 'Mamma!', '', 57534),
(276131, 'itIT', 'Certo che possiamo vedere gli animali, ma prima dobbiamo trovare tua madre e dirle che siamo arrivati tutti interi.', '', 57534),
(276130, 'itIT', 'Sì, ma non così freddo. Ehi! Possiamo fermarci a vedere gli animali? Chissà se hanno un leopardo delle nevi.', '', 57534),
(276129, 'itIT', 'Eh, non ti preoccupare. È solo un fenomeno atmosferico. Sai, Dagran, a volte il deserto può essere veramente freddo.', '', 57534),
(276128, 'itIT', 'Perché non vedo la mamma da nessuna parte. E poi, se questo dovrebbe essere un deserto, perché c\'è ghiaccio dappertutto?', '', 57534),
(276127, 'itIT', 'Certo che sono sicuro, perché lo chiedi...?', '', 57534),
(276126, 'itIT', 'Nonno? Sicuro di aver inserito le coordinate giuste?', '', 57534),
(276125, 'itIT', 'È vero. Due gelati doppi allora.', '', 57534),
(276119, 'itIT', '', 'Cosa? Non sei stato tu a dire che faceva terribilmente caldo?', 57534),
(276118, 'itIT', '', 'A me fammelo doppio.', 57534),
(276117, 'itIT', 'Due gelati, per favore.', '', 57534),
(276116, 'itIT', '', 'Sei stato via troppo a lungo. Ci sarà tempo per vedere tutto.', 57534),
(276115, 'itIT', 'E allora gelato sia.', '', 57534),
(276114, 'itIT', '', 'Ma certo, tutto quello che vuoi.', 57534),
(276113, 'itIT', 'No. Anzi, Cavalca-Mania.', '', 57534),
(276112, 'itIT', '', 'Molto bene, dopo di te, caro.', 57534),
(276111, 'itIT', 'Mania Modaiola!', '', 57534),
(276110, 'itIT', '', 'Da dove iniziamo, amore mio?', 57534),
(276106, 'itIT', 'Immagino tu abbia ragione. Però è dura vedere tutti che si divertono mentre noi dobbiamo stare qui a lavorare.', 'Immagino tu abbia ragione. Però è dura vedere tutti che si divertono mentre noi dobbiamo stare qui a lavorare.', 57534),
(276104, 'itIT', '', 'E c\'è tutto il tempo del mondo per compiere il vostro dovere e poi godervi la festa.', 57534),
(276103, 'itIT', 'Ma è tutto il giorno che siamo qui, Dama Marefiero.', 'Ma è tutto il giorno che siamo qui, Dama Marefiero.', 57534),
(276102, 'itIT', '', 'Fidati dei tuoi insegnanti. Siamo stati tutti apprendisti una volta, sai?', 57534),
(276099, 'itIT', 'Per quanto tempo ancora dobbiamo restare qui? Voglio andare alla festa. Magari riesco a salire su uno di quei palloncini.', 'Per quanto tempo ancora dobbiamo restare qui? Voglio andare alla festa. Magari riesco a salire su uno di quei palloncini.', 57534),
(276097, 'itIT', '', 'Ah, ricordo ancora i miei giorni da apprendista. Forse qualcuna delle mie storie imbarazzanti di quel periodo servirà a tirarli su.', 57534),
(276096, 'itIT', '', 'È tutto sotto controllo, anche se i maghi apprendisti vicino alla pista di ghiaccio apprezzerebbero qualche parola di incoraggiamento.', 57534),
(276095, 'itIT', '', 'Posso rendermi utile in qualche modo? A volte faccio fatica a rilassarmi in situazioni del genere.', 57534),
(276094, 'itIT', '', 'Dama Marefiero. Che bella sorpresa.', 57534),
(276092, 'itIT', '', 'Yllana, complimenti per la festa. È tutto bellissimo.', 57534),
(276089, 'itIT', '', 'Quello mi sembra il nome di un animale.\n\nCerca un posto in cui ci sono tanti animali, e forse ci troverai il tuo amico.', 57534),
(275964, 'itIT', 'Forse è una decisione da prendere meglio in considerazione.', '', 57534),
(275963, 'itIT', '', 'Se ne prendiamo uno, dovrai dargli da mangiare e pulirlo.', 57534),
(275962, 'itIT', 'Non ti sto suggerendo di non farlo.', '', 57534),
(275960, 'itIT', '', 'Stai cercando di dirmi che dovremmo prenderne uno?', 57534),
(275725, 'itIT', '', 'È per questo che Dot ti adora!', 57534),
(275624, 'itIT', 'Certo. Capisco.', '', 57534),
(275623, 'itIT', '', 'Non ora, caro. Non sono in servizio.', 57534),
(275622, 'itIT', 'È solo che... devo parlarle del suo carretto...', '', 57534),
(275621, 'itIT', '', 'Shhh. Sto cercando di godermi lo spettacolo.', 57534),
(275620, 'itIT', 'Uh... Avrebbe un momento, mercante di frutta.', '', 57534),
(275619, 'itIT', '', 'In un certo senso... Oh, ci vorrà tempo per spiegare tutto.', 57534),
(275618, 'itIT', 'Siete degli storici, no?', '', 57534),
(275617, 'itIT', '', 'Non credo tu abbia capito appieno il nostro ruolo...', 57534),
(275616, 'itIT', 'Sto solo dicendo, non è che sia granché come sfida quando puoi direttamente vedere la storia.', '', 57534),
(275615, 'itIT', '', 'Come, prego?', 57534),
(275614, 'itIT', 'Eh sì, non sembra AFFATTO il nome di un giocattolo.', '', 57534),
(275613, 'itIT', 'Aspetta di vedere il mio OOX-Bombardiere/MG.', '', 57534),
(275611, 'itIT', 'Certo. Le tue galline meccaniche.', '', 57534),
(275610, 'itIT', 'Sono stati creati per la battaglia!', '', 57534),
(275609, 'itIT', 'Ma ceeerto.', '', 57534),
(275608, 'itIT', 'Questi non sono GIOCATTOLI, Belgioioso.', '', 57534),
(275606, 'itIT', 'Oh, ti stupirebbe.', '', 57534),
(275605, 'itIT', 'Immagino sia particolarmente vero per i Reietti.', '', 57534),
(275603, 'itIT', 'Serve sempre nel nostro lavoro.', '', 57534),
(275602, 'itIT', 'Be\', non mi aspettavo che mi dessi effettivamente una mano.', '', 57534);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(275600, 'itIT', 'Sì?', '', 57534),
(275599, 'itIT', 'Quando hai deciso di darmi una mano...', '', 57534),
(275597, 'itIT', 'Presto saremo di nuovo in viaggio, vedrai.', '', 57534),
(275596, 'itIT', 'Umpf. Non mi è mai piaciuto stare nello stesso posto troppo a lungo.', '', 57534),
(275594, 'itIT', 'C\'è ancora un sacco di gente che vuole un autografo, papà.', '', 57534),
(275592, 'itIT', 'Dobbiamo restare ancora a lungo?', '', 57534),
(275584, 'itIT', '', 'Molto bene.', 57534),
(275583, 'itIT', 'No.', '', 57534),
(275582, 'itIT', '', 'Non hai pensato alla Luce, Lillehoff?', 57534),
(275581, 'itIT', '', 'Eccellente. Tra l\'altro, sono la persona migliore con cui avere piacevoli conversazioni qui.', 57534),
(275580, 'itIT', '', 'Finché ci sei tu qui con me, almeno.', 57534),
(275579, 'itIT', '', 'Io... No, ero sincera. Mi sto divertendo.', 57534),
(275578, 'itIT', '', 'Se non ti stai divertendo, non devi per forza fare finta per farmi piacere.', 57534),
(275577, 'itIT', '', 'Grazie infinite per avermi portata qui, cara.', 57534),
(275576, 'itIT', '', 'Oh, uh. Certo. Mi impegnerò a restarti accanto.', 57534),
(275575, 'itIT', 'Te lo scordi, ma è già qualcosa!', '', 57534),
(275574, 'itIT', 'Va bene. Mangerò un po\' di pollo, basta che mi lasci stare.', '', 57534),
(275573, 'itIT', 'Hai paura di divertirti troppo?', '', 57534),
(275572, 'itIT', 'Mmh. È solo abitudine.', '', 57534),
(275571, 'itIT', 'Lo stai facendo di nuovo. Osservare. Valutare la situazione.', '', 57534),
(275570, 'itIT', '', 'Vero. E alcuni di noi si danno da fare più di altri.', 57534),
(275569, 'itIT', '', 'La pace non è semplicemente l\'assenza di guerra. È qualcosa per il quale dobbiamo impegnarci tutti.', 57534),
(275568, 'itIT', '', 'Mi ha fatto piacere vedere che così tante persone abbiano scelto di partecipare.', 57534),
(275567, 'itIT', '', 'Non è stato meraviglioso il matrimonio di Lor\'themar e Thalyssra?', 57534),
(275321, 'itIT', 'Davvero? Chissà dove si saranno nascosti qui...', 'Davvero? Chissà dove si saranno nascosti qui...', 57534),
(275320, 'itIT', 'Sì. Ho sentito che l\'Arcimago Alvareaux li ha beccati nel suo ripostiglio.', 'Sì. Ho sentito che l\'Arcimago Alvareaux li ha beccati nel suo ripostiglio.', 57534),
(275317, 'itIT', 'Quei due? Allora di sicuro ci toccherà fare gli straordinari.', 'Quei due? Allora di sicuro ci toccherà fare gli straordinari.', 57534),
(275314, 'itIT', 'Un\'altra ora... Sempre se Feleene e Gerald non fanno ritardo, come al solito.', 'Un\'altra ora... Sempre se Feleene e Gerald non fanno ritardo, come al solito.', 57534),
(275312, 'itIT', 'Per quanto tempo dobbiamo andare avanti?', 'Per quanto tempo dobbiamo andare avanti?', 57534),
(275311, 'itIT', 'Eheh. Non ti piace beccarti una scottatura solare mentre ti si congelano le dita?', 'Eheh. Non ti piace beccarti una scottatura solare mentre ti si congelano le dita?', 57534),
(275310, 'itIT', 'Già... Be\', non so se ne sia valsa la pena.', 'Già... Be\', non so se ne sia valsa la pena.', 57534),
(275309, 'itIT', 'L\'Istruttrice Jenden ha detto che ci avrebbe dato un credito in più in studi elementali.', 'L\'Istruttrice Jenden ha detto che ci avrebbe dato un credito in più in studi elementali.', 57534),
(275308, 'itIT', 'Perché ci siamo lasciati coinvolgere?', 'Perché ci siamo lasciati coinvolgere?', 57534),
(275307, 'itIT', 'Oh, sì. Una tempesta in arrivo!', 'Oh, sì. Una tempesta in arrivo!', 57534),
(275306, 'itIT', 'Ehi, non è il tuo turno?', 'Ehi, non è il tuo turno?', 57534),
(275124, 'itIT', '', '<Un rossore viola scuro le colora le guance e le orecchie.>\n\nBe\', Stellagosa ha sicuramente buon gusto, questo non si può negare. E chi sono io per negarle la mia incantevole compagnia?\n\nA proposito, non voglio farla aspettare troppo. Dopotutto, mi ha invitata qui per farle compagnia. Se vuoi scusarmi.', 57534),
(275117, 'itIT', '', 'Be\', non volevo certo mandare via la mia guida turistica personale. Anche se i suoi consigli conducono quasi sempre a un piacevole incontro, come questo.\n\nCon lei al mio fianco, sono sempre in buona compagnia. E questo basta per sopportare qualunque avversità.', 57534),
(275114, 'itIT', '', 'Ovviamente, ho supportato la Prima Arcanista Thalyssra fino al limite delle mie capacità. Non ci si annoiava mai. Anche gli incontri diplomatici spesso sprofondano nel caos.\n\nComunque, mi sono anche concessa del tempo per esplorare il mondo al di fuori di Suramar. Mi affascinano molto le culture contadine.', 57534),
(275067, 'itIT', '', 'Che festa... pittoresca. Sembra che la gente comune da tutta Azeroth si sia sentita in condizioni di partecipare.\n\nLo ammetto, ancora non riesco facilmente a immedesimarmi nel clima festivo. La nostra ribellione contro la Gran Magistra Elisande è stata lunga e brutale. Anche ora che i Nobili Oscuri sono liberi, è difficile abituarsi ai tempi di pace.', 57534),
(275018, 'itIT', 'E cosa resterebbe da governare? Tutto questo non ha senso!', 'E cosa resterebbe da governare? Tutto questo non ha senso!', 57534),
(275017, 'itIT', 'È l\'ora della fine, Imperatore! Metti fine a questo ignobile mondo!', 'È l\'ora della fine, Imperatore! Metti fine a questo ignobile mondo!', 57534),
(275011, 'itIT', 'Anche se immagino potremmo unirci alla festa, serve sempre del personale medico durante eventi come questo. Anche se, per fortuna, finora ci siamo occupati solo di ginocchia sbucciate.', '', 57534),
(275010, 'itIT', 'Salve, $n. Non dimenticare di prenderti cura di te mentre ti godi la festa. Ho avuto una lista infinita di pazienti con leggeri casi di insolazione e disidratazione.', '', 57534),
(274887, 'itIT', '', 'Feguaci o combattenti. Mmmmmh... Forfe ha qualcofa a che fare con le mafcotte? Oh, quefta ultima parte fembra complicata... Uff!', 57534),
(274886, 'itIT', '', 'Libera la tua mente... mmmh. Oh! Fcommetto che è un indizio che vuole che riazzeriamo il Cappello Penfante per afficurarci che le coordinate combacino!', 57534),
(274885, 'itIT', '', 'Vediamo. Fcommetto che \"inizia da me,\" fignifica che dobbiamo riazzerare il Cappello Penfante dallo Zio Benny.', 57534),
(274866, 'itIT', 'Che genere di affari hai con la Fratellanza del Torio?', '', 57534),
(274865, 'itIT', 'Pensi che abbia qualche possibilità con la Signora? Forse se bevessi un altro po\'...', '', 57534),
(274798, 'itIT', 'Immagino che essere come Alamorte e distruggere tutto non sia così emozionante come credevo.', '', 57534),
(274796, 'itIT', 'Un\'inondazione è emozionante e non causerà tutta la distruzione di un incendio!', '', 57534),
(274795, 'itIT', 'È tutto sommerso e fantastico!', '', 57534),
(274794, 'itIT', 'Ecco, vedi? Non è spettacolare?', '', 57534),
(274793, 'itIT', 'Ugh, ok, quindi niente seguaci e niente distruzione vulcanica... Almeno l\'acqua? Un\'inondazione è emozionante e non causerà tutta la distruzione di un incendio!', '', 57534),
(274791, 'itIT', 'Stanno spargendo distruzione per ordine di Alamorte?', '', 57534),
(274790, 'itIT', 'Aspetta, questi sono cultisti?', '', 57534),
(274789, 'itIT', 'Voglio dei seguaci che facciano il mio volere! Come un culto!', '', 57534),
(274788, 'itIT', 'Va bene, ho capito. Forse riesco a convincere i miei seguaci a fare il mio volere. Ok, non devono per forza distruggere tutto, basta che facciano quello che gli dico! Come dei cultisti!', '', 57534),
(274786, 'itIT', 'Non è così male, vero? A me piace!', '', 57534),
(274785, 'itIT', 'Wow, questa cicatrice è ENORME!', '', 57534),
(274784, 'itIT', 'Voglio lasciare una cicatrice nel mondo! Le cicatrici hanno un certo stile, giusto?', '', 57534),
(274783, 'itIT', 'Ok, però voglio lasciare una cicatrice nel mondo! Le cicatrici hanno un certo stile, giusto?', '', 57534),
(274781, 'itIT', 'Perché è così buio e vuoto? Pensavo che la situazione fosse più emozionante.', '', 57534),
(274780, 'itIT', 'Bruciaaate!', '', 57534),
(274779, 'itIT', 'Sì! Voglio più FUOCO!', '', 57534),
(274696, 'itIT', '', 'Ehi bellezza, posso prenderti qualcosa da bere?', 57534),
(274655, 'itIT', '', 'Ci fono un facco di pofti che fono ftati diftrutti dalle inondazioni. Mi viene in mente una GRANDE area inondata.\n\nCome fi chiamava? Vash qualcofa... e quel posto Falato...', 57534),
(274654, 'itIT', '', 'Ci fono un facco di cultifti cattivi nella Breccia del Crepufcolo. Forfe fe Fuocovoto li vede, cambierà idea!', 57534),
(274652, 'itIT', '', 'Una cicatrice nel mondo? Nelle Maleterre c\'è qualcofa del genere.', 57534),
(274649, 'itIT', 'Buona giornata a te!\n\nChi l\'avrebbe mai detto che mi sarei ritrovato a lavorare in un deserto. Ma dopo gli ultimi eventi, mi sta bene qualsiasi posto in cui riesca a tenere ben saldo a terra il mio lavoro!\n\nSpero che ti stia piacendo la visita alla nostra festa e al nostro piccolo angolo di mondo. Speriamo che potrai soddisfare tutti i tuoi bisogni di trasmogrificazione. Fammi sapere se ti servono fili o forniture per sistemare il tuo equipaggiamento!', '', 57534),
(274631, 'itIT', 'Treni! I treni sono fantastici perché ci sto lavorando io e non devi preoccuparti di niente.', '', 57534),
(274629, 'itIT', 'Un po\', forse.', '', 57534),
(274569, 'itIT', '', 'Fuocovuoto fembra un drago molto cattivo. Vuole andare a vedere la diftruzione nei Regni Orientali?\n\nForfe vuole dire la zona delle Fteppe Ardenti.', 57534),
(274537, 'itIT', 'Andiamo nei Regni Orientali. Voglio vedere la distruzione vulcanica!', '', 57534),
(274536, 'itIT', 'Diventerò più forte di tutti gli altri Draghi!', '', 57534),
(274484, 'itIT', 'Già, proprio Cuocolomeo.', '', 57534),
(274482, 'itIT', 'Be\', una notte, molte stagioni fa, noialtri eravamo immersi fino al collo in tortine e rum. Il vecchio Cuocolo... ehm... Rufinwag cominciò a lamentarsi di come mangiavamo le tortine.\n\nA quanto pare erano una specie di prelibatezza e c\'era un modo tradizionale di mangiare quei dannati affari.\n\nAll\'inizio l\'abbiamo presa tutti a ridere, ma le sue lagne continue erano una vera seccatura.\n\nFinché qualcuno dal retro della stanza urlò, \"Grazie mille per le preziose informazioni, Cuocolomeo,\" e tutti si piegarono dal ridere. Anche il caro, vecchio Rufinwag.\n\nDa quel giorno non si è più scrollato di dosso il suo nuovo nome...', '', 57534),
(274480, 'itIT', 'Oh, sai quanto si è ironici, tra amici. Prima c\'era un Umano che salpava con noi. Il suo nome era Bartolomeo, o Bart per gli amici. Lo chiamavamo tutti Barty e lui lo ODIAVA.\n\nBarty ci informava sempre sulle regole locali prima che approdassimo da qualche parte, e certe volte diventava davvero puntiglioso nel farci notare le cose che sbagliavamo. Quelle erano le uniche volte in cui usavamo il suo vero nome. \"Grazie mille per le preziose informazioni, Bartolomeo,\" gli dicevamo.\n\nTutti ridevano e il vecchio Barty diventava rosso come un rubino!', '', 57534),
(274478, 'itIT', 'Non è il suo nome di nascita, su quello hai ragione. La sua cara mamma lo chiamava Rufinwag.', '', 57534),
(274475, 'itIT', 'Proprio così. Ed è stata l\'unica volta che ci hanno inviati così all\'improvviso che non abbiamo neanche potuto finire di mangiare. \n\nQuindi capisci il problema. Non abbiamo lo stomaco per diventare gente di terra, ma non torneremo su quella barca prima di aver assaggiato quel banchetto portafortuna.', '', 57534),
(274472, 'itIT', 'Come corsari, non ce la caviamo malaccio, ma a quanto pare hanno messo tutti i superstiziosi nello stesso equipaggio.\n\nÈ una vita ormai che Cuocolomeo ci prepara quel banchetto portafortuna prima di ogni viaggio, e abbiamo sempre avuto molta più fortuna di altri sventurati.', '', 57534),
(274469, 'itIT', 'Non posso dire di saperlo. Ma credo che tutto il personale delle cucine dorma insieme, quindi non dovrebbe poi essere così difficile.', '', 57534),
(274457, 'itIT', 'Sì! Non sono proprio sicuro di che cosa si tratti, però.\n\nUna volta gliel\'ho chiesto, ma si è limitato a ridacchiare e a dirmi: \"Nel profondo nord, comincia a pensare dove il Guardiano Solitario resta. Poi segui le dodici quando tramonta il sole, e le otto oltre la sua spalla sinistra.\"\n\n<Nasodossa scuote le spalle.>\n\nNon so che cosa significhi, ma temo sia tutto quello che posso dirti.', '', 57534),
(274455, 'itIT', 'Sembra che ormai ti manchi solo l\'ingrediente segreto.', '', 57534),
(274454, 'itIT', 'Ahoy.', '', 57534),
(274371, 'itIT', '<Kelsey sfoglia gli appunti.>\n\nMmmh. No, non c\'è nulla di familiare. Cuocolomeo era un tipo strano, questo è poco ma sicuro.\n\nSai chi ti potrebbe aiutare? Rosa la Losca. Quei due non facevano altro che parlare di libri, parole e lingue. Era il loro campo. Se c\'è qualcuno qui che può aiutarti, scommetto che è lei.', '', 57534),
(274319, 'itIT', 'Sì, noi delle cucine dormiamo insieme. Dama Gant ci ha preso sotto la sua ala per evitare che ci cacciassimo nei guai.', '', 57534),
(274313, 'itIT', 'Saluti e ossequi a te! Come può aiutarti il vecchio Kelsey quest\'oggi?', '', 57534),
(274311, 'itIT', 'Non ne ho la più pallida idea. I cuochi se ne stanno alla Vecchia Capitaneria di Porto. La segnerei sulla tua mappa ma, be\', non è lavoro mio.', '', 57534),
(274310, 'itIT', 'Tu, smamma.', '', 57534),
(274309, 'itIT', '', 'Cuocolomeo lo conoscevo. A te non t\'ho mai visto. Mi spiace, bellezza, perché non chiedi informazioni al porto, invece? Sicuramente qualcuno lì saprà darti una mano.', 57534),
(274222, 'itIT', '', 'Mmmh. Le ha chiamate aragofte delle colline, che fi traduce in aftice delle alture.\n\nFcommetto che parlava degli Aftici Furibondi! Ma dove fi trovano?', 57534),
(274220, 'itIT', '', 'Mmmh. Li ha chiamati urticanti di Sholazar, che fi traduce in ortiche di Sholazar.\n\nForfe vuole dire i Ragnoli che fi trovano le Bacino di Sholazar.', 57534),
(274219, 'itIT', '', 'Mmmh. Lo ha chiamato enorme fiore di acqua falmaftra, che fi traduce in pefce della crefcita gigante.\n\nForfe fi tratta del Pefce Mutante. È uno degli ingredienti per fare gli Elifir della Crefcita Gigante, credo.', 57534),
(274218, 'itIT', '', 'Mmmh. Lo ha chiamato gatto acquatico del refpiro dei morti, che fi traduce in Pefcegatto di Ventomorto.\n\nFecondo me parla del Pefcegatto Pinnaroffa. Fi trovano dappertutto nel Valico Ventomorto.', 57534),
(274217, 'itIT', '', 'Mmmh. Lo ha chiamato graffo di Koatini, che fi traduce in olio di Koatini.\n\nFecondo me fi tratta di Olio di Pesce Aromatico! Volendo lo puoi eftrarre anche tu, ma Koatini è l\'unico che conofco che lo vende.', 57534),
(274216, 'itIT', '', 'Mmmh. Li chiamava baccelli ghiacciati cercafuoco, che fi traduce in facche di peperoncini foffio di Fuoco.\n\nForfe vuole dire le Piccole Facche di Fuoco, quelle che fi ufano per fare il Chili alla Ruttafuoco.', 57534),
(274215, 'itIT', '', 'Mmh. Le chiamava bacche fuccose, che fi traduce in bacche di rum alla banana.\n\nFembra parli proprio delle Ftregobacche! Fi ufano per fare il Rum alla Banana, da come ho fentito.', 57534),
(274214, 'itIT', '', 'Mmh. La chiamava foglia ombrofa, nome in codice cardo carogna.\n\nFecondo me parla proprio degli Fboccialefto. I Ladri li ufano fempre per fare il Tè di Fboccialefto!', 57534),
(274203, 'itIT', '', 'Uuuh! Ricette fegrete e liste di ingredienti cifrate? Per quale ti ferve aiuto?', 57534),
(273998, 'itIT', 'Mh? Sì, che c\'è?', '', 57534),
(273176, 'itIT', 'Sono polli meccanici. Te l\'ho già detto.', '', 57534),
(273175, 'itIT', '', 'Ma è a quello che servono i polli! Che dovrei farmene dei tuoi?', 57534),
(273174, 'itIT', 'Alcuni sì, ma non puoi mangiare neanche quelle.', '', 57534),
(273172, 'itIT', '', 'E non fanno uova...', 57534),
(273170, 'itIT', 'Certo che no!', '', 57534),
(273169, 'itIT', '', 'Ma non si possono mangiare...', 57534),
(273152, 'itIT', 'È per questo che ti hanno dedicato uno spettacolo, sai?', '', 57534),
(273151, 'itIT', 'La Spada d\'Ebano dovrebbe pretendere di più da noi.', '', 57534),
(273150, 'itIT', 'Abbiamo sconfitto il Re dei Lich e i poteri che l\'hanno creato. Saremo pronti per qualsiasi cosa ci aspetti.', '', 57534),
(273149, 'itIT', 'Potremmo passare il tempo a prepararci o a pianificare.', '', 57534),
(273148, 'itIT', 'Non hai neanche tempo per fermarti a riflettere o per rilassarti? Anche la morte si merita una vacanza.', '', 57534),
(273147, 'itIT', 'Non capisco il senso di tutto ciò.', '', 57534),
(273014, 'itIT', '', 'Secondo me abbiamo quasi finito, qui! Tu che ne pensi?', 57534),
(272821, 'itIT', '', 'Be\', dovresti passarci più tempo, invece. Sei un tipo interessante e mi fa piacere averti incontrato.', 57534),
(272818, 'itIT', 'Non passo molto tempo tra la gente, dopo quello che mi è successo.', '', 57534),
(272812, 'itIT', 'Chello mi sembra il nome di n\'animale.\n\nCerca nu posto dove ci stanno tanti animali, e forse ci troverai l\'amico tuo.', '', 57534),
(272810, 'itIT', '', 'Non preoccuparti, ti stavo solo prendendo in giro.', 57534),
(272808, 'itIT', 'Cosa? Oh, chiedo scusa... ti ho bloccato la visuale?', '', 57534),
(272804, 'itIT', '', 'Ci vedi bene da lì, ragazzone?', 57534),
(272784, 'itIT', 'Ah, \'sto povero scoiattolo è stato inseguito da na creatura grossa. Mai vista na bestia così, cumpà.\n\nForse forse puoi trovare qualcosa nell\'area in cui hai trovato il giocattolo?', '', 57534),
(272781, 'itIT', 'Chesto è il mio posto preferito per nu poco di pace e tranquillità.', '', 57534),
(272776, 'itIT', '', 'Ci fono un facco di animali groffi a Dazar\'alor! Dai un\'occhiata in giro e vedi fe riefci a trovare degli indizi ful colpevole.\n\nPotrefti anche vedere fe il tizio degli fcoiattoli ne fa qualcofa.', 57534),
(272775, 'itIT', '', 'Felice di rivederti, $C.\n\nGli Artigli Oscuri sono in giro per i nostri asili per cercare i loro simili.\n\nIl mondo in cui ti trovi è molto diverso da quello che c\'era quando dormivamo. Gli Artigli Oscuri ora fanno parte dell\'Orda, una delle due fazioni principali di Azeroth.\n\nLascerò che tu ti faccia le tue opinioni, ma penso che ti piaceranno.', 57534),
(272773, 'itIT', '', 'Un cagnolino? Oohh, avrebbe fenfo fecondo gli indizi che hai trovato!\n\nNon ci fono un facco di animali grandi a Dazar\'alor? E anche Nocciolino vive lì! Credo che il noftro criminale a quattro zampe potrebbe effere lì!', 57534),
(272772, 'itIT', '', 'Hmmm... non credo che il tizio degli fcoiattoli ruberebbe i giocattoli al fuo animaletto.\n\nChi altro potrebbe effere ftato?', 57534),
(272766, 'itIT', 'Felice di rivederti, $C.\n\nI Custodi d\'Ossidiana sono in giro per i nostri asili per cercare i loro simili.\n\nIl mondo in cui ti trovi è molto diverso da quello che c\'era quando dormivamo. I Custodi d\'Ossidiana ora fanno parte dell\'Alleanza, una delle due fazioni principali di Azeroth.\n\nLascerò che tu ti faccia le tue opinioni, ma penso che ti piaceranno.', '', 57534),
(272760, 'itIT', 'T\'paartos portava speranza, e la Luce. Dovresti cavalcare con T\'paartos!', '', 57534),
(272759, 'itIT', 'Portavamo solo il terrore e la morte.', '', 57534),
(272755, 'itIT', 'T\'paartos urlava, \"T\'PAARTOS!\"', '', 57534),
(272754, 'itIT', 'Colpivamo dalle ombre, con gli zoccoli che rimbombavano come tuoni. E poi, sparivamo di nuovo nel buio...', '', 57534),
(272753, 'itIT', 'Anche T\'paartos cavalcava gloriosamente in battaglia.', '', 57534),
(272752, 'itIT', 'Questo mi ricorda i vecchi tempi nella Compagnia Bravo. Cavalcavamo per giorni, consapevoli che la fine avrebbe potuto nascondersi dietro ogni angolo.', '', 57534),
(272721, 'itIT', '', 'Hai trovato il giocattolo di Nocciolino! Dovrefti riportarglielo.\n\nLo troverai in una ftanza insieme a tanti altri animaletti!', 57534),
(272720, 'itIT', '', 'Evviva, hai trovato Nocciolino! Fe fta perdendo un facco di pelo, forfe ti bafterà feguire i peli per terra!', 57534),
(272700, 'itIT', '<Nocciolino è spaventato, ma accetta il tuo gesto. Noti che sta perdendo grossi batuffoli di pelo.>', '', 57534),
(272698, 'itIT', '<Nocciolino sembra sconvolto e irritato.>', '', 57534),
(272697, 'itIT', '<Nocciolino annusa il giocattolo e poi lo rifiuta.>', '', 57534),
(272695, 'itIT', '<Nocciolino guarda la tua mano con trepidazione.>', '', 57534),
(272685, 'itIT', '', 'Mi fa che Yennu ha detto di cercare Nocciolino a Dazar\'alor!\n\nFi trova in una ftanza infieme ad altri animaletti.', 57534),
(272661, 'itIT', 'Wow! Hai trovato la mia palla! Grazie!\n\nForse ora dovresti andare a cercare Nocciolino. Gli hanno rubato i suoi giocattoli a Dazar\'alor.', '', 57534),
(272582, 'itIT', '', 'Oh, non ci ho mai pensato. È davvero un tipetto!\n\nSarebbe fantastico se le mie... ehm, se le parole di Hemet Nesingwary la ispirassero a diventare una cacciatrice rinomata come lui.', 57534),
(272580, 'itIT', '', 'Oh, no. Che ha combinato stavolta?', 57534),
(272578, 'itIT', '', 'Proprio così! La conosci? È fantastica! Ha viaggiato per tutto il mondo con Hemet Nesingwary in persona. Si è anche addestrata insieme a lui.\n\nVoglio diventare proprio come Hemet Nesingwary un giorno! Ho letto anche tutti i suoi libri. Le Verdi Colline di Rovotorto è il mio preferito!', 57534),
(272576, 'itIT', '', 'Non vedo l\'ora di diventare una cacciatrice famosa!', 57534),
(272568, 'itIT', 'Riparti all\'avventura? Buona fortuna! Dopo che avremo risvegliato tutti i Dracthyr, ho intenzione di partire anch\'io.', 'Riparti all\'avventura? Buona fortuna! Dopo che avremo risvegliato tutti i Dracthyr, ho intenzione di partire anch\'io.', 57534),
(272493, 'itIT', '', 'Oh, non preoccuparti! Qualcuno l\'ha trovato e me l\'ha riportato. È un bene che io ci abbia scritto il mio nome! Sono solo felice che mia sorella Addie non fosse presente. Non l\'avrebbe più finita se avesse scoperto che avevo perso il suo regalo.\n\nVuoi ancora aiutarmi? In tal caso, puoi riprenderlo!', 57534),
(271846, 'itIT', '', 'Non mi sorprende, sono solo un po\' delusa.', 57534),
(271841, 'itIT', '', 'Non ci crederai, ma in genere non cantiamo per i muli.', 57534),
(271840, 'itIT', '', 'Se non sai il suo nome, come fai a scrivere canzoni per lui?', 57534),
(271838, 'itIT', '', 'Non ce l\'ha un nome, tanto non mi ascolta mai.', 57534),
(271837, 'itIT', '', 'Che bel mulo! Come si chiama?', 57534),
(271812, 'itIT', '', 'Ohh, quefta piuma è ficuramente arrivata da qualche pofto gelido.\n\nE quell\'odore... Quale pofto freddo ha la combinazione di tutti quefti odori? Forse un pofto dove ci fono delle navi?', 57534),
(271811, 'itIT', '', 'Fembra che quella piuma fia ftata ftrappata di propofito...\n\nForfe il numero di piccole piume rimafte ti può effere d\'aiuto? Guarda le piume mooolto attentamente. Fecondo me il Cappello Penfante ti può aiutare!\n\nPenfo che il numero a finiftra fia negativo e quello a deftra pofitivo. Afficurati di iniziare dove hai trovato l\'ultimo indizio! C\'erano un facco di pergamene lì vicino, giufto?', 57534),
(271807, 'itIT', '', 'Bleah, cof\'è che fta gocciolando da quella piuma?\n\nÈ come fe foffe ftata in battaglia. Mmm... Non c\'è ftata una grande battaglia in una città a Draenor?', 57534),
(271800, 'itIT', '', 'Arakkoa? Ho fentito che volavano per le Guglie di Arakk.\n\nProva a cercarli nei pofti più alti con il tuo idolo!', 57534),
(271799, 'itIT', 'L\'indizio è questo: il vento sussurra segreti, lontano da terra. Cerca le piume dove un tempo volavano gli Arakkoa.', '', 57534),
(271765, 'itIT', '|TInterface\\ICONS\\INV_10_Inscription_DarkmoonCards_Earth_8.BLP:50|t L\'Otto di Terra. \n\nRappresenta un artista della pietra al lavoro, che espone i propri lavori sotto forma di trofei.\n\nPresto dovrai svolgere un incarico che risulterà impegnativo, ma che ti permetterà anche di sfoggiare le tue abilità. Non aver paura di chiedere aiuto, ma assicurati di impegnarti al massimo.', '', 57534),
(271764, 'itIT', '|TInterface\\ICONS\\INV_10_Inscription_DarkmoonCards_Earth_7.BLP:50|t Il Sette di Terra. \n\nRappresenta un adolescente, appoggiato al suo bastone, che guarda intensamente sette dischi di pietra attaccati a un ciuffo di vegetazione alla sua destra. Si potrebbe dire che si tratti di tesori, e che il cuore del giovane si trovi lì con loro.\n\nQualcosa a cui hai dedicato molto tempo o risorse sta per dare i suoi frutti. Se ti stavi chiedendo se quell\'investimento valeva la pena, tutto questo indica di sì.', '', 57534),
(271763, 'itIT', '|TInterface\\ICONS\\INV_10_Inscription_DarkmoonCards_Earth_6.BLP:50|t Il Sei di Terra. \n\nRappresenta una persona vestita da mercante che pesa il denaro su una bilancia e poi lo distribuisce ai bisognosi e agli afflitti. È una testimonianza del suo successo nella vita e della sua bontà d\'animo.\n\nSta per arrivare un momento di grande solidarietà. Ma sarai la persona che gode di grande prosperità e che aiuta un bisognoso, o quella in difficoltà che sta per godere della bontà altrui? Difficile dirlo.', '', 57534),
(271762, 'itIT', '|TInterface\\ICONS\\INV_10_Inscription_DarkmoonCards_Earth_5.BLP:50|t Il Cinque di Terra. \n\nRappresenta due mendicanti in una tempesta di polvere mentre passano davanti a una finestra illuminata da cinque dischi di pietra luminosi.\n\nOh, cielo. Lo spettro dell\'insicurezza e delle avversità incombe all\'orizzonte. Stai per perdere qualcosa di grande importanza. Mi dispiace, $uamico:amica;.', '', 57534),
(271761, 'itIT', '|TInterface\\ICONS\\INV_10_Inscription_DarkmoonCards_Earth_4.BLP:50|t Il Quattro di Terra. \n\nRappresenta una figura incoronata con un disco di pietra sopra la testa, che stringe un altro disco con le braccia e tiene altri due dischi sotto i piedi. Una persona aggrappata a ciò che ha.\n\nÈ in arrivo un periodo in cui dovrai tenerti stretti i tuoi averi, ma non lasciare che questo diventi la tua nuova normalità. Essere frugali va benissimo, ma rischi di diventare una persona avara, se non farai attenzione.', '', 57534),
(271758, 'itIT', '|TInterface\\ICONS\\INV_10_Inscription_DarkmoonCards_Earth_3.BLP:50|t Il Tre di Terra.\n\nRappresenta uno scultore al lavoro in un monastero. L\'apprendista ha ricevuto la sua ricompensa e ora lavora con impegno.\n\nQuesta carta è sia un incoraggiamento che un avvertimento, $uamico mio:amica mia;. Qualcosa che hai già iniziato è stata fatta bene e sei sulla giusta strada. Ma prendi nota di chi ti circonda e assicurati di sfruttarne le capacità, altrimenti il tuo successo ristagnerà.', '', 57534),
(271757, 'itIT', '|TInterface\\ICONS\\INV_10_Inscription_DarkmoonCards_Earth_2.BLP:50|t Il Due di Terra. \n\nRappresenta un bambino nell\'atto di ballare con un disco di pietra in ciascuna mano. I dischi sono uniti da una corda infinita, che disegna il numero 8 ruotato.\n\nSta per verificarsi una situazione in cui avrai la tentazione di tenere attive contemporaneamente molte priorità in conflitto. Dovresti approfittare ora per valutare le risorse a tua disposizione, poiché potresti non riuscire a sostenerle ancora a lungo.', '', 57534),
(271738, 'itIT', '|TInterface\\ICONS\\INV_10_Inscription_DarkmoonCards_Earth_1.BLP:50|t L\'Asso di Terra. \n\nRappresenta una mano che emerge da un terreno roccioso in una nuvola di polvere mentre sostiene un disco di pietra.\n\nQualcosa di nuovo è pronto a entrare nella tua vita con grande abbondanza e prosperità!', '', 57534),
(271737, 'itIT', '|TInterface\\ICONS\\INV_10_Inscription_DarkmoonCards_Frost_8.BLP:50|t L\'Otto di Gelo. \n\nRappresenta una figura avvilita che vaga in giro, lasciando dietro di sé le coppe contenenti la sua fortuna, la sue imprese, il suo impegno o le sue preoccupazioni precedenti, che vengono tutte inghiottite dal ghiaccio.\n\nSi avvicina il momento di andare via. Presto dovrai lasciarti alle spalle qualcosa che ti trattiene. Non temere, poiché questa cosa è stata fonte di disincanto per te.', '', 57534),
(271736, 'itIT', '|TInterface\\ICONS\\INV_10_Inscription_DarkmoonCards_Frost_7.BLP:50|t Il Sette di Gelo. \n\nRappresenta strane visioni che sgorgano da sette calici fatti del ghiaccio più puro, ma sono immagini di uno spirito fantastico.\n\nPresto sarà necessario prendere delle decisioni e l\'istinto ti dirà di basare le tue scelte su pie speranze. Impara a distinguere i vani desideri dalla realtà pragmatica, così da avere la prontezza di prendere queste decisioni a mente lucida.', '', 57534),
(271735, 'itIT', '|TInterface\\ICONS\\INV_10_Inscription_DarkmoonCards_Frost_6.BLP:50|t Il Sei di Gelo. \n\nRappresenta dei bambini in un vecchio giardino che annaffiano i fiori con delle coppe piene di neve sciolta.\n\nPresto, cari ricordi e un forte senso di nostalgia ti riempiranno di grande felicità, ma assicurati di restare coi piedi per terra, per non perderti in quelle emozioni.', '', 57534),
(271734, 'itIT', '|TInterface\\ICONS\\INV_10_Inscription_DarkmoonCards_Frost_5.BLP:50|t Il Cinque di Gelo.\n\nRappresenta una figura scura, ammantata, che guarda di lato verso tre coppe rovesciate in pozze d\'acqua. Altre due coppe sono in piedi dietro la figura, su piedistalli di ghiaccio. Sullo sfondo, un ponte conduce a una piccola fortezza.\n\nLe cose potrebbero non andare come previsto, $uamico mio:amica mia;. Si avvicina un intoppo o una perdita, che inaugurerà una fase di profonda sofferenza e disincanto. Eppure, una volta raggiunto questo bivio, cerca di mantenere il sangue freddo. Ricorda, in questa esperienza ci saranno opportunità per crescere e imparare, che offrono un faro di speranza.', '', 57534),
(271733, 'itIT', '|TInterface\\ICONS\\INV_10_Inscription_DarkmoonCards_Frost_4.BLP:50|tIl Quattro di Gelo. \n\nRappresenta una figura avvolta da pellicce, seduta sotto un pino, che contempla tre calici adagiati nella neve davanti a sé. Un braccio che fuoriesce da una nuvola offre alla figura un quarto calice. La sua espressione tradisce malcontento.\n\nSi avvicina un periodo di stagnazione. Dovrai fare un po\' di introspezione per ravvivare la tua passione e ripartire. Una persona a te vicina noterà la tua difficoltà e potrà aiutarti, ma dovrai cercarla e accettare.', '', 57534),
(271732, 'itIT', '|TInterface\\ICONS\\INV_10_Inscription_DarkmoonCards_Frost_3.BLP:50|t Il Tre di Gelo. \n\nRappresenta persone che festeggiano ballando in un giardino con le coppe sollevate come se si stessero promettendo qualcosa. La neve soffice cade tutt\'intorno.\n\nSta per arrivare una tregua rinvigorente, $uamico mio:amica mia;! Potrai riprendere fiato e ritrovare amici e familiari.', '', 57534),
(271731, 'itIT', '|TInterface\\ICONS\\INV_10_Inscription_DarkmoonCards_Frost_2.BLP:50|t Il Due di Gelo. \n\nRappresenta due giovani in piedi in una scena invernale, che brindano tra loro con boccali di ghiaccio purissimo.\n\nPresto ti si presenterà un\'occasione per stringere una solida collaborazione o per rafforzare e rendere più equa una collaborazione esistente.', '', 57534),
(271730, 'itIT', '|TInterface\\ICONS\\INV_10_Inscription_DarkmoonCards_Frost_1.BLP:50|t L\'Asso di Gelo. \n\nRappresenta una mano che emerge dal terreno ricoperto di neve fresca, mentre stringe lo stelo di un calice pieno fino a traboccare di acqua fumante.\n\nQualcosa di nuovo si appresta a entrare nella tua vita. Liberati dei vecchi fardelli e sfrutta il tuo intuito per scorgere in fretta questa opportunità.', '', 57534),
(271728, 'itIT', '|TInterface\\ICONS\\INV_10_Inscription_DarkmoonCards_Fire_5.BLP:50|t Il Cinque di Fuoco. \n\nRappresenta un gruppo di cinque giovani che brandiscono dei bastoni come per giocare. È la finzione di una battaglia, e i loro occhi bruciano di fuoco.\n\nÈ in arrivo un conflitto. Dovrai necessariamente gestirlo in modi per te inusuali. Resta all\'erta, o un rivale potrebbe batterti.', '', 57534),
(271727, 'itIT', '|TInterface\\ICONS\\INV_10_Inscription_DarkmoonCards_Fire_8.BLP:50|t L\'Otto di Fuoco. \n\nRappresenta una raffica di otto frecce infuocate che solcano i cieli verso la fine della loro traiettoria. Il loro significato è imminente, quasi a portata di mano.\n\nLe carte ti esortano ad avere pazienza, ma anche a prepararti. Presto riceverai le notizie che aspettavi e, una volta giunte, dovrai agire tempestivamente!', '', 57534),
(271726, 'itIT', '|TInterface\\ICONS\\INV_10_Inscription_DarkmoonCards_Fire_7.BLP:50|t Il Sette di Fuoco. \n\nRappresenta una figura caparbia in piedi su una collina accidentata, che tiene sollevata una torcia. Altre sei torce vengono sollevate verso la figura dal basso.\n\nPresto i tuoi successi verranno messi alla prova. Dovrai prepararti, se vorrai perseverare e mantenere il controllo.', '', 57534),
(271725, 'itIT', '|TInterface\\ICONS\\INV_10_Inscription_DarkmoonCards_Fire_6.BLP:50|t Il Sei di Fuoco. \n\nRappresenta una figura a cavallo adornata da una corona d\'alloro e con in mano una spada fiammeggiante. Cinque fanti con spade fiammeggianti simili sono al suo fianco.\n\nSi avvicina una magnifica vittoria, un evento a cui assisteranno in molti e che ti darà riconoscimento pubblico!', '', 57534),
(271724, 'itIT', '|TInterface\\ICONS\\INV_10_Inscription_DarkmoonCards_Fire_4.BLP:50|t Il Quattro di Fuoco. \n\nRappresenta quattro grandi pilastri in primo piano, con una ghirlanda di puro fuoco sospesa tra di loro. Due figure reggono dei fiori profumati e al loro fianco c\'è un ponte sopra un fossato che conduce a un vecchio maniero.\n\nC\'è un festeggiamento all\'orizzonte! È il momento di gioire e di stringere legami con amici e persone care.', '', 57534),
(271723, 'itIT', '|TInterface\\ICONS\\INV_10_Inscription_DarkmoonCards_Fire_3.BLP:50|t Il Tre di Fuoco. \n\nRappresenta una figura calma e maestosa, di spalle, che guarda dal limitare di una scogliera le navi che scivolano sul mare. Nel terreno sono piantati tre bastoni, ognuno dei quali regge una lanterna, mentre la figura si appoggia leggermente a uno di essi.\n\nStai andando nella direzione giusta, $uamico mio:amica mia;. Quali che siano i piani che hai escogitato, stanno funzionando e presto vedrai rapidi progressi.', '', 57534),
(271722, 'itIT', '|TInterface\\ICONS\\INV_10_Inscription_DarkmoonCards_Fire_2.BLP:50|t Il Due di Fuoco. \n\nRappresenta un\'alta figura che guarda dal tetto di un bastione affacciato sul mare. Regge una sfera nella mano destra e un bastone ardente nella sinistra, che poggia sul bastione. Un altro bastone ardente è fissato a un anello alla destra della figura.\n\nÈ il momento di escogitare un piano. Il successo non è casuale, ma il risultato di pianificazioni ed esecuzioni meticolose. Prenditi il tempo necessario per gettarne le basi, poiché è il primo passo sul cammino verso il successo.', '', 57534),
(271721, 'itIT', '|TInterface\\ICONS\\INV_10_Inscription_DarkmoonCards_Fire_1.BLP:50|t L\'Asso di Fuoco. \n\nRappresenta una mano che emerge da una palla di fuoco ardente, mentre afferra una bacchetta avvolta dal fumo.\n\nUn buon presagio, $uamico mio:amica mia;! Presto avrai l\'opportunità di seguire la tua passione. Coglila!', '', 57534),
(271720, 'itIT', '|TInterface\\ICONS\\INV_10_Inscription_DarkmoonCards_Air_8.BLP:50|t L\'Otto di Aria. \n\nRappresenta un prigioniero bendato, circondato da otto venti tempestosi. Eppure, si tratta di una prigionia temporanea, piuttosto che una irrimediabile.\n\nPresto ti ritroverai in trappola e impotente. Quando accadrà, analizza in modo critico la situazione, poiché sarà probabilmente una trappola di tua stessa creazione.', '', 57534),
(271719, 'itIT', '|TInterface\\ICONS\\INV_10_Inscription_DarkmoonCards_Air_7.BLP:50|t Il Sette di Aria. \n\nRappresenta una persona nell\'atto di portar via rapidamente cinque venti. Altri due sono per terra, e nelle vicinanze c\'è un accampamento.\n\nPresto ti ritroverai in una situazione spinosa. Potrai uscirne soltanto con la dovuta furtività.', '', 57534),
(271715, 'itIT', '|TInterface\\ICONS\\INV_10_Inscription_DarkmoonCards_Air_6.BLP:50|t Il Sei di Aria.\n\nRappresenta un traghettatore che trasporta alcuni passeggeri sulla propria imbarcazione fino all\'altra sponda. La rotta è senza ostacoli e, dato il carico leggero, è evidente che questo lavoro non rappresenta una sfida particolare.\n\nC\'è una transizione all\'orizzonte. Ti dovrai probabilmente lasciare alle spalle qualcosa di importante per poter andare avanti.', '', 57534),
(271713, 'itIT', '|TInterface\\ICONS\\INV_10_Inscription_DarkmoonCards_Air_5.BLP:50|t Il Cinque di Aria. \n\nRappresenta un uomo sprezzante e accigliato verso due figure avvilite che si ritirano dopo aver lasciato i loro sacchi d\'aria per terra. L\'uomo porta altri due sacchi sulla spalla sinistra e un terzo nella mano destra.\n\nPresto si presenterà un\'opportunità per vincere. Ma questa vittoria sarà possibile soltanto con l\'inganno e l\'astuzia. Quanto vale per te l\'ambizione?', '', 57534),
(271711, 'itIT', '|TInterface\\ICONS\\INV_10_Inscription_DarkmoonCards_Air_4.BLP:50|t Il Quattro di Aria. \n\nRappresenta l\'effigie di un cavaliere in preghiera su quella che è presumibilmente la sua tomba.\n\nPresto avrai tempo per riposare e spazio per la contemplazione, finalmente.', '', 57534),
(271710, 'itIT', '|TInterface\\ICONS\\INV_10_Inscription_DarkmoonCards_Air_3.BLP:50|t Il Tre di Aria. \n\nRappresenta tre raffiche di forte vento che trafiggono un cuore, con nubi tempestose e pioggia sullo sfondo.\n\nOh... Oh, cielo. C\'è un cuore spezzato in vista. Mi dispiace tanto, $uamico:amica;.', '', 57534),
(271709, 'itIT', '|TInterface\\ICONS\\INV_10_Inscription_DarkmoonCards_Air_2.BLP:50|t Il Due di Aria. \n\nRappresenta una figura bendata che tiene in equilibrio due burrasche sulle spalle.\n\nPresto ti troverai davanti a una scelta difficile. Fa\' molta attenzione! Per quando la scelta possa essere ardua, l\'indecisione potrebbe portare a uno stallo. O peggio.', '', 57534),
(271708, 'itIT', '|TInterface\\ICONS\\INV_10_Inscription_DarkmoonCards_Air_1.BLP:50|t L\'Asso di Aria. \n\nRappresenta una mano che emerge da una nuvola, mentre afferra una spada circondata da una corona.\n\nLa lucidità mentale porterà presto a una svolta. Sta\' all\'erta!', '', 57534),
(271704, 'itIT', 'Per te? Ma certo! Offre la casa!\n\n<Alphonse mischia le carte.>\n\nUna mescolata per cominciare\npoi un riffle e un bridge da ammirare.\n\nUn taglio veloce, Monge leggero,\nma solo un tocco, senza esagerare davvero!\n\n<Alphonse mette tre carte a faccia in giù davanti a te.>\n\nOra sentiamo il sussurro del fato. O parla forte?\nGira una carta, tenta la sorte!', '', 57534),
(271672, 'itIT', '<Alphonse sorride e inizia ad annuire.>\n\nSì, assolutamente.\n\n<Alphonse continua ad annuire mentre il suo sguardo si allontana e sul volto gli compare un\'espressione assorta.>\n\nBe\', a parte quando mi sono chinato sotto uno dei padiglioni per indossare il mio costume da divinazione. Ho pensato che fosse un luogo come un altro per cambiarsi, dato che anche gli altri sopra di me stavano indossando vari costumi.\n\nE poi ho pensato che, magari, potevo saltare su anch\'io per provare a prendere qualche nastro! Questo completo di certo attirerebbe abbastanza attenzione da vincere, giusto?\n\n<Alphonse ti guarda e sfoggia un sorriso insicuro.>\n\nVoglio dire, sì! Giusto! Quale... qual era la domanda?', '', 57534),
(271670, 'itIT', 'Da quando sono arrivato, sono stato un po\' dappertutto.\n\n<Alphonse inizia a fare un elenco con le dita mentre parla.>\n\nHo preso da mangiare, sono andato in bagno, ho incontrato i coordinatori e ho dato un\'occhiata a tutti gli eventi che ci sono.', '', 57534),
(271668, 'itIT', 'Salute a te, salvezza delle piccole attività nascenti e baluardo delle amicizie! Gli affari vanno a gonfie vele, grazie a te!', '', 57534),
(271666, 'itIT', 'Salve, $uamico:amica;! Felice di vederti! Ti stai godendo i festeggiamenti?\n\n<Si guarda intorno e abbassa la voce.>\n\nCome va la ricerca? Ah, se riuscissi a tirarmi fuori da questo impiccio senza far scoprire a Marie-Ann che ho \"preso in prestito\" il suo mazzo, allora SÌ che sarebbe una giornata magica, non credi?', '', 57534),
(271657, 'itIT', '', 'E poi Naftro è faltato fuori ed è fcappato? Accidenti! Devi affolutamente prenderlo!\n\nForfe provando ad avvicinarti di foppiatto, così non fi fpaventerà!', 57534),
(271656, 'itIT', '', 'Dobbiamo feguire le orme di Sully! La fua direzione fembra portare all\'Offervatorio della Ferpe.', 57534),
(271630, 'itIT', 'Oh, cielo! Sì! Sia lode alla provvidenza!!\n\nPosso chiederti di trovare anche il resto? Ti ricompenserò volentieri per il tuo tempo.', '', 57534),
(271628, 'itIT', 'A dire il vero, sì!\n\nMa a quanto pare avevo un buco nella borsa e alcune carte sono cadute. Senza un mazzo completo, mi è impossibile farlo.\n\nPer non parlare del fatto che questo mazzo l\'ho preso in prestito dalla mia amica Marie-Anne, e che razza di amico sarei se glielo restituissi con metà delle carte mancanti?', '', 57534),
(271619, 'itIT', 'Getseng addestra molti giovani bakar. Forse lui ha le informazioni che cerchi.\n\nLo troverai in un posto di guardia in una delle entrate a Maruukai.', 'Getseng addestra molti giovani bakar. Forse lui ha le informazioni che cerchi.\n\nLo troverai in un posto di guardia in una delle entrate a Maruukai.', 57534),
(271607, 'itIT', 'Divinazione!\n\n<Inizia a fare un gesto teatrale con le mani, ma si ferma a metà per poi lasciarle cadere sui fianchi, abbattuto.>\n\nBe\'... prima o poi, spero.\n\nForse avrei dovuto leggere il MIO futuro prima di andarmene di casa, eh?\n\n<Ridacchia imbarazzato.>', '', 57534),
(271605, 'itIT', 'Oh, salve. Chiedo scusa, il mio negozio non è ancora aperto.', '', 57534),
(271549, 'itIT', 'Nastro adorava rovistare così nei mucchi di spazzatura...', '', 57534),
(271544, 'itIT', 'Ma che ne so. Quel tipo, la Rogna, non aveva mica degli scoiattoli o qualcosa del genere che sono stati ammazzati?', '', 57534),
(271542, 'itIT', 'Che vuoi? Non sono in servizio.', '', 57534),
(271539, 'itIT', '', 'Mmm... So che Sully McLeary una volta aveva un paio di procioni, ma ora non ci sono più.', 57534),
(271538, 'itIT', '', 'Mmm, hai provato a chiedere in giro? Parla con Riggo e Kiryn per capire che mafcotte vogliono entrambi e poi cerca degli indizi? Forfe quella Breanni delle mafcotte fa qualcofa.', 57534),
(271533, 'itIT', 'Kenshi? Non conosco nessuno con quel nome, mi spiace.\n\nForse è in un\'altra zona della città.', '', 57534),
(271531, 'itIT', 'Ho visto un piccolo Tuskarr che giocava in un fiume a Maruukai.', 'Ho visto un piccolo Tuskarr che giocava in un fiume a Maruukai.', 57534),
(271530, 'itIT', '', 'In realtà sì, ho visto un piccolo Tuskarr! Ha chiesto un passaggio su uno dei nostri carri dei rifornimenti.\n\nI carri sono appena tornati se vuoi dare uno sguardo in cerca di indizi.', 57534),
(271509, 'itIT', 'Oh, ciao! Il mio aquilone si è incastrato qui, ma fono sicuro che in quel momento avevo ancora la palla...\n\nVai a sud all\'accampamento dei draghi! È lì che sono andato poi.', '', 57534),
(271503, 'itIT', '', 'Evviva, hai trovato TUTTI i giocattoli!\n\nGrazie mille per il tuo aiuto! Fai fapere ad Alycf quanto fei incredibile!', 57534),
(271495, 'itIT', '', 'Vedi fe riefci a nafconderti vicino ai giocattoli del cagnolino e afpetta che efca!', 57534),
(271494, 'itIT', '', 'E io sono felice che tu sia $gsopravvissuto:sopravvissuta; a... aspetta. Sono la me del futuro.\n\n\nSono felice che tu sia $gsopravvissuto:sopravvissuta;, non posso dire altro.', 57534),
(271492, 'itIT', '', 'Grazie! Nonostante le apparenze, i Draghi di Bronzo hanno pochissimo tempo libero. Non sono nemmeno sicura se questa sia la me del presente o la me del futuro che ha avuto un po\' di tempo libero? O forse è la me del passato?', 57534),
(271489, 'itIT', '', '<Addie sospira, delusa.>\n\nSì, forse hai ragione. Sarò anche una cacciatrice nell\'anima, ma alcune battute di caccia dovrebbero accadere solo nei libri. A meno che...\n\nNo, no. Dev\'essere una storia e basta.', 57534),
(271487, 'itIT', '', 'Oh, ma certo, i Viaggiatori nel tempo sono stati molto chiari riguardo a cosa si può fare nei Flussi Temporali e cosa no.\n\nEppure, se ce ne fosse uno che sta collassando da qualche parte...', 57534),
(271485, 'itIT', '', 'Tutti questi viaggi nel tempo mi fanno pensare. Immagina se potessimo dare la caccia alle bestie del passato...', 57534),
(271484, 'itIT', '', 'Forfe puoi usare il giocattolo che hai trovato per attirare il cagnolino! Hai per caso trovato degli indizi vicino al giocattolo?', 57534),
(271482, 'itIT', '', 'Fi, fi! Chiamami con i cofi profumofi fe non riesci a profeguire e ti aiuterò a fbloccarti!\n\nInfieme poffiamo fcoprire tutto!', 57534),
(271480, 'itIT', '', 'Che fpettacolo, vero?\n\nGuarda, fto parlando con te, ma anche con me! Quefto è il potere dei baftoncini profumati.', 57534),
(271479, 'itIT', 'C\'è sempre qualcosa di nuovo dietro l\'angolo. Se sai dove cercare.', '', 57534),
(271463, 'itIT', 'Ciò che hai sentito, o visto, conta poco. Nella morte, non ho una famiglia. È così che... è così che dev\'essere.', '', 57534),
(271461, 'itIT', 'I miei unici amici sono i morti e la mia famiglia... non ne ho una.', '', 57534),
(271459, 'itIT', 'Tutto ciò che mi resta è il dovere.', '', 57534),
(271457, 'itIT', 'Lo faccio, solo non in modo evidente quanto gli altri.', '', 57534),
(271454, 'itIT', 'Non voglio rimuginare su ricordi dolorosi, ma non voglio nemmeno dimenticarli. Fingere che ci sia stata solo gioia nel passato ci condanna a ripetere gli stessi errori.', '', 57534),
(271451, 'itIT', 'È bello celebrare il passato, ma dobbiamo anche piangerlo.', '', 57534),
(271447, 'itIT', '', 'Per fortuna siamo riusciti a liberarti, $n.\n\nNon siamo stati così fortunati in altri degli asili che abbiamo scoperto.', 57534),
(271418, 'itIT', '', 'Mi fa che dovrefti cercare una groooffa ftatua di Ohn\'ahra.\n\nForfe l\'idolo può effere utile!', 57534),
(271416, 'itIT', '', 'Oooh, un disegno di un bakar! Avevi vifto quei cagnoloni in quel pofto dei Centauri dove hai trovato il giocattolo di Yennu, vero?', 57534),
(271415, 'itIT', 'Salve, come posso aiutarti?', '', 57534),
(271413, 'itIT', 'Abbiamo molti amici bakar che ci aiutano nella caccia.\n\nGli consentiamo di vagare come preferiscono, ma di recente un cucciolo di bakar si è cacciato nei guai.\n\nForse lo troverai nel suo solito nascondiglio. Cerca le Figlie di Ohn\'ahra sotto il suo sguardo vigile.', '', 57534),
(271404, 'itIT', 'Certo. Il mondo è un posto serio. Lavoriamo duramente, combattiamo duramente e se non stiamo attenti finiremo per indurirci anche noi. Giocattoli e festeggiamenti ci ricordano che la vita non è fatta solo di fatica e conflitto. E poi, sono divertenti!', '', 57534),
(271402, 'itIT', 'Sto sempre lavorando a qualcosa, ma per ora mi sto solo godendo i festeggiamenti. Ne abbiamo bisogno per gli stessi motivi per cui abbiamo bisogno dei giocattoli.', '', 57534),
(271395, 'itIT', 'I festeggiamenti sono l\'occasione perfetta per testare i giocattoli.', '', 57534),
(271368, 'itIT', 'Sì, ho visto un grosso cane. È passato di qui correndo e ha pure portato via qualcuno dei miei progetti!\n\nNon somigliava affatto ai cani che vivono da queste parti, quindi ho fatto uno schizzo veloce. Prendilo, se vuoi.', '', 57534),
(271366, 'itIT', '', 'Povera Kenshi! Essere inseguita da quella bestia sarà stato terrificante!\n\nNon so che tipo di animale fosse, ma non somigliava a quelli che si vedono di solito da queste parti.\n\nForse la creatura ha lasciato degli indizi vicino all\'albero sul quale si è arrampicata la mia povera Kenshi?', 57534),
(271349, 'itIT', '', 'Ci fono un facco di cagnolini a Boralus! Dai un\'occhiata in giro e vedi fe riefci a trovare indizi ful colpevole.\n\nPotrefti anche provare a chiedere se la fignora pazza dei gatti ne fa qualcosa.', 57534),
(271346, 'itIT', '', 'Cooofa? Io?\n\nMa perché dovrei rubare i miei giocattoli? Fecondo me ti ftai fbagliando.', 57534),
(271345, 'itIT', '', 'Uhhh... Non credo che Alyx avrebbe tempo di fare una cofa del genere. Voglio dire, mi fembra che l\'unica cosa che faccia fia lavorare?', 57534),
(271344, 'itIT', '', 'Un cagnolino? Oohh, avrebbe fenfo fecondo gli indizi che hai trovato!\n\nNon ci fono un facco di animali grandi a Boralus? E anche Kenshi vive a Boralus! Credo che il noftro criminale a quattro zampe potrebbe effere lì.', 57534),
(271343, 'itIT', '', 'Hmmm... Non credo che la fignora dei gatti ruberebbe i giocattoli ai fuoi gattini.\n\nFecondo te chi altro può effere ftato?', 57534),
(271338, 'itIT', '', 'Il giocattolo di Yennu era una palla che tu hai trovato fepolta fotto un cumulo di terra. L\'altro giocattolo che hai trovato era vicino a un albero e hai dovuto feguire una scia di peli per trovarlo.\n\nFecondo te chi farà ftato a rubare i giocattoli?', 57534),
(271328, 'itIT', '', '<Kenshi annusa il gomitolo di lana e rifiuta il giocattolo.>', 57534),
(271326, 'itIT', '', 'Hai trovato la lana di Kenfhi! Dovrefti riportargliela.', 57534),
(271262, 'itIT', 'Quindi gli assassini reali sono già qui nascosti tra le ombre... ho meno tempo di quanto pensassi.', '', 57534),
(271260, 'itIT', 'C\'è odore di morte nell\'aria... ho un brutto presentimento.', '', 57534),
(271253, 'itIT', 'Piangiamo la distruzione del nostro amico, Baelgrim.\n\nMa gli stranieri e la loro perseveranza mi danno speranza.', '', 57534),
(271236, 'itIT', 'Brinthe e io ne abbiamo passate tante insieme, vero?\n\nAll\'inizio ero solo curioso di saperne di più sull\'isola e i Terrigeni.\n\nMa ora è come se facesse parte della mia famiglia!', '', 57534),
(271176, 'itIT', '', 'Il mio Cappello Penfante perfonale! Non vedo l\'ora! E ne daranno uno anche a te?\n\nÈ il giorno più bello della mia vita!', 57534),
(271173, 'itIT', 'Non dimenticare di controllare il Libro d\'Indizi per sapere dove andare. Divertiti!', '', 57534);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(271171, 'itIT', '', 'Ora abbiamo tutto. Torniamo dallo zione!', 57534),
(271170, 'itIT', '', 'Fodera Imbottita per Elmetto! Comoda!', 57534),
(271169, 'itIT', '', 'Fì! L\'Apparato di Calcolo dei Penfieri! Perfetto!', 57534),
(271168, 'itIT', '', 'Evviva! Lenti criftalline!', 57534),
(271166, 'itIT', '', 'Credo di aver trovato qualcofa!', 57534),
(271141, 'itIT', 'Thrall fa un cenno di rispetto a Lufsela.', '', 57534),
(271116, 'itIT', 'Alla gloria, all\'onore e soprattutto agli amici.', '', 57534),
(271115, 'itIT', 'Ti riempio il boccale. Alzalo e brinda ai giorni felici.', '', 57534),
(271114, 'itIT', 'È passato troppo tempo. È ora di brindare a iosa.', '', 57534),
(271113, 'itIT', 'Mio caro amico Chen, sei venuto per bere qualcosa?', '', 57534),
(271112, 'itIT', 'Sì, esatto.', '', 57534),
(271111, 'itIT', 'Leerooooy! Jeeeeeenkins!', '', 57534),
(271110, 'itIT', 'È buono. Immagino significhi che ci sono molti modi per cucinare. Per esempio...', '', 57534),
(271109, 'itIT', 'Ma questo pollo è fritto. È proprio il contrario della pazienza.', '', 57534),
(271108, 'itIT', 'Una buona zuppa richiede tempo e pazienza.', '', 57534),
(271103, 'itIT', '', 'Sono sicura che sistemeremo il Codice in un attimo! Fammi sapere quando vuoi procedere.', 57534),
(271101, 'itIT', 'Salve, salve, salve.\n\nIo creo tavoli così tu puoi creare cose.\n\nChe tavolo vuoi?', '', 57534),
(271092, 'itIT', '', 'Evviva, hai trovato Kenfhi!\n\nFe fta perdendo un facco di pelo, forfe ti bafterà feguire i peli per terra!', 57534),
(271091, 'itIT', '', '<Kenshi è spaventata, ma accetta il tuo gesto. Noti che sta perdendo grossi batuffoli di pelo.>', 57534),
(271089, 'itIT', '', '<Kenshi sembra sconvolta e irritata.>', 57534),
(271088, 'itIT', '', '<Kenshi guarda la tua mano con trepidazione.>', 57534),
(271086, 'itIT', '', 'Mi fa che Yennu ha detto di cercare Kenfhi a Boraluf!\n\nPuò effere difficile da trovare, fpecialmente fe fta fonnecchiando da qualche parte, però paffa un sacco di tempo con la signora pazza e altri amici pelosi.', 57534),
(271085, 'itIT', 'Wow! Hai trovato la mia palla! Grazie!\n\nForse ora dovresti andare a cercare Kenshi. Le hanno rubato i suoi giocattoli a Boralus.', '', 57534),
(271082, 'itIT', '', 'Hai trovato la palla di Yennu! Dovrefti riportarla da lui a Ifkaara.', 57534),
(271080, 'itIT', '', 'Yennu ha fcritto qualcofa? \n\nOohh, a quanto pare, Yennu fi è diretto verfo una grande città dei centauri! Fcommetto che l\'idolo può aiutarci!', 57534),
(271079, 'itIT', '', 'Yennu ha parlato di un accampamento di draghi? Forfe voleva dire l\'accampamento delle Fcaglie di Drago!\n\nProva a cercare degli indizi lì col tuo idolo!', 57534),
(271078, 'itIT', '', 'Non pensare che il mio compito sia facile. Ho diverse opinioni su quello che è successo nei Sotterranei di Roccianera. Il ruolo che ho giocato, le cose che ho perduto... Quello che il clan dei Ferroscuro ha guadagnato da quella sconfitta.', 57534),
(271073, 'itIT', 'Una cosa del genere. E abbiamo comunque bisogno di altri per il cibo.', '', 57534),
(271071, 'itIT', 'No, $gamico mio:amica mia;. Essere testimoni del passato è una cosa, ma raccontarlo agli altri è una questione molto diversa.', '', 57534),
(271069, 'itIT', 'Così tanta gente è venuta per celebrare il passato. Mi scalda il cuore.', '', 57534),
(271062, 'itIT', '', 'Questa è la vita degli avventurieri. Bisogna riposare quando si può perché la prossima avventura è sempre dietro l\'angolo.', 57534),
(271060, 'itIT', '', 'Non senza una moneta o due. E poi, è ora di dare una pausa alla mia squadra.', 57534),
(271056, 'itIT', '', 'L\'avventura continua.', 57534),
(271054, 'itIT', 'Ma tantissima gente mi fissa. Non sono più uno di loro.', '', 57534),
(271043, 'itIT', 'Non dovrei essere qui.', '', 57534),
(271042, 'itIT', '', 'Questo non è vero. Anche adesso sto servendo gli spiriti. Imparare, festeggiare e anche mangiare cibi nuovi mi permette di crescere e di proteggerli meglio.', 57534),
(271040, 'itIT', '', 'È una piacevole pausa dalla lotta contro l\'oscurità.', 57534),
(271037, 'itIT', '', 'Gli spiriti sono con noi.', 57534),
(271036, 'itIT', 'Ah! Non c\'è mai abbastanza tempo. Prenderò ciò che posso e cercherò di imparare il possibile nel tempo che ho.', '', 57534),
(271034, 'itIT', 'No. Questa volta sono qui per ascoltare. Per imparare la saggezza del tuo mondo.', '', 57534),
(271032, 'itIT', 'Ah, salve.', '', 57534),
(271028, 'itIT', '', 'Ah, poverino. Sempre così veemente. Dovrebbe imparare a rilassarsi, come quell\'Ogre gentile. Dovrei far consegnare a lui le mele.', 57534),
(271026, 'itIT', '', 'Oh, sì. Ho sentito che è qui anche quell\'Orco di Shattrath. Forse dovrei dargli qualche mela in ricordo dei vecchi tempi.', 57534),
(271024, 'itIT', '', 'Una mela al giorno... è tutto quello che ho.', 57534),
(271018, 'itIT', 'Sì... facciamogli credere di averci ingannato. Lasciamogli pensare che non siamo pronti. E poi... marmellata di mele!', '', 57534),
(271016, 'itIT', 'La mente malvagia dietro al carretto delle mele e il suo esercito di Ogre continuano a sfuggirmi. Questa è la mia occasione di recuperare le forze per essere pronto quando finalmente colpirà.', '', 57534),
(271014, 'itIT', 'Non c\'è nemmeno una mela marcia qui.', '', 57534),
(271005, 'itIT', 'Mentre il Gran Oratore è impegnato col ripristino della Macchina del Risveglio, la maggior parte delle Profondità Risonanti è sprofondata nel caos. Nuove minacce si nascondono nell\'oscurità, come i Nerubiani, i Coboldi e i Terrigeni contaminati dal vuoto, conosciuti come \"Skardyn\".\n\nDopo aver spodestato il famoso Re delle Candele e aver stretto un\'alleanza con i Coboldi, l\'Oratrice Brinthe e gli stranieri hanno scoperto la verità sulla presenza degli Skardyn.\n\nPer ripristinare la Macchina del Risveglio, il Gran Oratore Eirich ha stretto un patto oscuro e ha iniziato a risvegliare i Terrigeni come Skardyn. \n\nL\'Oratrice Brinthe e gli stranieri hanno inseguito Eirich fino alla Sala del Risveglio. Con l\'aiuto di Magni Barbabronzea, sono riusciti a estirpare la corruzione che minacciava tutti i Terrigeni dormienti che aspettavano di essere risvegliati nella Macchina del Risveglio.', '', 57534),
(271000, 'itIT', 'Proteggete l\'Alba Infranta!', 'Proteggete l\'Alba Infranta!', 57534),
(270999, 'itIT', 'È davvero un incubo!', 'È davvero un incubo!', 57534),
(270998, 'itIT', 'Cos\'è questo mostro nerubiano?', 'Cos\'è questo mostro nerubiano?', 57534),
(270846, 'itIT', '<Sembra che il Terrigeno stia trascrivendo il testo trovato dentro questo enorme tomo runico nel suo registro personale.>\n\n\"...noi siamo i Terrigeni, della terra, ...la sua anima è la nostra, il suo dolore è il nostro, il battito del suo cuore è il nostro. Cantiamo la sua canzone... piangiamo per la sua bellezza. Chi non vorrebbe tornare a casa? È questo il perché, o figli della terra.\"\n\nSecondo il Consigliere Belgrum, questo testo si trovava sulla stessa tavola responsabile della trasformazione di Magni in... be\', qualsiasi cosa fosse.\n\nEra davvero uno dei Thraegar? Cosa sapevano i nostri simili a nord che noi non conoscevamo?\n\nChi avrebbe mai pensato che avremmo scoperto così tante cose su di noi dai nostri discendenti?', '', 57534),
(270845, 'itIT', '', '<Frunhil alza lo sguardo dal suo diario.>\n\nLe informazioni contenute dentro questi registri sono incredibili! Qui si parla di un enorme complesso dei Titani chiamato Ulduar.\n\nSecondo gli appunti della Lega degli Esploratori, i Terrigeni non erano le uniche creature forgiate dai Titani dentro quel posto. La struttura ospitava Meccagnomi, Vrykul di Ferro e una razza che chiamavano \"Nani di Ferro\". C\'erano persino dei Giganti!\n\nHai mai incontrato queste creature? Devo saperne di più!\n\nÈ strano che avessimo così poche informazioni su tutto questo. Non ricordo di aver mai letto nulla su Ulduar o le altre strutture nell\'Archivio.\n\nChissà quante altre informazioni sono andate perdute quando l\'Archivio si è spento? Quante cose ci saranno da riscoprire?', 57534),
(270836, 'itIT', '', 'Se vogliamo riparare la Cripta del Ricordo e accedere alle informazioni al suo interno, allora dovremo recuperare altri frammenti del disco dei Titani.\n\nPer fortuna, questi dischi una volta erano molto comuni per tutta Khaz Algar. Probabilmente ne troverai alcuni durante le tue intrepide esplorazioni e avventure.', 57534),
(270829, 'itIT', '', 'Abbiamo deciso di comune accordo che la mia tana sarà il centro delle operazioni dei Fili Recisi.', 57534),
(270827, 'itIT', '', 'Il comandante Nerubiano, Zirix, parlava come se stesse proteggendo la base di Xal\'atath.\n\nFarò tutto il possibile per privarla di un\'altra pedina.', 57534),
(270811, 'itIT', '<Il portale si muove e cambia di continuo. Non riesci a capire cosa ci sia dall\'altro lato.>', '', 57534),
(270805, 'itIT', '', 'Condoglianze? Oh, sono certa che sta bene.\n\nNoi Terrigeni non ci muoviamo propriamente con disinvoltura in acque profonde, quindi sarà colato a picco come un sasso. \n\nLa più grande sciagura in questa situazione è la perdita del tempo che avremmo potuto dedicare al nostro viaggio intorno al mondo. Conoscendolo, se la starà spassando esplorando le profondità. Ecco perché non abbiamo fermato la nave.\n\nIl capitano del vascello ha detto che stavamo navigando nei pressi dell\'Altura di Biel\'aran quando è caduto... e ha accennato qualcosa riguardo l\'avvistamento delle rovine di Vashj\'ir da sopra la superficie.\n\nQuindi credo che dovrò restare bloccata qui ad aspettarlo finché non si degnerà di ricomparire.\n\nMagari farò un salto in città mentre lo aspetto. Sembra che questi umani abbiano dell\'idromele fatto senza Mieldifuoco o Api di Cenere. \n\nMi chiedo quanto sia buono...', 57534),
(270803, 'itIT', '', 'Ovviamente qualcosa è andato storto! Quel dannato testaroccia non è riuscito a trattenersi! \n\nCi stavamo godendo il nostro viaggio per mare dal porto di Roccavento quando all\'improvviso l\'ho visto sporgersi dal bordo della nave, urlando qualcosa su qualche tipo di struttura avvistata sott\'acqua. \n\nPrima che riuscissi a riprenderlo, è precipitato fuori bordo sprofondando negli abissi!\n\n<Arnbourda incrocia le braccia in segno di frustrazione.>', 57534),
(270798, 'itIT', '', '<La Terrigena borbotta tra sé e sé mentre rivolge lo sguardo verso il porto.>\n\nTutto ciò che doveva fare era restare nella stiva e non sporgersi troppo oltre il bordo...', 57534),
(270793, 'itIT', '', 'N-No, mi dispiace. Preferirei parlare con un investigatore.', 57534),
(270791, 'itIT', '', 'Io... credo di aver assistito a un crimine. O almeno di aver visto un criminale.', 57534),
(270790, 'itIT', '<Il Terrigeno volge lo sguardo verso le rovine prima di accorgersi di te.>\n\nOh, ehilà. Anche tu sei qui per visitare le rovine? Anche tu sei caduto da una nave perché ti sei sporto troppo oltre il bordo?\n\nEro su un vascello diretto a Boralus quando ho... intrapreso questa deviazione!\n\nIn tutti i miei cicli, non ho mai immaginato che potesse esistere un intero mondo oltre la mia terra natia, eppure meraviglie come questa erano là fuori, ad aspettarci.\n\nCredo che trascorrerò del tempo a godermi questo posto prima di dirigermi a nord-ovest. \n\nI miei apparati per la respirazione subacquea dovrebbero reggere prima di subire danni permanenti.', '', 57534),
(270780, 'itIT', '', 'Le mie scuse, ma il bagno di fuoco del Custode del Concilio non è aperto al pubblico.', 57534),
(270777, 'itIT', '', '$n! Quaggiù! A quanto pare, hai sonnecchiato durante un bel po\' di storia. Non temere, ti ragguaglio io.', 57534),
(270776, 'itIT', '', 'Cerca un membro dello Stormo dei Draghi di Bronzo di nome Cromie. Ti aiuterà a capire cos\'è successo mentre dormivi.', 57534),
(270775, 'itIT', '', 'Neltharion ha pianificato ogni eventualità. Ha addestrato i Dracthyr in molte discipline.', 57534),
(270774, 'itIT', '', 'Neltharion impiegava molti Cacciatori. Nutriva grande interesse per le bestie. Credo che staranno meglio come tuoi famigli invece che come sue cavie.', 57534),
(270773, 'itIT', '', 'Dovevi far parte degli agenti furtivi impiegati da Neltharion. Le sue reti sono cadute, ma c\'è sempre bisogno di Ladri abili.', 57534),
(270772, 'itIT', '', 'I Dracthyr sono nati come esercito e tu avresti combattuto in prima linea. Ora dovrai scegliere una nuova causa per cui lottare.', 57534),
(270771, 'itIT', '', 'La maestria dell\'arcano non si limita allo Stormo dei Draghi Blu. La tua magia dimostrerà quanto i Dracthyr possano affermarsi.', 57534),
(270770, 'itIT', '', 'Hai imparato a evocare esseri e poteri al di là di Azeroth. Anche il nostro creatore era abile nelle arti oscure. Non fallire come lui.', 57534),
(270769, 'itIT', '', 'Il tuo addestramento sacerdotale ti consente di brandire sia la luce che l\'ombra. Il nostro creatore era affine solo a una delle due, ma tu puoi scegliere.', 57534),
(270768, 'itIT', '', 'Sembra però che tu voglia vedere le meraviglie che ti sono state negate. Per tracciare il tuo cammino.', 57534),
(270767, 'itIT', '', 'Non siamo più strumenti di distruzione. Siamo liberi. Molti sono rimasti con le Scaglie d\'Ebano per riconquistare la nostra casa.', 57534),
(270766, 'itIT', '', 'È stato spodestato da coloro che voleva dominare, da persone che ci hanno offerto la loro amicizia.', 57534),
(270762, 'itIT', '', 'Questo mi riempie di speranza. Dobbiamo ancora tessere un\'enorme tela, ma credo che questo primo filo resisterà.', 57534),
(270734, 'itIT', '', 'Cordiali saluti, pestasuolo.\n\nImmagino dovremo collaborare come co-cospiratori. Insolito per i nostri popoli, non trovi?\n\nQuando avrai bisogno di forgiare un nuovo Patto o acquistare... merci \"particolari\", rivolgiti pure a me.', 57534),
(270732, 'itIT', 'Mi spiace, ma sono nel mezzo di una lezione. Non possiamo parlarne dopo?', '', 57534),
(270730, 'itIT', '', 'Cordiali saluti, pestasuolo.\n\nImmagino dovremo collaborare come co-cospiratori. Insolito per i nostri popoli, non trovi?\n\nQuando avrai bisogno di forgiare un nuovo Patto o acquistare... merci \"particolari\", rivolgiti pure a me.', 57534),
(270728, 'itIT', 'Ah, ancora tu.\n\nL\'Esecutore Nizrek ci ha visto qualcosa in te. Non so cosa, ma non metterò in dubbio le opinioni del maestro. Questa volta hai vinto tu.', '', 57534),
(270726, 'itIT', 'Chk-chah! Chah chah chah!\n\nLa madre Tessitrice dice che hai un talento nel fare a pezzi corti e regine. Andremo proprio d\'amore e d\'accordo.', '', 57534),
(270724, 'itIT', 'Il Generale Anub\'azal mi ha parlato di te! Di te e della tua rocambolesca evasione da Forte d\'Assedio!\n\nMi piaci.', '', 57534),
(270723, 'itIT', 'Mi fa piacere vedere che stai bene.\n\nQuesti Nerubiani non finiscono mai. Temo che, almeno per adesso, Dalaran sia perduta.\n\nMa la gente rimane. Dev\'essere questa la nostra priorità.', '', 57534),
(270700, 'itIT', '', 'Ftai ancora cercando Yennu?\n\nForfe sta cercando qualcosa da mangiare. Dai un\'occhiata vicino al tizio groffo delle zuppe a Ifkaara!', 57534),
(270699, 'itIT', 'Avevo la palla quando il mio aquilone si è incastrato in un albero mooolto a nord delle Sponde del Risveglio. Poi fono andato mooolto a sud in un accampamento dei Draghi. Forse l\'ho pesfa lì?', '', 57534),
(270692, 'itIT', 'Hai visto la mia palla? Non ricordo dove l\'ho vista l\'ultima volta!', '', 57534),
(270690, 'itIT', 'Questa zuppa è ZUPPERLATIVA!', '', 57534),
(270680, 'itIT', '', 'Be\'... sai tenere un segreto?\n\nSmanettare col tempo in realtà è davvero divertente. Che succederebbe se due acerrimi rivali non si incontrassero mai? E se invece facessero amicizia per diventare un duo eroico? E se si innamorassero, ma il loro amore venisse ostacolato dalle loro famiglie? Così tante possibilità.\n\nNon dirlo a Eternus! Mi farebbe una ramanzina infinita.', 57534),
(270678, 'itIT', '', '<Maru esamina il messaggio.>\n\nNo no, quefto è troppo difficile perfino per un\'inveftigatrice come me!\n\nMi fa che dovrai chiedere aiuto ad altri amici.', 57534),
(270672, 'itIT', '', 'Il codice è qualcosa che ho creato come aiuto per ricordare momenti storici importanti. Nozdormu dice che ho la memoria di un Murloc, quindi ho creato il codice per ovviare.\n\nLa cosa bella è che posso letteralmente saltare nel codice per vedere la storia come se fossi davvero lì! Posso persino portare con me degli ospiti e apportare modifiche alla linea temporale che esistono solo nel codice. Pensavo che sarebbe stato bello dare alla gente nozioni sulla Guerra di Ahn\'qiraj.\n\nSto riscontrando qualche problema a farlo funzionare come si deve, però. Credo che tutte le disavventure temporali che mi sono capitate abbiano conciato per bene il povero aggeggio.', 57534),
(270666, 'itIT', 'Molto bene. Lascerò queste richieste di riscatto sulla bacheca qui vicino.\n\nSpero tu abbia degli alleati geniali che possano aiutarti in questa ricerca.', '', 57534),
(270664, 'itIT', 'Più impegnative? Be\', c\'è stato un problema con la precedente coordinatrice di eventi. Ha rubato alcuni dei nostri rifornimenti e ha iniziato a lasciare messaggi criptici che alludevano a come potremmo recuperarli.\n\nSono riluttante ad affidare queste informazioni a una persona qualsiasi, non vorrei che finisse per seguire all\'infinito indizi inutili.\n\nMa se insisti, posso condividere questi enigmi ESTREMAMENTE criptici.', '', 57534),
(270662, 'itIT', 'Sarebbe più semplice radunare dei felini che coordinare questo evento.', '', 57534),
(270640, 'itIT', 'Non era sicuramente il modo in cui mi aspettavo di ottenere l\'immortalità. Ma alcuni Draghi di Bronzo sono dei cari amici e passiamo del tempo insieme ogni volta che ricompaio qui.', '', 57534),
(270637, 'itIT', 'Temo di no. Quando ho provato a preservare la mia merce, le cose non sono andate secondo i piani. Ora dopo questi eventi, mi perdo nel tempo per cinque o più anni. Quindi, potrebbe volerci un po\' prima di rivederci.', '', 57534),
(270634, 'itIT', 'Oh, non l\'anno scorso. Sono passati diversi anni. Diciamo solo che un tentativo di preservare la mia merce con la magia temporale ha colpito anche me, e così ora, dopo l\'evento, svanisco. Potrebbero passare cinque anni o dieci, non posso saperlo.', '', 57534),
(270632, 'itIT', 'Salve, sono felice che tu mi abbia preso! Probabilmente ci vorrà un po\' prima del mio ritorno.', '', 57534),
(270631, 'itIT', 'È passato così tanto tempo... Ricordo di aver cercato un posto per dargli una degna sepoltura.\n\nEro al Cuore della Serpe e sono andato a ovest sul mio grifone, ho attraversato un ponte di pietra e poi ho imboccato un sentiero. Poi non ricordo cos\'è successo, volavo tra le lacrime e basta. Ma non dev\'essere stato più lontano di dieci passi a est e quaranta a sud.\n\nRicordo di aver fatto una fiaccolata con i custodi: potevamo scorgere un frutteto in fiore; al bagliore della luce, riuscivamo ancora a sentire la loro presenza tra noi.', '', 57534),
(270629, 'itIT', '<Sully inizia a singhiozzare.>\n\nNASTRO! AZZURRO! Oh, me li hanno portati via troppo presto.', '', 57534),
(270628, 'itIT', 'Questa sì che è una festa! Come stai?', '', 57534),
(270627, 'itIT', '', 'Credo di avere qualcosa del genere. Dai un\'occhiata!', 57534),
(270626, 'itIT', '', 'Come posso aiutarti?', 57534),
(270625, 'itIT', '', 'Voglio bene a Riggo, ma si caccia in tantissimi guai.', 57534),
(270621, 'itIT', '', 'Ne abbiamo di tutti i tipi! Che mascotte volevi?', 57534),
(270619, 'itIT', '', 'Secondo me, l\'ideale sarebbe qualcosa che non ha bisogno di mangiare. Le creature viventi hanno tantissimi bisogni.', 57534),
(270618, 'itIT', 'Riggo vuole qualcosa di carino, come quei grugghedetti procioni che mangiano la spazzatura.', '', 57534),
(270553, 'itIT', '', 'Magari, se le attraversaste saltando, tornerebbero giù? Vi darò una spintarella!', 57534),
(270501, 'itIT', 'Sto pensando che forse mi serve una pistola con più penetrazione, per sparare sott\'acqua.', '', 57534),
(270500, 'itIT', 'Questi pesci non sapranno cosa li ha colpiti.', '', 57534),
(270481, 'itIT', '', 'Ma Dagran avrebbe preferito radere al suolo l\'intero clan piuttosto che accettare la pace.', 57534),
(270453, 'itIT', '<Una barra di ferro impregnata è poggiata sull\'incudine.>', '', 57534),
(270407, 'itIT', '', 'Un\'altra distorsione corretta! Seguitemi e incamminiamoci verso la prossima!', 57534),
(270406, 'itIT', '', 'Ben fatto! Ora qui sembra tutto a posto. Proseguiamo!', 57534),
(270405, 'itIT', '', 'Fiuuu! Il codice è di nuovo stabile, ma l\'energia reindirizzata è ovunque! Potete raccoglierla e riportarla da me?', 57534),
(270404, 'itIT', '', 'Il codice diventa sempre più instabile, e rapidamente! Resistete, ci penso io.', 57534),
(270387, 'itIT', 'Sta\' lontano da mio figlio. E anche dalla gente della Conquista di Velhan!', '', 57534),
(270355, 'itIT', 'Precisamente! Be\'... più o meno.\n\nHo modificato una vecchia banca dati danneggiata che usavamo nelle nostre console per mandare e ricevere informazioni dall\'Archivio. \n\nCaso vuole che questa avesse un nucleo di memoria intatto, quindi l\'ho sincronizzata con un cristallo di protezione, ho modificato i protocolli di risposta e ho superato tutti i comandi crittografati di override che ho potuto... be\', non ti annoierò più con i dettagli tecnici.\n\nIl trasportabot è... be\', un trasportabot. Serve solo a sorreggere l\'emettitore, ma secondo me ci sta comunque bene, non credi?', '', 57534),
(270354, 'itIT', 'Conversazione iniziali... Voglio dire, salve, nuovo risvegliato!\n\nMi chiamo Kromleg, ex macchinista e... be\', effettivamente sono ancora un macchinista. \n\nAlba ha detto che possiamo essere chi vogliamo, ma... a me piaceva chi ero. Il mio lavoro non mi dispiaceva, quindi continuerò a farlo!\n\nNei giorni dopo il mio risveglio, ho notato che molti Terrigeni erano pieni di dubbi e incertezze. Alcuni di loro ricordavano ancora chi erano, mentre altri facevano fatica a collegare i pezzi. La stasi fa brutti scherzi alla matrice cognitiva.\n\nNel precedente ciclo avrei guardato a questi dubbi con sdegno, ma ora mi sento ispirato a dare risposte a queste domande, invece di ignorarle e basta.', '', 57534),
(270353, 'itIT', '', 'È bello poter tornare sottoterra dopo un lungo volo su di un oceano infinito e cieli aperti...\n\nLa prossima volta che la Gran Oratrice mi chiede di farle da ambasciatore fuori dall\'isola, mi assicurerò di organizzare il viaggio senza un Cavalcatempesta che si ostina a voler viaggiare per forza col suo Corvo della Tempesta ad alta velocità!\n\nComunque, sembra che le differenze di opinioni ed efficienza siano intrinsechi del nostro popolo... e della nostra stirpe, come mostra la storia violenta tra i vari clan di questo posto.\n\nMa vedere come vivono insieme oggi mi riempie di speranza per la mia gente a casa. La speranza di un\'unità.\n\nForse riuscirò a convincere Vokmar a prendere il Tram degli Abissi verso Roccavento. Una macchina sotterranea che viaggia ad alta velocità?\n\nMi sembra un eccellente compromesso.', 57534),
(270351, 'itIT', 'Questa è un\'ottima domanda per la classe. Abbiamo tanti ottimi piloti, $p. \n\nMolti studenti conoscono l\'ingegneria a menadito. Non tanti, ma un buon numero tra loro sa anche lavorare bene in squadra.\n\nÈ molto più importante saper bilanciare tutte le abilità necessarie piuttosto che specializzarsi in una cosa sola.', '', 57534),
(270350, 'itIT', 'Vedo gli studenti che mangiano qui ogni giorno. Non posso esprimermi sulle loro capacità di volo, ma faccio caso a chi lascia l\'ultimo pezzo di torta per gli amici e chi sa essere ordinato. \n\nCredo che quelli che si prendono cura degli altri siano capitani migliori di quelli che pensano solo a loro stessi.', '', 57534),
(270349, 'itIT', '', 'I migliori studenti sanno esattamente dove possono arrivare. \n\nDevi superare i tuoi limiti, ma quando si tratta di schiantarsi a terra o contro un\'altra nave, non possono esserci errori. \n\nDetto questo, quelli che restano a distanza di sicurezza e lontani da ogni ostacolo sono quelli più facili da abbattere.', 57534),
(270346, 'itIT', '', 'I migliori studenti sono quelli che capiscono perfettamente quello che la loro nave può o non può fare, e di cosa sono capaci i loro nemici. \n\nAlcuni studenti disdegnano le mie lezioni, ma una buona competenza in ingegneria spesso determina chi torna vivo a casa e chi no.', 57534),
(270345, 'itIT', 'Affermativo. Muradin in persona ci ha fatto fare un giro per la città. Credo di aver incontrato l\'intero clan Barbabronzea.\n\nHo anche fatto una lunga chiacchierata con Falstad. Era molto interessato al mio Corvo della Tempesta quando sono arrivato.\n\n<Vokmar fa un grande sorriso.>\n\nI corvi gli hanno ricordato delle creature che esistevano solo nelle leggende dei Granmartello. Mi ha raccontato delle storie sui grifoni che una volta erano considerati benedetti dalla tempesta stessa. Si pensava fossero araldi del consiglio dei primi Granmartello che li hanno incontrati per la prima volta molte generazioni fa.\n\nTerrigeni e corvi... Nani e grifoni. Comincio a pensare che Olbarig e quel georisanatore Harmot non erano completamente svitati, alla fine.\n\nNon dirgli che te l\'ho detto.', '', 57534),
(270344, 'itIT', 'Sono qui come emissario per conto della forza di difesa di Dornogal. Gli eventi nella storia recente hanno reso evidente che ci servono degli alleati.\n \nL\'Alleanza mi ha colpito col suo valore, con la sua risposta decisiva alla nostra chiamata e il suo coraggioso altruismo davanti alle avversità e alla disperazione.\n\nE dopo aver visto i Granmartello addestrare la nuova generazione di Cavalcatempesta... be\', ho dovuto vedere di persona da dove proviene questa Alleanza.\n\nAveva senso che venissimo a Forgiardente per ovvi motivi... Ma anche perché il giovane Dagran non ci avrebbe mai perdonati se non avessimo visitato la sua casa ancestrale prima di dirigerci a Roccavento.', '', 57534),
(270343, 'itIT', '$n! Le nostre direttive ci fanno incontrare ancora una volta.\n\nMi fa piacere. È sempre bello vederti.\n\nScommetto che hai già saputo che io e Olbarig siamo le nuove Guardie della Tempesta di Lufsela. La sua decisione mi ha sorpreso. Olbarig è sempre stato un\'ottima Guardia della Tempesta, nonostante le sue credenze fossero... poco ortodosse. Io non mi sento alla sua altezza.\n\nMa sembra che entrambi rappresentiamo qualcosa di cui la nuova Guardiatempesta ha bisogno. Olbarig ha visto il potenziale di Lufsela. Anche Baelgrim. La loro fiducia in me mi onora.\n\nVoglio pensare che Baelgrim sarebbe felice di vedere cosa siamo riusciti a fare.', '', 57534),
(270340, 'itIT', 'Sono io. Dopo che Lufsela è diventata Guardiatempesta, il Concilio ha discusso della sicurezza e del nostro bisogno di alleati. Mi sono offerto come emissario per conto delle nostre forze di difesa. \n\nDopo quello che l\'Orda ha fatto per il nostro popolo... Mi piacerebbe capirli di più e scoprire cosa hanno da offrire.\n\nPrima di partire, la Guardiapietra mi ha chiesto di portare con me un emissario Legapietra per rappresentare qualcosa di più dei soli Cavalcatempesta.\n\nNon mi aspettavo esattamente che assegnasse ad Harmot il ruolo di ambasciatore, ma lui era più che impaziente di viaggiare fuori dall\'isola.\n\nCom\'era prevedibile, quando ha sentito parlare delle Viverne dei Venti in città, è corso a cercarli e mi ha lasciato da solo a incontrare il Concilio dell\'Orda.\n\n<Olbarig scuote la testa, ma ha lo sguardo divertito.>', '', 57534),
(270339, 'itIT', 'È corretto.\n\nEro lì quando l\'Orda approdò sulle nostre coste per aiutare il nostro popolo.\n\nEro lì quando l\'Orda iniziò ad addestrare reclute Terrigene che non avevano mai brandito altro che attrezzi da lavoro.\n\nEro lì quando Thrall salì sul Dhar Oztan al fianco di Lufsela.\n\nNon conosceva le nostre usanze o la storia di quella montagna, eppure capiva il suo significato. Conosceva il suo potere. Conosceva i corvi e il legame che lei condivideva con loro.\n\nNon dimenticherò mai nulla di tutto ciò.', '', 57534),
(270338, 'itIT', 'È bello vedere un volto familiare in queste terre, $n.\n\nQuando Lufsela mi ha proposto di stare al suo fianco come Guardiatempesta, io... ho insistito che il ruolo spettasse a Vokmar. Era fedele ai Cavalcatempesta. Era lì per difendere l\'Arteria del Nucleo. Ha combattuto al fianco di Baelgrim mentre io...\n\n<Olbarig scuote la testa, come se volesse allontanare il pensiero.>\n\nAlla fine, ha scelto entrambi. Non dico di meritare questo mantello, ma so che avrà bisogno di aiuto se vorrà ricostituire i Cavalcatempesta e renderli ancora migliori. \n\nOra che il nostro popolo è stato esposto al mondo esterno, nuovi capi come la Guardiatempesta Lufsela contribuiranno a determinare quale sarà il nostro posto in esso.\n\nÈ per questo che sono qui.', '', 57534),
(270337, 'itIT', 'Ah, salve! Hai sentito? Abbiamo raccolto un assortimento di costumi, ciascuno infuso con l\'essenza di un personaggio famoso proveniente da ogni angolo di Azeroth. Che tu sia in cerca del valore di un cavaliere di Roccavento, dell\'astuzia di un Capoguerra o dell\'eleganza di un gran guardaboschi elfico, troverai tutto qui. Cerca le repliche di armi e oggetti sparsi in giro per unirti agli spiriti delle leggende e dare vita alle loro storie.', '', 57534),
(270336, 'itIT', '', 'Edwyn l\'ha ammesso in faccia a $p!', 57534),
(270335, 'itIT', 'Questa è un\'accusa pesante contro un tuo compagno. Hai delle prove? Dei testimoni?', '', 57534),
(270331, 'itIT', '', 'È davvero una benedizione poter camminare di nuovo fra queste sale sacre.\n\nUna volta che la Macchina si sarà risvegliata e avrà accolto abbastanza Terrigeni, sono certa che riusciremo a trasportare tutti quelli che sono ancora bloccati in stasi fuori da queste sale per metterli al sicuro nella Volta di Pietra.', 57534),
(270328, 'itIT', 'Sono una proiezione della manifestazione dell\'archivio interno di una delle banche dati dell\'Archivio.\n\nSecondo le indicazioni di Alba, il Macchinista Kromleg ha ricalibrato i miei protocolli di sicurezza informativi per sovrascrivere la maggior parte delle domande riguardo le designazioni assegnate e il loro scopo.\n\nVuoi informarti sulle designazioni dei Terrigeni?', '', 57534),
(270325, 'itIT', 'Salve, $n. Ti do il benvenuto alla Sala dell\'Interfaccia d\'Analisi.\n\nSono l\'Interfaccia di Alba, un archivio ricalibrato e interattivo progettato per aiutare tutti i Terrigeni risvegliati con qualsiasi domanda possano avere.\n\nCome posso assisterti?', '', 57534),
(270322, 'itIT', 'Procedura di override completata.\n\nLe informazioni archiviate rivelano che i Giurafrey erano ufficialmente incaricati della terraformazione degli acquedotti del Settore AR-938 durante la costruzione iniziale del Sito 0. \n\nIn seguito al completamento della Volta di Pietra, i Giurafrey furono considerati ridondanti per poi venire richiamati nella Sala del Risveglio per entrare in stasi.\n\nA causa delle loro calibrazioni irregolari, i Giurafrey originali non furono mai riassegnati e sono rimasti nella Volta di Pietra fino al recente risveglio della Macchina.\n\nI rapporti successivi riguardo i Giurafrey esistenti sulla superficie nel corso della storia sono da attribuire a un gruppo vulnerabile di Terrigeni esposto a una fuga di dati nella forma di editti non approvati.', '', 57534),
(270321, 'itIT', 'La designazione dei Giurafrey non è formalmente riconosciuta dall\'Archivio della Volta di Pietra.\n\nProcedura di override attivata.\n\nVuoi inserire un\'altra richiesta?', '', 57534),
(270320, 'itIT', 'Dopo il completamento della costruzione della Volta di Pietra, i Sorveglianti divennero custodi e curatori del ciclo vitale dei Terrigeni. \n\nDentro la Sala del Risveglio, i Sorveglianti si occupavano dei Terrigeni riemersi dalla stasi, mentre altri si occupavano di monitorare lo stato di quelli ancora conservati dentro le infinite sale della Volta di Pietra.\n\nMan mano che i Terrigeni si avvicinavano al momento della loro stasi, i Sorveglianti erano incaricati di guidarli e, in alcuni casi, trasportarli alla Volta di Pietra per ricaricarli e dare un nuovo inizio al loro ciclo. \n\nVisto che ci si aspetta che i Terrigeni di ogni designazione aderiscano al Protocollo di Risveglio e di Stasi, i Sorveglianti vengono assegnati a ogni ramo della società dei Terrigeni, inclusi i Cavalcatempesta, i Vincolapietra e gli Oratori delle Macchine.', '', 57534),
(270319, 'itIT', 'Prima che venisse inizializzato l\'Archivio dentro Dornogal, gli Archivisti, a volte chiamati Compilatori, si occupavano di catalogare tutti gli incidenti, le interruzioni ai progetti e gli aggiornamenti generali sul sito di lavoro.\n\nDopo aver completato l\'Archivio dentro Dornogal, agli Archivisti fu assegnato il ruolo di organizzare le informazioni estratte dalle Gemme del Ricordo dei Terrigeni.\n\nGli Archivisti vengono inviati in luoghi chiave per tutto il Settore AR-938 per tenere traccia di tutti gli eventi in corso. Tra questi luoghi c\'erano Dornogal, le Profondità Risonanti, il Trespolo della Tempesta e la Volta di Pietra.\n\nInformazioni aggiuntive sugli Archivisti e l\'Archivio sono state criptate secondo il Protocollo 141-LAK-1R1.', '', 57534),
(270318, 'itIT', 'Gli Oratori delle Macchine vengono assegnati alla manutenzione e l\'espansione delle numerose volte e macchine nascoste all\'interno del Settore AR-938. \n\nPresenti principalmente nelle Profondità Risonanti e per tutta l\'Arteria del Nucleo, gli Oratori delle Macchine usavano la loro potenza e la loro maestria delle macchine antiche per governare sui siti di lavoro sotterranei e sui lavoratori al loro interno. \n\nQuesta designazione includeva i ranghi di Lavratore, Caposquadra, Macchinista, Oratore e Gran Oratore. Il Gran Oratore era al servizio del Concilio di Dornogal e fungeva da suo rappresentante, ed era sotto le sue direttive che tutti gli Oratori delle Macchine e i lavoratori portavano avanti i loro compiti con dedizione assoluta.\n\nGli Oratori delle Macchine si trovano nelle Officine Terrigene, presso l\'Acquedotto, nell\'Officina dei Golem e nella Volta di Pietra.', '', 57534),
(270317, 'itIT', 'In seguito agli eventi riportati nell\'incidente 605-D9, venne istituita la designazione dei Cavalcatempesta nel tentativo di mantenere l\'Arteria del Nucleo e i settori circostanti al sicuro da minacce esterne.\n\nUtilizzando i nuovi elementali della tempesta vincolati (chiamati \"Corvi della Tempesta\"), i Cavalcatempesta sono calibrati con potenza militare e acume strategico per eseguire al meglio la designazione a loro assegnata.\n\nI Cavalcatempesta furono originariamente schierati nella torre di guardia occidentale di Dhar Oztan. Dopo la costruzione del Presidio della Tempesta, i Cavalcatempesta furono assegnati a vari siti sul loro percorso di pattuglia nel Settore AR-938.', '', 57534),
(270316, 'itIT', 'I Pacificatori sono impiegati come unità di protezione a terra per proteggere la città fortezza di Dornogal e per supportare i Cavalcatempesta. Era necessario provvedere al loro addestramento militare, anche se il combattimento e la guerra erano occorrenze rare nei confini della città. \n\nColoro che venivano designati come Pacificatori venivano assegnati a vari settori all\'interno di Dornogal. In rare occasioni, i Pacificatori potevano accompagnare i Cavalcatempesta nell\'Arteria del Nucleo per occuparsi di minacce inaspettate.\n\nOltre alle operazioni dentro Dornogal e per tutta l\'Arteria del Nucleo, alcuni Pacificatori venivano schierati nelle Torri di Guardia di Dhar ai limiti più esterni del Settore AR-938.', '', 57534),
(270315, 'itIT', 'I Legapietra sono la forza lavoro più vasta delle unità dei Terrigeni nei livelli in superficie del Settore AR-938. \n\nIncaricati di creare siti di lavoro all\'interno del settore, i Legapietra guidavano gli sforzi per la costruzione delle strutture più critiche e delle fondamenta di Dornogal e dell\'Arteria del Nucleo.\n\nSotto gli ordini del Guardapietra, la forza lavoro dei Legapietra includeva lavoratori, minatori, costruttori e georisanatori che operavano nelle varie rupi, cave e siti di lavoro sparsi per tutto il settore.', '', 57534),
(270314, 'itIT', 'Le unità operative di supporto si occupavano dei siti di lavoro sotterranei del Settore AR-938. \n\nQuesti lavoratori erano impegnati all\'installazione e alla manutenzione delle tante macchine e volte dentro e sotto le Profondità Risonanti.\n\nSotto gli ordini degli Oratori delle Macchine, queste unità servivano come manovali, lavoratori, fabbri e operatori dei macchinari. \n\nI Macchinisti delle Profondità erano impiegati in siti importanti come Taelloch, Gundargaz, la Stazione di Portaferro e le Officine Terrigene.', '', 57534),
(270312, 'itIT', 'Infusi con la benedizione della forgia, gli Ordinanti servono come mastri forgiatori dei Terrigeni.\n\nInfondendo l\'ordine e la perfezione in ognuna delle loro creazioni, il compito degli Ordinanti era assicurarsi che ogni designazione di Terrigeno fosse equipaggiata per fare il proprio lavoro. Non riuscire a fortificare, armare e proteggere con la loro arte significava provocare un disservizio a tutti i Terrigeni.\n\nPer assicurarsi che tutte le designazioni potessero trarre beneficio dal loro scopo, queste unità venivano scelte dal Custode della Pietra, ricevevano ordini dal Custode del Concilio e poi venivano schierate per servire in vari rami della società dei Terrigeni.\n\nLa loro altruistica devozione nel compiere il proprio dovere spesso risultava in cicli vitali più brevi, il che li portava a passare periodi di tempo prolungato in stasi.\n\nMolti Ordinanti aspettano di essere risvegliati nella Volta di Pietra.', '', 57534),
(270310, 'itIT', 'Gli Artigiani è l\'unità più fondamentale assegnata al Settore AR-938 e i suoi territori. \n\nCon una mente adattiva, occhi acuti e mani ferme come le loro funzioni primarie, gli Artigiani erano responsabili della manifattura giornaliera delle necessità di ogni designazione. \n\nQuesti creatori e manovali lavoravano con ogni genere di materiale, inclusi minerali, tessuti, flora, sottostrato elementale e sostanze magiche.\n\nLe unità degli Artigiani venivano assegnate ai siti di lavoro principali come Dornogal, Gundargaz e i vari depositi dei Legapietra sparsi per tutto il Settore AR-938.', '', 57534),
(270297, 'itIT', 'Su quale designazione vuoi avere più informazioni?', '', 57534),
(270295, 'itIT', 'Diventa un cartografo, diceva. Così potrai vedere il mondo, diceva.\n\nSe qualcuno degli abitanti del luogo dovesse chiedere, potresti farmi un favore? Digli che i Dracthyr sono troppo croccanti. Terribili da mangiare.', '', 57534),
(270294, 'itIT', '', 'Dunque esistono interi regni a questa profondità... E persino imperi oltre i mari!\n\nPrepara le valigie, caro Mercatethel. Resteremo in attività per molto, MOLTO tempo.', 57534),
(270291, 'itIT', '', 'Posso rimandarti nelle Caverne del Tempo quando vuoi. Torna a parlarmi quando avrai finito qui!', 57534),
(270289, 'itIT', '', 'E con questo, è fatta! Mi hai dato una mano enorme. Come sempre! O forse è la prima volta?', 57534),
(270288, 'itIT', 'Persino la sua stessa madre.', '', 57534),
(270282, 'itIT', '', 'Tichondrius? Perché c\'entrano sempre i Signori del Terrore? Aiutatemi a sistemare la distorsione!', 57534),
(270281, 'itIT', '', 'Quello è Kel\'Thuzad! A Silitus non dovrebbero esserci Lich. Se lo sconfiggete, sistemerò questa distorsione!', 57534),
(270280, 'itIT', 'Vuoi una lezione di storia?', 'Vuoi una lezione di storia?', 57534),
(270279, 'itIT', 'Siamo noi a decidere quali storie vanno raccontate.', 'Siamo noi a decidere quali storie vanno raccontate.', 57534),
(270278, 'itIT', 'Cammina piano. Queste vecchie mura hanno le orecchie.', 'Cammina piano. Queste vecchie mura hanno le orecchie.', 57534),
(270277, 'itIT', 'Come possono servirti le Storie?', 'Come possono servirti le Storie?', 57534),
(270276, 'itIT', 'Benvenuta ai Grovigli, strana creatura. Sei qui per migliorare ciò che sei?', 'Benvenuta ai Grovigli, strana creatura. Sei qui per migliorare ciò che sei?', 57534),
(270275, 'itIT', '', 'È Ossirian l\'Indomito, ma il codice sta amplificando il suo caratteraccio! Fate massima attenzione.', 57534),
(270270, 'itIT', '', 'Fai parte dello spettacolo? Allora perché ti comporti come un buffone?', 57534),
(270266, 'itIT', '', 'Torna presto. C\'è sempre qualcosa di nuovo da vedere.', 57534),
(270264, 'itIT', '', 'Lo spettacolo deve continuare!', 57534),
(270263, 'itIT', '', 'Conversazione terminata.', 57534),
(270262, 'itIT', '', 'Sei qui per vedere uno dei nostri spettacoli?', 57534),
(270261, 'itIT', '', 'Vuoi una copia del programma?', 57534),
(270260, 'itIT', '', 'Conversazione iniziata.', 57534),
(270257, 'itIT', '', 'Siamo tornati ai normali rituali dei Qiraji... e non ci attaccano! Sembra tutto a posto.', 57534),
(270255, 'itIT', 'Quando i Nerubiani mi hanno portato via, credevo fosse finita per me...\n\nMa invece di imprigionarmi, mi hanno portato qui, dall\'Esecutore Nizrek.\n\nVoleva soltanto parlare, nessuna violenza. E durante la nostra conversazione, ho appreso che questa città e i suoi abitanti hanno un disperato bisogno d\'aiuto. Questi non sono mostri, $n. Hanno bisogno di noi.\n\nMi rendo conto di quanto possa suonare strano. Ma sto dicendo la verità.', '', 57534),
(270251, 'itIT', '', 'Bleah! I rituali degli Gnoll sono tremendi! Interrompeteli mentre sistemo il codice. Poi potremo andarcene da qui.', 57534),
(270250, 'itIT', '', 'Ho visto abbastanza negromanzia del Flagello per questa vita. Fermiamoli e forse sistemeremo il codice!', 57534),
(270249, 'itIT', '', 'Il codice sta proiettando dei rituali dei Qiraji qui. È tecnicamente corretto, ma non dovrebbero attaccare nessuno. Fermiamoli!', 57534),
(270243, 'itIT', '', 'Quando stringi un patto con il Visir, cerca l\'archeologa più importante di Azj-Kahet, Dor\'garad.\n\nIl suo entusiasmo nello scoprire le storie ormai sepolte del nostro popolo a volte può essere... eccessivo. Ma è questo che la rende la candidata perfetta per la taglia che l\'Esecutore Nizrek ha messo su alcuni artefatti dei Kaheti perduti. Da quello che ho capito, li sta usando come status symbol per assicurarsi l\'alleanza delle varie famiglie reali.\n\nPorta dei vestiti che puoi sporcare. Prevedo infinite battaglie contro il fango e le creature sepolte mentre sarai con Dor\'garad.', 57534),
(270240, 'itIT', '', 'Quando stringi un patto con il Generale, incontrerai uno dei suoi migliori allievi, il Pretendente Kiipka.\n\nIl Pretendente Kiipka è secondo solo al suo mentore in quanto a furia combattiva. Spesso lo si può trovare vicino ai monumenti alla memoria delle battaglie ormai dimenticate, in attesa dei guerrieri che vengono a mostrare il loro rispetto.\n\nTi sfiderà sicuramente a duello. Kiipka è un avversario difficile, ma sa perdere con grazia.', 57534),
(270238, 'itIT', '', 'Quando stringi un patto con la Tessitrice, lavorerai insieme a una delle sue migliori tessispie, Tessitino.\n\nTessitino è un genio nell\'arte di scovare i segreti dei nemici, in particolare quelli che sono anche pieni di Kej. Se dovessi trovare Tessitino, è molto probabile che ti condurrà verso un tesoro nascosto nelle vicinanze.\n\nStai all\'erta. Tessitino non ha problemi a evitare i nemici, ma tu sarai una preda facile se ti allontani da lei.', 57534),
(270233, 'itIT', '', 'Una volta che avrai stretto un Patto, gli agenti del tuo patrono ti forniranno risorse e guadagni aggiuntivi. \n\nQuesti agenti viaggiano per tutti gli angoli della nostra gloriosa Azj-Kahet. Una volta trovati, ti chiederanno di fare loro un favore prima di poterti concedere la tua meritata ricompensa.\n\nSe vuoi sapere subito dove si trovano, posso darti delle mappe... a un costo.', 57534),
(270231, 'itIT', '', 'Una volta che avrai stretto un Patto, avrai accesso agli Informatori.\n\nGli Informatori sono al corrente di tutti gli eventi nella Città dei Fili e oltre. Se ne vedi uno durante i tuoi viaggi per Azj-Kahet, parlaci per ricevere un incarico. Ti aspetta una generosa ricompensa se riuscirai a completare queste Informazioni.\n\nSe vuoi scoprire subito dove si trovano gli Informatori senza doverli cercare personalmente, posso fornirti delle mappe sulla loro posizione attuale... a un prezzo.', 57534),
(270230, 'itIT', 'Ma insieme, possiamo dare loro il meritato riposo.', '', 57534),
(270229, 'itIT', 'Li ho delusi, $p.', '', 57534),
(270227, 'itIT', '', 'Ogni settimana, puoi stringere un Patto con uno dei patroni dei Fili Recisi.\n\nIl patrono del tuo Patto ti fornirà accesso a informazioni esclusive, dei consigli e ti darà anche un potere unico. \n\nUsa questi strumenti per progredire nei nostri obiettivi condivisi e sabotare le forze di Ansurek.', 57534),
(270226, 'itIT', '', 'Ora questo posto sembra seguire la storia. Evviva! Bloccherò la situazione e potremo proseguire!', 57534),
(270225, 'itIT', '', 'Il Flagello a Silitus? Che strano! Occupatevene mentre io mi darò da fare col codice.', 57534),
(270223, 'itIT', 'Salve, prego, prego, abbiamo molto di cui parlare. No, aspetta, non è così. Avremo molto di cui parlare. Dopo. Presto. Sì, presto.\n\n<Bobadormu ti lancia un occhiolino che per te non significa niente.>\n\nSono Bobadormu. Prima non lo sapevi, ma ora lo sai. Sì. Sta andando esattamente come ricordavo.\n\nOra credo che mi chiederai di farti vedere le mie merci. Sempre se la memoria non mi inganna.', '', 57534),
(270222, 'itIT', '', 'Di certo quei brutti Gnoll adorano gli alveari, ma non è quello il loro posto. Io allineerò il codice mentre voi li sconfiggete.', 57534),
(270221, 'itIT', '', 'Sei qua per accarezzare i miei cagnolini, oppure Yllana ti ha dato qualcosa da spendere con le merci mie?\n\nO forse tutt\'e due? Spero tutt\'e due.\n\nVieni, vieni. Nun mordono, e poi se gli gratti la pancia gli piace assai.\n\nNel frattempo, se ci sta qualcosa che attira la tua attenzione nel mio negozio, allora possiamo parlare di affari. Fino ad allora, continua pure ad accarezzare gli animali miei.', 57534),
(270219, 'itIT', '', 'Ho visto mentre parlavi con Yllana. Ho visto anche che ti ha dato un regalo per aiutarti a dare il via ai festeggiamenti.\n\nPuoi spenderlo dove vuoi, ma ti invito a valutare le mie merci prima che tu decida.\n\nCome dicevo, ho delle merci riforgiate all\'ultima moda. Dai pure un\'occhiata. Provatene qualcuna addosso. Vedrai che sapremo soddisfarti.', 57534),
(270218, 'itIT', 'Parlando a livello temporale, questa conversazione verrà registrata per ragioni di controllo qualità.\n\nSe ti trovi in una linea temporale di crisi, allora buone notizie. Posso aiutarti!\n\nSe ci siamo già incontrati, allora felice di rivederti.\n\nSe questa è la prima volta che ci vediamo, ti assicuro che non sarà l\'ultima.\n\nCosa può fare per te il tuo migliore amico... ehm, Bobadormu per te oggi?', '', 57534),
(270216, 'itIT', '', 'Oh, qualcuno di voi dovrebbe interpretare i cattivi. Ho giusto un incantesimo di camuffamento che ho sempre voluto provare!', 57534),
(270215, 'itIT', '', 'Ehi, somigliate proprio a questi eroi. E se provaste ad avvicinarvi? Assumete una posa eroica!', 57534),
(270210, 'itIT', '', 'Di\' a Yamas ciò che vuoi sapere...', 57534),
(270208, 'itIT', '', 'Ti do un grande benvenuto, pestasuolo.\n\nCosa può fare Yamas per te oggi?', 57534),
(270207, 'itIT', 'Il tunnel qui ti porterà a Dornogal.\n\nQuesti passaggi sono il risultato del tuo lavoro per aprire i rapporti con i Kaheti. Ben fatto.\n\nDetto questo... non mi sono ancora abituato alla Tessitrice e al suo popolo. Forse succederà col tempo, ma per ora cerco di evitare i turni di guardia il più possibile.', 'Il tunnel qui ti porterà a Dornogal.\n\nQuesti passaggi sono il risultato del tuo lavoro per aprire i rapporti con i Kaheti. Ben fatto.\n\nDetto questo... non mi sono ancora abituata alla Tessitrice e al suo popolo. Forse succederà col tempo, ma per ora cerco di evitare i turni di guardia il più possibile.', 57534),
(270200, 'itIT', '', '[risata malvagia]', 57534),
(270199, 'itIT', 'L\'Evoluzionista del Trasformatorio esamina con sospetto i tuoi feromoni, ma si tira indietro.', 'L\'Evoluzionista del Trasformatorio esamina con sospetto i tuoi feromoni, ma si tira indietro e ti lascia passare.', 57534),
(270196, 'itIT', 'Salve, $gamico:amica; del Concilio.\n\nSono interessato a tutti i Cristalli di Risonanza che hai trovato durante le tue avventure.\n\n<Lo sguardo dell\'Ispettore Balwurz è freddo e duro, perfino per un Terrigeno.>\n\nDavvero molto interessato.', '', 57534),
(270194, 'itIT', '', 'Parla di noi Fili Recisi a tutti i tuoi compagni. Magari verranno da noi a cercare sicurezza e riposo se ce ne fosse la necessità.', 57534),
(270190, 'itIT', '', 'Attivate i Gettaombre. Li useremo per parlare con Anub\'azal e Nizrek.', 57534),
(270189, 'itIT', 'La prima regola quando si hanno dei Nerubiani come clienti: non sembrare deliziosi. O almeno, non sembrare più delizioso della persona accanto a te.\n\nNon c\'entra niente, ma mi tieni un attimo queste spezie?', 'La prima regola quando si hanno dei Nerubiani come clienti: non sembrare deliziosi. O almeno, non sembrare più delizioso della persona accanto a te.\n\nNon c\'entra niente, ma mi tieni un attimo queste spezie?', 57534),
(270188, 'itIT', 'Non prenderla male, ma un po\' di tempo fa molti miei amici alla Valle di Rovotorto sono stati uccisi da uno che assomigliava proprio a te.', 'Non prenderla male, ma un po\' di tempo fa molti miei amici alla Valle di Rovotorto sono stati uccisi da una che assomigliava proprio a te.', 57534),
(270187, 'itIT', 'Prima di iniziare qualsiasi cosa con sua altezza della ragnatela, sappi soltanto che è lei che ci paga i conti. Vedi di non farmi interrompere la mia pausa.', 'Prima di iniziare qualsiasi cosa con sua altezza della ragnatela lassù, sappi soltanto che è lei che ci paga i conti. Vedi di non farmi lavorare.', 57534),
(270186, 'itIT', 'Non capita spesso che mi paghino all\'ora per stare seduto ad aspettare di pestare qualcuno.', 'Non capita spesso che mi paghino all\'ora per stare seduto ad aspettare di pestare qualcuno.', 57534),
(270184, 'itIT', '', '<L\'Arborista Giurafrey sorride amabilmente.>\n\nIo sono Svincolata. E sono libera di seguire qualsiasi sentiero desideri.\n\nQuesto è sempre stato il mio sentiero.\n\nIl fiume della vita ti porta lontano, viandante.', 57534),
(270183, 'itIT', '', 'Ti do il benvenuto, abitante della superficie.\n\nSua altezza della ragnatela ci ha avvertiti che avremmo potuto trovare altri della tua specie da queste parti. Ti proporrei qualche affare, ma tutta la mia merce è esclusiva per sua altezza.\n\nVedi, stiamo facendo una buona cosa qui. Lei ha bisogno degli ingredienti che coltiviamo per i suoi piani, e noi abbiamo bisogno degli odori che solo lei può darci per tenere lontani i membri meno gradevoli della società dei ragni e i loro scagnozzi brulicanti. \n\nTi auguro buona fortuna con i tuoi affari coi ragni. Possono essere degli ottimi clienti, ma anche una grande spina nel fianco se non gli vai a genio.', 57534),
(270180, 'itIT', '<La luce quasi spenta dell\'occhio dell\'ombra ti penetra l\'anima.>', '<La luce quasi spenta dell\'occhio dell\'ombra ti penetra l\'anima.>', 57534),
(270178, 'itIT', '', 'Oh! Salve, viandante.\n\nHai una domanda?', 57534),
(270175, 'itIT', '<Questo Giurafrey emette un suono stridulo, non dissimile da un ronzio, mentre pota la vegetazione intorno a sé con le sue mani ruvide>.', '', 57534),
(270172, 'itIT', 'Gli Ossequenti si definiscono la base e le fondamenta.\n\nNoi invece siamo le radici profonde e il letto mutevole del fiume.', '', 57534),
(270169, 'itIT', 'Vai a infastidire qualcun altro. Non voglio che mi vedano parlare con te.', 'Vai a infastidire qualcun altro. Non voglio che mi vedano parlare con te.', 57534),
(270168, 'itIT', 'Mi domando... quali fiori erano i preferiti della nostra Custode?', '', 57534),
(270167, 'itIT', '<Questo Arborista sta sussurrando dolcemente alle piante che lo circondano.>\n\nSì, figlia della natura selvaggia. Cresci. Cresci come ci ordina la nostra Custode.', '', 57534),
(270166, 'itIT', '', 'Sono felice di nutrire gli alberi e i fiori qui a Dornogal.\n\nA prescindere dalle circostanze.', 57534),
(270165, 'itIT', 'Non posso interfacciarmi con te in questo momento.\n\nLa mia attenzione deve essere interamente rivolta alla comunione con questo albero per risolvere la sua mancata crescita.', '', 57534),
(270164, 'itIT', '', 'Non temere. Sto facendo come Merrix ha chiesto.', 57534),
(270162, 'itIT', 'Non hai sentito i terremoti ultimamente? Credo siano quei dannati Nerubiani e i loro scavi verso la superficie. O forse ci sono davvero dei terremoti sotto di noi...\n\nA ogni modo, presumo che il gigante stesse dormendo sulla rupe e i terremoti l\'hanno staccato dalla cascata.\n\nNon chiedermi come abbia fatto a non svegliarsi! Immagino abbia il sonno davvero pesante.', '', 57534),
(270150, 'itIT', '', 'Ogni forma di vita ha bisogno di cure.', 57534),
(270147, 'itIT', '', 'L\'arguzia non ti manca, vedo. Sono una Giurafrey.\n\nCi permettono di entrare a Dornogal solo per curare gli animali e le piante che vi si trovano.\n\nNoi seguiamo i titani ma aderiamo a un ulteriore editto non riconosciuto dagli Ossequenti, nostri simili.\n\nÈ un antico editto che ci è stato rivelato da..\n\n<L\'Arborista Giurafrey si guarda intorno furtivamente.>\n\nDevo porre fine alla nostra conversazione. Non ci è consentito parlare di questo a Dornogal. Ma se vedi qualcuno di noi sui colli...', 57534),
(270141, 'itIT', 'Perché sei così impassibile? Non hai mai visto delle creature così interessanti?\n\nSecondo alcuni rapporti che precedono l\'Archivio, esistevano molti giganti di montagna in queste terre. Col passare dei nostri cicli, questi giganti venivano menzionati sempre di meno, e un po\' alla volta sono diventati solo parole nel nostro Archivio.\n\nAlcuni rapporti degli Archivisti di Dornogal indicano che sono stati giganti come questo ad aiutarli a creare la regione in cui è sorta la città, anche se nessuno è in grado di confermarlo. Nessuno conserva ancora ricordi così distanti.', '', 57534),
(270139, 'itIT', 'Sulla pergamena è scritto un glifo cabalistico in nerubiano.', 'Sulla pergamena è scritto un glifo cabalistico in nerubiano.', 57534),
(270138, 'itIT', 'Be\', non sono più un thraegar... se mai lo sono stato. Sono ancora l\'Oratore di Azeroth?\n\nImmagino che non ci sia modo di saperlo finché non mi contatterà di nuovo.\n\nSe penso all\'energia che Azeroth aveva racchiuso in me e a tutto il tempo che mi ci è voluto per venire qui e usarla...\n\nPensare che liberare e guarire questa gente, e me stesso, era ciò che avrei dovuto fare fin dall\'inizio...\n\nMi serve un momento per pensare e riprendere fiato. \n\nE sentire il vento soffiarmi tra la barba!', '', 57534),
(270136, 'itIT', 'Che ficcanaso. Non ho nulla da dirti.', 'Che ficcanaso. Non ho nulla da dirti.', 57534),
(270135, 'itIT', '<Anduin guarda con curiosità la sua mano.> \n\nIo... non so cosa pensare. \n\nDevo solo cercare di respirare?\n\nCredevo di dover trovare qualche risposta... qualche grande rivelazione su me stesso per poter trovare di nuovo la pace.\n\nForse non è possibile. Forse l\'unica risposta è lasciare perdere. \n\nO forse la grande rivelazione è che... non c\'è niente di male ad andare avanti.\n\nMa come posso andare avanti dopo tutto il dolore che ho causato?', '', 57534),
(270134, 'itIT', '', 'Ero forse l\'unica Ossequente con una direttiva che prevedeva la recitazione.\n\nHuberta è una mia vecchia amica. Quando mi indicò che il Proscenium sarebbe stato utilizzato per le rappresentazioni teatrali, accettai gentilmente di unirmi alla troupe come attrice protagonista.', 57534),
(270132, 'itIT', '', 'Salve. \n\nHai ricevuto fiori in esubero? Sono sempre felice di accontentarti.', 57534),
(270130, 'itIT', 'Huberta metteva in scena piccoli spettacoli a Borgo Frey! Con un solo Terrigeno in scena. Erano molto avvincenti.\n\nQuando lei e Kralden hanno iniziato a espandersi, ho deciso di unirmi a loro.', '', 57534),
(270129, 'itIT', 'Dobbiamo uscire da qui. Dev\'esserci un modo. \n\nMi sembra di soffocare. Stiamo finendo l\'aria? \n\nDobbiamo... dobbiamo andarcene.', '', 57534),
(270128, 'itIT', 'Una volta mi sarei chiesto cos\'ha spinto questi cultisti ad abbracciare l\'ombra e prendersela con i loro simili.\n\nOra... \n\n<Anduin sospira.>\n\nSo fin troppo bene come ci si sente quando l\'oscurità ti afferra da dentro. Infila gli artigli nella tua carne e non ti lascia andare. Distorce tutto con le sue menzogne. \n\nHo avuto la fortuna di avere persone intorno a me che mi hanno salvato prima che mi strappasse anche gli occhi e mi facesse diventare cieco davanti alla sofferenza degli altri.\n\nNon tutti hanno avuto la stessa fortuna.', '', 57534),
(270127, 'itIT', 'Non vedo l\'ora di assistere alla nostra prossima rappresentazione, $p! Hai un vero talento per il dramma.\n\nUn bel climax straziante è la parte migliore di uno spettacolo, non credi?', '', 57534),
(270125, 'itIT', 'Xal\'atath dev\'essere fermata.\n\nSperiamo che questi Terrigeni non ci mettano i bastoni tra le ruote.', '', 57534),
(270124, 'itIT', 'Un tempo tenevo i registri di Borgo Frey. Pensavo di poter essere d\'aiuto qui, ma Huberta e Kralden mi hanno detto di cimentarmi sulle scene. \n\nCredo che mi piaccia di più, anche se non sono molto bravo. È divertente raccontare storie con i miei amici.', '', 57534),
(270120, 'itIT', '<Anduin fissa le fiamme oscure con un\'espressione assorta.> \n\nIo avrei dovuto...\n\n<Scuote la testa.> \n\nRiesco quasi a sentirne il sapore. La Luce... è proprio lì, a portata di mano.\n\nMa... non riesco a raggiungerla.', '', 57534),
(270119, 'itIT', 'Ciao. Hai fatto faville l\'ultima volta. È sempre stata questa la tua direttiva? \n\nSono ancora alle prime armi, anche dopo cento anni. Spero di non rallentarti.', '', 57534),
(270118, 'itIT', '', 'Sono felice che i Terrigeni ci abbiano trovati in tempo.\n\nMa temo che ci servirà ben più del loro aiuto se vogliamo organizzare un\'offensiva contro i Nerubiani e Xal\'atath.', 57534),
(270117, 'itIT', '', 'Ovviamente rimaniamo bloccati qui e la prima cosa che fa mio padre è ferirsi.\n\nDeve sempre fare l\'eroe.\n\nSpero che Dagran non si faccia venire qualche strana idea.\n\nMa chi voglio prendere in giro? Essere trascinata via e restare bloccata in un luogo remoto? Incontrare un nano scuro e alto appena arrivata?\n\nPosso cavarmela in questa situazione.\n\nFatti sotto, Khaz Algar.', 57534),
(270116, 'itIT', '', 'È una cosa nuova ed eccitante! Voglio dire, sei qui! E posso provare così tanti costumi. \n\nLa mia direttiva era di mantenere alto il morale a Dornogal. Prima organizzavo feste con degustazione di perle. Ma questo va altrettanto bene, giusto?', 57534),
(270114, 'itIT', 'Morte... e distruzione. Ovunque \n\nOgni volta, perdo altri amici. Altri familiari. \n\nKhadgar. Alleria...\n\nFinirà con Xal\'atath? Quando finirà?', '', 57534),
(270112, 'itIT', '', 'Sei $gpronto:pronta; per lo spettacolo, $p? Sarà un successo clamoroso!\n\nSpero di riuscire a trattenermi quando Burkhalt narrerà gli ologrammi.', 57534),
(270111, 'itIT', 'Ho tutti i miei libri e le mie pergamene, ma...\n\nGuarda quello che abbiamo perso. Tutta la conoscenza conservata a Dalaran. Tutte le persone...\n\nSpero che i Terrigeni riescano a trovare altri sopravvissuti.\n\nE magari il fatto che il nonno sia un \"thraegar\" ci aiuterà a farceli amici.\n\nForse anche loro hanno una biblioteca. Non sarebbe fantastico?', '', 57534),
(270109, 'itIT', 'Su una rastrelliera giacciono sospese varie pozioni e misture.', 'Su una rastrelliera giacciono sospese varie pozioni e misture.', 57534),
(270108, 'itIT', 'Non mi serve aiuto, $n.\n\n<Magni sussulta per il dolore. è evidentemente sfinito e ferito.>\n\nMagari... giusto una spintarella per salire su un grifone, o qualsiasi cosa siano questi uccelli...', '', 57534),
(270102, 'itIT', 'Ah, ci sono un paio di membri della tribù a sud-est che sapranno dirti di più.', '', 57534),
(270098, 'itIT', 'L\'Esecutore Nizrek ha insistito molto perché la nostra missione sia il più segreta possibile.', 'L\'Esecutore Nizrek ha insistito molto perché la nostra missione sia il più segreta possibile.', 57534),
(270097, 'itIT', '', 'È difficile da spiegare a $guno straniero:una straniera; ma cercherò di essere breve. \n\nTutti i Terrigeni sono codificati da un Guardiano e, per estensione, dai titani con una funzione primaria che devono svolgere. \n\nNell\'Isola di Dorn, ogni Ossequente garantisce uno di questi due Editti: \"Costruisci e mantieni le strutture e gli impianti di Dornogal e dell\'Arteria del Nucleo\" oppure \"Proteggi la città di Dornogal e l\'Arteria del Nucleo\".\n\nNelle Profondità Risonanti, la terra sotto l\'Isola di Dorn, c\'è un terzo Editto: \"Mantieni le macchine dei titani sopra e lungo l\'Arteria del Nucleo\". Questo è il mio Editto e quello del mio popolo. \n\nTutti i Terrigeni guardano a noi per salvarci dalla stasi e dallo spegnimento finale riparando la Volta di Pietra e la Macchina del Risveglio. Finora non abbiamo avuto successo.', 57534),
(270090, 'itIT', 'Il Generale Anub\'azal è la dimostrazione di come dovrebbe essere il guerriero ideale di Azj-Kahet. Lo seguo con la speranza di poter imparare da lui. Col tempo mi guadagnerò anch\'io un posto tra i caduti, onorato per sempre da un monumento di pietra.', '', 57534),
(270088, 'itIT', 'Salve, combattente. D\'ora in poi mi occuperò di assistere il Generale al posto di Anub\'okki.', '', 57534),
(270085, 'itIT', '', '<Faerin si acciglia.> \n\nCreature orribili, violente e... maleodoranti. Ci hanno tormentato sin da quando siamo arrivati nei Precipizi Sacri.\n\nAbbiamo cercato di capire il livello della loro intelligenza, nella speranza di poter parlare di pace. Ma ogni studioso che abbiamo inviato a cercare di comunicare con loro è stato ucciso. Ucciso e mangiato, di solito. \n\nE non solo, sembra padroneggino una sorta di necromanzia. Come se mangiarci non bastasse, a volte decidono anche di disonorare la memoria dei nostri alleati riportandoli in vita per costringerci ad affrontarli. Non so perché scelgano di mangiare alcuni di noi e usarne altri in battaglia. Magari riportano in vita i più forti e mangiano quelli più morbidi? \n\nSolo pensare a queste cose mi fa venire il voltastomaco.', 57534),
(270083, 'itIT', '', '<Abohba sembra visibilmente scossa.>\n\nÈ successo tutto così in fretta...\n\nIl momento prima stavamo mangiando e un attimo dopo sentiamo delle urla provenire dalle strade.\n\nUda è stata la prima a uscire per aiutare a respingere quelle... cose. È riuscita a ucciderne due prima che la prendessero e...\n\n<Abohba scuote la testa mentre si interrompe e il suo sguardo si posa sui lupi davanti a lei.>', 57534),
(270081, 'itIT', 'Comodo.', 'Comodo.', 57534),
(270077, 'itIT', 'Alcuni dei nostri alleati Terrigeni hanno evocato un portale che conduce a Dornogal, la loro capitale che sorge sull\'Isola di Dorn a Khaz Algar. Puoi trovarlo nel livello superiore della Torre della Magia.', 'Alcuni dei nostri alleati Terrigeni hanno evocato un portale che conduce a Dornogal, la loro capitale che sorge sull\'Isola di Dorn a Khaz Algar. Puoi trovarlo nel livello superiore della Torre della Magia.', 57534),
(270074, 'itIT', '', 'Vorrei solo che Dagran fosse qui per vederlo. I Ferroscuro liberi. Il leader che sta diventando nostro figlio. Ne sarebbe fiero.', 57534);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(270073, 'itIT', '', 'Dagran Thaurissan II. Ha il nome di suo padre. Non volevo che la gente dimenticasse le sue radici. Le cose buone... e quelle brutte. Tutto quanto.', 57534),
(270072, 'itIT', '', 'E il piccolo Dagran. Mio figlio. Il simbolo della nostra unità e il retaggio che mio marito non ha potuto vedere.', 57534),
(270070, 'itIT', '', 'I Ferroscuro, i Barbabronzea e i Granmartello. Un unico popolo, di nuovo.', 57534),
(270069, 'itIT', '', 'Nella sconfitta, i nostri nemici ci liberarono e trovammo un posto nell\'Alleanza. Da pari a pari.', 57534),
(270068, 'itIT', '', 'All\'inizio, i Ferroscuro erano solo strumenti per la mia ambizione. Poi l\'amore mi ha accecata.', 57534),
(270067, 'itIT', '', 'Ero accecata dall\'odio per mio padre. Dal mio orgoglio. Volevo di più.', 57534),
(270066, 'itIT', '', 'Non difenderò le azioni dei Ferroscuro. Né le mie. Il sangue sulle nostre mani.', 57534),
(270065, 'itIT', 'Alcuni dei nostri alleati Terrigeni hanno evocato un portale che conduce a Dornogal, la loro capitale che sorge sull\'Isola di Dorn a Khaz Algar. Puoi trovarlo nel livello inferiore della nostra stanza dei portali.', 'Alcuni dei nostri alleati Terrigeni hanno evocato un portale che conduce a Dornogal, la loro capitale che sorge sull\'Isola di Dorn a Khaz Algar. Puoi trovarlo nel livello inferiore della nostra stanza dei portali.', 57534),
(270064, 'itIT', '', 'Ma so come finisce questa storia, ormai. E non ce la faccio a guardare.', 57534),
(270063, 'itIT', '', 'Volevo che Dagran fosse un imperatore, non un servo. Volevo che regnassimo insieme.', 57534),
(270062, 'itIT', '', 'Dagran non poteva fare altro per evitare che il suo impero crollasse dall\'interno.', 57534),
(270061, 'itIT', '', 'Non voleva accettarlo, ma io lo sapevo. Era Ragnaros a guidare davvero i Ferroscuro.', 57534),
(270059, 'itIT', '', 'Era usato per radunare l\'esercito dei Ferroscuro. Come gran parte dei Sotterranei di Roccianera.', 57534),
(270058, 'itIT', '', 'Il Liceo. Una parodia del suo nome. Qui non si tenevano grandi dibattiti.', 57534),
(270056, 'itIT', '', 'Condannando i Ferroscuro alla servitù.', 57534),
(270055, 'itIT', '', 'Il Thane Occultista Thaurissan evocò Ragnaros per la prima volta durante la Guerra dei Tre Martelli.', 57534),
(270054, 'itIT', '', 'Altri... per lealtà verso la stirpe dei Thaurissan.', 57534),
(270053, 'itIT', '', 'Certi Nani hanno servito volentieri il Signore del Fuoco per alimentare le loro ambizioni. La loro crudeltà.', 57534),
(270052, 'itIT', '<Urla di battaglia affaticate.>', '', 57534),
(270051, 'itIT', 'Stai cercando Auralia? È vicino alla fontana nella piazza principale.', 'Stai cercando Auralia? È vicino alla fontana nella piazza principale.', 57534),
(270050, 'itIT', '', 'Come sorrideva Dagran...', 57534),
(270049, 'itIT', '', 'La tesoreria è stata svuotata dalle infinite guerre dei Ferroscuro. Ho offerto persino i miei gioielli per la causa.', 57534),
(270048, 'itIT', '', 'Spesso mi sono chiesta, nonostante tutto quell\'odio verso gli altri... se non odiassero soprattutto se stessi.', 57534),
(270047, 'itIT', '', 'Persino le taverne erano piene di risse e urla. E non di quelle divertenti, sia chiaro.', 57534),
(270046, 'itIT', '', 'Credevo di poterli cambiare. Di poter cambiare... lui.', 57534),
(270043, 'itIT', '', 'Di norma, c\'è una sola Guardia della Tempesta, ma ho deciso di cambiare le cose.\n\nPrima del diverbio, Olbarig prestava servizio come Guardia della Tempesta di Baelgrim. A sostituirlo è stato Gorren, ma la sua caduta nella Corveria... Be\', ho pensato che fosse ora di far tornare Olbarig.\n\nMi farà da braccio destro per guidare le nuove generazioni di Cavalcatempesta e aiutarli a comprendere il nostro legame con i Corvi della Tempesta.\n\nLa mia seconda Guardia della Tempesta è Vokmar, che rappresenta davvero ciò che Baelgrim e i Cavalcatempesta incarnavano.\n\nVokmar è uno degli ultimi Cavalcatempesta originali e, sebbene la sua filosofia sia diversa da quella di Olbarig, credo che fungerà da simbolo del bene che rappresentavano i Cavalcatempesta originali.\n\nCon l\'aiuto di entrambi, so che i Cavalcatempesta potranno essere molto di più.', 57534),
(270042, 'itIT', '', 'Legge marziale. Un popolo in guerra. I Ferroscuro si sentivano maledetti. La violenza era la loro unica opzione.', 57534),
(270041, 'itIT', '', 'Volevo che le loro voci venissero ascoltate. Volevo dar loro forza, influenza... Sono stata una sciocca.', 57534),
(270040, 'itIT', '', 'Non tutti i Ferroscuro di Forgiascura erano d\'accordo nel servire Ragnaros.', 57534),
(270039, 'itIT', '', 'Erano tenuti in riga da Ragnaros, che ci considerava solo pedine. Niente più di questo.', 57534),
(270038, 'itIT', '', 'Ai signori delle Terre del Fuoco non importava dei Ferroscuro. Tormentavano chiunque potessero.', 57534),
(270037, 'itIT', '', 'E pensare che lui sedeva qui, ciclo dopo ciclo... Ancora non so se sono degna di questo trono.\n\nMa so che farò il possibile per onorare l\'eredità di Baelgrim e guidare una nuova generazione di Cavalcatempesta verso un futuro più radioso.\n\nCon il mondo intero alla nostra portata e nuovi alleati sulla nostra soglia, credo sia ora di riflettere su cosa significhi il nostro posto nel mondo.\n\nForse, man mano che altri Cavalcatempesta si risvegliano nella Volta di Pietra e nuove reclute si uniscono a noi, possiamo espandere la nostra presenza oltre l\'Isola di Dorn.\n\nFino ad allora, farò affidamento sulle mie nuove Guardie della Tempesta per guidare il nostro cammino.', 57534),
(270036, 'itIT', '', 'Anche dopo la morte del suo guardiano, la porta resta chiusa...', 57534),
(270035, 'itIT', '', 'Forgiascura celava molti segreti, persino a me, la Regina Reggente. Per esempio, cosa si trova alla fine della Strada dei Ferroscuro...', 57534),
(270034, 'itIT', '', 'Quando la Caldera Magmatica cadde, la crudeltà dei Ferroscuro aumentò, e accolsero il Martello del Crepuscolo.', 57534),
(270033, 'itIT', '', 'Quando non c\'erano abbastanza operai, ci mandavano altri Ferroscuro...', 57534),
(270032, 'itIT', '', 'I Ferroscuro ridussero in schiavitù molte creature per lavorare in miniera e fomentare lo sforzo bellico.', 57534),
(270031, 'itIT', '', 'Avete rovinato la mia vita e quella del mio nascituro!', 57534),
(270030, 'itIT', '', 'Thaurissan era un Nano valoroso e onorevole.', 57534),
(270029, 'itIT', '', 'No!', 57534),
(270028, 'itIT', '', 'Amore mio, ti ho deluso.', 57534),
(270027, 'itIT', 'Risparmiate mio figlio...', '', 57534),
(270026, 'itIT', 'Vi aspettavate davvero di sconfiggere un Nano maestoso come me?', '', 57534),
(270025, 'itIT', 'Respingete gli invasori!', '', 57534),
(270024, 'itIT', 'Guardie! Al mio fianco.', '', 57534),
(270023, 'itIT', 'Ammirate la potenza del mio impero.', '', 57534),
(270022, 'itIT', 'La vostra insolenza verrà punita.', '', 57534),
(270021, 'itIT', 'Fuggite dinanzi a me.', '', 57534),
(270020, 'itIT', 'Io non fallirò.', '', 57534),
(270019, 'itIT', 'Il terreno vi tradisce.', '', 57534),
(270018, 'itIT', 'Il fuoco sottostante si piega al mio volere!', '', 57534),
(270017, 'itIT', 'IO SONO L\'IMPERATORE!', '', 57534),
(270016, 'itIT', 'Ah! Inutile.', '', 57534),
(270015, 'itIT', 'Non sei nulla.', '', 57534),
(270014, 'itIT', 'I vostri sforzi sono del tutto inutili, sciocchi! Non riuscirete mai a sconfiggermi!', '', 57534),
(270013, 'itIT', 'Preparatevi a morire per mano del più potente servitore di Ragnaros.', '', 57534),
(270012, 'itIT', 'Grazie per aver eliminato quegli stolti senatori.', '', 57534),
(270011, 'itIT', 'Risorgerò per volontà di Ragnaros!', '', 57534),
(270010, 'itIT', 'Siete insetti dinanzi al Signore del Fuoco.', '', 57534),
(270009, 'itIT', 'Date fuoco al mondo intero!', '', 57534),
(270008, 'itIT', 'Servite il vostro padrone, figli della Fiamma Vivente!', '', 57534),
(270007, 'itIT', 'Preparatevi a bruciare!', '', 57534),
(270006, 'itIT', 'Non potete nascondervi da noi.', '', 57534),
(270005, 'itIT', 'Guerriero delle Terre del Fuoco! A me!', '', 57534),
(270004, 'itIT', 'Al mio fianco, Calcafiamme.', '', 57534),
(270003, 'itIT', 'Polvere al vento.', '', 57534),
(270002, 'itIT', 'Nient\'altro che cenere.', '', 57534),
(270001, 'itIT', 'Il vostro regno di terrore finirà ora! Preparatevi a morire, mortali!', '', 57534),
(270000, 'itIT', 'L\'Imperatore Thaurissan ha del lavoro da svolgere. Non interferirete.', '', 57534),
(269999, 'itIT', 'Mio signore, io... ti ho deluso...', '', 57534),
(269998, 'itIT', 'Mi aspettavo di meglio.', '', 57534),
(269997, 'itIT', 'Tutte le forze rimaste, all\'attacco!', '', 57534),
(269996, 'itIT', 'Mantenete la posizione!', '', 57534),
(269995, 'itIT', 'Annientate gli invasori!', '', 57534),
(269994, 'itIT', 'In marcia! Avanti!', '', 57534),
(269993, 'itIT', 'Manovra di fiancheggiamento!', '', 57534),
(269992, 'itIT', 'Divide et impera.', '', 57534),
(269991, 'itIT', 'Ti ho sopraffatto.', '', 57534),
(269990, 'itIT', 'Ferroscuro, difendete il vostro imperatore!', '', 57534),
(269989, 'itIT', 'Schiacciate questi invasori con la potenza bellica dei Ferroscuro.', '', 57534),
(269988, 'itIT', 'La mia guardia... è finita...', '', 57534),
(269987, 'itIT', 'Voi non potete passare.', '', 57534),
(269986, 'itIT', 'Spezzatevi.', '', 57534),
(269985, 'itIT', 'Frantumatevi.', '', 57534),
(269984, 'itIT', 'Non potete nascondervi.', '', 57534),
(269983, 'itIT', 'Le fiamme vi avvolgono.', '', 57534),
(269982, 'itIT', 'Vi seppellirò.', '', 57534),
(269981, 'itIT', 'Il mondo si sgretola.', '', 57534),
(269980, 'itIT', 'Immeritevole.', '', 57534),
(269979, 'itIT', 'Soffice e molle.', '', 57534),
(269978, 'itIT', 'La via non dev\'essere aperta.', '', 57534),
(269977, 'itIT', 'Questa conoscenza non è per te. È il volere del Signore del Fuoco.', '', 57534),
(269976, 'itIT', '', 'Fatti forza! Dobbiamo salvare i cittadini e uccidere questi mostri!', 57534),
(269975, 'itIT', 'Non ne voglio parlare.', '', 57534),
(269974, 'itIT', 'Fatto... a... pezzi...', '', 57534),
(269973, 'itIT', 'Cala di nuovo il silenzio.', '', 57534),
(269972, 'itIT', '', 'La distruzione di Dalaran ha portato via la nostra casa, ma coloro che camminano nell\'ombra trovano sempre un modo. Ci adattiamo e resistiamo, pronti a colpire al momento giusto.', 57534),
(269971, 'itIT', 'Sotto pressione.', '', 57534),
(269970, 'itIT', 'Annegate in una marea di magma.', '', 57534),
(269969, 'itIT', 'Io sono molteplice.', '', 57534),
(269968, 'itIT', 'Sorgete, figli miei.', '', 57534),
(269967, 'itIT', 'Magma liquido e rovente.', '', 57534),
(269966, 'itIT', 'Ammirate il potere della creazione.', '', 57534),
(269965, 'itIT', 'Così fragile.', '', 57534),
(269964, 'itIT', 'Folle mortale.', '', 57534),
(269963, 'itIT', 'Arriva una nuova sfida.', '', 57534),
(269962, 'itIT', 'Chi disturba il mio isolamento?', '', 57534),
(269961, 'itIT', 'L\'oblio attende...', '', 57534),
(269960, 'itIT', 'Il vostro destino è segnato.', '', 57534),
(269959, 'itIT', 'Una fine tragica.', '', 57534),
(269958, 'itIT', 'Unisciti a noi nella morte.', '', 57534),
(269957, 'itIT', 'Avete sfidato i Sette e per questo ora morirete!', '', 57534),
(269956, 'itIT', 'Non potete passare. Non appartenete alla nostra razza e non avete lanciato la sfida.', '', 57534),
(269955, 'itIT', 'La morte della nostra carne ha segnato la nascita del nostro spirito e del nostro sacro compito.', '', 57534),
(269954, 'itIT', 'Il nostro destino è la condanna di tutti coloro che affrontano il Grande Fuoco.', '', 57534),
(269953, 'itIT', 'La mia... immortalità.', '', 57534),
(269952, 'itIT', 'Io sono al di là della morte.', '', 57534),
(269951, 'itIT', 'L\'immortalità è mia!', '', 57534),
(269950, 'itIT', 'È ora di una risoluzione vincolante.', '', 57534),
(269949, 'itIT', 'Gli alleati sono una debolezza.', '', 57534),
(269948, 'itIT', 'Hai fatto il passo più lungo della gamba.', '', 57534),
(269947, 'itIT', 'Il mio genio non ha eguali!', '', 57534),
(269946, 'itIT', 'La fragilità della carne.', '', 57534),
(269945, 'itIT', 'Ora soffocate!', '', 57534),
(269944, 'itIT', 'Un risultato esplosivo.', '', 57534),
(269943, 'itIT', 'Immergetevi nelle fiamme!', '', 57534),
(269942, 'itIT', 'Geniale!', '', 57534),
(269941, 'itIT', 'Manifattura superiore.', '', 57534),
(269940, 'itIT', 'Intrusi? I miei costrutti vi annienteranno!', '', 57534),
(269939, 'itIT', 'La mia ascensione è vicina. I limiti mortali non mi incateneranno più.', '', 57534),
(269937, 'itIT', 'Le mie fiamme... domate...', '', 57534),
(269936, 'itIT', 'Ah! Cotti a puntino.', '', 57534),
(269935, 'itIT', 'Fiamma, a me!', '', 57534),
(269934, 'itIT', 'Bruciate e sfrigolate.', '', 57534),
(269933, 'itIT', 'È ora di scaldare la situazione.', '', 57534),
(269932, 'itIT', 'Bruciamo tutto.', '', 57534),
(269931, 'itIT', 'Fiamma, ardi!', '', 57534),
(269930, 'itIT', 'Una fine ardente.', '', 57534),
(269929, 'itIT', 'Come falene sulla fiamma.', '', 57534),
(269928, 'itIT', 'Ridurrò la vostra carne e le vostre ossa in cenere!', '', 57534),
(269927, 'itIT', 'La vostra intrusione finirà in un tripudio di gloria. La mia gloria.', '', 57534),
(269925, 'itIT', '', 'È ciò che hai davanti agli occhi. Il sangue di antichi nemici che si è condensato nelle viscere della terra. Questo Sangue Nero venne rimosso quando il mondo risentì di una grave ferita. Da allora si sta diffondendo... ed è pericoloso.\n\nÈ tutto ciò che devi sapere.', 57534),
(269916, 'itIT', '', 'Lashk è proprio gentile. E ti ringrazio, $n, per aver richiamato qui il mio spirito.\n\nCredo... credo di essere pronta a riposare, ora.\n\nDi\' a Lashk che questa vecchia Tortolliana ha raggiunto l\'acqua.', 57534),
(269914, 'itIT', '', 'Che bello. Questi giovani innamorati saranno il nostro futuro.', 57534),
(269912, 'itIT', 'Se solo Nola fosse stata qui per vederlo.', '', 57534),
(269911, 'itIT', 'Giù di morale? Che parola debole per esprimere un grande dolore.\n\nLa Pergamologa Nola, la più saggia fra noi, è partita per il suo ultimo pellegrinaggio.\n\nHa affidato le sue ultime volontà a un gruppo della tribù Tortaka. Spero siano riusciti a esaudirli, ma sono comunque preoccupato.\n\nSe dovessi trovarti a Zandalar, porta i tuoi rispetti alla nostra cara Nola.', '', 57534),
(269907, 'itIT', '', 'Questa mappa non ha senso... So che mi sta sfuggendo qualcosa, ma cosa di preciso?', 57534),
(269906, 'itIT', '', 'Apprezzo il tuo aiuto con la chiave cifrata, ma devi scusarmi per un minuto. Questa mappa non si leggerà da sola.', 57534),
(269904, 'itIT', '', 'Meerah trattiene il respiro.', 57534),
(269902, 'itIT', 'Coboldi sempre felici di prendere cera. Adorano cera! Un po\' come voi amare cose luccicanti.\n\nMedias lavora a nuovo progetto. Scultura di cera! Lui prende tutta quella che tu trova. Tu dà abbastanza e lui regala roccia luccicante per tuo aiuto.\n\nPorta a me! Io ha tutta roba che troviamo per aiutarti. Alcune cose buone!\n\nTu prova! Io dà un po\'di vecchia cera per Medias, tu porta lui così lui regala sassi luccicanti.\n\n<Roditutto si china verso di te, sussurrando in modo circospetto.>\n\nAbbiamo anche riserva segreta di merce davvero speciale. Se tutti collabora e porta tanta cera, noi divide e baratta con Graffiagrinfia!', 'Coboldi sempre felici di prendere cera. Adorano cera! Un po\' come voi amare cose luccicanti.\n\nMedias lavora a nuovo progetto. Scultura di cera! Lui prende tutta quella che tu trova. Tu dà abbastanza e lui regala roccia luccicante per tuo aiuto.\n\nPorta a me! Io ha tutta roba che troviamo per aiutarti. Alcune cose buone!\n\nTu prova! Io dà un po\'di vecchia cera per Medias, tu porta lui così lui regala sassi luccicanti.\n\n<Roditutto si china verso di te, sussurrando in modo circospetto.>\n\nAbbiamo anche riserva segreta di merce davvero speciale. Se tutti collabora e porta tanta cera, noi divide e baratta con Graffiagrinfia!', 57534),
(269894, 'itIT', '', 'Piano! Hai dormito a lungo! Sono cambiate molte cose nel frattempo.', 57534),
(269892, 'itIT', '', 'Va\' e rendi fiero il tuo Weyrn.', 57534),
(269891, 'itIT', '', 'Il mondo è molto più grande di quanto credevamo. C\'è molto ancora da scoprire.', 57534),
(269889, 'itIT', 'Conosco Alleria da tanti, tanti anni. Ma non smette mai di stupirmi.\n\nLa battaglia contro i Nerubiani cambiò radicalmente quando Beledar tornò a schierarsi con la Luce. Non so come, ma in qualche modo sapevo che era merito dell\'impresa di Alleria... che aveva camminato fieramente nell\'oscurità per poi riemergerne vittoriosa.\n\nIo... non riesco proprio a fidarmi del Vuoto, o del potere che promette. Ma mi fido di Alleria con tutto me stesso.\n\nPer ora, può bastare.', '', 57534),
(269888, 'itIT', '', 'Il nostro signore, Neltharion, è morto. La sua era falsa benevolenza. Voleva controllare noi e il mondo intero.', 57534),
(269884, 'itIT', '', 'Sono successe molte cose mentre dormivi. Non saprei da dove iniziare.', 57534),
(269882, 'itIT', '<Il naso di Roditutto freme di eccitazione mentre ti avvicini.>\n\nOdore di sporcizia... e cera! Trovato qualcosa di buono? Qualcosa nascosto?\n\nC\'è molto da trovare se tu tiene naso aperto.', '<Il naso di Roditutto freme di eccitazione mentre ti avvicini.>\n\nOdore di sporcizia... e cera! Trovato qualcosa di buono? Qualcosa nascosto?\n\nC\'è molto da trovare se tu tiene naso aperto.', 57534),
(269874, 'itIT', 'Wow.', '', 57534),
(269873, 'itIT', '', 'Fta funzionando!', 57534),
(269872, 'itIT', '', 'Bene! Il baftone ha vifto qualcofa!', 57534),
(269854, 'itIT', '', 'Quali magnifici destrieri hai portato con te oggi?', 57534),
(269853, 'itIT', '', 'Salute, amante delle cavalcature!', 57534),
(269852, 'itIT', '', 'Tieni a freno le redini! Queste cavalcature non fanno solo scena, sai!', 57534),
(269851, 'itIT', '', 'Le tue incessanti molestie stanno rovinando il mio stile impeccabile.', 57534),
(269850, 'itIT', '', 'Mostrami i tuoi abiti migliori!', 57534),
(269849, 'itIT', '', 'Hai una passione smodata per la moda?', 57534),
(269848, 'itIT', 'So che ti piace stare al chiuso. Anche a me. Ma hai bisogno di prendere un po\' di sole, Hoofy! Forza!\n\nArgh, di questo passo non ti crescerà mai il muschio!', 'So che ti piace stare al chiuso. Anche a me. Ma hai bisogno di prendere un po\' di sole, Hoofy! Forza!\n\nArgh, di questo passo non ti crescerà mai il muschio!', 57534),
(269847, 'itIT', 'Dobbiamo proteggere i bambini, $p! Libera la zona di pesca!', '', 57534),
(269845, 'itIT', 'Il signor Tearon ha detto che mi insegnerà a pescare, ma ci sono troppi mostri qui in giro! Quando sarò grande, diventerò forte abbastanza da scacciarli via e fare un favore agli anziani!', 'Il signor Tearon ha detto che mi insegnerà a pescare, ma ci sono troppi mostri qui in giro! Quando sarò grande, diventerò forte abbastanza da scacciarli via e fare un favore agli anziani!', 57534),
(269844, 'itIT', 'È mio dovere occuparmi di ogni gatto di Dornogal, ma non riesco a trovare Ruufus!\n\nIl fatto che Sheaf si trovi esattamente dov\'era ieri mi rende decisamente entusiasta.', 'È mio dovere occuparmi di ogni gatto di Dornogal, ma non riesco a trovare Ruufus!\n\nIl fatto che Sheaf si trovi esattamente dov\'era ieri mi rende decisamente entusiasta.', 57534),
(269841, 'itIT', 'Se Gert non riesce a capire a cosa si riferiscono le etichette \"Solo senza peluria\" e \"Serpi Vaporose\" presenti su queste bottiglie, forse nei suoi appunti ci sarà qualche indizio.\n\nLa direttiva di mantenere questi libri in ordine è entrata in vigore con qualche ciclo di ritardo, a quanto pare.', 'Se Gert non riesce a capire a cosa si riferiscono le etichette \"Solo senza peluria\" e \"Serpi Vaporose\" presenti su queste bottiglie, forse nei suoi appunti ci sarà qualche indizio.\n\nLa direttiva di mantenere questi libri in ordine è entrata in vigore con qualche ciclo di ritardo, a quanto pare.', 57534),
(269840, 'itIT', 'Ho archiviato i ricordi che includevano lo scopo di queste bottiglie. Le diciture sulle etichette non mi dicono nulla. Come ho fatto a scrivere delle note tanto criptiche?', 'Ho archiviato i ricordi che includevano lo scopo di queste bottiglie. Le diciture sulle etichette non mi dicono nulla. Come ho fatto a scrivere delle note tanto criptiche?', 57534),
(269835, 'itIT', 'Oh, è facile intrattenerti.', '', 57534),
(269834, 'itIT', 'Nel dubbio, balla.', '', 57534),
(269833, 'itIT', 'Lasciati andare ai festeggiamenti.', '', 57534),
(269832, 'itIT', 'Hai bisogno di un consiglio?', '', 57534),
(269831, 'itIT', 'Come posso esserti d\'aiuto?', '', 57534),
(269825, 'itIT', '', 'Te l\'ho detto, l\'ho nascosta vicino alla Mania Modaiola! Ora sparisci da qui!', 57534),
(269813, 'itIT', 'E va bene, prendine un po\' e lasciami in pace.', '', 57534),
(269812, 'itIT', 'Sparisci! Le mie reliquie non sono destinate a te.', '', 57534),
(269811, 'itIT', '', 'Merrix mi ha chiamata alla Sala della Fondazione per poter discutere sui prossimi passi da intraprendere come Concilio.\n\n<L\'Oratrice Brinthe annuisce ad Adelgonn.>\n\nSono felice di vedere il ritorno del Guardiapietra. Adelgonn è stata una Guardiapietra onesta e leale. Avrei voluto vedere una leader simile per il mio popolo.\n\nCome rappresentante degli Oratori delle Macchine, farò del mio meglio per esercitare la loro volontà.\n\nE non appena la strada sarà libera, scenderò di persona nelle Profondità Risonanti per accertarmi dello stato del mio popolo e del Gran Oratore.', 57534),
(269810, 'itIT', '', 'I Nerubiani hanno messo a ferro e fuoco l\'Arteria del Nucleo, ma Dornogal era già in pessime condizioni. È evidente dalle crepe e dalle riparazioni frettolose che sono state fatte. Gli Ossequenti avevano bisogno di noi molto prima di avercelo fatto sapere.\n\n<Adelgonn ride. È un suono duro ma allegro, come delle pietruzze gettate in un fiume.>\n\nTutto mi fa tornare voglia di lavorare. Anche al Borgo Frey volevo costruire per la mia gente. E ora che siamo di nuovo a Dornogal, credo proprio che ricomincerò a farlo, per tutti loro.\n\n<Sorride leggermente.>\n\nDiamoci da fare, allora. I supporti dell\'Arteria del Nucleo stanno su a malapena. Dobbiamo iniziare con le riparazioni. Subito.', 57534),
(269809, 'itIT', '<Baelgrim è stranamente tranquillo. La sua abituale intraprendenza sembra un po\' sottotono. Lascia trasparire una calma sin troppo evidente, persino per un Terrigeno.>\n\nAdelgonn ha mantenuto la promessa di tornare ad aiutarci. E quindi eccoci qui, un concilio effettivo con tutto il nostro popolo riunito che vede rappresentati i propri interessi.\n\nInsieme, faremo in modo che i Terrigeni non siano mai più colti di sorpresa.', '', 57534),
(269808, 'itIT', 'Il futuro è ancora un valore sconosciuto, ma ora che Adelgonn è tornata, e con Brinthe al posto del Gran Oratore, il Concilio è tornato a essere com\'è sempre stato per migliaia di anni.\n\nNon avrei mai pensato di riuscire a vederlo prima di entrare in stasi.\n\nLa nostra prossima mossa è liberare l\'Arteria del Nucleo per aiutare gli Oratori delle Macchine, e anche i nostri nuovi alleati mentre sono sulle tracce dei Nerubiani e questa \"Xal\'atath\".', '', 57534),
(269771, 'itIT', 'La mia direttiva è catturare e prendermi cura di un caleidoscopio di svariate migliaia di gemmocchi selvaggi. Me ne servono solamente altri quattromila prima del prossimo ciclo!', 'La mia direttiva è catturare e prendermi cura di un caleidoscopio di svariate migliaia di gemmocchi selvaggi. Me ne servono solamente altri quattromila prima del prossimo ciclo!', 57534),
(269770, 'itIT', 'Se per seta, cuoio e acciaio ardete con passione, la Mania Modaiola è una vera occasione. Che la gente vi ammiri e vi invidi febbrile, divenendo per tutti un\'icona di stile!', '', 57534),
(269761, 'itIT', 'Tu in gamba. Ultimo!', 'Tu in gamba. Ultimo!', 57534),
(269760, 'itIT', 'Lavoro buono! Altri due resta.', 'Lavoro buono! Altri due resta.', 57534),
(269735, 'itIT', 'Eccoti. Mi fa piacere tu sia $garrivato tutto intero:arrivata tutta intera;.\n\nIo non posso dire lo stesso. Quella bestia che mi ha dato Adelgonn... nessuno avrebbe mai dovuto addomesticare quelle creature, tantomeno cavalcarle!\n\nStarò meglio quando tornerà Zarein.', '', 57534),
(269719, 'itIT', '', 'Vai a efplorare le rovine con la torcia! Fi illuminerà quando ti avvicinerai a qualcosa di utile!', 57534),
(269718, 'itIT', '', 'Non promette niente di buono.', 57534),
(269715, 'itIT', '', 'Qui sembra tutto a posto! Troviamo la prossima distorsione.', 57534),
(269714, 'itIT', '', 'Il Flagello a Vento del Sud? No, non ci siamo proprio. Sconfiggerlo dovrebbe riparare il codice!', 57534),
(269712, 'itIT', 'Ammirate una gara senza eguali al mondo e saltate in sella per l\'evento giocondo. Una sfilata di pellicce, zoccoli e ali in gran quantità, nella Cavalca-Mania non son rarità!', '', 57534),
(269695, 'itIT', 'Mi pento di essere venuto qui. Adelgonn deve aver visto per forza lo schianto della vostra Dalaran e il fumo che si sollevava da Dornogal.\n\nSe questo non è bastato a spingerla verso la nostra causa, allora non so cosa fare.\n\nMi occuperò di trovare le risorse necessarie anche senza il suo aiuto, se devo.', '', 57534),
(269651, 'itIT', 'Puoi raggiungerli adesso o occuparti delle questioni ancora in sospeso.', '', 57534),
(269643, 'itIT', '', 'Non perdiamo altro tempo. Seguitemi, gente!', 57534),
(269642, 'itIT', '', 'Segnerò i punti distorti sulla mappa, oppure potete semplicemente seguirmi.', 57534),
(269640, 'itIT', '', 'Non so bene che aspetto avranno queste distorsioni o come sistemarle. Ma lo sapremo quando le vedremo!', 57534),
(269639, 'itIT', '', 'Veniamo al dunque. Alcune distorsioni nella simulazione del codice stanno deformando eventi che hanno avuto davvero luogo.', 57534),
(269636, 'itIT', '', 'Grazie ancora per avermi aiutata a riparare il codice. Tutte le nostre disavventure temporali non devono avergli fatto molto bene.', 57534),
(269626, 'itIT', 'OK, va bene! Il tuo aiuto è gradito! Si può uscire senza correre rischi, ora?', '', 57534),
(269623, 'itIT', '<Il cannocchiale sembra puntato sulla fortezza in lontananza.>', '<Il cannocchiale sembra puntato sulla fortezza in lontananza.>', 57534),
(269621, 'itIT', 'Assolutamente niente. Questo caso è praticamente risolto. Come dico sempre, il primo istinto non sbaglia mai!\n\nE poi dubito che qualcuno qui abbia visto qualcosa.', '', 57534),
(269619, 'itIT', '<Sembra che il cadavere sia stato pugnalato e privato di diverse ossa e organi.>', '<Sembra che il cadavere sia stato pugnalato e privato di diverse ossa e organi.>', 57534),
(269618, 'itIT', '<Sulla sedia c\'è un appunto scarabocchiato in fretta.>\n\nIl sole è migliore dall\'altro lato. \n-Herrkrop', '', 57534),
(269612, 'itIT', '', 'Lo sanno solo i Titani. I Nerubiani hanno fatto la loro comparsa pochi cicli dopo il nostro arrivo. \n\nErano malvagi, e uccidevano e imprigionavano chiunque non li ascoltasse. \n\nQuindi abbiamo fatto l\'unica cosa che sappiamo fare noi Terrigeni. Abbiamo obbedito.', 57534),
(269608, 'itIT', '', 'Cuci. Richiudi. Guarisci. \n\nIo... ero Neferess. Lo sono.', 57534),
(269559, 'itIT', 'Qui è dove raccogliamo tutte le cose recuperate durante le Scorribande a Khaz Algar.\n\nOh, certo! Le Scorribande sono aree che includono tesori preziosi insieme a nemici temibili. \n\nTi aiuterò a esplorare tutte le aree di questo tipo disponibili. Puoi portare qualche amico se ti va, oppure saremo solo noi due! Potremo collaborare per conquistare qualche tesoro.', '', 57534),
(269556, 'itIT', '', 'Quel mutaforma è fcappato? Fecondo me, è ora di tirare fuori l\'idolo e feguire le orme nella fabbia!', 57534),
(269555, 'itIT', '', 'Fecondo me, è ora di paffare alle domande! Parla con entrambi gli inveftigatori e fegui le loro ftorie!', 57534),
(269546, 'itIT', 'Con quello lì? Senti questa: arriva di mattina presto e ordina un pollo. Va bene.\n\nPoi torna a pranzo. Gli chiedo se voleva lo stesso e lui mi fa: \"Ehm, ah, cosa?\". Balbetta per un minuto, poi cambia idea e se ne va.\n\n<Leeroy alza le spalle.>\n\nAllora, lo vuoi un pollo o no?', '', 57534),
(269545, 'itIT', '', 'Sì, certo! Be\', ha provato a farmi delle domande, ma non sono riuscita a dire nemmeno una parola. \n\nHa finito per parlare tutto il tempo di sé!', 57534),
(269544, 'itIT', 'Sì, mi ha chiesto se avessi assistito a eventuali crimini. Ho parlato un po\' di caccia e di come avvistare le prede, e devo dire che quell\'investigatore è un ottimo ascoltatore.\n\nPerò no, non ho visto criminali aggirarsi nei dintorni.', '', 57534),
(269540, 'itIT', 'Vediamo... Sono andato a pranzare al Pollo di Leeroy, poi ho interrogato i potenziali testimoni. Ovviamente, ho fatto qualche domanda anche al cacciatore più famoso di tutti, Hemet Nesingwary. Ahimè, niente di buono.\n\nSenza alcuna pista, sono venuto qui.', '', 57534),
(269539, 'itIT', 'Vediamo... Sono andato al Pollo di Leeroy stamattina presto, poi ho interrogato dei possibili testimoni. Questa Addie sembrava una cacciatrice attenta, ma non aveva visto il nostro criminale.\n\nSapevo che doveva pur esserci una soluzione, quindi sono venuto qui a pensare.', '', 57534),
(269537, 'itIT', 'Anche i migliori fra noi accuserebbero il colpo in queste circostanze. E così è stato.', '', 57534),
(269536, 'itIT', 'Vedo dei segnali nello sguardo di Shinda, quando ho tempo di farci caso. C\'è della spossatezza nei suoi occhi. Impazienza. Fragilità.', '', 57534),
(269532, 'itIT', 'Preparatevi a viaggiare con il Ramingo della Sapienza Cho al Palco del Cantastorie!', '', 57534),
(269531, 'itIT', 'Che gli appassionati di moda sfoggino il loro stile più di tendenza nella Mania Modaiola!', '', 57534),
(269530, 'itIT', 'Saltate in sella e mettete alla prova il vostro serraglio con la Cavalca-Mania!', '', 57534),
(269529, 'itIT', 'Salute ai nobili ospiti che cercan baldorie, per voi una storia vi attende al Palco del Cantastorie. Il Ramingo della Sapienza Cho è pronto, accorrete tutti per un gran racconto.', '', 57534),
(269521, 'itIT', 'Io e Alleria non abbiamo avuto occasione di parlare prima della sua partenza per Dalaran. \n\nSapere cos\'ha passato...', '', 57534),
(269520, 'itIT', 'C\'è voluto del tempo per assemblare le flotte. L\'armistizio tra l\'Alleanza e l\'Orda è più fragile di quanto sembri.\n\nQuanto a me... Devo parlare con Alleria, ma c\'è molto lavoro da fare. I nostri doveri hanno la precedenza.\n\n<Turalyon serra le labbra in una linea sottile.>\n\nE così, ancora una volta, i discorsi dovranno attendere.', '', 57534),
(269518, 'itIT', '', '$gStraniero:Straniera;, quanto hai visto dell\'isola? Hai visto lo stato della nostra società? \n\nC\'è una disfunzione. \n\nQuesti opali hanno il potere di ripristinare della tecnologia fondamentale che può porvi rimedio. Se c\'è una possibilità di sistemare la nostra società, ognuno di noi è disposto a mettere in gioco la propria esistenza.', 57534),
(269516, 'itIT', 'Io e i Cavalcatempesta abbiamo protetto l\'Isola sin dall\'inizio dei tempi.\n\nAbbiamo fatto rispettare gli Editti dopo che la Guardiapietra e il Gran Oratore se ne sono andati.\n\nSe fossero stati dei veri Terrigeni, sarebbero già qui, pronti a proteggere la loro casa da ulteriori attacchi.\n\nMa se devo essere solo io a difendere Dornogal e l\'Arteria del Nucleo, allora così sia.', '', 57534),
(269515, 'itIT', 'Ho tenuto segretamente sotto osservazione Adelgonn e la sua gente sin dalla sua partenza. Dopotutto, è mio dovere come Custode del Concilio essere al corrente di tutto ciò che accade sull\'Isola di Dorn.\n\nHa da tempo rinunciato al titolo di Guardiapietra. Lei e gli Svincolati hanno stabilito un insediamento a sud. Non so se ho compreso quale sia la loro nuova missione. Molti di loro hanno cercato di rinnegare il proprio obbligo verso i Titani, ma sono i nostri Editti a definire la nostra identità. Possono ancora definirsi Terrigeni in quel caso?\n\nSo già cosa direbbe il Guardiatempesta Baelgrim.\n\nIn un certo senso, ha ragione. Non dovrebbe essere possibile pensare al di fuori del nostro codice, delle nostre direttive...\n\nL\'esistenza degli Svincolati è un miracolo.', '', 57534),
(269513, 'itIT', 'È incredibile che questa macchina stia di nuovo emettendo segnali di vita dopo così tanti anni di silenzio...', '', 57534),
(269506, 'itIT', '', 'Cerca di non farti cullare da un effimero senso di vittoria, $p. Xal\'atath è ancora in vita.\n\nFinché trama nelle ombre, la sicurezza che abbiamo conquistato qui resta precaria... proprio come lo era a Dalaran. \n\nMentre Anduin e i Terrigeni si occupano dei feriti, io cercherò di fare del mio meglio proteggendoli.\n\n<Alleria stringe il pugno.>\n\nMassacrando chiunque osi minacciarci.', 57534),
(269503, 'itIT', '', 'Non riefci a trovare indizi? Forfe Rexxar ha qualche idea!', 57534),
(269502, 'itIT', '', 'Hai provato a parlare con Rexxar e Spirito?', 57534),
(269501, 'itIT', '', 'Forfe qualcuno all\'accampamento di Nesingwary l\'ha vifto...? Chiedi in giro! E chiedi un facco!', 57534),
(269500, 'itIT', '', 'Mmm. Forfe quefto qui è andato a nord in un pofto più umido? Fe lo trovi, fai un fifchio a Rexxar!', 57534),
(269499, 'itIT', '', 'Cerca intorno alla X fulla mappa usando la torcia e quell\'idolo. Ficuramente troverai un indizio!', 57534),
(269497, 'itIT', '', 'Mmm. Dunque, hai ricevuto un meffaggio di Rexxar con delle coordinate. Fe ci penfi fu un attimo, di certo faprai trovarlo!\n\nA meno che... Fe quella nave non ti porta dove vuoi andare, dovrai trovare il porto per conto tuo!', 57534),
(269496, 'itIT', 'Sì, così pare. Un qualche demone scaltro sta seminando il caos.\n\nPurtroppo, presto dovrò tornare ai miei altri doveri. Sono sicuro che riuscirai a trovare questo demone e a chiudere questa storia.', '', 57534),
(269493, 'itIT', 'Mmm. Avverto una presenza oscura che aleggia sul cadavere.', '', 57534),
(269489, 'itIT', 'Che ne pensi?', '', 57534),
(269483, 'itIT', '<Esamini il corpo sfruttando la tua conoscenza delle ferite. Sgraffio è stato colpito con degli artigli, probabilmente da una bestia o da un Satiro.>', '<Esamini il corpo sfruttando la tua conoscenza delle ferite. Sgraffio è stato colpito con degli artigli, probabilmente da una bestia o da un Satiro.>', 57534),
(269482, 'itIT', '<Utilizzando la tua abilità magica, avverti dei residui di magia illusoria.>', '<Utilizzando la tua abilità magica, avverti dei residui di magia illusoria.>', 57534),
(269481, 'itIT', '<Utilizzando la tua capacità di percezione dei demoni, avverti i residui di una presenza demoniaca.>', '<Utilizzando la tua capacità di percezione dei demoni, avverti i residui di una presenza demoniaca.>', 57534),
(269479, 'itIT', '<Sembra che Sgraffio sia stato ucciso.>', '<Sembra che Sgraffio sia stato ucciso.>', 57534),
(269478, 'itIT', 'Qualche? È pieno di nascondigli, $C! Da qui fino alla Corona di Ghiaccio ce ne sono una marea.\n\nE anche a nord del Bacino, quei Vrykul hanno una fortezza quasi impenetrabile strapiena di nascondigli e nicchie.', '', 57534),
(269477, 'itIT', '', 'A dire il vero, sì. È passato di qui, lamentandosi del caldo. Ha detto che era diretto a nord.', 57534),
(269473, 'itIT', 'Spirito si libra nei cieli. Segnerò sulla tua mappa cosa vedo attraverso i suoi occhi.', '', 57534),
(269453, 'itIT', 'A volte, la pietà è una scelta difficile. Ma dobbiamo sempre ricordare che è comunque una scelta.', '', 57534),
(269452, 'itIT', 'La pietà mostrata a Magatha giovò a tutti. Ma ci sono altre storie in cui la pietà portò alla rovina...', '', 57534),
(269451, 'itIT', 'Forse agì per i propri interessi. Ma le sue azioni, alla fin fine, aiutarono a proteggere Azeroth.', '', 57534),
(269450, 'itIT', 'In esilio, Magatha riuscì a fermare il Martello del Crepuscolo, proteggendo la Pietra del Destino e respingendo l\'invasione della Legione Infuocata.', '', 57534),
(269449, 'itIT', 'Baine avrebbe forse dovuto vendicarsi e ucciderla?', '', 57534);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(269448, 'itIT', 'Anche Magatha, il cui veleno aveva ucciso suo padre, ricevette la stessa pietà.', '', 57534),
(269447, 'itIT', 'Invece di giustiziare i ribelli Totem Truce, Baine li fece esiliare.', '', 57534),
(269446, 'itIT', 'Dopo aver sconfitto i suoi nemici, il Tauren fece qualcosa di arduo per qualsiasi figlio in lutto. Ebbe pietà.', '', 57534),
(269445, 'itIT', 'In un giorno di sangue e tuono, Baine liberò la sua casa e catturò la tribù ribelle dei Totem Truce.', '', 57534),
(269444, 'itIT', 'Egli radunò i Tauren ancora fedeli, insieme ad alleati improbabili, e marciò su Picco del Tuono.', '', 57534),
(269443, 'itIT', 'Molti vecchi alleati di Cairne furono uccisi e suo figlio, Baine Zoccolo Sanguinario, fuggì per miracolo.', '', 57534),
(269442, 'itIT', 'Magatha e i Totem Truce ne approfittarono per prendere il controllo di Picco del Tuono, la capitale dei Tauren.', '', 57534),
(269441, 'itIT', 'Durante il duello, Cairne fu sopraffatto dal veleno e morì.', '', 57534),
(269440, 'itIT', 'Ma non ci fu onore quel giorno. Magatha Totem Truce, una Sciamana Tauren, avvelenò la lama di Garrosh a sua insaputa.', '', 57534),
(269439, 'itIT', 'Cairne Zoccolo Sanguinario, capo tribù dei Tauren, chiese un Mak\'gora. Un duello mortale e onorevole.', '', 57534),
(269438, 'itIT', 'Durante il Cataclisma, Garrosh Malogrido divenne il Capoguerra dell\'Orda. Ma non tutti erano d\'accordo a seguirlo.', '', 57534),
(269437, 'itIT', 'Talvolta, la pietà è una scelta difficile. Ma è quella giusta?', '', 57534),
(269415, 'itIT', 'Incatenato alla sua sofferenza. Consumato dall\'odio. L\'uomo che fece quella promessa non c\'è più. O forse è solo ciò che crede lui...', '', 57534),
(269414, 'itIT', 'Sebbene Thassarian avesse promesso a sua sorella di cercarla dopo aver sconfitto il Re dei Lich, non l\'ha ancora fatto.', '', 57534),
(269413, 'itIT', 'Ma il Cavaliere della Morte riuscì a sconfiggere il Principe del Sangue e a salvare sua sorella. Sfortunatamente, la tragedia di questa storia... è questa.', '', 57534),
(269412, 'itIT', 'Valanar rapì la sorella di Thassarian per usarla contro di lui.', '', 57534),
(269411, 'itIT', 'Lì, Thassarian fu tradito ancora quando il comandante dell\'Alleanza sotto il quale serviva si rivelò essere il Principe del Sangue Valanar, appartenente al Flagello.', '', 57534),
(269410, 'itIT', 'Thassarian e gli altri cavalieri formarono l\'ordine della Spada d\'Ebano e insieme marciarono su Nordania per annientare il Re dei Lich.', '', 57534),
(269409, 'itIT', 'Questo tradimento, unito al potere degli eroi di Azeroth, spezzò il controllo di Arthas su questi Cavalieri della Morte.', '', 57534),
(269408, 'itIT', 'Ma egli, per stanare i suoi nemici, inviò un contingente di Cavalieri della Morte in una battaglia senza speranze.', '', 57534),
(269407, 'itIT', 'Dopo lunghi anni, Arthas era divenuto il Re dei Lich.', '', 57534),
(269406, 'itIT', 'Su ordine di Arthas, Thassarian massacrò coloro che aveva giurato di proteggere.', '', 57534),
(269405, 'itIT', 'Quando il principe tornò, uccise Thassarian e lo rianimò come Cavaliere della Morte.', '', 57534),
(269404, 'itIT', 'Lì, il Principe Arthas scoprì la lama runica Gelidanima. Ma conosciamo già questa storia.', '', 57534),
(269403, 'itIT', 'Fu presente all\'Epurazione di Stratholme e seguì il suo principe a Nordania per vendicarsi del Flagello.', '', 57534),
(269402, 'itIT', 'Quando il Principe Arthas partì per affrontare il Flagello, Thassarian fu uno dei primi volontari.', '', 57534),
(269401, 'itIT', 'Dopo la sua morte, egli seguì le orme del padre e si arruolò nell\'esercito di Lordaeron.', '', 57534),
(269400, 'itIT', 'Thassarian crebbe in una fattoria, ma suo padre lo educò come soldato.', '', 57534),
(269399, 'itIT', 'Mentre la storia di Thassarian non è altrettanto tragica, ma vale comunque la pena di raccontarla.', '', 57534),
(269398, 'itIT', 'La storia dei Cavalieri della Morte del Re dei Lich è una vera tragedia.', '', 57534),
(269396, 'itIT', 'Qui il vento soffia con forza verso est. Dobbiamo continuare la caccia senza Spirito, per ora.', '', 57534),
(269395, 'itIT', '', 'Ho servito il Guardiatempesta Baelgrim per molti cicli, ma questo è il primo in cui siamo sotto attacco.\n\nL\'Oratrice Brinthe ha detto anche che non esistono rapporti scritti di altri attacchi, ma i rapporti che abbiamo al momento sono successivi alla perdita dell\'accesso all\'Archivio.\n\nMa noi Cavalcatempesta ci siamo preparati proprio per questo momento per tutto il nostro periodo di attività. Non concederemo altro terreno ai Nerubiani.', 57534),
(269389, 'itIT', '', 'C\'è un mistero più grande da risolvere qui.\n\nPotrebbe rivelare una verità scomoda, ma intendo andare fino in fondo.', 57534),
(269387, 'itIT', '', 'Tutto qui? Ti arrendi già?\n\nPotrebbero esserci chissà quanti altri omicidi finché quel cretino è libero di andarsene dove vuole!', 57534),
(269382, 'itIT', '', 'Mi sa che non posso metterli in un vaso, ma è il pensiero che conta.', 57534),
(269381, 'itIT', '', 'Sono grata a Orren perché mi fa sentire una persona speciale, ma ogni tanto mi diverto a metterlo in difficoltà.', 57534),
(269380, 'itIT', 'Mi sono sempre piaciuti i Boccioli d\'Ambra, a dire la verità.', '', 57534),
(269379, 'itIT', '', 'Orren fa sempre dei gesti affettuosi come quello.', 57534),
(269378, 'itIT', 'Mi dispiace vedere che Velhan non abbia fatto niente al riguardo, ma neanche mi sorprende.', '', 57534),
(269377, 'itIT', '', 'Orren non deve sapere quanti Kobyss ci sono qui! Non facciamolo preoccupare.', 57534),
(269376, 'itIT', '', 'Interessante e sottile.', 57534),
(269375, 'itIT', 'Meglio combattere contro i Nerubiani che i Kobyss.', '', 57534),
(269344, 'itIT', 'Preparati alla caccia.', '', 57534),
(269341, 'itIT', 'Gli Archivi sono ancora in piedi.\n\nIl Custode del Concilio Merrix e l\'Oratrice Brinthe funzionano ancora.\n\nSiano ringraziati gli Editti!', '', 57534),
(269334, 'itIT', '<L\'espressione di Gartwin si rabbuia per un istante.>\n\nQuando i Legapietra hanno iniziato ad allontanarsi da Dornogal, hanno perso l\'accesso alla Macchina.\n\nCiò significava che, anche da spenta, le loro forme non potevano essere ospitate al sicuro all\'interno. Dal momento che avevano rinunciato al diritto di rinnovare il loro ciclo, l\'idea di spegnersi e perdersi per sempre era... terrificante per molti.\n\nGli Svincolati si sono aggrappati a un barlume di speranza, una piccola parte della loro programmazione restia ad accettare la finalità dello spegnimento permanente della stasi. Hanno scelto di spegnersi nei Siti dei Tributi come questo. \n\nE come Sorvegliante, benché Svincolato, è mio dovere occuparmi di loro e di coloro che si sono spenti nelle terre selvagge.', '<L\'espressione di Gartwin si rabbuia per un istante.>\n\nQuando i Legapietra hanno iniziato ad allontanarsi da Dornogal, hanno perso l\'accesso alla Macchina.\n\nCiò significava che, anche da spenta, le loro forme non potevano essere ospitate al sicuro all\'interno. Dal momento che avevano rinunciato al diritto di rinnovare il loro ciclo, l\'idea di spegnersi e perdersi per sempre era... terrificante per molti.\n\nGli Svincolati si sono aggrappati a un barlume di speranza, una piccola parte della loro programmazione restia ad accettare la finalità dello spegnimento permanente della stasi. Hanno scelto di spegnersi nei Siti dei Tributi come questo. \n\nE come Sorvegliante, benché Svincolato, è mio dovere occuparmi di loro e di coloro che si sono spenti nelle terre selvagge.', 57534),
(269332, 'itIT', 'Questo è un luogo di tributo e ricordo per gli Svincolati che un tempo hanno eletto Borgo Frey a loro dimora.\n\n Alcune di queste pietre che vedi qui sono Pietre dei Tributi, e hanno le sembianze dei Terrigeni che abbiamo perduto nel corso del tempo o che, in alcuni casi, si trovavano nella Cripta quando si è spenta.\n\nAnche se la maggior parte di noi non si aspetta che le macchine si risveglino, per alcuni è stato difficile lasciare andare i propri amici e i propri cari. \n\nPer questo motivo, hanno scolpito delle Pietre dei Tributi per ricordare i nomi di coloro che non sono più con noi.', 'Questo è un luogo di tributo e ricordo per gli Svincolati che un tempo hanno eletto Borgo Frey a loro dimora.\n\n Alcune di queste pietre che vedi qui sono Pietre dei Tributi, e hanno le sembianze dei Terrigeni che abbiamo perduto nel corso del tempo o che, in alcuni casi, si trovavano nella Cripta quando si è spenta.\n\nAnche se la maggior parte di noi non si aspetta che le macchine si risveglino, per alcuni è stato difficile lasciare andare i propri amici e i propri cari. \n\nPer questo motivo, hanno scolpito delle Pietre dei Tributi per ricordare i nomi di coloro che non sono più con noi.', 57534),
(269331, 'itIT', 'Ti do il benvenuto nel Sito dei Tributi di Borgo Frey. Io sono Gartwin, Sorvegliante di questi Terrigeni e delle Pietre dei Tributi.\n\nPrenditi un momento per riflettere, se lo desideri. Questo è un luogo tranquillo e sicuro per molti Svincolati, e sono felice di offrirtelo se ne hai bisogno.', 'Ti do il benvenuto nel Sito dei Tributi di Borgo Frey. Io sono Gartwin, Sorvegliante di questi Terrigeni e delle Pietre dei Tributi.\n\nPrenditi un momento per riflettere, se lo desideri. Questo è un luogo tranquillo e sicuro per molti Svincolati, e sono felice di offrirtelo se ne hai bisogno.', 57534),
(269315, 'itIT', '', 'Ne hai parlato con l\'Investigatore Jonef? Fembra proprio la perfona giufta!', 57534),
(269314, 'itIT', '', 'Fembra ora di altri interrogatori! Cerchiamo dei teftimoni vicino alla fpiaggia.', 57534),
(269313, 'itIT', '', 'Ultimo avviftamento fu una fpiaggia, eh? La Fpiaggia Ondalunga non è diftante da qui.\n\nForfe la tua torcia può rivelare gli indizi!', 57534),
(269312, 'itIT', 'Lok\'tar ogar!', 'Lok\'tar ogar!', 57534),
(269311, 'itIT', '', 'Fcommetto che qualcuno qui fa dove fi trova la tua perfona fcomparfa. Prova a usare l\'idolo per trovare dei teftimoni!\n\nMi fa che è ora di un bell\'interrogatorio!', 57534),
(269305, 'itIT', 'E pensare che... siamo i primi nani a mettere piede in questa città. Una città Terrigena. Una civiltà Terrigena! \n\nE l\'Arteria del Nucleo! Una strada verso il nucleo del mondo. Che impresa strabiliante. Vorrei averla vista prima dell\'attacco.\n\nNon so cosa faremo dopo, ma prenderò appunti per tutto il tempo!', '', 57534),
(269300, 'itIT', 'Io? Ma come ti vengono in mente delle sciocchezze simili!', '', 57534),
(269299, 'itIT', 'Ammiro la tua confessione, ma ho dei seri dubbi.', '', 57534),
(269298, 'itIT', 'E quali sarebbero le tue prove? No, no, non può essere vero.', '', 57534),
(269291, 'itIT', 'Pensi di poter superare il grande Investigatore Jones?\n\nVa bene, sentiamo. Quale sarebbe la tua ipotesi?', '', 57534),
(269286, 'itIT', 'A un certo punto, eravamo più numerosi. Ma dopo che le macchine si sono spente, il numero si è ridotto perché altri Cavalcatempesta sono stati messi nella Volta di Pietra... e nessuno ne è uscito.\n\nCol tempo, anche i miei compagni Sorveglianti si sono spenti e sono svaniti, e io sono rimasto ultimo in servizio.\n\n<Odarrok stringe lentamente il pugno per riflesso, il braccio leggermente tremante.>\n\nAhimè, il mio ciclo si avvicina alla fine. Nel tempo che mi rimane, mi occuperò di coloro che si trovano qui nel Sito dei Tributi, prima di tornare al Trespolo della Tempesta. Spero solo che, se le macchine dovessero risvegliarsi, qualcuno si prenda cura della mia forma in modo da poter riprendere il mio ciclo.\n\nOra devo tornare al lavoro. Conversazione terminata.', 'A un certo punto, eravamo più numerosi. Ma dopo che le macchine si sono spente, il numero si è ridotto perché altri Cavalcatempesta sono stati messi nella Volta di Pietra... e nessuno ne è uscito.\n\nCol tempo, anche i miei compagni Sorveglianti si sono spenti e sono svaniti, e io sono rimasto ultimo in servizio.\n\n<Odarrok stringe lentamente il pugno per riflesso, il braccio leggermente tremante.>\n\nAhimè, il mio ciclo si avvicina alla fine. Nel tempo che mi rimane, mi occuperò di coloro che si trovano qui nel Sito dei Tributi, prima di tornare al Trespolo della Tempesta. Spero solo che, se le macchine dovessero risvegliarsi, qualcuno si prenda cura della mia forma in modo da poter riprendere il mio ciclo.\n\nOra devo tornare al lavoro. Conversazione terminata.', 57534),
(269285, 'itIT', 'Ufficialmente? Negativo. Non lo sono. Sono uno dei Sorveglianti incaricati di occuparsi dei Cavalcatempesta. Era nostro compito curare i cavalieri feriti e assicurarci che venissero condotti sani e salvi alla Volta di Pietra del Risveglio all\'approssimarsi della fine del loro ciclo.\n\nPer meglio assistere i Cavalcatempesta, abbiamo ricevuto un addestramento adeguato per occuparci di quelli sul campo. Raramente abbiamo preso parte a combattimenti, ma ci sono state fornite le funzioni adeguate per guidare un Corvo della Tempesta per conto nostro.\n\nLa vecchia Wahrvren sulla collina dietro di me è un po\' stanca dopo i suoi lunghi anni di servizio, ma è una creatura fedele.', 'Ufficialmente? Negativo. Non lo sono. Sono uno dei Sorveglianti incaricati di occuparsi dei Cavalcatempesta. Era nostro compito curare i cavalieri feriti e assicurarci che venissero condotti sani e salvi alla Volta di Pietra del Risveglio all\'approssimarsi della fine del loro ciclo.\n\nPer meglio assistere i Cavalcatempesta, abbiamo ricevuto un addestramento adeguato per occuparci di quelli sul campo. Raramente abbiamo preso parte a combattimenti, ma ci sono state fornite le funzioni adeguate per guidare un Corvo della Tempesta per conto nostro.\n\nLa vecchia Wahrvren sulla collina dietro di me è un po\' stanca dopo i suoi lunghi anni di servizio, ma è una creatura fedele.', 57534),
(269284, 'itIT', '<Odarrok ti guarda mentre lavora, con movimenti lenti e tremanti>\n\nÈ raro vedere visitatori nel Sito dei Tributi di Dhar Oztan, tanto meno stranieri provenienti dall\'isola. I cavalieri possono essere eccessivamente cauti nei confronti degli estranei, ma mi piace pensare che coloro che sono ricordati qui siano contenti di ricevere una visita.\n\nAttenzione a dove metti i piedi, e rimani quanto vuoi.', '<Odarrok ti guarda mentre lavora, con movimenti lenti e tremanti>\n\nÈ raro vedere visitatori nel Sito dei Tributi di Dhar Oztan, tanto meno stranieri provenienti dall\'isola. I cavalieri possono essere eccessivamente cauti nei confronti degli estranei, ma mi piace pensare che coloro che sono ricordati qui siano contenti di ricevere una visita.\n\nAttenzione a dove metti i piedi, e rimani quanto vuoi.', 57534),
(269282, 'itIT', 'Gran bel giorno per pescare, vero? Ahah!', '', 57534),
(269281, 'itIT', '', 'In realtà sì!\n\nHo visto quel Reietto che faceva un bel po\' di casino dietro quelle tende. È tutta la mattina che sono nervosa a causa sua! E guarda qui... ho trovato questo coltello insanguinato nella sabbia, a un tiro di schioppo da dove se ne sta seduto ora.\n\nTi prego, consegna quel... mostro alla giustizia!', 57534),
(269278, 'itIT', 'E a te che importa? Ho detto di lasciarmi in pace!\n\n<Sgraffio bofonchia alcune parole particolarmente offensive su di te e tua madre.>', '', 57534),
(269275, 'itIT', 'Lasciami stare. Mi stai facendo ombra.', '', 57534),
(269274, 'itIT', 'Non mi è sembrato, ma oggi i pesci abboccano che è una bellezza!', '', 57534),
(269266, 'itIT', 'Il lavoro puzzolente è quello che ai Niffen riesce meglio!', 'Il lavoro puzzolente è quello che ai Niffen riesce meglio!', 57534),
(269265, 'itIT', 'Le bestie sciamanti sono creature affascinanti, non trovi?', 'Le bestie sciamanti sono creature affascinanti, non trovi?', 57534),
(269264, 'itIT', 'Il kith di Mmarn fa le cose per bene!', 'Il kith di Mmarn fa le cose per bene!', 57534),
(269263, 'itIT', 'Qualunque cosa Anub\'azal intenda farne delle bestie sciamanti non mi riguarda!', 'Qualunque cosa Anub\'azal intenda farne delle bestie sciamanti non mi riguarda!', 57534),
(269262, 'itIT', 'Spero che Vetiverian sappia cosa ha accettato...', 'Spero che Vetiverian sappia cosa ha accettato...', 57534),
(269261, 'itIT', 'Non c\'è tempo per riposarsi e annusare gli insetti, bisogna lavorare!', 'Non c\'è tempo per riposarsi e annusare gli insetti, bisogna lavorare!', 57534),
(269260, 'itIT', 'Vix\'aron tiene tutti in riga...', 'Vix\'aron tiene tutti in riga...', 57534),
(269259, 'itIT', 'È molto impegnativo mantenere Rak-Ush in funzione, ma noi Niffen siamo all\'altezza del compito!', 'È molto impegnativo mantenere Rak-Ush in funzione, ma noi Niffen siamo all\'altezza del compito!', 57534),
(269213, 'itIT', '', 'Ti do il benvenuto, $uavventuriero:avventuriera;. Abbiamo qualcosa per te! I nostri fabbri si sono impegnati molto e hanno riforgiato alcune armature iconiche del passato.', 57534),
(269212, 'itIT', '', 'Divertiti e fai attenzione ai gatti.', 57534),
(269211, 'itIT', '', 'Salve a te. Hai trovato la strada verso i Monili Tempestivi di Ma\'di.\n\nChe posso fare per te oggi?', 57534),
(269210, 'itIT', '', 'Ti do il benvenuto al Paradiso delle Mascotte.\n\nAbbiamo tutto quello di cui la tua mascotte ha bisogno. Biscottini, grattini alla pancia, spazzole, grattini alle orecchie, acqua fresca, gambe su cui sedersi. Tutto.\n\nDai pure un\'occhiata in giro, accarezza gli animaletti e mettetevi nei guai insieme. Se dovessi arrivare allo sfinimento, ti invitiamo a concederti un riposino.', 57534),
(269209, 'itIT', '', 'Oh cielo. Oh cielo! Tintinny! C\'è un potenziale cliente.\n\n<Vanni riesce a malapena a contenere l\'emozione.>\n\nPrego, dai pure un\'occhiata alle nostre offerte sui pacchetti di foto, gli oggetti di scena e i panorami e cerca quello giusto per te. Possiamo fare sia foto di gruppo che in solitaria.\n\nQuando avrai scelto gli elementi che fanno per te, Tintinny ti scatterà una foto, così da congelare questo momento nel tempo e non dimenticarlo mai!\n\nInoltre, abbiamo a disposizione una serie di portali verso dei fantastici panorami e terre esotiche per i tuoi ricordi fotografici.', 57534),
(269208, 'itIT', '', 'Amooore! Ce l\'hai fatta! La Mania Modaiola non sarebbe la stessa senza di te.\n\nAlthea si sta occupando dello spettacolo, quindi spero tu abbia portato le tue vesti della migliore qualità. Lei sì che sa riconoscere la roba vintage. Dopotutto, è vintage anche lei.\n\n<Thrynviel ride di gusto.>\n\nFai pure visita ai Mistici Distorcenti se devi fare modifiche dell\'ultimo minuto alla tua mise per soddisfare le richieste di stile di Althea.\n\nUna volta che Althea avrà fatto il suo annuncio e tutti si saranno resi presentabili, raggiungi mio fratello, Thryninval per prendere le coccarde da dare agli altri partecipanti.\n\nBuona fortuna, amooore.\n\nBaci baci.', 57534),
(269207, 'itIT', 'Questa è Cavalca-Mania, una collezione di gloriosi destrieri provenienti da tutto il mondo e oltre.\n\nSe volete partecipare e mostrare a tutti cosa si nasconde nelle vostre stalle, allora non vi allontanate.\n\nMia sorella, Abigail, conduce l\'evento dal palco e sta raccogliendo un\'eclettica esibizione di bestie per mettere alla prova anche il più tenace tra i collezionisti di cavalcature.\n\nDovrebbe dare il via alla festa entro un\'ora.', '', 57534),
(269206, 'itIT', 'Benvenuti al Palco del Cantastorie, un angolo di meraviglia e mistero pensato per deliziare tutti i visitatori.\n\nOggi abbiamo preparato uno spettacolo fantastico per voi, presentato dal Ramingo della Sapienza Cho, venuto apposta da Pandaria! È una storia che richiede la partecipazione del pubblico, quindi vi invitiamo a non restare in silenzio.\n\nIntanto prendetevi pure una bevanda e uno snack mentre aspettate l\'inizio dello spettacolo!', '', 57534),
(269196, 'itIT', 'Il mercato delle lumache è saturo a casa nostra, la Caverna di Zaralek.\n\nLa corsa delle lumache è un passatempo popolare, quindi solo i molluschi più veloci finiscono in vendita.\n\nQui ad Azj-Kahet, però, la velocità sembra contare poco. Speriamo di trovare presto un acquirente per la maggior parte della nostra merce.', '', 57534),
(269195, 'itIT', 'Veniamo da lontano. Dalla Caverna di Zaralek.\n\nDesideriamo vendere la nostra merce. Trattiamo lumache e prodotti a base di lumache.\n\nLe nostre lumache si venderanno a buon prezzo qui. I Niffen farebbero quasi di tutto per averle e i Nerubiani sembrano essere altrettanto attratti dagli odori.\n\nPotremmo forse organizzare qualcosa di più permanente?', '', 57534),
(269193, 'itIT', 'Quando hai escluso l\'impossibile... no, non l\'hai fatto. La magia esiste e la realtà è terrificante.', '', 57534),
(269190, 'itIT', 'Vai via, per favore. Sto cercando di divertirmi.', '', 57534),
(269189, 'itIT', 'Che bella festa! Ho già mangiato sei Polli di Leeroy.', '', 57534),
(269185, 'itIT', 'Ha detto che doveva occuparsi di alcuni affari...\n\nPersonalmente non so di cosa stesse parlando, ma probabilmente Ellanoir lo sa.', '', 57534),
(269184, 'itIT', 'Hai sentito parlare di me? Va bene, cosa volevi chiedermi?', '', 57534),
(269183, 'itIT', 'Uccidere i demoni, che altro?\n\nNe abbiamo scacciati un bel po\' da Val\'sharah prima che ci invitassero a questa festa.\n\nHo dovuto portare via Juddson a forza per fargli prendere un momento di pausa. Ha una grande passione per la caccia ai demoni.', '', 57534),
(269181, 'itIT', 'Già, è un buon amico. Fedele fino alla fine.', '', 57534),
(269180, 'itIT', '', 'Oh, Juddson ha detto che è partito per un viaggio d\'affari?\n\n<Ellanoir ridacchia.>\n\nQuando Herrkrop parte per \"affari\", significa che va verso la spiaggia più vicina. Adora farsi un drink in riva al mare.', 57534),
(269179, 'itIT', '', 'Non lo vedo da quando siamo tornati dalle Isole Disperse. Forse qualcuno degli altri sa dov\'è andato a finire.', 57534),
(269178, 'itIT', '', 'Così come all\'interno di un modulo di Razzy! E proprio come Razzy, sembra freddo fuori ma ha una personalità elettrizzante.', 57534),
(269173, 'itIT', '', 'Ehi, bellezza! Che fai dopo la festa?', 57534),
(269163, 'itIT', 'Ora cambieranno molte cose. Non solo per gli abitanti della Città dei Fili, ma anche per Nizrek.\n\nSpero che questa si riveli la decisione giusta. Oserei dire che la tua reputazione in ascesa ha dato un po\' alla testa al Visir.', '', 57534),
