INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(259441, 'koKR', '전 인내심 있게, 예의 있게, 당신의 그 거만한 표정을 뭉개버리고 싶은 욕구를 온종일 참았습니다.', '', 57534),
(259440, 'koKR', '그것도 나쁘진 않죠.', '', 57534),
(259439, 'koKR', '오늘은 보수 작업, 내일은 수리.', '', 57534),
(259438, 'koKR', '톱니바퀴를 멈추지 마세요.', '', 57534),
(259437, 'koKR', '몸조심하세요.', '', 57534),
(259436, 'koKR', '연장은 가져오셨겠죠.', '', 57534),
(259435, 'koKR', '수리할 시간이군요.', '', 57534),
(259434, 'koKR', '두 번 재고, 한 번 벤다.', '', 57534),
(259433, 'koKR', '제대로 해보자고요.', '', 57534),
(259432, 'koKR', '사업 이야기를 하러 가지... 이 일도 덮어야 하니.', '', 57534),
(259431, 'koKR', '잘됐네. 세상 저 혼자만 잘난 줄 아는 개자식, 꼴도 보기 싫었는데.', '', 57534),
(259430, 'koKR', '의회지기는 내가 적당히 배제할 수 있소. 걱정하지 마시오.', '', 57534),
(259429, 'koKR', '그... 알겠다. 광석을 구할 수 있다면... 그런데 이젠 의회지기에게 쫓기는 신세 아닌가?', '', 57534),
(259428, 'koKR', '아드그린, 우리를 믿으시오. 도르노갈의 상황이 좋아졌소. 광석을 전달하리다. 내 새 감사관으로서 옵스토른이 확실히 처리해 줄 거요.', '', 57534),
(259425, 'koKR', '아드그린 님, 전 제 의지로 스틸베인에게 가담했습니다. 토석인이 고통받지 않게 물류를 유지하려면 어쩔 수 없었습니다.', '', 57534),
(259423, 'koKR', '게다가 당신은 내가 유일하게 믿는 사람을 붙잡고 있잖아! 메릭스가 옵스토른을 체포했다고 들었다. 의회지기를 매수했나?', '', 57534),
(259421, 'koKR', '누가 보면 도르노갈의 문제만 문제인 줄 알겠군! 기계 대변자들도 나름의 골칫거리가 있지만, 그래도 우리는 할 일은 했어.', '', 57534),
(259419, 'koKR', '광석 운송은 지연이 생긴 거요. 당신도 알잖소. 해방자들이 떠나면서...', '', 57534),
(259418, 'koKR', '대화? 난 행동을 원해. 합의한 대로 광석을 전부 내놔! 그리고 옵스토른을 풀어줘!', '', 57534),
(259417, 'koKR', '싸우러 온 게 아니오. 그냥 대화를 할까 해서.', '', 57534),
(259416, 'koKR', '이래선... 해결도... 안 되는데...', '', 57534),
(259415, 'koKR', '고위... 대변자께... 알려야 해...', '', 57534),
(259413, 'koKR', '정의는... 없는... 건가?', '', 57534),
(259412, 'koKR', '용납할... 수... 없어...', '', 57534),
(259411, 'koKR', '내가 이 문제를 해결하지.', '', 57534),
(259410, 'koKR', '적당히 하지.', '', 57534),
(259409, 'koKR', '철저하게... 박살 내주지.', '', 57534),
(259408, 'koKR', '징벌이 필요하겠군.', '', 57534),
(259406, 'koKR', '복종해라.', '', 57534),
(259405, 'koKR', '당신은 우리의 가장 기본적인 원칙을 무시하는 버릇이 있는 것 같군. 어떻게 해야 댁을 정지시킬 수 있을까?', '', 57534),
(259404, 'koKR', '당신 방식은... 이상하군.', '', 57534),
(259403, 'koKR', '발밑을 조심해.', '', 57534),
(259402, 'koKR', '늘 복종하도록.', '', 57534),
(259401, 'koKR', '고위 대변자를 찬양하라.', '', 57534),
(259400, 'koKR', '수상쩍은... 이야기를.', '', 57534),
(259399, 'koKR', '듣고 있다... 일단은.', '', 57534),
(259398, 'koKR', '또 농땡이 피우고 있지.', '', 57534),
(259397, 'koKR', '용건만 빨리 말해.', '', 57534),
(259396, 'koKR', '메릭스가 미소를 짓습니다.', '', 57534),
(259395, 'koKR', '하여간 기계 대변자들의 음모론이란...', '', 57534),
(259393, 'koKR', '그자와 한통속이었나!', '', 57534),
(259392, 'koKR', '말도 안 되긴? 그게 아니고서야 여길 어떻게 알았겠소? 스틸베인이 댁 수족이든가, 아니면...', '', 57534),
(259389, 'koKR', '<당신을 노려보는 옵스토른>\n\n그래! 의회지기는 스틸베인만 가진 게 아니라 외지인도 수하로 두고 있었군.\n\n그럼 그렇지.', '', 57534),
(259388, 'koKR', '날 광낼 생각이라면 좀 더 노력하게. 여기 보이지, 응? 때 빼고 광내란 말이야!', '', 57534),
(259387, 'koKR', '난 아제로스가 놓아주는 그날까지 계속 나아갈 걸세.', '', 57534),
(259386, 'koKR', '영웅에게 쉴 틈은 없지. 안 그런가, 용사?', '', 57534),
(259385, 'koKR', '에이, 얼른 가보게.', '', 57534),
(259384, 'koKR', '투쟁을 멈추지 말게, 용사여.', '', 57534),
(259383, 'koKR', '할 일은 늘 있지.', '', 57534),
(259382, 'koKR', '세상이 치유될 때, 나도 치유받을 수 있으면 좋겠구먼.', '', 57534),
(259380, 'koKR', '통행룬 확인. 들어가십시오.', '', 57534),
(259378, 'koKR', '<골렘이 출입구 앞에 움직이지 않고 서 있습니다. 육중한 몸체에 희미한 표식이 새겨져 있습니다.>', '', 57534),
(259375, 'koKR', '땅이 참 부드럽지 않나? 지하가 새끼 사슴 엉덩이 같을 줄 누가 알았겠어?', '', 57534),
(259374, 'koKR', '대지 깊은 곳에서 무엇이 있을지 누가 알겠나?', '', 57534),
(259373, 'koKR', '동족이 날 필요로 하는 곳이라면 어디든 달려갈 걸세.', '', 57534),
(259372, 'koKR', '굴복해선 안 되네. 계속 싸워야 해!', '', 57534),
(259371, 'koKR', '난 부족과 가문 없이 절대 성공 못 했을 걸세.', '', 57534),
(259370, 'koKR', '아제로스는 우리에게 달려 있네.', '', 57534),
(259369, 'koKR', '듣고 있네.', '', 57534),
(259368, 'koKR', '누구나 두 번째 기회를 누릴 자격이 있네.', '', 57534),
(259367, 'koKR', '무슨 일이든 준비됐네!', '', 57534),
(259366, 'koKR', '외교 관련 질문은 내 딸 모이라에게 하게나.', '', 57534),
(259365, 'koKR', '이 몸이 바로... 마그니일세!', '', 57534),
(259364, 'koKR', '용사여! 반갑네.', '', 57534),
(259363, 'koKR', '우린 앞으로 나아가야 해.', '', 57534),
(259362, 'koKR', '아제로스는 여전히 우리가 필요하네.', '', 57534),
(259361, 'koKR', '아! 와줘서 고맙네!', '', 57534),
(259360, 'koKR', '때로는 운명이 속삭이기도, 소리를 지르기도 하지.', '', 57534),
(259358, 'koKR', '이보게. 나 아직 살아있네!', '', 57534),
(259357, 'koKR', '크게 말하게!', '', 57534),
(259355, 'koKR', '지금도 난 아제로스를 섬길 수밖에 없어.', '', 57534),
(259354, 'koKR', '요즘은 누굴 대변하지도 않아.', '', 57534),
(259353, 'koKR', '얼른 말하게!', '', 57534),
(259352, 'koKR', '\"아제로스를 위하여!\" 흐흐.', '', 57534),
(259349, 'koKR', '나는 이 친구들을 횃대로 데려갈 테니, 자네가 확인해 보게나.', '', 57534),
(259348, 'koKR', '용사, 그 늙은 폭풍기수 있잖나. 올바리그 말이야. 양조장 근처를 날아가는 걸 봤네.', '', 57534),
(259347, 'koKR', '모두 수고했네. 이제 폭풍떼까마귀에게 휴식 시간을 주지.', '', 57534),
(259346, 'koKR', '중요한 건 나이가 아니라 경험이죠.', '', 57534),
(259345, 'koKR', '', '젊은 친구들? 우리 나이가 수천 살인데.', 57534),
(259344, 'koKR', '저것들을 심연으로 돌려보내게, 젊은 친구들!', '', 57534),
(259343, 'koKR', '바로 그거야. 저 야수들을 사냥하지.', '', 57534),
(259341, 'koKR', '가만히 있는 표적을 맞히는 건 능숙해졌군. 그럼 움직이는 표적은 어떨까?', '', 57534),
(259340, 'koKR', '으으으음...!', '', 57534),
(259339, 'koKR', '하! 그러면 네루비안이 봐줄 것 같나?', '', 57534),
(259338, 'koKR', '흐으으으...', '', 57534),
(259337, 'koKR', '', '이게 저희로서는 새로운 지령이라는 거, 알고 계시죠?', 57534),
(259336, 'koKR', '나쁘지 않아. 하지만 그것만으로는 부족해.', '', 57534),
(259335, 'koKR', '전투라면 생각 있는데요.', '', 57534),
(259334, 'koKR', '멋진 구경이 되겠네요. 공중 곡예 해보고 싶으신 분?', '', 57534),
(259333, 'koKR', '떼까마귀를 몰고 고도를 높이게. 높아질수록 마력을 방출할 걸세.', '', 57534),
(259332, 'koKR', '', '아니... 어떻게요?', 57534),
(259331, 'koKR', '슈라우벤더를 타고 표적을 날려버리게!', '', 57534),
(259329, 'koKR', '그래. 공격은 그렇게 하는 거야.', '', 57534),
(259326, 'koKR', '그르르르르...?', '', 57534),
(259320, 'koKR', '<잠시 생각에 잠긴 채 콧노래를 흥얼거리는 메릭스>\n\n단언할 수는 없지만, 해방자들이 보내는 보급품과 원자재가 줄어들기 시작한 것과 이번 사건이 관련이 있을 것 같구려.\n\n안타까운 일이오. 교역량은 상승 곡선을 그리고 있는데, 정작 심연으로 가는 화물은 수가 줄어들고 있었다는 의미가 되니.\n\n공식적으로는 도르노갈은 상호 무역 협정을 맺고 있소. 그렇다면 개인적인 불만에서 비롯된 행각이란 건데....', '', 57534),
(259318, 'koKR', '고맙다, 자레인. 함께 바엘그림을 기리자꾸나.', '', 57534),
(259317, 'koKR', '이제 알겠어... 우리 둘 다 옳았던 겁니다, 바엘그림. 폭풍떼까마귀는 자유로우면서도 섬기기를 택할 수 있는 거였어요. 나와 함께 날겠느냐?', '', 57534),
(259316, 'koKR', '이제 넌 자유의 몸이다. 자! 어서 가!', '', 57534),
(259315, 'koKR', '자레인! 안 떠난 게냐? 바엘그림은 이제 없는데도?', '', 57534),
(259312, 'koKR', '그리고 그 망할 놈의 티탄 기계는 그런 기억을 몇 번이나 지웠을까요?', '', 57534),
(259311, 'koKR', '우린 폭풍떼까마귀를 두고 틈만 나면 입씨름을 벌였죠. 그게 몇 번이나 될까요, 바엘그림?', '', 57534),
(259309, 'koKR', '도시에서 일어나는 모든 일을 파악하는 건 의회지기로서 마땅히 지켜야 할 의무요.\n\n토석인 간의 분쟁은 흔한 편이지만, 이번 사건처럼 다수가 연루되는 사례는 드물지.', '', 57534),
(259304, 'koKR', '베브루시는 금빛 교환소 근처에서 일을 본다오. 당신과 거래할 의향이 있을지도 모르지.', '', 57534),
(259302, 'koKR', '', '스틸베인이 없었다면 우린 어떻게 됐을까요?\n\n그분은 항상 필요한 것을 구해다 주셨어요.\n\n그분이 칙령을 비틀어 주시는 덕분에 우린 그러지 않아도 되는 거죠.', 57534),
(259300, 'koKR', '', '거수예요! 빨리 쓰러뜨려야 합니다!', 57534),
(259299, 'koKR', '', '조심하세요. 마귀들이 더 옵니다!', 57534),
(259298, 'koKR', '', '머릿수가 줄어들면서 몇몇은 믿음이 흔들리고 있어요. 그러다 어둠에서 들려오는 속삭임에 귀를 기울이게 되죠.', 57534),
(259297, 'koKR', '', '적이 옵니다! 창을 사용하세요!', 57534),
(259296, 'koKR', '스틸베인은 자신을 돕는 사람들을 챙깁니다. 우리도 신뢰를 얻으려고 얼마나 노력했는데요.', '', 57534),
(259295, 'koKR', '', '요령이 좋군요! 다음에 퇴치할 일이 생기면 부르겠습니다.', 57534),
(259294, 'koKR', '', '저번 순간이동 당시 우리 밖에 나와 있던 나비들이 버려진 적 있습니다. 나비 잡는 것 좀 도와주십시오.', 57534),
(259281, 'koKR', '', '부서진 해변으로 옮겼을 때 고작 일주일 만에 겨울 뿌리가 모두 상했던 거 있죠! 도와주셔서 감사해요.', 57534),
(259280, 'koKR', '', '아, $n 님! 부탁인데 이 음식 좀 드셔주실래요? 날씨가 바뀌면 상할 것 같아서요.', 57534),
(259264, 'koKR', '감사합니다. 저번에는 단검이 아슬아슬하게 눈을 스쳐 지나갔거든요! 물론, 안대를 쓰면 멋져 보이겠지만...', '', 57534),
(259263, 'koKR', '달라란이 다시 이사를 간다고 들었어요. 이번엔 선반이 넘어지지 않게 도와주실 수 있을까요?', '', 57534),
(259262, 'koKR', '', '베룬드가 공포파괴자를 지키고 서 있는 야수를 정찰하고 있다. 이걸로 충분하길 바라자고. 얼른 아버지에게 망치를 돌려주고 싶거든.', 57534),
(259251, 'koKR', '일을 마무리해 주셔서 감사합니다. 우리는 모드히나를 기억할 겁니다.\n\n아득한 세월을 살아왔는데, 삶에서 가장 큰 변화는 고작 하루 만에 일어났군요.\n\n당신의 도시가 우리 해안으로 추락했고, 우리 종족은 처음으로 외부 세계를 만났죠. 고대의 적이 나타나 섬을 황폐하게 만들기도 했고요. 분열됐던 토석인은 다시 하나가 되었습니다.\n\n이것들은 우리 해방자가 서약신도에서 갈라져 나온 이후로 쭉 찾아 헤매던 징조입니다.\n\n우리가 모르고 있던 게 너무나 많습니다... 지금까지 알아낸 것도 빙산의 일각에 불과하겠죠.', '', 57534),
(259250, 'koKR', '', '새로운 도자기를 만들어 올게요, 코둔. 모드히나의 노력이 헛되지 않게 할 거예요.', 57534),
(259249, 'koKR', '', '고맙습니다, $p. 이렇게 깊은 감사를 느껴보긴 처음입니다.', 57534),
(259248, 'koKR', '흐레카...', '', 57534),
(259247, 'koKR', '', '또, 유약이 너무 건조해서 깨질 것 같아요. 새로운 광석을 제련하는 연습이 더 해야겠군요.', 57534),
(259246, 'koKR', '', '비대칭적이고, 길쭉하고.', 57534),
(259245, 'koKR', '', '...흠. 테두리가 울퉁불퉁하네요.', 57534),
(259244, 'koKR', '', '어디 보자.', 57534),
(259243, 'koKR', '', '광석이 녹아 유약이 됐군요. 이제 바르겠습니다. 하던 일을 계속하세요.', 57534),
(259242, 'koKR', '', '이 항독소로 다른 사람들을 풀어주도록 해!', 57534),
(259241, 'koKR', '', '일단 눈에 안 띄는 곳으로 가서 다음 계획을 세우세.', 57534),
(259240, 'koKR', '', '자네, 진정하게.', 57534),
(259239, 'koKR', '', '무한한 눈... 무한한 발톱...', 57534),
(259238, 'koKR', '', '너는? 이 한낱 벌레가! 그분이 모든 걸 집어삼키리라!', 57534),
(259237, 'koKR', '', '자네가 막게. 뒤는 내게 맡겨.', 57534),
(259236, 'koKR', '', '이테키는... 제정신이 아닐세. 뭐라도 손을 써야 해.', 57534),
(259234, 'koKR', '방직공은 반기지 않겠지만 안수레크도 반드시 축출해야 해.', '', 57534),
(259233, 'koKR', '좋아, 그만! 대화를 하고 싶나, 스틸베인? 많은 걸 해명해야 할 거야.', '', 57534),
(259232, 'koKR', '잘아타스가 확실히 문제이긴 하나, 여왕이 영향을 받고 있는 건 아니야. 본인의 신념에 따라 선택한 거니까.', '', 57534),
(259231, 'koKR', '시간이 촉박하다. 여기 대시장에 들어갈 수 있게 해줄 페로몬이다. 몸에 바르도록.', '', 57534),
(259225, 'koKR', '브론즈비어드는 누구도 통제할 수 없다고요. 보물을 챙겨서 빠져나가죠.', '', 57534),
(259223, 'koKR', '이번 공연에서는 이겼지만 언젠가 당신은 내 것이 될 겁니다.', '', 57534),
(259216, 'koKR', '', '됐어요! 이제 가열로에 넣어 구울 겁니다.', 57534),
(259215, 'koKR', '', '너무 깊이 생각하지 마세요. 흐름에 따르세요. 손을 믿는 겁니다.', 57534),
(259214, 'koKR', '', '제련 작업을 할 동안 탁자에 놓인 점토로 모양을 만들어 보시겠어요?', 57534),
(259213, 'koKR', '아쉽게 됐네. 난 불만이 있거든!', '', 57534),
(259212, 'koKR', '난 그냥 얘기하고 싶을 뿐이오, 아드그린. 합의만 할 수 있다면 굳이 다툴 생각은 없소.', '', 57534),
(259211, 'koKR', '스틸베인? 이것 좀 봐라. 드디어 용기란 게 생겼나 보군.', '', 57534),
(259210, 'koKR', '', '광석을 제련해야 하니 여기 놓으세요.', 57534),
(259208, 'koKR', '저 앞이 아드그린의 집무실이오.', '', 57534),
(259207, 'koKR', '와줘서 고맙소, $p. 이제 끝을 냅시다.', '', 57534),
(259206, 'koKR', '알겠소, 의회... 어, 스틸베인.', '', 57534),
(259205, 'koKR', '옵스토른, 따라오시오. 당신이 증인이 되어줬으면 좋겠소.', '', 57534),
(259204, 'koKR', '토석인은 둘 다 필요하오. 둘 중 하나가 없으면 고통을 겪을 테지. 하지만 몇몇 문제는 스틸베인만이 할 수 있는... 독특한 대응법이 필요하지.', '', 57534),
(259203, 'koKR', '그래서 칙령을 비틀어 해석하는 방법을 택했소. 내 정신이... 두 갈래로 갈라지는 기분이더군. 메릭스와 스틸베인. 심상과 정수. 표면과 내부 구조.', '', 57534),
(259202, 'koKR', '도르노갈을 버린 자들을 돕는다면 칙령이 깨질 테니까. 근데 그렇다고 돕지 않는다면 나로선 칙령을 어기는 것과 마찬가지였지.', '', 57534),
(259201, 'koKR', '토석인이 칙령에 등지고 떠난 해방자와 철저히 준수하는 서약신도로 분열됐을 때, 나는 모순에 사로잡혔소.', '', 57534),
(259200, 'koKR', '나의 목적은 모든 토석인을 보호하는 거요. 이것이 제가 다른 무엇보다도 따르는 칙령이오.', '', 57534),
(259198, 'koKR', '다 구했네요. 인형술사를 찾아서 막죠!', '', 57534),
(259195, 'koKR', '', '방직공께서 이곳 포로들의 구출을 요청하셨다.', 57534),
(259192, 'koKR', '', '인형술사에게 붙잡힌 녀석이 하나 더 있어. 기다려 봐. 내가 풀어주지.', 57534),
(259188, 'koKR', '이 문제는 여전히 평화롭게 해결될 수 있소. 난 그렇게 확신하오.', '', 57534),
(259187, 'koKR', '알고 있소. 그래도... 일이 터지기 전에 설득하지 않은 게 후회되는군.', '', 57534),
(259186, 'koKR', '새삼 놀랍지 않은 반응이군. 의심을 사지 않고 당신의 새로운 지위를 알릴 방법이 없었던 터라.', '', 57534),
(259185, 'koKR', '이 뒤에 아드그린이 있소. 스틸베인이 돈을 써서 날 묶어둔 줄 알고 있을 테지. 설마 손을 잡았을 거란 데까지는 생각이 안 닿을 것이오.', '', 57534),
(259184, 'koKR', '보물을 찾으실 동안 제가 지켜볼게요.', '', 57534),
(259183, 'koKR', '골드 아래에 숨은 작은 녀석들을 조심하세요!', '', 57534),
(259182, 'koKR', '다른 네루비안들이 돌아오기 전에 보물을 가져가자고요.', '', 57534),
(259180, 'koKR', '믿어지시나요? 이제 저 지독한 녀석만 치우면 보물을 손에 넣을 수 있어요. 운이 좋은 거죠!', '', 57534),
(259178, 'koKR', '행실이 워낙 인상 깊어서 말이야... 포로 호송대의 방향을... 틀지 않고는 못 배기겠더군.', '', 57534),
(259176, 'koKR', '그러니까, 아버지가 했던 것처럼 하면...', '', 57534),
(259175, 'koKR', '이해해 주실 거라곤 생각 않습니다. 다만... 비행선에서, 그 순간에 말입니다.', '', 57534),
(259174, 'koKR', '', '좋아요, 안두인. 말해봐요.', 57534),
(259173, 'koKR', '', '아. 무슨 일이죠?', 57534),
(259172, 'koKR', '', '코둔! 일찍 못 와서 미안해요. $p 님이 친절하게도...', 57534),
(259170, 'koKR', '얘기를 들을 생각 따윈 없소! 당신이나 메릭스나 스틸베인하고 한패잖소!', '', 57534),
(259169, 'koKR', '밀수업자 스틸베인과의 연루가 의심되어 이 저장소는 무기한 폐쇄합니다.', '', 57534),
(259168, 'koKR', '', '마그니 님을 회복시킬 수 있기를 빌어야죠. 준비되는 대로 달라란에서 뵙겠습니다.', 57534),
(259166, 'koKR', '알레리아. 이 전쟁을 끝낼 수 있어요! 시위를 놓으면 전부 수포가 될 거예요.', '', 57534),
(259164, 'koKR', '그리고 카즈 알가르라는 이름도.', '', 57534),
(259161, 'koKR', '', '당신은 최고의 아버지는 아니었어. 하지만 좋은 할아버지는 될 수 있잖아. 제발... 일어나.', 57534),
(259160, 'koKR', '', '하지만 내 아들, 어린 다그란은 아직 당신이 필요해. 인정하기 싫지만 나도 마찬가지고.', 57534),
(259159, 'koKR', '', '이젠... 그 벽을 허물 기회가 영영 안 올지도 모르겠네.', 57534),
(259158, 'koKR', '', '내게서 그이를 빼앗아 간 당신이 미웠어. 그 분노가 우리 둘 다 넘을 수 없는 벽을 만들었지.', 57534),
(259157, 'koKR', '', '당신 의도가 어쨌든 내 남편은 죽었어. 당신 때문에.', 57534),
(259156, 'koKR', '', '내가 내 의지로 검은무쇠에 남은 줄은 몰랐겠지. 다그란과 내가 실제로 사랑했던 것도.', 57534),
(259155, 'koKR', '', '이 좀생이 자식!', 57534),
(259154, 'koKR', '나쁘게 생각 마세요! 이 망치로 당신을 추모하며 위대한 업적을 세울 테니!', '', 57534),
(259152, 'koKR', '', '가요, 안두인. 도르노갈로.', 57534),
(259140, 'koKR', '어디요, 마그니? 어디로 가야 하오?', '', 57534),
(259139, 'koKR', '앞으로 어떤 일이 닥치더라도 우리는 함께 이겨낼 것이오.', '', 57534),
(259138, 'koKR', '주민들에게 알리고, 도시의 순간이동을 준비하시오.', '', 57534),
(259137, 'koKR', '\"카즈 알가르\" 수호에 달라란의 전력을 투입하겠소.', '', 57534),
(259136, 'koKR', '대마법사 드렌덴이 제안했고, 의회는 동의하였으니...', '', 57534),
(259135, 'koKR', '다급한 목소리였어. 빨리 행동해야 한다는 의미겠지.', '', 57534),
(259134, 'koKR', '아제로스는 고대 땅 카즈 알가르에서 불타오르는 도시의 환영을 보여주었네.', '', 57534),
(259133, 'koKR', '골렘에게 통행룬을 보여주시오. 인사는 들어가서 하지.', '', 57534),
(259132, 'koKR', '고위 대변자가 오래 전에 이 공간을 청했지. 난 의회지기로 일하면서 한 번도 이곳에 들어간 적이 없다오.', '', 57534),
(259130, 'koKR', '달라란이 또 한 번 세상을 도와야 하오. 마그니?', '', 57534),
(259126, 'koKR', '', '용사님, 슈라우벤더를 타고 가세요! 녀석은 당신을 믿어요. 티탄의 기계로부터 폭풍기수들을 지켜주실 거라 믿어 의심치 않아요.', 57534),
(259125, 'koKR', '이건...', '', 57534),
(259124, 'koKR', '아니, 보이네!', '', 57534),
(259121, 'koKR', '하여간 이놈의 형제들은 행성하고 얘기하려다 잠드는 게 몇 번째인지...\n\n<눈가의 물기를 황급히 훔치는 브란>\n\n아, 나이를 먹으니 감성적으로 변하네요.', '', 57534),
(259120, 'koKR', '마그니에게 해답을 들을 수 있으면 좋겠는데.', '', 57534),
(259119, 'koKR', '통행룬을 얻었으니 아드그린의 수하를 찾아가 이야기를 나눕시다. 아래 땅굴로 오시오.', '', 57534),
(259109, 'koKR', '진정하고 활 내리세요. 이자는 아군입니다.', '', 57534),
(259103, 'koKR', '', '당장 멀리 떨어져라, 괴물아!', 57534),
(259101, 'koKR', '예, 의회지기님!', '', 57534),
(259100, 'koKR', '수고했소, 크라가드. 언제나처럼 경계를 늦추지 마시오. 난 집무실로 돌아갈 테니, 뭐라도 찾으면 알려주시오.', '', 57534),
(259090, 'koKR', '물고기를 못 잡으면 개구리를 먹으면 되잖아요!', '', 57534),
