INSERT INTO `mount_locale` (`ID`, `locale`, `Name_lang`, `SourceText_lang`, `Description_lang`, `VerifiedBuild`) VALUES
(2683, 'deDE', 'Knochenross des Vergessens', '|cFFFFD200Weltereignis:|r WoW Remix: Legion', 'Ein gutes Schlachtross ist treu bis auf die Knochen. Dieses Pferd hat seine sterbliche Hülle abgelegt und ist ein Vorbote von Mangel und Tod.', 63906),
(2682, 'deDE', 'Knochenross der Seuche', '|cFFFFD200Weltereignis:|r WoW Remix: Legion', 'Ein gutes Schlachtross ist treu bis auf die Knochen. Dieses Pferd hat seine sterbliche Hülle abgelegt und ist ein Vorbote von Krankheit und Leid.', 63906),
(2681, 'deDE', 'Knochenross des Blutvergießens', '|cFFFFD200Weltereignis:|r WoW Remix: Legion', 'Ein gutes Schlachtross ist treu bis auf die Knochen. Dieses Pferd hat seine sterbliche Hülle abgelegt und ist ein Vorbote des ewigen Kampfes.', 63906),
(2679, 'deDE', 'Knochenross des Triumphs', '|cFFFFD200Weltereignis:|r WoW Remix: Legion', 'Ein gutes Schlachtross ist treu bis auf die Knochen. Dieses Pferd hat seine sterbliche Hülle abgelegt und ist ein Vorbote heldenhafter Siege.', 63906),
(2732, 'deDE', 'Kobaltfarbener Phasenjäger', '|cFFFFD200Aktion:|r Burning Crusade Classic', 'Die außerweltlichen Energien, die Draenor verstrahlten, verleihen diesen Kreaturen die Fähigkeit, sich beliebig durch den Raum zu teleportieren. Man sieht sie nur selten kommen... bis es zu spät ist.', 63906),
(2640, 'deDE', 'Bomber des Braufests', '|cFFFFD200Beute:|r Coren Düsterbräu|n|cFFFFD200Feiertag:|r Braufest', 'Passt auf, dass Ihr nicht die Munition verschlingt.', 63906),
(2566, 'deDE', 'Lana\'thels blutrote Kaskade', '|cFFFFD200WoW-Shop|r ', 'Ihre Beherrschung der Blutkünste hat es der Blutkönigin erlaubt, einen grauenhaften Diener zu beschwören, der sie in die Schlacht trägt. In einer Tracht gekleidet, die ihrem Hof angemessen ist, präsentiert er ein furchterregendes Antlitz sowie Flügeln und Klauen, die den Zorn ihrer Meisterin zum Ausdruck bringen.', 63906);
