INSERT INTO `ui_splash_screen_locale` (`ID`, `locale`, `Header_lang`, `TopLeftFeatureTitle_lang`, `TopLeftFeatureDesc_lang`, `BottomLeftFeatureTitle_lang`, `BottomLeftFeatureDesc_lang`, `RightFeatureTitle_lang`, `RightFeatureDesc_lang`, `VerifiedBuild`) VALUES
(85, 'deDE', 'Neu in Patch 11.2.0!', 'Ökologische Erhaltung', 'Helft Ve\'nari, Kreaturen aus ganz Azeroth zu sammeln, um in K\'aresh neues Leben erblühen zu lassen.', 'Biokuppel Al\'dani', 'Verteidigt die verborgene Biokuppel und lüftet die uralten Geheimnisse, die darin verborgen sind!', 'K\'aresh', 'Stürzt Euch in die dunklen Geheimnisse der Leere auf dem verlorenen Planeten der Astralen.', 63906),
(84, 'deDE', 'Neu in Patch 11.1.7!', 'Lehrensuche', 'Sprecht mit Lehrensucher Cho in einer Hauptstadt, um im Zuge von legendären Geschichten neue Questerfahrungen zu erleben!', 'Kampfassistent', 'Aktiviert dieses Feature in den Einstellungen und erhaltet Hilfe bei der Kampfrotation!', 'Tiefen: Überladen!', 'Mehr Macht mit den Titanenmaschinen, um neue Tiefen zu erkunden und neue Herausforderungen zu meistern!', 63906),
(80, 'deDE', 'Neu in Patch 11.1.5!', 'Abklingzeitmanager', 'Aktiviert dieses Feature in den Einstellungen und behaltet Eure Abklingzeiten im Blick!', 'In Kürze verfügbar: Durchtriebene Duos', 'Kämpft gegen ein Duo an Bösewichten aus Azeroths Vergangenheit und versucht, die Höchstpunktzahl zu erlangen!', 'Verstörende Visionen erneut erlebt', 'Kehrt zu den Tiefen des Wahnsinns zurück, um neue Herausforderungen zu erleben, aber auch neue Belohnungen zu erhalten!', 63906),
(76, 'deDE', 'Neu in Patch 11.1.0!', 'F.A.H.R.E.N.', 'Erlebt mit dem 99-G-Genickbrecher in Lorenhall eine neue Art der Fortbewegung!', 'Passt Euren Lagerplatz an', 'Erstellt neue Lager im Charakterauswahlbildschirm und wählt aus einer Reihe von freischaltbaren Hintergründen aus!', 'Lorenhall', 'Führt die Verfolgung von Xal\'atath fort und stellt Euch Gallywix\' Streitkräften in den weitläufigen Straßen Lorenhalls.', 63906),
(73, 'deDE', 'Neu in Saison 1!', 'Saison 1 beginnt', 'Verdient Belohnungen in mythischen Dungeons und auf dem neuen gewerteten Blitzschlachtfeld!', 'Palast der Nerub\'ar', 'Greift den Sitz von Königin Ansureks Macht in Azj-Kahet an.', 'Zekvirs Höhle', 'Schreitet in den saisonalen Inhalten der Tiefen voran, um Zekvirs Höhle freizuschalten.', 63906),
(72, 'deDE', 'Neu in The War Within!', 'Tiefen', 'Taucht in die Tiefen ein, lüftet Geheimnisse und sucht Schätze.', 'Heldentalente', 'Erweitert Eure Klassenfantasy und passt Euren Talentbuild an!', 'The War Within', 'Reist nach Khaz Algar und folgt dem Ruf des strahlenden Gesangs!', 63906),
(71, 'deDE', 'Neu im The War Within Pre-Patch!', 'Strahlende Echos', 'Bezwingt Feinde aus Azeroths Vergangenheit, wenn Erinnerungen wieder emporkommen. Verfügbar ab dem 31. Juli!', 'The War Within erscheint in Kürze', 'Erscheint am 26. August (Frühzugang ab dem 22. August)', 'Kriegsmeuten', 'Eure Charaktere haben gemeinsamen Zugriff auf eine Kriegsmeutenbank, Dragonflight-Ruf und mehr!', 63906),
(70, 'deDE', 'Neu in 10.2.7', 'Erbe der Draenei und Trolle', 'Sammelt besondere Rüstungssets, während Ihr ihre Kulturen und Geschichte erkundet.', 'Pandaria Remixed', 'Eine wilde Art in der Stufe aufzusteigen ab nächster Woche!', 'Bote der Dunkelheit', 'Auf den Dracheninseln kehrt wieder Frieden ein, aber ein finsterer Plan wird in Gang gesetzt.', 63906),
(69, 'deDE', '10.2.7 Mists of Pandaria: Remix', 'Verdient Euch Accountbelohnungen', 'Sammelt Bronze, um Reittiere, Haustiere und Vorlagen zu verdienen!', 'Los geht\'s', 'Erstellt einen neuen Zeitläufer in der Charakterauswahl.', 'Mists of Pandaria: Remix', 'Erlebt eine wilde Wendung von Mists of Pandaria!', 63906),
(68, 'deDE', '', 'Belohnungen verdienen', 'Es gibt Beute zu sammeln, um Belohnungen wie Reittiere, Haustiere und Transmogrifikationen zu verdienen!', 'Los geht\'s', 'Zum Spielen ausloggen und im Charakterauswahlbildschirm \"Plunderstorm\" auswählen', 'Plunderstorm', 'Piraten treffen in einem großen Battle Royale aufeinander!', 63906),
(64, 'deDE', 'Neu in Saison 4!', 'Saison 4 von Dragonflight', 'Erhaltet neue Belohnungen.', 'Dungeonfortschritt aktualisiert', 'Schwierigkeitsgrade und Belohnungen für Heroisch, Mythisch und Mythisch+ wurden überarbeitet.', 'Erweckte Schlachtzüge', 'Bezwingt alle drei Schlachtzüge von Dragonflight für verbesserte Belohnungen!', 63906),
(63, 'deDE', 'Neu in 10.2.5!', 'Archive von Azeroth', 'Stürzt Euch in die vergessenen Geschichten und Mythen der Dracheninseln.', 'Anhängerdungeons', 'Meldet Euch mit NSC-Anhängern für Dungeons von Dragonflight an!', 'Rückeroberung von Gilneas', 'Schließt Euch Genn Graumähne und den Gilneern an, während sie ihr Heimatland zurückerobern.', 63906),
(61, 'deDE', 'Neu in Patch 10.2.0!', 'Traumwachen', 'Schließt Euch der Verteidigung des Traums an, mit neuen Ruhmstufen, Ereignissen und Belohnungen.', 'Blitzschlachtfeld', 'Gönnt Euch ein 8vs8-Solowarteschlangen-Schlachtfelderlebnis. In diesem Modus geht alles sehr schnell!', 'Der Smaragdgrüne Traum', 'Betretet den Traum und verteidigt den blühenden Weltenbaum Amirdrassil gegen Fyrakk und die Druiden der Flamme.', 63906),
(60, 'deDE', 'Neu in Patch 10.1.7!', 'Traditionsrüstungssets der Nachtelfen und Verlassenen', 'Sammelt besondere Rüstungssets, während Ihr die Kulturen und Geschichte dieser Völker erkundet.', 'Wir stellen vor: das Ping-System!', 'Ruft das Optionsmenü auf, um schnellere Kommunikation mit Pings zu starten.', 'Traumsprünge', 'Kämpft gegen vom Traum ermächtigte Feinde auf den Dracheninseln, bis Amirdrassil endlich erblüht!', 63906),
(59, 'deDE', 'Neu in Patch 10.1.5!', 'Verstärkung', 'Eine neue Schadensspezialisierung mit einem einzigartigen, auf Unterstützung ausgelegten Spielstil!', 'Zerfallende Zeitwege', 'Unterstützt den bronzenen Drachenschwarm an der Grenze des Äons und in Zeitrissen!', 'Dämmerung des Ewigen', 'Kämpft in einem mythischen Megadungeon gegen den ewigen Drachenschwarm!', 63906),
(58, 'deDE', 'Neu in Saison 2!', 'Saison 2 von Dragonflight', 'Für mythische Schlüsselsteindungeons und PvP-Kämpfe winken neue Belohnungen.', 'Auffrischung für Mythisch+', 'Erlebt neue Affixe und aufgefrischte Dungeons auf Mythisch+!', 'Aberrus, Schmelztiegel der Schatten', 'Geht Sarkareth nach und erkundet in diesem uralten Labor das Vermächtnis von Neltharion.', 63906),
(57, 'deDE', 'Neu in Patch 10.1.0!', 'Schlängelnder Kriecherdrache', 'Rast auf dem neuen hiesigen Reitdrachen durch die Zaralekhöhle!', 'Fraktionsübergreifende Gilden', 'Zum ersten Mal können Spielercharaktere der Horde und Allianz sich in einer Gilde zusammenschließen!', 'Zaralekhöhle', 'Führt die Geschichte der Splitterflammen fort und stellt die Streitkräfte der Inkarnationen in dieser vergessenen, unterirdischen Landschaft.', 63906),
(56, 'deDE', 'Neu in 10.0.7!', 'Erbe der Menschen und Orcs', 'Sammelt besondere Rüstungssets, während Ihr die Kulturen und Geschichte dieser Völker erkundet.', 'Update: Werbt einen Freund', 'Erhaltet spannende neue Belohnungen, wenn Ihr Eure Freunde nach Azeroth bringt!', 'Kehrt zur Verbotenen Insel zurück.', 'Ergründet die Geheimnisse der Dracthyr, die während ihrer Flucht zurückgelassen wurden.', 63906),
(55, 'deDE', 'Neu in Patch 10.0.5!', 'Der Zorn des Sturms', 'Kämpft in der Zukunft der Primalisten gegen die Primalisten und erhaltet epische Belohnungen.', 'Transmogrifikation-Updates', 'Weiße und graue Gegenstände können jetzt für Transmogrifikationen gesammelt werden!', 'Der Handelsposten hat geöffnet!', 'In Sturmwind und Orgrimmar hat jetzt ein neuer Laden für kosmetische Gegenstände geöffnet.', 63906),
(50, 'deDE', 'Neu in Saison 1!', 'PvP-Saison 1', 'Verdient Belohnungen im neuen Modus gewertetes gemischtes Einzel.', 'Mythisch+ von Saison 1', 'Eine neue Dungeonrotation und das Affix \"Donnernd\" erwarten Euch.', 'Gewölbe der Inkarnationen', 'Hindert die Primalisten daran, Raszageths gefangene Artgenossen zu befreien.', 63906),
(49, 'deDE', 'Neu in Dragonflight!', 'Aktualisierung der Berufe', 'Neue Funktionen wie Handwerksaufträge, Ausrüstung, Spezialisierungen und vieles mehr!', 'Drachenreiten', 'Segelt durch die Lüfte der Dracheninseln mit einem aufregenden neuen Bewegungssystem!', 'Dracheninseln', 'Reist in die alte Heimat der Drachenschwärme und helft ihnen, ihr Vermächtnis zurückzugewinnen.', 63906);
