INSERT INTO `ui_splash_screen_locale` (`ID`, `locale`, `Header_lang`, `TopLeftFeatureTitle_lang`, `TopLeftFeatureDesc_lang`, `BottomLeftFeatureTitle_lang`, `BottomLeftFeatureDesc_lang`, `RightFeatureTitle_lang`, `RightFeatureDesc_lang`, `VerifiedBuild`) VALUES
(85, 'esMX', '¡Novedades del parche 11.2.0!', 'Sucesión ecológica', 'Reúne criaturas de todo Azeroth para ayudar a Ve\'nari a restaurar la vida de K\'aresh.', 'Ecodomo Al\'dani', '¡Defiende el ecodomo oculto y descubre los secretos antiguos que guarda!', 'K\'aresh', 'Explora los secretos oscuros del Vacío en el planeta perdido de los etéreos.', 63906),
(84, 'esMX', '¡Novedades del parche 11.1.7!', 'Cronoleyenda', '¡Habla con el eremita Cho en una ciudad capital para probar nuevas misiones que te guiarán por momentos emblemáticos de la historia!', 'Asistente de combate', '¡Habilita esta característica en el menú de configuración para recibir ayuda con tu rotación de combate!', '¡Abismos sobrecargados!', 'Poténciate con maquinaria titánica para explorar nuevas profundidades y superar nuevos desafíos.', 63906),
(80, 'esMX', '¡Novedades del parche 11.1.5!', 'Administrador de habilidades', '¡Activa está función en el menú de opciones para administrar tus habilidades de una forma más eficiente!', 'Próximamente: Dúos indómitos', '¡Enfréntate a un dúo de enemigos del pasado de Azeroth e intenta conseguir una puntuación alta!', 'Visiones horribles revividas', '¡Regresa a las profundidades de la locura para superar nuevos desafíos y obtener nuevas recompensas!', 63906),
(76, 'esMX', '¡Novedades del parche 11.1.0!', 'M.O.T.O.R.', '¡Conoce una nueva forma de viajar en Minahonda con el Rompenucas G-99, completamente personalizable!', 'Personaliza tu sitio de acampado', '¡Crea nuevos campamentos en la pantalla de selección de personaje y elige entre un conjunto de fondos desbloqueables!', 'Minahonda', 'Continúa con la persecución de Xal\'atath y enfrenta a las fuerzas de Gallywix en las extensas calles de Minahonda.', 63906),
(73, 'esMX', '¡Nuevo en la temporada 1!', 'Empieza la temporada 1', '¡Consigue recompensas en calabozos míticos y en el nuevo Campo de batalla frenético puntuado!', 'Palacio Nerub-ar', 'Ataca la sede del poder de la reina Ansurek en Azj-Kahet.', 'Guarida de Zekvir', 'Avanza en el nuevo contenido de temporada de abismos para desbloquear Guarida de Zekvir.', 63906),
(72, 'esMX', '¡Nuevo en The War Within!', 'Abismos', 'Sumérgete en abismos, descubre misterios y busca tesoros.', 'Talentos de héroe', '¡Lleva la fantasía de tu clase a nuevos niveles y personaliza tu selección de talentos!', 'The War Within', '¡Viaja a Khaz Algar y oye el llamado de la Canción radiante!', 63906),
(71, 'esMX', '¡Novedades del preparche de The War Within!', 'Ecos Radiantes', 'Vence a enemigos del pasado de Azeroth que surgen en forma de recuerdos. ¡Disponible el 30 de julio!', 'Próximamente: The War Within', 'Disponible el 26 de agosto (acceso anticipado disponible el 22 de agosto).', 'Tropas', '¡Tus personajes comparten acceso al banco de tropa, reputaciones de Dragonflight y más!', 63906),
(70, 'esMX', 'Nuevo en el parche 10.2.7', 'Legado draenei y trol', 'Consigue conjuntos de armaduras distintivos mientras exploras sus culturas e historias.', 'Pandaria Remixado', '¡Una forma alocada de subir de nivel comienza la próxima semana!', 'Emisaria de la oscuridad', 'Cuando la paz regresa a las Islas Dragón, un plan oscuro se pone en marcha.', 63906),
(69, 'esMX', '10.2.7 Mists of Pandaria: Remix', 'Obtén recompensas para la cuenta', '¡Obtén bronce para conseguir monturas, mascotas y apariencias!', 'Cómo empezar', 'Crea un cronoaventurero nuevo en la pantalla de selección de personaje.', 'Mists of Pandaria: Remix', '¡Explora una nueva versión alocada de Mists of Pandaria!', 63906),
(68, 'esMX', '', 'Obtén recompensas', '¡Recolecta Saqueo para conseguir una variedad de recompensas, como monturas, mascotas y transfiguraciones!', 'Cómo empezar', 'Para jugar, cierra tu sesión y elige Plunderstorm en la pantalla de selección de personaje.', 'Plunderstorm', '¡Los piratas se enfrentan en un gran \"battle royale\"!', 63906),
(64, 'esMX', '¡Nuevo en la temporada 4!', 'Temporada 4 de Dragonflight', 'Gana nuevas recompensas.', 'Progreso de calabozos actualizado', 'Se han rediseñado las dificultades heroica, mítica y mítica+ y sus recompensas.', 'Bandas del despertar', '¡Conquista las tres bandas de Dragonflight para conseguir recompensas actualizadas!', 63906),
(63, 'esMX', '¡Nuevo en el parche 10.2.5!', 'Archivos de Azeroth', 'Explora las leyendas y tradiciones perdidas de las Islas Dragón.', 'Calabozos con seguidores', '¡Ingresa en la cola de calabozos de Dragonflight con seguidores PNJ!', 'Gilneas recuperada', 'Únete a Genn Cringris y los gilneanos para recuperar su hogar.', 63906),
(61, 'esMX', '¡Nuevo en el parche 10.2.0!', 'Guardianes del Sueño', 'Únete a la defensa del Sueño con reconocimiento, eventos y recompensas nuevos.', 'Campo de batalla frenético', '¡Únete a una cola en solitario del Campo de batalla para una partida 8c8 en este modo en el que todo es más rápido!', 'El Sueño Esmeralda', 'Entra en el Sueño y defiende al floreciente Árbol del Mundo Amirdrassil de Fyrakk y los druidas de la Llama.', 63906),
(60, 'esMX', '¡Nuevo en el parche 10.1.7!', 'Legado de los elfos de la noche y los Renegados', 'Consigue conjuntos de armaduras distintivos mientras exploras la cultura e historia de estas razas.', 'Presentamos el sistema de marcado', 'Visita el menú de opciones para empezar a comunicarte más rápido con marcas.', 'Oleadas oníricas', '¡Lucha contra enemigos potenciados por el sueño en las Islas Dragón mientras Amirdrassil se prepara para florecer!', 63906),
(59, 'esMX', '¡Nuevo en el parche 10.1.5!', 'Aumento Desbloqueado', '¡Una nueva especialización de daño con un estilo de juego único basado en el apoyo!', 'Portales del tiempo deshilvanados', '¡Apoya al Vuelo Bronce en la Periferia de los Eones y en las fallas temporales!', 'El Alba del Infinito', '¡Enfréntate al Vuelo Infinito en un megacalabozo mítico!', 63906),
(58, 'esMX', '¡Nuevo en la temporada 2!', 'Temporada 2 de Dragonflight', 'Obtén nuevas recompensas en calabozos de piedra angular mítica y JcJ', 'Actualización mítica+', '¡Experimenta nuevos afijos y una renovada selección de calabozos míticos+!', 'Aberrus, el Crisol Penumbroso', 'Persigue a Sarkareth y explora el legado de Neltharion en este laboratorio antiguo.', 63906),
(57, 'esMX', '¡Nuevo en el parche 10.1.0!', 'Dracorreptante sinuoso', '¡Recorre la Caverna Zaralek en la nueva montura de dracoequitación nativa!', 'Hermandades interfacciones', '¡Por primera vez, los jugadores de la Horda y de la Alianza se unen en una misma hermandad!', 'Caverna Zaralek', 'Continúa la historia de la Llama Hendida y enfrenta a las fuerzas de las encarnaciones en esta expansión subterránea olvidada.', 63906),
(56, 'esMX', '¡Nuevo en el parche 10.0.7!', 'Legado de los humanos y los orcos', 'Consigue conjuntos de armaduras distintivos mientras exploras la cultura e historia de estas razas.', 'Actualizaciones de Recluta a un amigo', '¡Consigue grandes recompensas por traer a tus amigos a Azeroth!', 'Regreso al Confín Prohibido', 'Indaga en los misterios de los que quedaron después de su escape.', 63906),
(55, 'esMX', '¡Nuevo en el parche 10.0.5!', 'La furia de la tormenta', 'Lucha contra los primalistas en el Futuro Primalista para recibir recompensas épicas.', 'Actualización de la transfiguración', '¡Ahora los objetos blancos y grises se pueden coleccionar para transfiguraciones!', '¡El puesto de venta ya abrió!', 'Se ha abierto una nueva tienda de artículos estéticos en Ventormenta y Orgrimmar.', 63906),
(50, 'esMX', '¡Nuevo en la temporada 1!', 'Temporada 1 de JcJ', 'Obtén recompensas con el nuevo modo Aleatorio en solitario puntuado.', 'Temporada mítica+ 1', 'Una nueva rotación de calabozos y el afijo \"Atronador\" esperan.', 'Bóveda de las Encarnaciones', 'No dejes que los primalistas liberen a los hermanos de Raszageth encerrados.', 63906),
(49, 'esMX', '¡Nuevo en Dragonflight!', 'Actualización de profesiones', '¡Nuevas funciones, como pedidos de fabricación, equipamiento, especializaciones y más!', 'Dracoequitación', '¡Elévate por las Islas Dragón con un apasionante sistema de movimiento!', 'Islas Dragón', 'Viaja al hogar ancestral de los Vuelos y ayúdalos a recuperar su legado.', 63906);
