INSERT INTO `ui_splash_screen_locale` (`ID`, `locale`, `Header_lang`, `TopLeftFeatureTitle_lang`, `TopLeftFeatureDesc_lang`, `BottomLeftFeatureTitle_lang`, `BottomLeftFeatureDesc_lang`, `RightFeatureTitle_lang`, `RightFeatureDesc_lang`, `VerifiedBuild`) VALUES
(85, 'itIT', 'Novità nella Patch 11.2.0!', 'Successione Ecologica', 'Aiuta Ve\'nari raccogliendo creature in giro per Azeroth per riportare la vita su K\'aresh.', 'Ecosfera Al\'dani', 'Difendi l\'ecosfera nascosta e svela gli antichi segreti al suo interno!', 'K\'aresh', 'Scopri gli oscuri segreti del Vuoto nel pianeta perduto degli Eterei.', 63906),
(84, 'itIT', 'Novità nella Patch 11.1.7!', 'Cammino della Storia', 'Parla con il Ramingo della Sapienza Cho in una capitale per sperimentare missioni rinnovate attraverso momenti iconici della storia!', 'Assistente di Combattimento', 'Ottieni assistenza nella rotazione in combattimento attivando questa funzione nel menu Impostazioni!', 'Scorribande: Sovraccaricate!', 'Accendi il macchinario dei Titani per esplorare nuove profondità e affrontare nuove sfide.', 63906),
(80, 'itIT', 'Novità nella Patch 11.1.5!', 'Gestore tempi di recupero', 'Tieni traccia dei tuoi tempi di recupero attivando questa funzionalità dal menu delle impostazioni!', 'Prossimamente: Compari Ignobili', 'Affronta una coppia di avversari provenienti dal passato di Azeroth e prova a ottenere il punteggio più alto!', 'Visioni Orripilanti Rivisitate', 'Torna negli abissi della follia per affrontare nuove sfide e ottenere nuove ricompense!', 63906),
(76, 'itIT', 'Novità nella Patch 11.1.0!', 'G.U.I.D.A.', 'Scopri un nuovo modo di viaggiare a Cavafonda con il bolide personalizzabile G-99 Rottadicollo!', 'Personalizza il tuo campeggio', 'Crea nuovi accampamenti nella schermata di selezione del personaggio e scegli tra una serie di sfondi sbloccabili!', 'Cavafonda', 'Continua la ricerca di Xal\'atath e affronta le forze di Gallywix tra le vie tentacolari della città di Cavafonda.', 63906),
(73, 'itIT', 'Novità nella Stagione 1!', 'Inizia la Stagione 1', 'Ottieni ricompense nelle spedizioni Mitiche e nel nuovo Blitz nel Campo di Battaglia classificato!', 'Palazzo dei Nerub\'ar', 'Assalta il seggio di potere della Regina Ansurek ad Azj-Kahet.', 'Antro di Zekvir', 'Avanza nei nuovi contenuti stagionali delle Scorribande per sbloccare l\'Antro di Zekvir.', 63906),
(72, 'itIT', 'Novità in The War Within!', 'Scorribande', 'Affronta le Scorribande, svela misteri e cerca tesori.', 'Talenti Eroici', 'Espandi l\'immaginario della tua classe e personalizza la tua configurazione talenti!', 'The War Within', 'Viaggia fino a Khaz Algar e rispondi alla chiamata del Canto Radioso!', 63906),
(71, 'itIT', 'Novità nella pre-patch di The War Within!', 'Echi Radiosi', 'Annienta nemici provenienti dal passato di Azeroth con il riaffiorare dei ricordi. Disponibile dal 31 luglio!', 'The War Within è in arrivo', 'Disponibile dal 26 agosto (accesso anticipato: 22 agosto).', 'Brigate', 'I tuoi personaggi condividono l\'accesso alla Banca della Brigata, alle reputazioni di Dragonflight e molto altro!', 63906),
(70, 'itIT', 'Novità della patch 10.2.7', 'Retaggio dei Draenei e dei Troll', 'Colleziona set di armature unici mentre esplori la loro cultura e storia.', 'Remix di Pandaria', 'Un nuovo modo per salire di livello ti attende!', 'Araldo dell\'Oscurità', 'Col ritorno della pace nelle Isole dei Draghi, un oscuro piano viene messo in atto.', 63906),
(69, 'itIT', '10.2.7 - Mists of Pandaria: Remix', 'Ottieni ricompense per l\'account', 'Colleziona Bronzo per ottenere cavalcature, mascotte e aspetti!', 'Come iniziare', 'Crea un nuovo Corridore nel Tempo dalla schermata di selezione del personaggio.', 'Mists of Pandaria: Remix', 'Vivi una nuova avventura a Mists of Pandaria!', 63906),
(68, 'itIT', '', 'Ottieni ricompense', 'Raccogli il Bottino per ottenere ricompense come cavalcature, mascotte e trasmogrificazioni!', 'Come iniziare', 'Per giocare, disconnettiti e seleziona Plunderstorm dalla schermata di selezione del personaggio.', 'Plunderstorm', 'Un maestoso scontro tra pirati in stile battle royale!', 63906),
(64, 'itIT', 'Novità nella Stagione 4!', 'Stagione 4 di Dragonflight', 'Ottieni nuove ricompense.', 'Progresso delle spedizioni aggiornato', 'Modalità Eroica, Mitica e Mitica+ e relative ricompense riprogettate.', 'Incursioni Risvegliate', 'Conquista tutte e tre le incursioni di Dragonflight per ottenere ricompense aggiornate!', 63906),
(63, 'itIT', 'Novità della patch 10.2.5!', 'Archivi Azerothiani', 'Esplora la storia e le leggende perdute delle Isole dei Draghi.', 'Spedizioni con Seguace', 'Mettiti in coda per le spedizioni di Dragonflight con i seguaci PNG!', 'Gilneas Rivendicata', 'Unisciti a Genn Mantogrigio e ai Gilneani mentre riconquistano la loro terra natale.', 63906),
(61, 'itIT', 'Novità nella Patch 10.2.0!', 'Guardiani del Sogno', 'Unisciti alla difesa del Sogno con nuovi eventi, ricompense e Fama.', 'Blitz nel Campo di Battaglia', 'Unisciti a un campo di battaglia 8v8 in solitaria in una modalità in cui tutto è più veloce!', 'Sogno di Smeraldo', 'Entra nel Sogno e difendi l\'Albero del Mondo Amirdrassil che sta fiorendo da Fyrakk e dai Druidi delle Fiamme.', 63906),
(60, 'itIT', 'Novità nella Patch 10.1.7!', 'Retaggio degli Elfi della Notte e dei Reietti', 'Colleziona set di armature unici esplorando la cultura e la storia di queste razze.', 'Introduzione ai segnali', 'Controlla il menu Opzioni per iniziare a comunicare più velocemente con i segnali.', 'Sovronirici', 'Combatti contro i nemici potenziati dal Sogno sulle Isole dei Draghi mentre Amirdrassil si prepara a fiorire!', 63906),
(59, 'itIT', 'Novità nella Patch 10.1.5!', 'Fortificazione sbloccata', 'Una nuova specializzazione da assaltatore con uno stile di gioco unico basato sul supporto!', 'Sbrogliare i Flussi Temporali', 'Sostieni lo Stormo dei Draghi di Bronzo al Confine degli Eoni e nelle Fenditure del Tempo!', 'Alba degli Infiniti', 'Affronta lo Stormo dei Draghi dell\'Infinito in una mega-spedizione Mitica!', 63906),
(58, 'itIT', 'Novità nella Stagione 2!', 'Stagione 2 di Dragonflight', 'Ottieni nuove ricompense nelle spedizioni Mitiche+ e nel PvP', 'Aggiornamento Mitiche+', 'Sperimenta nuovi modificatori e una nuova selezione di spedizioni Mitiche+!', 'Aberrus, il Crogiolo d\'Ombra', 'Insegui Sarkareth ed esplora il retaggio di Neltharion in questo antico laboratorio.', 63906),
(57, 'itIT', 'Novità nella Patch 10.1.0!', 'Dracondoso Serpeggiante', 'Corri attraverso la Caverna di Zaralek sulla nuova cavalcatura nativa del Volo Draconico!', 'Gilde multi-fazione', 'Per la prima volta, i giocatori dell\'Orda e dell\'Alleanza possono unirsi nella stessa gilda!', 'Caverna di Zaralek', 'Continua la storia della Fiamma Frammentata e affronta le forze delle Incarnazioni in questa distesa sotterranea dimenticata.', 63906),
(56, 'itIT', 'Novità della patch 10.0.7!', 'Retaggio degli Umani e degli Orchi', 'Colleziona set di armature unici esplorando la cultura e la storia di queste razze.', 'Aggiornamento della funzione Recluta un amico', 'Nuove entusiasmanti ricompense per aver portato i tuoi amici su Azeroth!', 'Torna all\'Isola Proibita', 'Scopri i misteri che i Dracthyr si sono lasciati alle spalle durante la loro fuga.', 63906),
(55, 'itIT', 'Novità nella Patch 10.0.5!', 'Furia dei Cieli', 'Lotta contro i Primalisti nel Futuro dei Primalisti per ottenere epiche ricompense.', 'Aggiornamenti alla trasmogrificazione', 'Gli oggetti bianchi e grigi ora possono essere collezionati per la trasmogrificazione!', 'Emporio, in piena attività!', 'Un nuovo negozio di oggetti cosmetici ha aperto a Roccavento e Orgrimmar.', 63906),
(50, 'itIT', 'Novità nella Stagione 1!', 'PvP - Stagione 1', 'Ottieni ricompense con la nuova modalità Classificata Mischia Solitaria.', 'Mitiche+ - Stagione 1', 'Ti aspettano una nuova rotazione di spedizioni e il nuovo modificatore \"Tuono\".', 'Segrete delle Incarnazioni', 'Impedisci ai Primalisti di liberare i compagni imprigionati di Raszageth.', 63906),
(49, 'itIT', 'Novità in Dragonflight!', 'Aggiornamento alle professioni', 'Le nuove caratteristiche includono Ordini d\'Artigianato, equipaggiamento, specializzazioni e altro!', 'Volo Draconico', 'Sfreccia nei cieli delle Isole dei Draghi con un nuovo emozionante sistema di movimento!', 'Isole dei Draghi', 'Viaggia nell\'ancestrale casa degli Stormi dei Draghi e aiutali a riscattare la loro eredità.', 63906);
