INSERT INTO `mount_locale` (`ID`, `locale`, `Name_lang`, `SourceText_lang`, `Description_lang`, `VerifiedBuild`) VALUES
(2683, 'ptBR', 'Corcel Ósseo do Oblívio', '|cFFFFD200Evento Mundial:|r WoW Remix: Legion', 'Um bom cavalo de guerra é leal até a morte. Por ter abandonado a forma mortal, este corcel é prenúncio de escassez e morte.', 63906),
(2682, 'ptBR', 'Corcel Ósseo da Praga', '|cFFFFD200Evento Mundial:|r WoW Remix: Legion', 'Um bom cavalo de guerra é leal até a morte. Por ter abandonado a forma mortal, este corcel é um prenúncio de doença e sofrimento.', 63906),
(2681, 'ptBR', 'Corcel Ósseo do Derramamento de Sangue', '|cFFFFD200Evento Mundial:|r WoW Remix: Legion', 'Um bom cavalo de guerra é leal até a morte. Por ter abandonado a forma mortal, este corcel é um prenúncio de uma luta eterna.', 63906),
(2679, 'ptBR', 'Corcel Ósseo do Triunfo', '|cFFFFD200Evento Mundial:|r WoW Remix: Legion', 'Um bom cavalo de guerra é leal até a morte. Por ter abandonado a forma mortal, este corcel é um prenúncio de uma conquista audaciosa.', 63906),
(2732, 'ptBR', 'Caçador Dimensional Cobáltico', '|cFFFFD200Promoção:|r Burning Crusade Classic', 'Devido a energias sinistras que incidiram sobre Draenor, essas criaturas podem deslocar-se pelo espaço a seu bel-prazer. Exatamente por isso, são avistadas raramente... até que seja tarde demais.', 63906),
(2640, 'ptBR', 'Bombardeiro da CervaFest', '|cFFFFD200Saque:|r Coren Cervatroz|n|cFFFFD200Feriado:|r CervaFest', 'Cuidado para não consumir a munição.', 63906),
(2566, 'ptBR', 'Cascata Carmesim de Lana\'thel', '|cFFFFD200Loja do jogo|r ', 'O domínio das artes sanguíneas da Rainha de Sangue permitia que ela conjurasse um temível servo para carregá-la em batalha. Adornado nas cores da corte dela, e com uma máscara de semblante assustador, além de asas e garras que lembram a ira da sua dona.', 63906);
