INSERT INTO `ui_splash_screen_locale` (`ID`, `locale`, `Header_lang`, `TopLeftFeatureTitle_lang`, `TopLeftFeatureDesc_lang`, `BottomLeftFeatureTitle_lang`, `BottomLeftFeatureDesc_lang`, `RightFeatureTitle_lang`, `RightFeatureDesc_lang`, `VerifiedBuild`) VALUES
(85, 'ptBR', 'Novidade do Patch 11.2.0!', 'Sucessão ecológica', 'Ajude Ve\'nari reunindo criaturas por Azeroth para restaurar a vida em K\'aresh.', 'Ecodomo Al\'dani', 'Defenda o ecodomo oculto e revele os segredos ancestrais contidos nele!', 'K\'aresh', 'Mergulhe nos segredos sombrios do Caos, no planeta perdido dos etéreos.', 63906),
(84, 'ptBR', 'Novidade do Patch 11.1.7!', 'Trilha das Lendas', 'Fale com o Andarilho das Lendas Cho em uma capital para começar uma experiência de missões renovada, através de momentos marcantes da história!', 'Assistente de Combate', 'Obtenha ajuda com sua rotação de combate ativando este recurso no menu de Configurações!', 'Imersões: Sobrecarregadas!', 'Ganhe força com maquinário titânico para explorar mais fundo e enfrentar novos desafios.', 63906),
(80, 'ptBR', 'Novidade do Patch 11.1.5!', 'Gerenciador de recarga', 'Acompanhe suas recargas ativando esse recurso no menu de Configurações!', 'Em breve: Duplas da Discórdia', 'Enfrente uma dupla de inimigos do passado de Azeroth e tente obter uma pontuação alta!', 'Visões Horrendas Revividas', 'Volte às profundezas da loucura para viver novos desafios e ganhar novas recompensas!', 63906),
(76, 'ptBR', 'Novidade do Patch 11.1.0!', 'V.R.U.M.M.', 'Experimente uma nova forma de viajar na Inframina com o Pé-na-Tábua G-99 personalizável!', 'Personalize seu Acampamento', 'Crie novos agrupamentos na Tela de Seleção do Personagem e escolha um de vários fundos desbloqueáveis!', 'Inframina', 'Continue a perseguição de Xal\'atath e enfrente as forças de Gallywix nas ruas da Inframina.', 63906),
(73, 'ptBR', 'Novidade na Série 1!', 'Início da Série 1', 'Ganhe recompensas nas masmorras Míticas e no novo Ataque Relâmpago Ranqueado!', 'Palácio Nerub-ar', 'Ataque a sede do poder da Rainha Ansurek em Azj-Kahet.', 'Covil de Zekvir', 'Progrida no novo conteúdo de imersão da série para desbloquear o Covil de Zekvir.', 63906),
(72, 'ptBR', 'Novidades em The War Within!', 'Imersões', 'Mergulhe fundo nas imersões, desvele mistérios e procure tesouros.', 'Talentos Heroicos', 'Expanda a fantasia da sua classe e personalize sua seleção de talentos!', 'The War Within', 'Viaje para Khaz Algar para atender ao chamado do Canto Radiante!', 63906),
(71, 'ptBR', 'Novidades no pré-patch de The War Within!', 'Ecos Radiantes', 'Derrote os inimigos do passado de Azeroth enquanto as memórias ressurgem. Disponível em 30 de julho!', 'The War Within em breve', 'Lançamento em 26 de agosto (acesso antecipado em 22 de agosto).', 'Bandos de Guerra', 'Seus personagens compartilham acesso a um banco dos Bandos de Guerra, reputações de Dragonflight e muito mais!', 63906),
(70, 'ptBR', 'Novidade do Patch 10.2.7', 'Herança draeneica e trolesa', 'Colete conjuntos de armadura exclusivos enquanto explora suas culturas e histórias.', 'Pandária remixada', 'Uma nova forma de subir de nível começa na próxima semana!', 'Precursor das Trevas', 'Enquanto as Ilhas do Dragão ficam em paz novamente, um plano sombrio é colocado em ação.', 63906),
(69, 'ptBR', '10.2.7 Mists of Pandaria: Remix', 'Ganhe recompensas de conta', 'Colete Bronze para ganhar montarias, mascotes e aparências!', 'Começar', 'Crie um novo Trilha-tempo na tela de seleção de personagem.', 'Mists of Pandaria: Remix', 'Vivencie uma nova reviravolta em Mists of Pandaria!', 63906),
(68, 'ptBR', '', 'Ganhe recompensas', 'Pilhe o Butim para ganhar recompensas, como montarias, mascotes e transmogrificações!', 'Começar', 'Para jogar, saia do jogo e selecione Plunderstorm na tela de seleção do personagem.', 'Plunderstorm', 'Piratas se enfrentam em um grande battle royale!', 63906),
(64, 'ptBR', 'Novidade na Série 4!', 'Série 4 de Dragonflight', 'Ganhe novas recompensas.', 'Progresso em masmorras atualizado', 'Modos Heroico, Mítico, Mítico+ e recompensas reformulados.', 'Raides Despertos', 'Conquiste todos os três raides de Dragonflight para receber recompensas atualizadas!', 63906),
(63, 'ptBR', 'Novidade do Patch 10.2.5!', 'Arquivo Azerothiano', 'Mergulhe na história e nas lendas perdidas das Ilhas do Dragão.', 'Masmorras de seguidor', 'Entre na fila para masmorras de Dragonflight com seguidores PNJs!', 'A Retomada de Guilnéas', 'Junte-se a Genn Greymane e os guilneanos na retomada da terra natal.', 63906),
(61, 'ptBR', 'Novidade do Patch 10.2.0!', 'Guardiões do Sonho', 'Participe da defesa do Sonho com novo renome, novos eventos e recompensas.', 'Ataque Relâmpago', 'Participe de uma experiência de Campo de Batalha 8x8 em fila solo. Neste modo, tudo acontece rápido!', 'O Sonho Esmeralda', 'Entre no Sonho e defenda Amirdrassil, a Árvore do Mundo florescente, de Fyrakk e os Druidas da Chama.', 63906),
(60, 'ptBR', 'Novidade do Patch 10.1.7!', 'Tradição dos Elfos Noturnos e Renegados', 'Colete conjuntos de armadura exclusivos ao explorar as culturas e histórias das raças.', 'Apresentamos o sistema de pings', 'Abra o menu de Opções para começar a se comunicar mais rapidamente com pings.', 'Surtos Oníricos', 'Lute contra inimigos potencializados pelo sonho nas Ilhas do Dragão enquanto Amirdrassil se prepara para brotar!', 63906),
(59, 'ptBR', 'Novidade do Patch 10.1.5!', 'Aprimoramento desbloqueado', 'Uma nova especialização de dano com um estilo de jogo exclusivo baseado em suporte!', 'Percursos Temporais do Desenlace', 'Ajude a Revoada Dragônica Bronze na Orla do Éon e nas Fendas Temporais!', 'Despertar do Infinito', 'Enfrente a Revoada Dragônica Infinita em uma megamasmorra mítica.', 63906),
(58, 'ptBR', 'Novidade na Série 2!', 'Série 2 de Dragonflight', 'Ganhe novas recompensas nas Masmorras de Pedra-chave Mítica e no JxJ.', 'Atualização de Míticas+', 'Experimente novos afixos e uma seleção atualizada de masmorras Míticas+!', 'Aberrus, o Crisol Sombrio', 'Persiga Sarkareth e explore o legado de Neltharion neste antigo laboratório.', 63906),
(57, 'ptBR', 'Novidades do Patch 10.1.0!', 'Dracoleante Sinuoso', 'Corra pela Caverna Zaralek na nova montaria nativa de dragonaria!', 'Guildas Interfacção', 'Pela primeira vez, jogadores da Horda e da Aliança podem se reunir na mesma guilda!', 'Caverna Zaralek', 'Continue a história da Chama Partida e enfrente as forças dos Encarnados nesta vastidão subterrânea esquecida.', 63906),
(56, 'ptBR', 'Novidade do Patch 10.0.7!', 'Tradição Humana e Órquica', 'Colete conjuntos de armadura exclusivos ao explorar as culturas e histórias das raças.', 'Atualização do Programa Recrute um Amigo', 'Novas recompensas empolgantes por trazer seus amigos para Azeroth!', 'Volte ao Recôndito Proibido', 'Mergulhe nos mistérios dos dracthyrs deixados para trás durante a fuga.', 63906),
(55, 'ptBR', 'Novidade do Patch 10.0.5!', 'A Fúria da Tempestade', 'Combata os Primevistas no Futuro Primevista para ganhar recompensas épicas.', 'Atualizações de Transmogrificação', 'Itens cinzas e brancos agora podem ser coletados para transmogrificação!', 'O Posto Comercial está aberto!', 'Uma nova loja de itens cosméticos já abriu em Ventobravo e em Orgrimmar.', 63906),
(50, 'ptBR', 'Novidade na Série 1!', 'Série 1 de JxJ', 'Ganhe recompensas com o novo modo Solo Mix Ranqueado.', 'Série 1 Mítica+', 'Uma nova rotação de masmorra e o afixo \"Trovejante\" estão à espera.', 'Câmara dos Encarnados', 'Impeça os Primevistas de libertarem o povo aprisionado de Raszageth.', 63906),
(49, 'ptBR', 'Novidades em Dragonflight!', 'Atualização das profissões', 'Novos recursos, como pedidos de criação, equipamentos, especializações e muito mais!', 'Dragonaria', 'Voe pelos céus das Ilhas do Dragão com um novo sistema de movimento emocionante!', 'Ilhas do Dragão', 'Viaje para o antigo lar das revoadas dragônicas e ajude-as a resgatar seu legado.', 63906);
