INSERT INTO `ui_splash_screen_locale` (`ID`, `locale`, `Header_lang`, `TopLeftFeatureTitle_lang`, `TopLeftFeatureDesc_lang`, `BottomLeftFeatureTitle_lang`, `BottomLeftFeatureDesc_lang`, `RightFeatureTitle_lang`, `RightFeatureDesc_lang`, `VerifiedBuild`) VALUES
(85, 'zhCN', '11.2.0版本最新内容！', '生态演替', '协助威·娜莉收集艾泽拉斯的生物，让卡雷什恢复生机。', '奥尔达尼生态圆顶', '守卫隐藏的生态圆顶，揭露深埋其中的远古秘密！', '卡雷什', '踏上虚灵的破碎母星，探索虚空的黑暗奥秘。', 63906),
(84, 'zhCN', '11.1.7版本最新内容！', '游学探奇', '与主城中的游学者周卓交谈，在全新的任务体验中亲历经典时刻！', '战斗辅助', '在设置菜单中启用这项功能，即可获得战斗循环辅助！', '地下堡：过载！', '在泰坦机械的加持下探索深渊，面对新的挑战。', 63906),
(80, 'zhCN', '11.1.5版本最新内容！', '冷却管理器', '在设置菜单中启用这项功能，追踪技能的冷却时间！', '即将推出：卑鄙双雄', '迎战艾泽拉斯昔日的反派双人组，冲击最高分！', '重访惊魂幻象', '重返疯狂的深渊，面对焕然一新的挑战，收集全新的奖励！', 63906),
(76, 'zhCN', '11.1.0版本最新内容！', 'D.R.I.V.E.', '乘上可改装的G-99疾飙飞车，体验全新的安德麦出行方式！', '定制你的营区', '在角色选择界面中创建新的营地，挑选一系列可解锁的背景！', '安德麦', '继续追击萨拉塔斯，在安德麦的街头巷尾与加里维克斯的势力交锋。', 63906),
(73, 'zhCN', '第1赛季全新内容！', '第1赛季开始', '在史诗地下城和新评级战场闪电战中赢得奖励！', '尼鲁巴尔王宫', '在艾基-卡赫特突袭安苏雷克女王的权力王座。', '泽克维尔的巢穴', '完成新赛季地下堡内容，解锁泽克维尔的巢穴。', 63906),
(72, 'zhCN', '“地心之战”新内容！', '地下堡', '深入探索地下堡，解开谜团并寻找宝藏。', '英雄天赋', '丰富职业风采，自定义天赋配置！', '地心之战', '前往卡兹阿加，回应光耀之歌的呼唤！', 63906),
(71, 'zhCN', '“地心之战”前夕的新内容！', '光耀回响', '艾泽拉斯的回忆开始浮现。征服艾泽拉斯过去的敌人。活动开始时间：8月1日！', '“地心之战”即将到来', '8月27日上线（可于8月23日抢先体验）。', '战团', '你的所有角色都可以共享战团银行、“巨龙时代”声望等等！', 63906),
(70, 'zhCN', '10.2.7版本最新内容！', '德莱尼和巨魔的传承', '收集独特的护甲套装，同时探索各自的文化和历史。', '幻境新生的潘达利亚', '下周起，练级有了一个狂野的新方式！', '黑暗先驱', '和平再度降临巨龙群岛，但一个黑暗的阴谋也开始涌动。', 63906),
(69, 'zhCN', '10.2.7 熊猫人之谜：幻境新生！', '赢取账号奖励', '收集青铜，兑换坐骑、宠物和外观！', '立即体验', '在角色选择画面创建一个新的时空奔行者角色。', '熊猫人之谜：幻境新生！', '体验“熊猫人之谜”的狂野新变化！', 63906),
(68, 'zhCN', '', '获取奖励', '收集珍宝，获取坐骑、宠物和幻化等奖励！', '立即体验', '登出游戏，在角色选择界面选择“霸业风暴”。', '霸业风暴', '海盗们在超大规模的乱斗比赛中一决高下！', 63906),
(64, 'zhCN', '全新的第4赛季！', '“巨龙时代”第4赛季', '获取全新奖励。', '地下城进度系统更新', '英雄、史诗、史诗钥石难度和奖励进行了重新设计。', '觉醒团队副本', '征服全部三个巨龙时代团队副本，获取全新的奖励！', 63906),
(63, 'zhCN', '10.2.5版本最新内容！', '艾泽拉斯档案馆', '探究巨龙群岛失落的知识和传说。', '追随者地下城', '与NPC追随者一起加入“巨龙时代”地下城队列！', '收复吉尔尼斯', '加入吉恩·格雷迈恩以及其他吉尔尼斯人，跟他们一起夺回故土。', 63906),
(61, 'zhCN', '10.2.0版本最新内容！', '梦境守护者', '一起保卫梦境，还有全新的名望、事件和奖励。', '战场闪电战', '体验8v8的单排战场。此模式下，一切节奏快如闪电！', '翡翠梦境', '进入梦境，击退菲莱克和烈焰德鲁伊，守护即将盛放的世界之树阿梅达希尔。', 63906),
(60, 'zhCN', '10.1.7版本最新内容！', '暗夜精灵和被遗忘者的传承', '探索这些种族的文化和历史，收集独特的护甲套装。', '新功能：信号系统', '查看选项菜单，使用信号来更便捷地进行交流。', '梦涌', '巨龙群岛各地出现了梦境之力强化的敌人，在阿梅达希尔即将盛放之时，与他们作战！', 63906),
(59, 'zhCN', '10.1.5版本最新内容！', '增辉解锁', '全新的伤害专精，具有独特的支援型玩法！', '散乱的时间流', '在纪元边界和时光裂隙支援青铜龙军团！', '永恒黎明', '在史诗难度的大型地下城中与永恒龙军团一决高下！', 63906),
(58, 'zhCN', '第2赛季全新内容！', '“巨龙时代”第2赛季', '在史诗钥石地下城和PvP活动中赢得全新奖励。', '史诗钥石地下城更新', '体验全新的词缀，以及焕然一新的史诗钥石地下城轮换池！', '亚贝鲁斯，焰影熔炉', '追击萨卡雷斯，在这座上古实验室里探索奈萨里奥留下的遗产。', 63906),
(57, 'zhCN', '10.1.0版本最新内容！', '盘曲蜿变幼龙', '骑上查拉雷克洞窟当地的全新驭龙术坐骑，翱翔竞速！', '跨阵营公会', '终于，部落和联盟玩家可以在同一个公会里齐聚一堂！', '查拉雷克洞窟', '继续碎裂之焰的故事，在被遗忘的广阔地窟中直面化身巨龙的爪牙。', 63906),
(56, 'zhCN', '10.0.7版本最新内容！', '人类和兽人的传承', '探索这些种族的文化和历史，收集独特的护甲套装。', '战友招募更新', '把你的朋友带到艾泽拉斯，就能获得全新的奖励！', '重返禁忌离岛', '深入了解龙希尔撤退时留下的秘密。', 63906),
(55, 'zhCN', '10.0.5版本最新内容！', '暴风之怒', '在拜荒者的未来中迎战拜荒者，获取史诗奖励。', '幻化更新', '现在可以收集白色和灰色物品加入幻化！', '商栈营业中！', '有一家新的商店已经在暴风城和奥格瑞玛开张了。', 63906),
(50, 'zhCN', '全新的第1赛季！', 'PVP第1赛季', '通过全新的单排轮斗模式赢取奖励。', '史诗钥石第1赛季', '新的地下城轮换以及“雷霆”词缀在等待着你。', '化身巨龙牢窟', '阻止拜荒者释放莱萨杰丝被囚禁的同胞。', 63906),
(49, 'zhCN', '“巨龙时代”的新内容！', '专业更新', '新功能：制造订单、装备、专精、等等！', '驭龙术', '通过激动人心的全新系统进行移动，翱翔于巨龙群岛的天空！', '巨龙群岛', '前往巨龙军团的古老家园，帮助他们夺回自己的遗产。', 63906);
