INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES
(85948, 'zhTW', '穿越時光的燃燒旅程', '完成5次時光漫遊地城冒險。', '熾炎之星照耀大地，燒灼土壤、烘烤地面。這是一枚無情之星，幾近惡意。在它的影響之下，可能會開啟通往遠方、甚至前往其他時間的新道路。賢者們無法瞭解這個徵兆，但好運往往眷顧英勇之士。', '', '', '', '', '', '', 57534),
(85947, 'zhTW', '穿越時光的初始旅程', '完成5次時光漫遊地城冒險。', '老戰士一直在提當年勇，說在他們那個時候，艾澤拉斯有多麼危機四伏。\n\n我賭他們會說，在他們那個時代，你這個弱雞絕對撐不了10分鐘。\n\n覺得自己沒那麼弱嗎？那就證明給我看。', '', '', '', '', '', '在塔納利斯的時光之穴外與波巴多姆對話。', 57534),
(85718, 'zhTW', '閃亮的石頭', '', '', '', '', '', '', '', '', 57534),
(85667, 'zhTW', '探究者之喚：深淵堡', '在任一難度下完成深淵堡探究。', '織絲者的指示\n\n有人在名為深淵堡的地方進行黑暗的行動。\n\n我希望這些行動遭到制止。', '', '', '', '', '', '回去織絲者巢穴找寡婦阿拉克娜依。', 57534),
(85666, 'zhTW', '探究者之喚：迴繞地道', '在任一難度下完成迴繞地道探究。', '需要幫手\n\n據說奈幽蟲族在迴繞地道內展開了大規模軍事行動。\n\n阻止他們的行動並協助達拉然墜毀的生還者！', '', '', '', '', '', '回去法林的先遣營地找強納森‧真心爵士。', 57534),
(85664, 'zhTW', '探究者之喚：夜暮聖所', '在任一難度下完成夜暮聖所探究。', '呼叫支援！\n\n黑夜教團似乎在名為夜暮聖所的地方設立了基地。\n\n阻止他們的行動乃是當務之急。', '', '', '', '', '', '回去米雷達爾找瑞恩‧唐卡瓦莉爾。', 57534),
(85649, 'zhTW', '探究者之喚：水力發電壩', '在任一難度下完成水力發電壩探究。', '注意\n\n水力發電壩的運作急轉直下，需要有志之士前去處理。', '', '', '', '', '', '在剛達加茲與代言者朱拉克斯交談。', 57534),
(85648, 'zhTW', '探究者之喚：地行礦坑', '在任一難度下完成地行礦坑探究。', '需要幫手\n\n地行礦坑內依舊麻煩不斷，搞定麻煩者將獲豐厚獎賞。', '', '', '', '', '', '到十字路口廣場找邦達茲。', 57534),
(85574, 'zhTW', '模糊不清的慶典箱', '將模糊不清的慶典箱還給時光之穴外的阿利克斯。', '<這個帶著黑暗死靈能量的木箱在現實中忽隱忽現。無論如何，阿利克斯都會很感激你讓這個補給品物歸原主。>', '', '', '', '', '', '', 57534),
