UPDATE `quest_template_locale` SET `QuestDescription`='Es ist an der Zeit, Euren Mut wieder auf die Probe zu stellen.\n\nMeine Bronzekollegen sind nach Nordend gereist, um weitere Herausforderungen bereitzustellen. Wir würden gerne sehen, ob mutige Himmelsreiter alle Strecken abschließen können!\n\nSeid Ihr bereit?', `VerifiedBuild`=58162 WHERE (`ID`=79130 AND `locale`='deDE');
