INSERT INTO `mount_locale` (`ID`, `locale`, `Name_lang`, `SourceText_lang`, `Description_lang`, `VerifiedBuild`) VALUES
(2743, 'itIT', 'Celestiale Tempesta Dorata', '|cFFFFD200Negozio di gioco|r', 'Dorato nel tuono, scelto dal destino. Saette dorate avvolgono i suoi zoccoli, squarciando il cielo come se strappassero la notte. Le leggende lo associano all\'Imperatore Shaohao.', 65727),
(2742, 'itIT', 'Celestiale Zoccolo Tonante', '|cFFFFD200Negozio di gioco|r', 'Dopo la caduta del Re del Tuono, il tuono che persisteva in lui si è unito allo spirito di un drago bianco. Avanza su saette azzurre, con le nuvole che si addensano al suo seguito... Dove prima regnava la tirannia, ora permane una luce guardiana.', 65727),
(2798, 'itIT', 'Alagreve Bronzeo', '|cFFFFD200Impresa: |rMaestria dei Flussi Temporali Turbolenti IV|n|cFFFFD200Categoria:|r Viaggi nel Tempo|n', 'Sebbene l\'oscuro e l\'infausto siano lo stile prediletto di chi vive a Revendreth, c\'è ancora chi predilige uno splendore lucente.', 65727),
(2797, 'itIT', 'Insetto Necrofago Bronzeo', '|cFFFFD200Impresa: |rMaestria dei Flussi Temporali Turbolenti IV|n|cFFFFD200Categoria: |rViaggi nel Tempo', 'Ci vuole un bel po\' d\'impegno per decorare un insetto necrofago, ma c\'è chi direbbe che ne vale la pena.', 65727),
(2796, 'itIT', 'Aquilon Bronzeo', '|cFFFFD200Impresa: |rMaestria dei Flussi Temporali Turbolenti IV|n|cFFFFD200Categoria:|r Viaggi nel Tempo|n|n', 'I fidati destrieri del Bastione mirano a rappresentare i fieri e nobili guerrieri degli Ascesi.', 65727),
(2795, 'itIT', 'Selvante Bronzeo', '|cFFFFD200Impresa: |rMaestria dei Flussi Temporali Turbolenti IV|n|cFFFFD200Categoria:|r Viaggi nel Tempo|n|n', 'I selvanti regali serpeggiano nel cielo, liberi come la magia senza vincoli che sostiene il loro volo.', 65727),
(1729, 'itIT', 'Viscidone Cittadino', '|cFFFFD200Missione:|r Sgusciare|n|cFFFFD200Zona: |rCaverna di Zaralek', 'Grande cuore, grandi sogni, grande bava.', 65727);
