INSERT INTO `mount_locale` (`ID`, `locale`, `Name_lang`, `SourceText_lang`, `Description_lang`, `VerifiedBuild`) VALUES
(2743, 'ptBR', 'Celestial Tempestáureo', '|cFFFFD200Loja do jogo|r', 'Envolto em trovões, escolhido pelo destino. Relâmpagos dourados circundam seus cascos, rasgando o céu como quem dilacera a noite. As lendas o ligam ao Imperador Shaohao.', 65727),
(2742, 'ptBR', 'Casco do Trovão Celestial', '|cFFFFD200Loja do jogo|r', 'Após a queda do Rei Trovão, seu trovão persistente fundiu-se com o espírito de um dragão branco. Ele caminha sobre relâmpagos azulados, com nuvens rodopiando em seu rastro — onde antes reinava a tirania, agora permanece a luz guardiã.', 65727),
(2798, 'ptBR', 'Tumulasa de Bronze', '|cFFFFD200Conquista: |rMestre dos Percursos Temporais Turbulentos IV|n|cFFFFD200Categoria: |rCaminhada Temporal|n', 'Embora o sombrio e o ameaçador sejam a estética preferida daqueles que habitam em Revendreth, há ainda quem prefira o brilho reluzente.', 65727),
(2797, 'ptBR', 'Moscadáver de Bronze', '|cFFFFD200Conquista: |rMestre dos Percursos Temporais Turbulentos IV|n|cFFFFD200Categoria: |rCaminhada Temporal', 'É preciso muito esforço para decorar uma moscadáver, mas há quem diga que vale a pena.', 65727),
(2796, 'ptBR', 'Áquilon de Bronze', '|cFFFFD200Conquista: |rMestre dos Percursos Temporais Turbulentos IV|n|cFFFFD200Categoria: |rCaminhada Temporal|n|n', 'Os fiéis corcéis do Bastião são concebidos para refletir os orgulhosos e nobres guerreiros dos Ascendidos.', 65727),
(2795, 'ptBR', 'Silvestrito Bronze', '|cFFFFD200Conquista: |rMestre dos Percursos Temporais Turbulentos IV|n|cFFFFD200Categoria: |rCaminhada Temporal|n|n', 'Os régios silvestritos serpenteiam pelos céus, tão livres quanto a magia indomada que sustenta seu voo.', 65727),
(1729, 'ptBR', 'Rei da Pista', '|cFFFFD200Missão:|r Bom pra carácoles|n|cFFFFD200Área: |rCaverna Zaralek', 'Grande coração, grandes sonhos, grande Rei da Pista.', 65727);
