INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `comment`) VALUES
(33817, 0, 0, 'Crusaders and combatants, a true hero stands before you this day! $N, of the Sunreavers, has restored the ancient blade of Quel\'Delar as a symbol of our resolve to defeat the Lich King!', 14, 0, 100, 66, 0, 0, 38084, 'Justicar Mariel Trueheart to Player'),
(33817, 1, 0, 'Crusaders and combatants, a true hero stands before you this day! $n, of the Silver Covenant, has restored the ancient blade of Quel\'Delar as a symbol of our resolve to defeat the Lich King!', 14, 0, 100, 66, 0, 0, 38039, 'Justicar Mariel Trueheart to Player'),
(37763, 0, 0, 'Look at it, Lor\'themar. It is certainly Quel\'Delar.', 12, 0, 100, 1, 0, 0, 37444, 'Grand Magister Rommath to Player'),
(37763, 1, 0, 'What is the meaning of this treachery?', 12, 0, 100, 0, 0, 0, 37389, 'Grand Magister Rommath to Player'),
(37763, 2, 0, 'It seems the captain is correct. Quel\'Delar is not to be ours after all.', 12, 0, 100, 0, 0, 0, 37433, 'Grand Magister Rommath to Player'),
(37763, 3, 0, 'Guards, return to your posts.', 12, 0, 100, 0, 0, 0, 37432, 'Grand Magister Rommath to Player'),
(37763, 4, 0, 'You will have what you have come for, $N. Take the sword and begone. And you, Auric, have a care what you say in this sacred place.', 12, 0, 100, 1, 0, 0, 37434, 'Grand Magister Rommath to Player'),
(37764, 0, 0, 'We shall see.', 12, 0, 100, 1, 0, 0, 37442, 'Lor\'themar Theron to Player'),
(37764, 1, 0, 'So it is. You have my thanks, $N, for restoring Quel\'Delar to its rightful owners.', 12, 0, 100, 1, 0, 0, 37388, 'Lor\'themar Theron to Player'),
(37765, 0, 0, 'This is not my doing, Rommath. There is no treachery here.', 12, 0, 100, 1, 0, 0, 37430, 'Captain Auric Sunchaser to Player'),
(37765, 1, 0, 'Call off your men. Lor\'themar\'s own foolishness caused his injuries. Quel\'Delar is not chosen; it chooses its master.', 12, 0, 100, 1, 0, 0, 37431, 'Captain Auric Sunchaser to Player'),
(37765, 2, 0, 'Carry the sword back to Dalaran through that portal, $N. You have done what many quel\'dorei have dreamt of for years. Quel\'Delar has been restored at last.', 12, 0, 100, 25, 0, 0, 37440, 'Captain Auric Sunchaser to Player'),
(37781, 0, 0, 'My Lord Regent! What have they done to you?', 14, 0, 100, 0, 0, 0, 37439, 'Sunwell Honor Guard to Player'),
(37781, 1, 0, 'Drop your weapon and surrender, traitor.', 12, 0, 100, 0, 0, 0, 37390, 'Sunwell Honor Guard to Player'),
(38056, 0, 0, 'My lords and ladies, I present $N, bearer of Quel\'Delar.', 14, 0, 100, 1, 0, 0, 37820, 'Chamberlain Galiros to Player');
