UPDATE `locales_quest` SET `Title_loc6`='Cazar para comer', `Objectives_loc6`='Llévale 10 piezas de carne de jabalí al cocinero Torka en Cerrotajo.$BDurotar' WHERE `Id`=814;
UPDATE `locales_quest` SET `Title_loc6`='La recompensa por la misión', `Details_loc6`='Tu cuerpo alberga un auténtico espíritu de fuerza, ¿o acaso fue un golpe de suerte?$B$BEn eso lo basa todo la juventud ahora: suerte... y la generosidad de los que son más poderosos que ellos. Pocos se toman el tiempo de hallar su propio camino. Siempre rogando que los ayuden... siempre la recompensa y nunca la misión. Seguro que serás igual que todos ellos.$B$BAunque hay esperanza. Necesito una cosa más para mi poción, $N: 8 hierbas de cardaluz. Se encuentran en Monte del Trueno al noroeste de Cerrotajo.', `Objectives_loc6`='Llévale 8 hierbas de cardaluz al maestro Vornal en Poblado Sen\'jin.$BDurotar' WHERE `Id`=820;
