UPDATE `locales_quest` SET `Title_loc6`='En la oscuridad Fue Creado', `Details_loc6`='Me gustaría enseñarte todo lo que necesitas saber, $N, pero hay cosas que solo tu propia tribu puede mostrarte. Una seria cómo crearte un traje nuevo... uno que refleje tu puesto en la fe.$B$BAelthalyste dice que te dirijas a ella en Entrañas. Ella te estará esperando en el Barrio de la Guerra. Si no fueses ella se digustará bastante con nosotros.', `Objectives_loc6`='Habla con Aelthalyste en Entrañas' WHERE `Id`=5692;
UPDATE `locales_quest` SET `Title_loc6`='En la oscuridad Fue Creado', `Details_loc6`='Me alegra ver que estás ... vivo, $N.$B$BAelthalyste me ha pedido que vuelvas a Entrañas cuando hayas terminado tus negocios aqui en Cima del Trueno. Parece que eres lo suficientemente poderoso para necesitar nuevas prendas... una túnica, Creo.$B$BElla te está esperando en el Barrio de la Guerra.', `Objectives_loc6`='Habla con Aelthalyste en Entrañas' WHERE `Id`=5693;
