UPDATE `locales_quest` SET `Title_loc6`='Visita la sacerdotisa Alathea', `Details_loc6`='Siempre es un día de regocijo cuando puedo mandar a uno de los nuestros de vuelta a casa porque ha llegado la hora para ellos de conseguir el próximo conjunto de togas.$B$BLa sacerdotisa Alathea está en el Templo de la Luna y desea hablar contigo. Cuando puedas, vuelve a Darnassus para la prueba siguiente, y que Elune esté contigo, $N.' WHERE `Id`=5696;
