-- Remove all creature and gameobject spawns from map 109
DELETE FROM `creature` WHERE `map`=109;
DELETE FROM `creature_addon` WHERE `guid` NOT IN (SELECT `guid` FROM `creature`);
DELETE FROM `gameobject` WHERE `map`=109;
DELETE FROM `gameobject_addon` WHERE `guid` NOT IN (SELECT `guid` FROM `gameobject`);

-- Remove old waypoint and formation
DELETE FROM `waypoint_data` WHERE `id`=336580;
DELETE FROM `creature_formations` WHERE `leaderGUID`=33658;

-- Respawn creatures
SET @CGUID := 251251;
DELETE FROM `creature` WHERE `guid` BETWEEN @CGUID+0 AND @CGUID+104;

