INSERT ignore INTO `spell_script_names` (`spell_id`, `ScriptName`) VALUES ('126123', 'spell_pri_confession');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`) VALUES 
(126123, 0, 1, 'признается: Я поджигаю все вокруг и кричу: "Очистимся огнем!", пока никто не видит. - Рагнарос', 12, 0, 0, 0, 0, 0), 
(126123, 0, 2, 'признается: Понятия не имею, кто такие син`дорай.', 12, 0, 0, 0, 0, 0), 
(126123, 0, 3, 'признается: Да, мне случалось бывать в подземельях, но что делать, я не представляла. Было так стыдно.', 12, 0, 0, 0, 0, 0), 
(126123, 0, 4, 'признается: На свое первое ездовое животное я занимала деньги у друга.', 12, 0, 0, 0, 0, 0), 
(126123, 0, 5, 'признается: Я постоянно забываю вставить камни в снаряжение.', 12, 0, 0, 0, 0, 0), 
(126123, 0, 6, 'признается: Я не помню, чтобы мы виделись в горах. - Локен', 12, 0, 0, 0, 0, 0), 
(126123, 0, 7, 'признается: Я не помню Солнечный Колодец.', 12, 0, 0, 0, 0, 0), 
(126123, 0, 8, 'признается: Мне казалось, пандарены - это подвид фурболгов.', 12, 0, 0, 0, 0, 0), 
(126123, 0, 9, 'признается: Я полгода гонялась за затерянным во времени протодраконом. - рарный дракон', 12, 0, 0, 0, 0, 0), 
(126123, 0, 10, 'признается: Я никогда не использую Колодец Света.', 12, 0, 0, 0, 0, 0), 
(126123, 0, 11, 'признается: Мы и правда были не готовы. Но кто же знал? - Иллидан', 12, 0, 0, 0, 0, 0), 
(126123, 0, 12, 'признается: Я постоянно прошу свет дать мне сил, но я не уверена, что будет толк.', 12, 0, 0, 0, 0, 0), 
(126123, 0, 13, 'признается: Гаррош, конечно, свинья, но что-то в нем есть такое...', 12, 0, 0, 0, 0, 0), 
(126123, 0, 14, 'признается: Мне когда-то казалось, что байки - это звери, потому что их травят.', 12, 0, 0, 0, 0, 0), 
(126123, 0, 15, 'признается: Все эти имена с апострофами сбивают с толку.', 12, 0, 0, 0, 0, 0), 
(126123, 0, 16, 'признается: Я пнула гнома как-то раз. Просто так, ни за что. День был паршивый.', 12, 0, 0, 0, 0, 0), 
(126123, 0, 17, 'признается: Некоторые ездовые животные мне дороже остальных.', 12, 0, 0, 0, 0, 0), 
(126123, 0, 18, 'признается: Мне случалось стоять в огне.', 12, 0, 0, 0, 0, 0), 
(126123, 0, 19, 'признается: Всегда хотелось быть паладином.', 12, 0, 0, 0, 0, 0), 
(126123, 0, 20, 'признается: Встретился мне как-то маг, произнес заклинание, у меня сразу челюсть отвисла.', 12, 0, 0, 0, 0, 0), 
(126123, 0, 21, 'признается: Я пошла в Альтеракскую долину и ничем не помогла своей команде.', 12, 0, 0, 0, 0, 0), 
(126123, 0, 22, 'признается: Мне случалось заимствовать вещи из гильдейского банка.', 12, 0, 0, 0, 0, 0), 
(126123, 0, 23, 'признается: Иногда я прошу чернокнижника призвать меня, хотя добираться не так уж и далеко.', 12, 0, 0, 0, 0, 0), 
(126123, 0, 24, 'признается: В парикмахерской меня уже несколько месяцев не видели. Гоблины с ножницами, тоже мне удовольствие.', 12, 0, 0, 0, 0, 0), 
(126123, 0, 25, 'признается: Это просто досадное недоразумение.', 12, 0, 0, 0, 0, 0), 
(126123, 0, 26, 'признается: От троллей у меня мурашки по коже.', 12, 0, 0, 0, 0, 0), 
(126123, 0, 27, 'признается: Я взяла свечу.', 12, 0, 0, 0, 0, 0), 
(126123, 0, 28, 'признается: Как-то раз на аукционе я перебил ставку своего друга на вещь, которая не была мне нужна.', 12, 0, 0, 0, 0, 0), 
(126123, 0, 29, 'признается: Однажды я упала с Даларана.', 12, 0, 0, 0, 0, 0), 
(126123, 0, 30, 'признается: Бывает, я еду до цели верхом, даже если пешком до цели пять секунд. Да даже если всего секунда!', 12, 0, 0, 0, 0, 0), 
(126123, 0, 31, 'признается: Я иду в подземелья не ради того, чтобы помочь Азероту, а за добычей.', 12, 0, 0, 0, 0, 0), 
(126123, 0, 32, 'признается: Мне давно интересно, похожи ли таурены по вкусу на... понятно на кого.', 12, 0, 0, 0, 0, 0), 
(126123, 0, 33, 'признается: Когда я делаю задания, мне иногда хочется побыть одной. Тогда я делаю вид, что не слышу своих друзей.', 12, 0, 0, 0, 0, 0), 
(126123, 0, 34, 'признается: Я как-то раз на подъемнике умерла. А может, и не раз.', 12, 0, 0, 0, 0, 0), 
(126123, 0, 35, 'признается: Я занимаюсь археологией, но история меня абсолютно не интересует.', 12, 0, 0, 0, 0, 0), 
(126123, 0, 36, 'признается: Иногда я забываю, Нордскол на юге или на сервере.', 12, 0, 0, 0, 0, 0), 
(126123, 0, 37, 'признается: Не могу решить, Миллхаус хороший или плохой.', 12, 0, 0, 0, 0, 0), 
(126123, 0, 38, 'признается: Я сказала рейд-лидеру, что готова, но это не было правдой.', 12, 0, 0, 0, 0, 0), 
(126123, 0, 39, 'признается: На самом деле меня не было на открытии Ан`Киража, я только читала об этом.', 12, 0, 0, 0, 0, 0), 
(126123, 0, 40, 'признается: Однажды я забрала лук, который был нужен охотнику.', 12, 0, 0, 0, 0, 0), 
(126123, 0, 41, 'признается: Мне так жаль моих нелетающих скакунов. Я их теперь почти не использую.', 12, 0, 0, 0, 0, 0), 
(126123, 0, 42, 'признается: До сих пор задаюсь вопросом, почему дренейкам нравятся гномы.', 12, 0, 0, 0, 0, 0), 
(126123, 0, 43, 'признается: Когда на моих друзей накладывают контроль над разумом, я изо всех сил стараюсь их убить.', 12, 0, 0, 0, 0, 0), 
(126123, 0, 44, 'признается: Я не выставляю на бой самых сильных боевых питомцев. Только самых умилительных.', 12, 0, 0, 0, 0, 0), 
(126123, 0, 45, 'признается: Ох и стырю я добычу того охотника под шумок...', 12, 0, 0, 0, 0, 0), 
(126123, 0, 46, 'признается: Я часто осматриваю других, чтобы узнать, не осматривают ли они меня.', 12, 0, 0, 0, 0, 0), 
(126123, 0, 47, 'признается: Не могу выбрать между Ордой и Альянсом. У Орды есть гоблины-магички с четырьмя хвостиками, а у Альянса – дворфийки-жрицы с косичками-баранками.', 12, 0, 0, 0, 0, 0), 
(126123, 0, 48, 'признается: Я раньше думала, что Медив – друид. Это все из-за перьев.', 12, 0, 0, 0, 0, 0), 
(126123, 0, 49, 'признается: Я всегда продаю свои старые доспехи одному и тому же торговцу. Он вроде как богатый.', 12, 0, 0, 0, 0, 0), 
(126123, 0, 50, 'признается: Когда пандарен говорит мне не торопиться, так и хочется его пнуть.', 12, 0, 0, 0, 0, 0), 
(126123, 0, 51, 'признается: Красивая экипировка для меня намного важнее, чем полезная.', 12, 0, 0, 0, 0, 0), 
(126123, 0, 52, 'признается: Когда мне надо перековать доспехи, я всегда прошу у кого-нибудь совета.', 12, 0, 0, 0, 0, 0), 
(126123, 0, 53, 'признается: По-моему, я как-то пыталась убить Лэй Ши вместо Лэй Шэня.', 12, 0, 0, 0, 0, 0), 
(126123, 0, 54, 'признается: Я уже несколько месяцев не была на ферме Солнечной Песни. Представляю, сколько там развелось сорняков и гну-синей...', 12, 0, 0, 0, 0, 0), 
(126123, 0, 55, 'признается: Я все время путаю сердце дикой природы со знаком.', 12, 0, 0, 0, 0, 0);