INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `probability`, `emote`, `duration`, `comment`) VALUES ('68955', '0', '0', 'We are the HORDE. We are slaves no nothing and no one!', '12', '100', '1', '5', 'Garrosh text1 on quest Breath of Darkest Shadow');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `probability`, `emote`, `duration`, `comment`) VALUES ('68955', '0', '1', 'With the Divine Bell. I will burn away any remnants of weakness witchin us. Fear.. despair...harmed...doubt.The lesser races are buried beneath their weight.Buit WE will control their power.', '12', '100', '1', '5', 'Garrosh text2 on quest Breath of Darkest Shadow');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `probability`, `emote`, `duration`, `comment`) VALUES ('68955', '0', '2', 'Together we will destroy the Alliance and claim what is rightfully ours. Let our song of victory begin.', '12', '100', '1', '5', 'Garrosh text3 on quest Breath of Darkest Shadow');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `probability`, `emote`, `duration`, `comment`) VALUES ('68955', '0', '3', 'This is but a  test of strength. We will pass trial just as we have passed others!', '12', '100', '1', '5', 'Garrosh text4 on quest Breath of Darkest Shadow');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `probability`, `emote`, `duration`, `comment`) VALUES ('68955', '0', '4', 'Yes! Feel the power withim you fight your fears. Conquer your hatred!', '12', '100', '1', '5', 'Garrosh text5 on quest Breath of Darkest Shadow');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `probability`, `emote`, `duration`, `comment`) VALUES ('68955', '0', '5', 'If your enemies should choose to push us, we will drive them into the ground!', '12', '100', '1', '5', 'Garrosh text6 on quest Breath of Darkest Shadow');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `probability`, `emote`, `duration`, `comment`) VALUES ('68955', '0', '6', 'Ahahahaha...so in the end, it is not Varian, but his whelp who comes to face me. You run bravely to your death, young one.', '12', '100', '1', '5', 'Garrosh text7 on quest Breath of Darkest Shadow');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `probability`, `emote`, `duration`, `comment`) VALUES ('68955', '0', '7', 'Stop me then, human.', '12', '100', '1', '5', 'Garrosh text8 on quest Breath of Darkest Shadow');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `probability`, `emote`, `duration`, `comment`) VALUES ('68955', '0', '8', 'Fight the sha - master it! Use it!', '12', '100', '1', '5', 'Garrosh text9 on quest Breath of Darkest Shadow');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `probability`, `emote`, `duration`, `comment`) VALUES ('68955', '0', '9', 'Doubt is for the weak! Do not question your strength Ishi. We will survive because we must! The Horde will prevail!', '12', '100', '1', '5', 'Garrosh text10 on quest Breath of Darkest Shadow');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `probability`, `emote`, `duration`, `comment`) VALUES ('68955', '0', '10', 'Our suffering is at an end. When this war is won, our people will see prosperity at last.', '12', '100', '1', '5', 'Garrosh text11 on quest Breath of Darkest Shadow');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `probability`, `emote`, `duration`, `comment`) VALUES ('68955', '0', '11', 'Control your hatred, Ishi. There is killing, and then there is murder. Direct your rage to our enemies.', '12', '100', '1', '5', 'Garrosh text12 on quest Breath of Darkest Shadow');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `probability`, `emote`, `duration`, `comment`) VALUES ('68955', '0', '12', 'Use your fear, Ishi! Turn it on your foes. When death arrives, will you stand and face it or kneel in defeat?', '12', '100', '1', '5', 'Garrosh text13 on quest Breath of Darkest Shadow');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `probability`, `emote`, `duration`, `comment`) VALUES ('68955', '0', '13', 'Yes, Ishi. You have. Your interference has cost me a great warrior, young prince. You\'ll pay with your life.', '12', '100', '1', '5', 'Garrosh text14 on quest Breath of Darkest Shadow');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `probability`, `emote`, `duration`, `comment`) VALUES ('68955', '0', '14', 'Die, whelp!', '12', '100', '1', '5', 'Garrosh text15 on quest Breath of Darkest Shadow');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `probability`, `emote`, `duration`, `comment`) VALUES ('68955', '0', '15', 'There is much I do not know about this artifact. The weak-willed cannot control this sha energy, but I WILL master it.
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `probability`, `emote`, `duration`, `comment`) VALUES ('68873', '0', '0', 'Stop Garrosh! You do not know what that bell is capable of!', '12', '100', '1', '5', 'Anduin text1 on quest Breath of Darkest Shadow');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `probability`, `emote`, `duration`, `comment`) VALUES ('68873', '0', '1', 'I will not let you do this. I swear to it.', '12', '100', '1', '5', 'Anduin text2 on quest Breath of Darkest Shadow');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `probability`, `emote`, `duration`, `comment`) VALUES ('68873', '0', '2', 'Yes, Ishi. You have. Your interference has cost me a great warrior, young prince. You\'ll pay with your life.', '12', '100', '1', '5', 'Anduin text3 on quest Breath of Darkest Shadow');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `probability`, `emote`, `duration`, `comment`) VALUES ('68873', '0', '3', 'That is where you are wrong Garrosh.', '12', '100', '1', '5', 'Anduin text4 on quest Breath of Darkest Shadow');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `probability`, `emote`, `duration`, `comment`) VALUES ('68225', '0', '0', 'Aaaaauuuuughh! This pain! I cannot control it!', '12', '100', '1', '5', 'Ishi text1 on quest Breath of Darkest Shadow');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `probability`, `emote`, `duration`, `comment`) VALUES ('68225', '0', '1', 'Do you speak the truth, Garrosh? Can the Horde truly survive another war?', '12', '100', '1', '5', 'Ishi text2 on quest Breath of Darkest Shadow');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `probability`, `emote`, `duration`, `comment`) VALUES ('68225', '0', '2', 'We have lost so much, Warchief. Our families dead. Our friends, lost. Our homeland, gone. How much more must our people suffer?', '12', '100', '1', '5', 'Ishi text3 on quest Breath of Darkest Shadow');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `probability`, `emote`, `duration`, `comment`) VALUES ('68225', '0', '3', 'Why should we be slaves to honor, when we could slaughter the children of our enemies while they sleep? My blade thirsts for Alliance blood!', '12', '100', '1', '5', 'Ishi text4 on quest Breath of Darkest Shadow');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `probability`, `emote`, `duration`, `comment`) VALUES ('68225', '0', '4', 'At night, I see the faces of all those I have slain. Where do our spirits go after our bodies lay cold? I fear...death.', '12', '100', '1', '5', 'Ishi text5 on quest Breath of Darkest Shadow');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `probability`, `emote`, `duration`, `comment`) VALUES ('68225', '0', '5', 'Warchief! I...I have failed you.', '12', '100', '1', '5', 'Ishi text6 on quest Breath of Darkest Shadow');
