INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (24536, 1, 3, 'Oh, we\'re from Blackrock Mountain, $BWe\'ve come ta drink yer brew!$BDark Iron dwarves, they do not lie, $BAnd so yeh know it\'s true!', 12, 0, 100, 0, 0, 0, 'Dark Iron Herald');
