INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (24536, 2, 0, 'RETREAT! We\'ve taken a beating and had 0 casualties! We can\'t keep taking these losses! FALL BACK!!', 14, 0, 100, 0, 0, 0, 'Dark Iron Herald');
