INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (35840, 0, 1, 'The King\'s men will protect us. We`re almost here.', 12, 0, 1, 0, 0, 0, 'Gwen Armstead - Text 2');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (35840, 0, 2, 'The plan is to keep moving south. We can`t go back to the Merchant Square. It\'s not safe.', 12, 0, 1, 0, 0, 0, 'Gwen Armstead  - Text 3');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (35906, 0, 0, 'We need to keep the worgen\'s attention in the city, Genn. It\'s the only shot we have for the survivors to make it to Duskhaven.', 12, 0, 100, 0, 0, 0, 'Text on Quest Finish');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (35906, 1, 0, 'Not a chance, boy. Gilneas is going to need its king\'s undivided attention. Can\'t have your father wondering whether his child is alive or not.', 12, 0, 100, 0, 0, 0, 'Text on Quest Finish');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (35906, 2, 0, 'My men and I will hole up inside the Light\'s Dawn Cathedral. I\'ve already given the order and the cannons are on their way. Lead our people well, Genn.', 12, 0, 100, 0, 0, 0, 'Text on Quest Finish');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (35911, 0, 0, 'If we can make it past the gates into Duskhaven we\'ll be safe. The eastern mountains are virtually impassable.', 12, 0, 100, 0, 0, 0, 'Text on Quest Finish');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (35911, 1, 0, 'I\'ll stay behind with the Royal Guard, father. It is my duty to Gilneas.', 12, 0, 100, 0, 0, 0, 'Text on Quest Finish');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (35911, 2, 0, 'We were fools to take up arms against each other, Darius. The worgen would\'ve never stood a chance.', 12, 0, 100, 0, 0, 0, 'Text on Quest Finish');
