INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49141, 3, 0, 'Let\'s see now... WHERE could they be...', 12, 0, 100, 1, 0, 0, 'Darnell');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49141, 4, 0, 'Maybe they\'re over here?', 12, 0, 100, 1, 0, 0, 'Darnell');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49141, 5, 0, 'Nice work! You\'ve found them. Let\'s bring these back to Mordo.', 12, 0, 100, 1, 0, 0, 'Darnell');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49141, 6, 0, 'I saw someone up there whose jaw fell off. I wonder if Mordo can fix him up?', 12, 0, 100, 1, 0, 0, 'Darnell');
