INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49141, 1, 0, 'The shadow grave is in this direction. Follow me.', 12, 0, 100, 1, 0, 0, 'Darnell');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49148, 1, 0, 'Come, my children! Defend me!', 14, 0, 100, 0, 0, 0, 'Dark Ritualist Zakahn - Yell 2');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49148, 0, 0, 'Serpents of the depths! Come and aid your master!', 14, 0, 100, 0, 0, 0, 'Dark Ritualist Zakahn - Yell 1');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49226, 24, 0, 'A shame to lose that experiment...', 14, 0, 0, 0, 0, 23361, 'VO_BD_Nefarian_ChimaronDead01');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49226, 22, 0, 'Now that was really unnecessary.', 14, 0, 0, 0, 0, 23387, 'VO_BD_Nefarian_Event19');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49226, 23, 0, 'Impressive! You managed to destroy one of my most horrific creations - a task I\'d thought impossible until now.', 14, 0, 0, 0, 0, 20088, 'VO_BD_Nefarian_Event20');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49226, 21, 0, 'Atramedes, are you going deaf as well as blind? Hurry up and kill them all!', 14, 0, 0, 0, 0, 23360, 'VO_BD_Nefarian_AtramedesIntro');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49226, 18, 0, 'You should\'ve been disposed of a long time ago.', 14, 0, 0, 0, 0, 23357, 'VO_BD_Nefarian_AtramedesDead');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49226, 19, 0, 'These shields are proving quite the nuisance!', 14, 0, 0, 0, 0, 23358, 'VO_BD_Nefarian_AtramedesGong');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49226, 20, 0, 'Atramedes, the heroes are RIGHT THERE!', 14, 0, 0, 0, 0, 23359, 'VO_BD_Nefarian_AtramedesImp01');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49226, 17, 0, 'We never did see eye to eye... Good riddance I say!', 14, 0, 0, 0, 0, 20089, 'VO_BD_Nefarian_Event21');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49226, 16, 0, 'Congratulations! Allow me to grant you a title befitting the amazing achievement you just performed! Henceforth, you shall be known as the Slayer of Incompetent, Stupid and Disappointing Minions!', 14, 0, 0, 0, 0, 23371, 'VO_BD_Nefarian_MaloriakDead01');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49226, 15, 0, 'Your mixtures are weak, Maloriak! They need a bit more... kick!', 14, 0, 0, 0, 0, 23370, 'VO_BD_Nefarian_Maloriak01');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49226, 13, 0, 'On second thought, what fun is an adequate challenge?', 14, 0, 0, 0, 0, 23369, 'VO_BD_Nefarian_MagmawSummon01');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49226, 14, 0, 'Maloriak, try not to lose to these mortals! Semi-competent help is SO hard to create.', 14, 0, 0, 0, 0, 23372, 'VO_BD_Nefarian_MaloriakIntro01');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49226, 12, 0, 'INCONCEIVEABLE! You may actually defeat my lava worm! Perhaps I can help... TIP the scales.', 14, 0, 0, 0, 0, 23368, 'VO_BD_Nefarian_MagmawLowHealth');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49226, 11, 0, 'You won! How... disappointing. I could\'ve easily watched you all continue to stand in the fire.', 14, 0, 0, 0, 0, 23366, 'VO_BD_Nefarian_MagmawDead01');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49226, 10, 0, 'Finally someone to deal with that unsightly pest! You have my thanks, \"heroes.\"', 14, 0, 0, 0, 0, 20085, 'VO_BD_Nefarian_Event17');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49226, 9, 0, 'I found this fascinating speciman in the lava underneath this very room. Magmaw should provide an adequate challenge for your pathetic little band!', 14, 0, 0, 0, 0, 23367, 'VO_BD_Nefarian_MagmawIntro01');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49226, 8, 0, 'That malfunctioning piece of junk was murder on the repair bills.', 14, 0, 0, 0, 0, 20086, 'VO_BD_Nefarian_Event18');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49226, 7, 0, 'Were you planing on using Toxitron\'s chemicals to damage the other constructs? Clever plan. Let me ruin that for you.', 14, 0, 0, 0, 0, 23378, 'VO_BD_Nefarian_OmnitronSpell04');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49226, 6, 0, 'Lightning Conductor works much better in a crowd, yes?', 14, 0, 0, 0, 0, 23377, 'VO_BD_Nefarian_OmnitronSpell03');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49226, 5, 0, 'Stupid dwarves and your fascination with runes. Why would you create something that would help your enemy?', 14, 0, 0, 0, 0, 23376, 'VO_BD_Nefarian_OmnitronSpell02');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49226, 4, 0, 'You would like to move away from that, wouldn\'t you? Too bad.', 14, 0, 0, 0, 0, 23375, 'VO_BD_Nefarian_OmnitronSpell01');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49226, 3, 0, 'Hmm, the Omnotron Defense System. Centuries ago, these constructs were considered the dwarves greatest tactical achievements. With so many counters to each construct\'s attacks, I\'ll have to rectify these designs for them to provide me ANY entertainment!', 14, 0, 0, 0, 0, 23374, 'VO_BD_Nefarian_OmnitronIntro01');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49226, 2, 0, 'I should\'ve known better than to rely on something so stupidly named... to entertain me for long.', 14, 0, 0, 0, 0, 23373, 'VO_BD_Nefarian_OmnitronDead01');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49226, 1, 0, 'Mortals that fancy themselves HEROES have entered the Broken Hall! Oh, I do hope this \'raid\' will amuse me more than the last!', 14, 0, 0, 0, 0, 23379, 'VO_BD_Nefarian_ZoneIntro01');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49226, 0, 0, 'Welcome to my personal sanctum, heroes! I hope you find the accommodations to your liking. Herein you will find many of my failed experiments and ultimately my greatest triumph!', 14, 0, 0, 0, 0, 20090, 'VO_BD_Nefarian_Event22');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49226, 25, 0, 'Ah, Chimaeron! Truly a living testament to my scientific prowess. I reworked and twisted his form countless times over the years and the final result is truly something to behold.', 14, 0, 0, 0, 0, 23362, 'VO_BD_Nefarian_ChimaronIntro01');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49226, 26, 0, 'Oh. You were expecting something else to happen just then, weren\'t you? Let\'s see how YOU adapt after a new set of adjustments, shall we?', 14, 0, 0, 0, 0, 23363, 'VO_BD_Nefarian_ChimaronSpell01');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49226, 27, 0, 'Stop fighting yourself THIS INSTANT Chimaeron!', 14, 0, 0, 0, 0, 23364, 'VO_BD_Nefarian_ChimaronSpell02');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49226, 28, 0, 'This fight has gone a bit stale. Allow me to spice things up!', 14, 0, 0, 0, 0, 23365, 'VO_BD_Nefarian_ChimaronSpell03');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49337, 3, 0, 'Ah, here he is.', 12, 0, 100, 1, 0, 0, 'Darnell');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49337, 2, 0, 'Good, you\'re still here. Now, WHERE\'s Deathguard Saltain?', 12, 0, 100, 1, 0, 0, 'Darnell');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49337, 0, 0, 'Hello again..', 12, 0, 100, 3, 0, 0, 'Darnell');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49337, 1, 0, 'I know the way to Deathknell. Come with me!', 12, 0, 100, 273, 0, 0, 'Darnell');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49337, 4, 0, 'We make our way. Saltain said that we can find a few dead bodies up here.', 12, 0, 100, 1, 0, 0, 'Darnell');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49424, 0, 0, 'BLEEAAAGGHHH! I\'m a monster, don\'t look at me!', 14, 0, 100, 0, 0, 0, 'Marschall Rotpfad');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49426, 3, 0, 'How about you? I\'ve heard that Sarvis is going to send you off to Calston Estate. Big assignment, eh?', 12, 0, 100, 1, 0, 0, 'Todeswache Darnell');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49426, 2, 0, 'I\'ll probably be staying here in Deathknell. If things work out though... who knows? Maybe I\'ll get to meet the Dark Lady herself.', 12, 0, 100, 6, 0, 0, 'Todeswache Darnell');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49426, 0, 0, 'Hi, $s! What do you think about my new gear?', 12, 0, 100, 23, 0, 0, 'Todeswache Darnell');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49426, 1, 0, 'They\'re making me into a Deathguard. Can you believe it?', 12, 0, 100, 1, 0, 0, 'Todeswache Darnell');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49426, 4, 0, 'Well, I won\'t hold you up. It\'s been a pleasure. I hope our paths cross again soon.', 12, 0, 100, 66, 0, 0, 'Todeswache Darnell');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49429, 1, 0, 'Deep within his soul, the one thing he feared most of all was...himself.', 12, 0, 100, 0, 0, 24603, 'Vanessa VanCleef glubtok 2');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49429, 2, 0, 'Most rogues prefer to cloak themselves in the shadows, but not Helix.', 12, 0, 100, 0, 0, 24604, 'Vanessa VanCleef helix 1');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49429, 3, 0, 'You never know what skitters in the darkness.', 12, 0, 100, 0, 0, 24605, 'Vanessa VanCleef helix 2');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49429, 4, 0, 'Can you imagine the life of a machine?', 12, 0, 100, 0, 0, 24606, 'Vanessa VanCleef foereaper 1');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49429, 5, 0, 'A simple spark can mean the difference between life...and death.', 12, 0, 100, 0, 0, 24607, 'Vanessa VanCleef foereaper 2');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49429, 6, 0, 'Ripsnarl wasn\'t always a bloodthirsty savage. Once, he even had a family.', 12, 0, 100, 0, 0, 24608, 'Vanessa VanCleef admiral 1');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49429, 7, 0, 'He was called James Harrington. A tragedy in three parts.', 12, 0, 100, 0, 0, 24609, 'Vanessa VanCleef admiral 2');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49429, 8, 0, 'I\'ve been waiting a long time for this, you know.', 12, 0, 100, 0, 0, 24613, 'Vanessa VanCleef intro 1');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49429, 9, 0, 'Biding my time, building my forces, studying the minds of my enemies.', 12, 0, 100, 0, 0, 24614, 'Vanessa VanCleef intro 2');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49429, 10, 0, 'I was never very good at hand-to-hand combat, you know. Not like my father.', 12, 0, 100, 0, 0, 24615, 'Vanessa VanCleef intro 3');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49429, 11, 0, 'But I always excelled at poisons.', 12, 0, 100, 0, 0, 24616, 'Vanessa VanCleef intro 4');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49429, 12, 0, 'Especially venoms that affect the mind.', 12, 0, 100, 0, 0, 24617, 'Vanessa VanCleef intro 5');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49429, 0, 0, 'Poor Glubtok. When his powers manifested, his own ogre mound was the first to burn.', 12, 0, 100, 0, 0, 24602, 'Vanessa VanCleef glubtok 1');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (49541, 4, 0, 'Fools! This entire ship is rigged with explosives! Enjoy your fiery deaths!', 14, 0, 100, 0, 0, 24621, 'Vanessa VanCleef spell 1');
