INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54432, 8, 0, 'You crawl unwitting, like a blind, writhing worm, towards endless madness and despair. I have witnessed the true End Time. This? This is a blessing you simply cannot comprehend.', 14, 0, 100, 0, 0, 25935, 'Murozond intro 02');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54432, 7, 0, 'The \"End Time,\" I once called this place. I had not seen, by then; I did not know. You hope to... what? Stop me, here? Change the fate I worked so tirelessly to weave?', 14, 0, 100, 0, 0, 25934, 'Murozond intro 01');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54432, 6, 0, 'The Hourglass\' power is exhausted. No more games, mortals. Relent, or perish.', 14, 0, 100, 0, 0, 25933, 'Murozond event 05');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54432, 4, 0, 'Another chance will make no difference. You will fail.', 14, 0, 100, 0, 0, 25931, 'Murozond event 03');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54432, 5, 0, 'Again...? Is this your plot, your scheme?', 14, 0, 100, 0, 0, 25932, 'Murozond event 04');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54432, 2, 0, 'The powers of the Hourglass do nothing to me!', 14, 0, 100, 0, 0, 25929, 'Murozond event 01');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54432, 3, 0, 'To repeat the same action and expect different results is madness.', 14, 0, 100, 0, 0, 25930, 'Murozond event 02');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54432, 1, 0, 'You know not what you have done. Aman\'Thul... What I... have... seen...', 14, 0, 100, 0, 0, 25928, 'Murozond death');
