INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54445, 4, 1, 'A little ice ought to quench the fire in your hearts...', 14, 0, 100, 0, 0, 25925, 'Jaina spell 02');
