INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54544, 1, 0, 'Moon goddess, your light dims! I am lost without your guidance!', 14, 0, 100, 0, 0, 25973, 'Tyrande 80%');
