INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54544, 9, 0, 'The moonlight can bring rest to your weary souls in this forgotten place.', 12, 0, 100, 0, 0, 25979, 'Tyrande event 03');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54544, 10, 0, 'Give yourselves to the night, Elune will guide you from this mortal prison.', 12, 0, 100, 0, 0, 25980, 'Tyrande event 04');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54544, 11, 0, 'You have chosen a path of darkness. Mother moon, guide my hand; allow me to bring rest to these misbegotten souls.', 12, 0, 100, 0, 0, 25981, 'Tyrande event 05');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54544, 12, 0, 'Elune guide you through the night.', 12, 0, 100, 0, 0, 25982, 'Tyrande kill');
