INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54590, 7, 2, 'The Aspects misplaced their trust.', 14, 0, 100, 0, 0, 25806, 'arcurion kill 3');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54590, 7, 1, 'Your shaman can\'t protect you.', 14, 0, 100, 0, 0, 25805, 'arcurion kill 2');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54590, 8, 0, 'You\'re a mere mortal. It is time you die like one.', 14, 0, 100, 0, 0, 25804, 'arcurion intro 2');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54590, 7, 0, 'Mere mortals...', 14, 0, 100, 0, 0, 25803, 'arcurion kill 1');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54590, 6, 0, 'You are a fool if you think to take your place as the Aspect of Earth, Shaman!', 14, 0, 100, 0, 0, 25802, 'arcurion intro 1');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54590, 5, 0, 'The Hour of Twilight falls - the end of all things - you cannot stop it. You are nothing. NOTHING!', 14, 0, 100, 0, 0, 25801, 'arcurion event 4');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54590, 3, 0, 'Destroy them all, but bring the Shaman to me!', 14, 0, 100, 0, 0, 25799, 'arcurion event 2');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54590, 4, 0, 'You will go nowhere, Shaman.', 14, 0, 100, 0, 0, 25800, 'arcurion event 3');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54590, 2, 0, 'Shaman! The Dragon Soul is not yours. Give it up, and you may yet walk away with your life.', 14, 0, 100, 0, 0, 25798, 'arcurion event 1');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54590, 1, 0, 'Nothing! Nothing...', 14, 0, 100, 0, 0, 25797, 'arcurion death');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54590, 0, 0, 'Give up the Dragon Soul, and I may yet allow you to live.', 14, 0, 100, 0, 0, 25796, 'arcurion aggro');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54590, 9, 0, 'Enough, shaman!', 14, 0, 100, 0, 0, 25807, 'arcurion spell 1');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54590, 9, 1, 'None will survive!', 14, 0, 100, 0, 0, 25808, 'arcurion spell 2');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54590, 9, 2, 'The Shaman is mine, focus on his companions!', 14, 0, 100, 0, 0, 25809, 'arcurion spell 3');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54590, 9, 3, 'Freeze!', 14, 0, 100, 0, 0, 25810, 'arcurion spell 4');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54751, 5, 0, 'All that matters... is this moment.', 12, 0, 100, 0, 0, 25947, 'Nozdormu event 05');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54751, 4, 0, 'Nozdormu turns away from where Murozond died and looks up at the Hourglass of Time.', 16, 0, 100, 0, 0, 0, 'Nozdormu emote');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54751, 3, 0, 'What matters is that Azeroth did not fall; that we survived to fight another day.', 12, 0, 100, 0, 0, 25946, 'Nozdormu event 04');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54751, 2, 0, 'Still, in time, I will... fall to madness. And you, heroes... will vanquish me. The cycle will repeat. So it goes.', 12, 0, 100, 0, 0, 25945, 'Nozdormu event 03');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54751, 1, 0, 'At last it has come to pass. The moment of my demise. The loop is closed. My future self will cause no more harm.', 12, 0, 100, 0, 0, 25944, 'Nozdormu event 02');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54751, 0, 0, 'Mortals! I cannot follow you any further - accept my blessing and use the Hourglass of Time to defeat Murozond!', 14, 0, 100, 0, 0, 25943, 'Nozdormu event 01');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54853, 8, 0, 'Serve Azshara, puppets, and rejoice.', 14, 0, 100, 0, 0, 26026, 'woe Azshara spell 3');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54853, 7, 1, 'If you intend to play the fool, you may as well look the part.', 14, 0, 100, 0, 0, 26025, 'woe Azshara spell 2');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54853, 7, 0, 'Dance for the Eternal Court.', 14, 0, 100, 0, 0, 26024, 'woe Azshara spell 1');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54853, 6, 2, 'Unfortunate, but deserved.', 14, 0, 100, 0, 0, 26023, 'woe Azshara slay 03');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54853, 6, 1, 'Your conduct was inexcusable.', 14, 0, 100, 0, 0, 26022, 'woe Azshara slay 02');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54853, 6, 0, 'I am unimpressed.', 14, 0, 100, 0, 0, 26021, 'woe Azshara slay 01');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54853, 5, 0, 'To prepare for a world of perfection, the imperfect must be swept away.', 14, 0, 100, 0, 0, 26020, 'woe Azshara reset 1');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54853, 4, 0, 'My noble Varo\'then, do return and dispose of this murderous band.', 12, 0, 100, 0, 0, 26019, 'woe Azshara event 6');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54853, 3, 0, 'Riders, to me!', 14, 0, 100, 0, 0, 26018, 'woe Azshara event 5');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54853, 2, 0, 'Enough! As much as I adore playing hostess, I have more pressing matters to attend to.', 14, 0, 100, 0, 0, 26017, 'woe Azshara event 4');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54853, 1, 2, 'Do not ask for mercy, after such an act.', 14, 0, 100, 0, 0, 26016, 'woe Azshara event 3');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54853, 1, 1, 'Such insolence. My temper grows short.', 14, 0, 100, 0, 0, 26015, 'woe Azshara event 2');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54853, 1, 0, 'Bold of you, to strike a queen. A lesser monarch might be enraged.', 14, 0, 100, 0, 0, 26014, 'woe Azshara event 1');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54853, 0, 0, 'Ah, welcome. You are here to join us in the coming celebration? No? A pity.', 14, 0, 100, 0, 0, 26013, 'woe Azshara aggro');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (54853, 9, 0, 'I have no time for such diversions. Keepers of Eternity, will you stand for your queen?', 14, 0, 100, 0, 0, 26027, 'woe Azshara spell 4');
