INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (55532, 15, 0, 'The sword has pierced his infernal armor! Strike him down!', 14, 0, 100, 0, 0, 26095, 'woe illidan mannoroth 14');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (55532, 12, 0, 'Yes...YES. I can feel the raw power of the Well of Eternity! It is time to end this charade!', 12, 0, 100, 0, 0, 26092, 'woe illidan mannoroth 9');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (55532, 13, 0, 'They are not where they appear to be! Strike in an area, it is the only way to uncover the real one!', 12, 0, 100, 0, 0, 26093, 'woe illidan mannoroth 10');
